-- Grammar Topics Import
-- Generated from practice-japanese data
-- Total topics: 392

-- Create grammar_topics table if not exists
CREATE TABLE IF NOT EXISTS grammar_topics (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  slug TEXT UNIQUE NOT NULL,
  title TEXT NOT NULL,
  source_url TEXT,
  category TEXT NOT NULL,
  jlpt_level TEXT,
  sections JSONB NOT NULL DEFAULT '[]',
  examples JSONB NOT NULL DEFAULT '[]',
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Create index for faster lookups
CREATE INDEX IF NOT EXISTS idx_grammar_topics_category ON grammar_topics(category);
CREATE INDEX IF NOT EXISTS idx_grammar_topics_jlpt ON grammar_topics(jlpt_level);
CREATE INDEX IF NOT EXISTS idx_grammar_topics_slug ON grammar_topics(slug);

-- Insert topics

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'i-adjectives',
  'I-adjectives (い形容詞)',
  'https://practice-japanese.com/docs/i-adjectives/',
  'adjective-conjugation',
  NULL,
  '[{"level":2,"heading":"What are I-adjectives?","content":"I-adjectives (い形容詞) are adjectives that end in い in their dictionary form. They conjugate like verbs and can stand alone as predicates.\n\n高い (takai) - expensive, tall, high 大きい (ookii) - big 美味しい (oishii) - delicious 楽しい (tanoshii) - fun, enjoyable"},{"level":2,"heading":"Characteristics","content":"","children":[{"level":3,"heading":"1. Always End in い","content":"高い - expensive 安い - cheap 新しい - new 古い - old"},{"level":3,"heading":"2. Conjugate Independently","content":"Unlike na-adjectives, they don''t need です:\n\n高い ✓ \"It''s expensive\" 高いです ✓ \"It''s expensive\" (polite)"},{"level":3,"heading":"3. Modify Nouns Directly","content":"高い車 - expensive car 大きい家 - big house 美味しい料理 - delicious food"}]},{"level":2,"heading":"Common I-adjectives","content":"","children":[{"level":3,"heading":"Size","content":"大きい (ookii) - big 小さい (chiisai) - small 長い (nagai) - long 短い (mijikai) - short 広い (hiroi) - wide, spacious 狭い (semai) - narrow, cramped 高い (takai) - tall, high 低い (hikui) - low, short (height)"},{"level":3,"heading":"Quality","content":"良い (yoi/ii) - good 悪い (warui) - bad 新しい (atarashii) - new 古い (furui) - old 正しい (tadashii) - correct 間違い (machigai) - wrong (noun, not adjective)"},{"level":3,"heading":"Price/Value","content":"高い (takai) - expensive 安い (yasui) - cheap"},{"level":3,"heading":"Taste","content":"美味しい (oishii) - delicious 不味い (mazui) - bad-tasting 甘い (amai) - sweet 辛い (karai) - spicy, salty 苦い (nigai) - bitter 酸っぱい (suppai) - sour"},{"level":3,"heading":"Temperature","content":"暑い (atsui) - hot (weather) 寒い (samui) - cold (weather) 熱い (atsui) - hot (objects) 冷たい (tsumetai) - cold (objects) 温かい (atatakai) - warm 涼しい (suzushii) - cool"},{"level":3,"heading":"Feelings","content":"嬉しい (ureshii) - happy, glad 楽しい (tanoshii) - fun, enjoyable 悲しい (kanashii) - sad 恐ろしい (osoroshii) - frightening 恥ずかしい (hazukashii) - embarrassing"},{"level":3,"heading":"Difficulty","content":"難しい (muzukashii) - difficult 易しい (yasashii) - easy 忙しい (isogashii) - busy"},{"level":3,"heading":"Physical State","content":"痛い (itai) - painful 眠い (nemui) - sleepy 重い (omoi) - heavy 軽い (karui) - light (weight)"},{"level":3,"heading":"Colors (Some)","content":"赤い (akai) - red 青い (aoi) - blue 白い (shiroi) - white 黒い (kuroi) - black 黄色い (kiiroi) - yellow 茶色い (chairoi) - brown"}]},{"level":2,"heading":"How to Use I-adjectives","content":"","children":[{"level":3,"heading":"1. Modifying Nouns","content":"Place before the noun:\n\n高い車 \"expensive car\" 美味しい食べ物 \"delicious food\" 大きい犬 \"big dog\" 新しい本 \"new book\""},{"level":3,"heading":"2. As Predicate (です Optional)","content":"この車は高い \"This car is expensive\" この車は高いです \"This car is expensive\" (polite)"},{"level":3,"heading":"3. Multiple Adjectives","content":"大きくて白い犬 \"a big, white dog\" 安くて美味しいレストラン \"a cheap and delicious restaurant\""}]},{"level":2,"heading":"Special I-adjective: いい/よい","content":"Good has two forms:\n\nいい (ii) - casual, more common よい (yoi) - formal, traditional\n\nBoth mean \"good\" but conjugate from よい:\n\nPresent: いい / よい Negative: よくない (NOT いくない) Past: よかった"},{"level":2,"heading":"Identifying I-adjectives","content":"","children":[{"level":3,"heading":"True I-adjectives","content":"Must end in い preceded by:\n\nあ, い, う, え, or お sound\n\n高い (taka-i) ✓ 大きい (ooki-i) ✓ 美味しい (oishi-i) ✓"},{"level":3,"heading":"Not I-adjectives","content":"Some words end in い but aren''t adjectives:\n\n嫌い (kirai) - dislike (na-adjective!) きれい (kirei) - pretty (na-adjective!) 有名 (yuumei) - famous (na-adjective!)\n\nRule: If it looks foreign or can''t conjugate as い-adjective, it''s probably な-adjective!"}]},{"level":2,"heading":"Sentence Patterns","content":"","children":[{"level":3,"heading":"Basic Statement","content":"この本は面白い \"This book is interesting\" 東京は大きい \"Tokyo is big\""},{"level":3,"heading":"With です (Polite)","content":"この本は面白いです \"This book is interesting\" 東京は大きいです \"Tokyo is big\""},{"level":3,"heading":"Question Form","content":"この本は面白いですか？ \"Is this book interesting?\" 日本語は難しいですか？ \"Is Japanese difficult?\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Adding な to I-adjectives","content":"Wrong: 高いな車 ❌ Right: 高い車 ✓ \"expensive car\" い-adjectives modify directly!"},{"level":3,"heading":"Mistake 2: Thinking All い Words are I-adjectives","content":"Wrong: きれい is i-adjective ❌ Right: きれい is na-adjective ✓ きれいな花 ✓ \"beautiful flower\""},{"level":3,"heading":"Mistake 3: Wrong Conjugation of いい","content":"Wrong: いくない ❌ Right: よくない ✓ \"not good\" Wrong: いかった ❌ Right: よかった ✓ \"was good\""},{"level":3,"heading":"Mistake 4: Using です Before Noun","content":"Wrong: 高いです車 ❌ Right: 高い車 ✓ Right: 車は高いです ✓"}]},{"level":2,"heading":"I-adjectives vs Na-adjectives","content":"| I-adjectives | Na-adjectives | | -------------------- | -------------------- | | End in い | Various endings | | 高い車 | 静かな車 | | Conjugate like verbs | Need な/だ | | Native Japanese | Often foreign origin |","children":[{"level":3,"heading":"Examples","content":"I-adjective: 高い車 \"expensive car\" 車は高い \"the car is expensive\" Na-adjective: 静かな車 \"quiet car\" 車は静かだ \"the car is quiet\""}]},{"level":2,"heading":"Describing Things","content":"","children":[{"level":3,"heading":"Objects","content":"大きい箱 \"big box\" 重い荷物 \"heavy luggage\" 新しいパソコン \"new computer\""},{"level":3,"heading":"Places","content":"広い部屋 \"spacious room\" 高い山 \"tall mountain\" 古い町 \"old town\""},{"level":3,"heading":"Food","content":"美味しい料理 \"delicious dish\" 辛い食べ物 \"spicy food\" 甘いケーキ \"sweet cake\""},{"level":3,"heading":"People (Appearance/State)","content":"若い人 \"young person\" 忙しい学生 \"busy student\" 眠い子供 \"sleepy child\""}]},{"level":2,"heading":"Conversation Examples","content":"","children":[{"level":3,"heading":"Example 1: Shopping","content":"A: この服はどうですか？ \"How is this clothing?\" B: 高いですね \"It''s expensive\" A: じゃあ、こっちは？ \"Then, how about this one?\" B: 安くていいですね \"It''s cheap and good\""},{"level":3,"heading":"Example 2: Food","content":"A: このレストランはどう？ \"How''s this restaurant?\" B: 美味しいよ \"It''s delicious\" A: 高い？ \"Is it expensive?\" B: いいえ、安いよ \"No, it''s cheap\""},{"level":3,"heading":"Example 3: Weather","content":"A: 今日は暑いね \"It''s hot today\" B: 本当に。昨日は涼しかったのに \"Really. Yesterday was cool though\""}]},{"level":2,"heading":"Key Patterns","content":"","children":[{"level":3,"heading":"Asking Opinions","content":"どうですか？ \"How is it?\" 美味しいですか？ \"Is it delicious?\" 難しいですか？ \"Is it difficult?\""},{"level":3,"heading":"Describing","content":"とても大きい \"very big\" ちょっと高い \"a little expensive\" あまり面白くない \"not very interesting\""}]},{"level":2,"heading":"Key Takeaways","content":"✓ I-adjectives end in い ✓ Modify nouns directly (no な) ✓ Conjugate independently ✓ Can be predicate without です ✓ いい conjugates from よい ✓ Not all い words are i-adjectives ✓ Native Japanese words ✓ Essential for descriptions\n\nMaster i-adjectives - they''re everywhere in Japanese!\n\nNext: Na-adjectives (な形容詞)\n\nRelated:\n\nI-adjective Conjugation Adjective Comparison Present Tense","children":[]}]'::jsonb,
  '["あ, い, う, え, or お sound","I-adjective Conjugation","Adjective Comparison","Present Tense"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'particle-wa',
  'は (wa) - Topic Marker',
  'https://practice-japanese.com/docs/particle-wa/',
  'particles',
  NULL,
  '[{"level":2,"heading":"Core Function","content":"は marks the topic of a sentence - what you''re talking about.\n\nPronunciation: Written は but pronounced \"wa\"\n\n私は学生です watashi wa gakusei desu \"As for me, (I''m) a student\""},{"level":2,"heading":"Main Uses","content":"","children":[{"level":3,"heading":"1. Introducing Topics","content":"Set the context for your statement:\n\nこれは本です - \"This is a book\" 東京は大きいです - \"Tokyo is big\" 猫は好きです - \"I like cats\" (general statement)"},{"level":3,"heading":"2. Contrasting","content":"Implies \"but not others\":\n\n寿司は好きですが、刺身は嫌いです \"I like sushi, but I dislike sashimi\" ビールは飲みますが、ワインは飲みません \"I drink beer, but I don''t drink wine\"\n\nEven without explicit contrast:\n\nビールは飲みます \"I drink beer (but maybe not other things)\""},{"level":3,"heading":"3. General Statements","content":"Known or general information:\n\n犬は動物です - \"Dogs are animals\" 水は大切です - \"Water is important\""},{"level":3,"heading":"4. Existing Topics","content":"When the topic is already established:\n\nA: 田中さんはどこですか？ \"Where is Tanaka?\" B: 田中さんは図書館です \"Tanaka is at the library\""}]},{"level":2,"heading":"は vs が","content":"Key differences:\n\n| は (wa) | が (ga) | | ------------------ | ------------------ | | Sets topic/context | Identifies subject | | Known information | New information | | General statements | Specific emphasis | | Can show contrast | No contrast |\n\n私は学生です \"I am a student\" (general statement) 私が学生です \"I am the student\" (emphasis: me specifically)\n\nSee: は vs が Explained"},{"level":2,"heading":"Position","content":"Usually comes after the first or second word:\n\n私は毎日勉強します \"I study every day\" 今日は雨です \"Today it''s raining\""},{"level":2,"heading":"With Other Particles","content":"は can replace が, を, but not に, で, へ:","children":[{"level":3,"heading":"Replaces が","content":"雨が降る → 雨は降る \"Rain falls\" → \"As for rain, it falls\""},{"level":3,"heading":"Replaces を","content":"寿司を食べる → 寿司は食べる \"Eat sushi\" → \"Sushi, I eat (but not other things)\""},{"level":3,"heading":"Combines with に/で/へ","content":"東京には行きます (に + は = には) \"To Tokyo, I''ll go\" 図書館では勉強しません (で + は = では) \"At the library, I don''t study\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Topic + Description","content":"象は鼻が長い \"Elephants have long trunks\" (As for elephants, noses are long)"},{"level":3,"heading":"Question-Answer","content":"Q: 何を飲みますか？ \"What will you drink?\" A: 水を飲みます \"I''ll drink water\" (answering what, not topic) But if asked about topic: Q: あなたは何を飲みますか？ A: 私は水を飲みます"}]},{"level":2,"heading":"Double は","content":"Can have two は for double contrast:\n\n私は魚は好きですが、肉は嫌いです \"As for me, fish I like, but meat I dislike\""},{"level":2,"heading":"Omitting は","content":"In casual speech, は often dropped when obvious:\n\nFormal: 私は学生です Casual: 学生です \"(I''m) a student\""},{"level":2,"heading":"Key Takeaways","content":"✓ Marks the topic/context ✓ Written は, pronounced \"wa\" ✓ Creates contrast (implicit or explicit) ✓ Use for general statements ✓ Different from が (subject marker)\n\nRelated:\n\nが (ga) - Subject Marker は vs が Explained Topic vs Subject","children":[]}]'::jsonb,
  '["が (ga) - Subject Marker","は vs が Explained","Topic vs Subject"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'academic-writing',
  'Academic Writing',
  'https://practice-japanese.com/docs/academic-writing/',
  'other',
  NULL,
  '[{"level":2,"heading":"Academic Writing","content":"Academic writing in Japanese (学術論文) follows strict conventions including である style, logical structure, specialized vocabulary, and objective tone. It''s essential for research papers, theses, and scholarly publications.\n\nKey features: - である style exclusively - Logical structure (序論・本論・結論) - Hedging language - Citations and references - Objective stance Scholarly tone Rigorous methodology"},{"level":2,"heading":"である Style Required","content":"","children":[{"level":3,"heading":"Formal assertion","content":"この研究は重要である \"This research is important\" 結果は明らかである \"The results are clear\" 仮説は正しいと考えられる \"The hypothesis is thought to be correct\" NOT です・ます Always である Academic standard"}]},{"level":2,"heading":"Paper Structure","content":"","children":[{"level":3,"heading":"Standard sections","content":"序論 (Introduction) - 研究背景 \"research background\" - 目的 \"objective\" - 先行研究 \"previous research\" 本論 (Main body) - 方法 \"methodology\" - 結果 \"results\" - 考察 \"discussion\" 結論 (Conclusion) - まとめ \"summary\" - 今後の課題 \"future issues\" Standard organization Clear hierarchy Logical flow"}]},{"level":2,"heading":"Hedging Language","content":"","children":[{"level":3,"heading":"Academic caution","content":"〜と考えられる \"is thought to be\" 結果は有意であると考えられる 〜と思われる \"seems to be\" 問題であると思われる 〜可能性がある \"there is a possibility\" 影響する可能性がある 〜傾向がある \"tends to\" 増加する傾向がある Avoiding absolute claims Scholarly caution Nuanced assertion"}]},{"level":2,"heading":"Stating Research Purpose","content":"","children":[{"level":3,"heading":"Objective expression","content":"本研究の目的は〜である \"The objective of this research is...\" 本稿では〜を明らかにする \"This paper clarifies...\" 本論文は〜を論じる \"This thesis discusses...\" 〜を検討する \"examine\" 〜を分析する \"analyze\" 〜を考察する \"consider\" Clear purpose statement Academic framing"}]},{"level":2,"heading":"Methodology Description","content":"","children":[{"level":3,"heading":"Research methods","content":"方法は以下の通りである \"The method is as follows\" 実験は〜のように行った \"The experiment was conducted as follows\" データは〜によって収集された \"Data was collected by...\" 〜を用いて \"using, by means of\" 統計を用いて分析 \"analyzed using statistics\" Precise description Reproducibility Scientific rigor"}]},{"level":2,"heading":"Results Presentation","content":"","children":[{"level":3,"heading":"Findings","content":"結果は表1に示す \"Results are shown in Table 1\" 図2に見られるように \"As can be seen in Figure 2\" 有意な差が認められた \"A significant difference was observed\" 〜という結果が得られた \"The result... was obtained\" Objective reporting Visual reference Statistical precision"}]},{"level":2,"heading":"Discussion Section","content":"","children":[{"level":3,"heading":"Interpretation","content":"この結果から〜が示唆される \"From these results, ... is suggested\" 先行研究と一致する \"Consistent with previous research\" 〜と解釈できる \"Can be interpreted as...\" 考えられる理由は〜である \"A possible reason is...\" Careful interpretation Connecting to literature Logical reasoning"}]},{"level":2,"heading":"Citation Phrases","content":"","children":[{"level":3,"heading":"Referencing previous work","content":"〜によると \"according to\" 田中(2020)によると 〜は〜と述べている \"... states that...\" 〜が指摘するように \"As ... points out\" 〜の研究では \"In ...''s research\" Proper attribution Academic integrity Building on literature"}]},{"level":2,"heading":"Listing and Enumeration","content":"","children":[{"level":3,"heading":"Organizing points","content":"第一に \"firstly\" 第二に \"secondly\" 第三に \"thirdly\" まず \"first\" 次に \"next\" 最後に \"finally\" 一つ目は \"the first is\" 二つ目は \"the second is\" Clear organization Logical progression Reader guidance"}]},{"level":2,"heading":"Comparison and Contrast","content":"","children":[{"level":3,"heading":"Analytical language","content":"〜に対して \"in contrast to\" A国に対してB国では 一方で \"on the other hand\" 〜である一方で、〜でもある 〜と異なり \"unlike\" 従来の方法と異なり 比較すると \"comparing\" 両者を比較すると Analytical thinking Balanced discussion Critical evaluation"}]},{"level":2,"heading":"Causation","content":"","children":[{"level":3,"heading":"Showing relationships","content":"〜により \"due to, by\" この要因により 〜によって \"through, by means of\" 実験によって明らかになった 〜ため \"because\" 重要であるため 〜結果 \"as a result\" その結果、〜となった Logical connections Cause-effect relationships Clear reasoning"}]},{"level":2,"heading":"Stating Limitations","content":"","children":[{"level":3,"heading":"Acknowledging constraints","content":"本研究の限界は〜である \"The limitation of this research is...\" サンプル数が少ないという制約がある \"There''s a constraint that sample size is small\" 今後の課題として〜が挙げられる \"As a future issue, ... can be mentioned\" Scholarly honesty Self-critical Future directions"}]},{"level":2,"heading":"Conclusions","content":"","children":[{"level":3,"heading":"Summarizing findings","content":"以上のことから〜と結論づけられる \"From the above, it can be concluded that...\" 本研究では〜が明らかになった \"In this research, ... became clear\" 〜ということが示された \"It was shown that...\" Final assertions Key takeaways Research contribution"}]},{"level":2,"heading":"Technical Vocabulary","content":"","children":[{"level":3,"heading":"Discipline-specific terms","content":"仮説 \"hypothesis\" 変数 \"variable\" 相関 \"correlation\" 有意性 \"significance\" 妥当性 \"validity\" 信頼性 \"reliability\" 標本 \"sample\" 母集団 \"population\" Field-specific language Precise terminology Professional vocabulary"}]},{"level":2,"heading":"Passive Voice","content":"","children":[{"level":3,"heading":"Objective stance","content":"実験が行われた \"Experiment was conducted\" データが収集された \"Data was collected\" 結論が導き出された \"Conclusion was drawn\" Emphasis on action, not agent Scientific objectivity Standard academic style"}]},{"level":2,"heading":"Avoiding First Person","content":"","children":[{"level":3,"heading":"Objective writing","content":"Avoid: 私は〜と思う \"I think\" Prefer: 〜と考えられる \"it is thought\" 〜と言える \"it can be said\" 〜が示唆される \"it is suggested\" Impersonal stance Scholarly objectivity Professional distance"}]},{"level":2,"heading":"Acknowledging Sources","content":"","children":[{"level":3,"heading":"Previous research","content":"先行研究において〜が報告されている \"In previous research, ... has been reported\" 既存の研究では〜とされている \"In existing research, it is said that...\" 従来〜と考えられてきた \"Traditionally it has been thought that...\" Literature review Building on foundations Academic conversation"}]},{"level":2,"heading":"Presenting Arguments","content":"","children":[{"level":3,"heading":"Thesis development","content":"〜という点で重要である \"Is important in that...\" 〜という意味で意義がある \"Has significance in the sense that...\" 〜から〜が導かれる \"From ..., ... is derived\" Logical argumentation Clear reasoning Scholarly discourse"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Frequently used expressions","content":"〜ことが分かる \"it is understood that\" 〜ことが明らかである \"it is clear that\" 〜ことができる \"it is possible to\" 〜ことが重要である \"it is important that\" 〜と言える \"it can be said that\" 〜と考えられる \"it is thought that\" Standard academic phrases Building blocks Professional writing"}]},{"level":2,"heading":"Footnotes and References","content":"","children":[{"level":3,"heading":"Citation format","content":"参考文献 \"References\" 注 \"Notes\" 引用文献 \"Cited works\" 田中太郎(2020)『研究の方法』東京:出版社 \"Tanaka Taro (2020) ''Research Methods'' Tokyo: Publisher\" Proper citation Academic honesty Standard format"}]},{"level":2,"heading":"Common Mistakes","content":"❌ Using です・ます Academic writing requires である ✓ である style ✓ 〜である、〜だ ❌ Personal opinions without hedging ✗ これは正しい ✓ Hedged statements ✓ これは正しいと考えられる ❌ Casual vocabulary Avoid colloquial language ✓ Formal academic vocabulary ✓ Technical precision ❌ Unclear logic Must show clear reasoning ✓ Explicit connections ✓ Logical flow markers Formal register essential Objective tone required Precise language needed"},{"level":2,"heading":"Practical Example","content":"","children":[{"level":3,"heading":"Abstract sample","content":"要旨 本研究の目的は、日本語学習における動機づけの要因を明らかにすることである。先行研究では内発的動機と外発的動機が重要であるとされているが、両者の関係については十分に検討されていない。 本研究では200名の日本語学習者を対象に質問紙調査を実施した。その結果、内発的動機が高い学習者は学習時間が長い傾向があることが明らかになった。 この結果から、日本語教育において内発的動機を高めることが重要であると示唆される。今後の課題として、長期的な追跡調査が必要である。 \"Abstract The objective of this research is to clarify motivational factors in Japanese language learning. Previous research suggests intrinsic and extrinsic motivation are important, but the relationship between the two has not been sufficiently examined. In this research, a questionnaire survey was conducted with 200 Japanese learners. As a result, it became clear that learners with high intrinsic motivation tend to study for longer periods. From these results, it is suggested that increasing intrinsic motivation is important in Japanese language education. As a future issue, long-term follow-up research is necessary.\" Standard structure である style throughout Hedging language Clear purpose Methodology stated Results presented Implications discussed Future directions Professional tone"}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'action-direction',
  'Direction of Actions',
  'https://practice-japanese.com/docs/action-direction/',
  'other',
  NULL,
  '[{"level":2,"heading":"Overview: Three Directional Patterns","content":"When you do an action FOR someone or someone does an action FOR you, Japanese uses three patterns based on direction:\n\n| Pattern | Direction | Meaning | | ---------- | ----------- | --------------------- | | 〜てあげる | I → Others | I do for them | | 〜てくれる | Others → Me | They do for me | | 〜てもらう | Others → Me | I have them do for me |\n\n手伝ってあげた \"I helped (them)\" 手伝ってくれた \"(They) helped me\" 手伝ってもらった \"I had (them) help me\""},{"level":2,"heading":"〜てあげる - Doing FOR Others","content":"","children":[{"level":3,"heading":"Pattern: Verb-て + あげる/やる/さしあげる","content":"I do something FOR someone else:\n\n友達を手伝ってあげた \"I helped my friend\" 弟に本を読んであげる \"I''ll read a book to my younger brother\" 先生に説明してさしあげました \"I explained to the teacher\" (honorific)"},{"level":3,"heading":"Structure","content":"Doer + は + Recipient + に/を + Verb-て + あげる\n\n私は友達に英語を教えてあげた \"I taught English to my friend\" 母が妹に服を作ってあげた \"My mother made clothes for my younger sister\""},{"level":3,"heading":"Politeness Variations","content":"手伝ってあげる (casual) 手伝ってあげます (polite) 手伝ってやる (very casual, can be rough) 手伝ってさしあげる (honorific)"},{"level":3,"heading":"Warning: Can Sound Condescending","content":"Using 〜てあげる too much can sound like bragging:\n\nSounds pompous: 教えてあげたんだよ ⚠️ Better: 教えたよ ✓ \"I taught (them)\"\n\nUse it naturally, not to show off your kindness!"}]},{"level":2,"heading":"〜てくれる - Others Doing FOR Me","content":"","children":[{"level":3,"heading":"Pattern: Verb-て + くれる/くださる","content":"Someone does something FOR ME:\n\n友達が手伝ってくれた \"My friend helped me\" 母が料理を作ってくれた \"My mother cooked for me\" 先生が教えてくださいました \"My teacher taught me\" (honorific)"},{"level":3,"heading":"Structure","content":"Doer + が + (me/us) + に + Verb-て + くれる\n\n友達が(私に)英語を教えてくれた \"My friend taught me English\" 先生が推薦状を書いてくださった \"My teacher wrote a recommendation letter for me\""},{"level":3,"heading":"Often Used for Gratitude","content":"来てくれてありがとう \"Thanks for coming\" 手伝ってくれて助かった \"Your help really saved me\" 覚えていてくれて嬉しい \"I''m happy you remembered\""},{"level":3,"heading":"Politeness Variations","content":"手伝ってくれる (casual) 手伝ってくれます (polite) 手伝ってください (imperative - please) 手伝ってくださる (honorific)"}]},{"level":2,"heading":"〜てもらう - Having Someone Do FOR Me","content":"","children":[{"level":3,"heading":"Pattern: Verb-て + もらう/いただく","content":"I have/get someone to do something FOR ME:\n\n友達に手伝ってもらった \"I had my friend help me\" 先生に説明していただきました \"I had my teacher explain\" (humble) 母に作ってもらった \"I had my mother make it\""},{"level":3,"heading":"Structure","content":"Receiver + は + Doer + に + Verb-て + もらう\n\n私は友達に英語を教えてもらった \"I had my friend teach me English\" 弟は姉に宿題を手伝ってもらった \"My younger brother had our older sister help with homework\""},{"level":3,"heading":"Used for Requests (Potential Form)","content":"手伝ってもらえますか？ \"Can you help me?\" (Can I have you help me?) 説明してもらえる？ \"Can you explain?\" (casual) 教えていただけますか？ \"Could you teach me?\" (humble)"},{"level":3,"heading":"Politeness Variations","content":"手伝ってもらう (casual) 手伝ってもらいます (polite) 手伝っていただく (humble) 手伝っていただきます (polite humble)"}]},{"level":2,"heading":"Comparing the Three","content":"","children":[{"level":3,"heading":"Same situation, three perspectives:","content":"I help friend (〜てあげる):\n\n私は友達を手伝ってあげた \"I helped my friend\" Focus: My favor to them\n\nFriend helps me (〜てくれる):\n\n友達が私を手伝ってくれた \"My friend helped me\" Focus: Their favor to me (gratitude)\n\nI have friend help (〜てもらう):\n\n私は友達に手伝ってもらった \"I had my friend help me\" Focus: I received help (my benefit)"}]},{"level":2,"heading":"Choosing the Right Pattern","content":"","children":[{"level":3,"heading":"Use 〜てあげる when:","content":"You''re the one doing the favor Talking about helping others Third person helps another\n\n私が教えてあげた \"I taught (them)\" 母が作ってあげた \"My mother made it (for someone)\""},{"level":3,"heading":"Use 〜てくれる when:","content":"Someone does something for you Expressing gratitude Appreciating someone''s action\n\n友達が教えてくれた \"My friend taught me\" ありがとう、来てくれて \"Thanks for coming\""},{"level":3,"heading":"Use 〜てもらう when:","content":"You receive a favor You request someone to do something Focus on your benefit\n\n友達に教えてもらった \"I had my friend teach me\" 手伝ってもらえますか？ \"Can you help me?\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Helping","content":"A: 昨日手伝ってくれてありがとう \"Thanks for helping yesterday\" B: いいよ、また困ったら言ってね \"No problem, tell me if you need help again\" A: 今度何か手伝ってあげるよ \"I''ll help you with something next time\""},{"level":3,"heading":"Example 2: Request","content":"A: ちょっと手伝ってもらえる？ \"Can you help me a bit?\" B: いいよ、何？ \"Sure, what is it?\" A: この箱を運ぶの手伝って \"Help me carry this box\""},{"level":3,"heading":"Example 3: Gratitude","content":"A: 先生が推薦状を書いてくださいました \"My teacher wrote a recommendation letter for me\" B: よかったですね \"That''s great\" A: 本当に感謝しています \"I''m truly grateful\""}]},{"level":2,"heading":"Making Requests","content":"","children":[{"level":3,"heading":"Casual Requests","content":"手伝ってくれる？ \"Will you help me?\" (using くれる) 手伝ってもらえる？ \"Can I get you to help?\" (using もらう)"},{"level":3,"heading":"Polite Requests","content":"手伝ってくれますか？ \"Will you help me?\" 手伝ってもらえますか？ \"Could you help me?\" 手伝っていただけますか？ \"Could you help me?\" (humble)"},{"level":3,"heading":"Very Polite Requests","content":"手伝ってくださいますか？ \"Would you help me?\" 手伝っていただけますでしょうか？ \"May I ask you to help?\""}]},{"level":2,"heading":"With Different Verbs","content":"","children":[{"level":3,"heading":"Teaching","content":"教えてあげる - I''ll teach (them) 教えてくれる - (They) teach me 教えてもらう - I have (them) teach me"},{"level":3,"heading":"Making/Cooking","content":"作ってあげる - I''ll make (for them) 作ってくれる - (They) make for me 作ってもらう - I have (them) make for me"},{"level":3,"heading":"Explaining","content":"説明してあげる - I''ll explain (to them) 説明してくれる - (They) explain to me 説明してもらう - I have (them) explain to me"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Wrong direction with あげる","content":"Wrong: 友達が私に教えてあげた ❌ Right: 友達が私に教えてくれた ✓ \"My friend taught me\" あげる is for giving OUT, not receiving!"},{"level":3,"heading":"Mistake 2: Overusing てあげる","content":"Sounds pompous: いろいろ教えてあげたんだよ ⚠️ More humble: いろいろ教えたよ ✓ \"I taught various things\""},{"level":3,"heading":"Mistake 3: Wrong particle","content":"Wrong: 友達を手伝ってもらった ❌ Right: 友達に手伝ってもらった ✓ \"I had my friend help me\" もらう uses に for the doer!"},{"level":3,"heading":"Mistake 4: Confusing くれる and もらう","content":"Both correct but different nuance: 手伝ってくれた \"(They) helped me\" (gratitude focus) 手伝ってもらった \"I had (them) help me\" (benefit focus)"}]},{"level":2,"heading":"Nuance Differences","content":"","children":[{"level":3,"heading":"くれる vs もらう","content":"Both mean someone did something for you:\n\n〜てくれる - Emphasizes their kindness:\n\n友達が教えてくれた \"My friend (kindly) taught me\" Feels grateful, appreciative\n\n〜てもらう - Emphasizes your benefit:\n\n友達に教えてもらった \"I had my friend teach me\" More neutral, factual"}]},{"level":2,"heading":"Politeness Hierarchy","content":"","children":[{"level":3,"heading":"Casual","content":"〜てあげる / 〜てやる 〜てくれる 〜てもらう"},{"level":3,"heading":"Polite","content":"〜てあげます 〜てくれます 〜てもらいます"},{"level":3,"heading":"Honorific/Humble","content":"〜てさしあげる (giving up) 〜てくださる (receiving from above) 〜ていただく (receiving from above)"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"友達を手伝ってあげた \"I helped my friend\" 母が料理を作ってくれた \"My mother cooked for me\" 先生に説明していただいた \"I had my teacher explain\""},{"level":3,"heading":"Intermediate","content":"弟に宿題を教えてあげました \"I taught my younger brother his homework\" 友達が駅まで送ってくれた \"My friend took me to the station\" 先生に推薦状を書いてもらった \"I had my teacher write a recommendation\""},{"level":3,"heading":"Advanced","content":"困っている友達を助けてあげたい \"I want to help my friend who''s in trouble\" みんなが応援してくれたおかげで成功できた \"I succeeded thanks to everyone''s support\" 専門家に詳しく説明していただけますか？ \"Could I have the expert explain in detail?\""}]},{"level":2,"heading":"Related Grammar Points","content":"Giving: あげる/やる/さしあげる - Giving verbs Receiving: もらう/いただく - Receiving verbs Favor: くれる/くださる - Someone gives to me Perspective in Japanese - Point of view Benefactive expressions - Doing favors Request patterns - Asking politely","children":[]}]'::jsonb,
  '["You''re the one doing the favor","Talking about helping others","Third person helps another","Someone does something for you","Expressing gratitude","Appreciating someone''s action","You receive a favor","You request someone to do something","Focus on your benefit","Giving: あげる/やる/さしあげる - Giving verbs","Receiving: もらう/いただく - Receiving verbs","Favor: くれる/くださる - Someone gives to me","Perspective in Japanese - Point of view","Benefactive expressions - Doing favors","Request patterns - Asking politely"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'adjective-adverb',
  'Adjective Adverb Forms',
  'https://practice-japanese.com/docs/adjective-adverb/',
  'verb-conjugation',
  NULL,
  '[{"level":2,"heading":"Adverb Forms Overview","content":"Japanese adjectives can become adverbs to modify verbs. The formation differs for i-adjectives and na-adjectives.\n\nI-adjective: 速い → 速く (quickly) Na-adjective: 静か → 静かに (quietly)"},{"level":2,"heading":"I-adjective Adverb Form","content":"","children":[{"level":3,"heading":"Formation","content":"Change い to く\n\n速い (hayai) → 速く (hayaku) - quickly, fast 高い (takai) → 高く (takaku) - highly, expensively 美しい (utsukushii) → 美しく (utsukushiku) - beautifully 大きい (ookii) → 大きく (ookiku) - greatly, largely"},{"level":3,"heading":"Common I-adjective Adverbs","content":"早い → 早く (hayaku) - early 遅い → 遅く (osoku) - late 近い → 近く (chikaku) - nearby, closely 遠い → 遠く (tooku) - far 強い → 強く (tsuyoku) - strongly 弱い → 弱く (yowaku) - weakly 多い → 多く (ooku) - many, much 少ない → 少なく (sukunaku) - few, little"}]},{"level":2,"heading":"Na-adjective Adverb Form","content":"","children":[{"level":3,"heading":"Formation","content":"Add に to the na-adjective\n\n静か (shizuka) → 静かに (shizuka ni) - quietly きれい (kirei) → きれいに (kirei ni) - beautifully, cleanly 丁寧 (teinei) → 丁寧に (teinei ni) - politely, carefully 真面目 (majime) → 真面目に (majime ni) - seriously 簡単 (kantan) → 簡単に (kantan ni) - simply, easily"},{"level":3,"heading":"Common Na-adjective Adverbs","content":"上手 → 上手に (jouzu ni) - skillfully 下手 → 下手に (heta ni) - poorly, unskillfully 親切 → 親切に (shinsetsu ni) - kindly 元気 → 元気に (genki ni) - energetically, vigorously 便利 → 便利に (benri ni) - conveniently 安全 → 安全に (anzen ni) - safely"}]},{"level":2,"heading":"Usage Patterns","content":"","children":[{"level":3,"heading":"Modifying Verbs","content":"速く走る \"run fast\" 静かに話す \"speak quietly\" きれいに書く \"write neatly\" 丁寧に説明する \"explain politely/carefully\""},{"level":3,"heading":"Modifying Entire Sentences","content":"早く帰ります \"I''ll go home early\" 大きく育つ \"grow big/greatly\" 真面目に勉強する \"study seriously\""}]},{"level":2,"heading":"Special Case: いい/よい","content":"いい / よい → よく (well) よく分かる \"understand well\" よく寝る \"sleep well\" よく食べる \"eat well\""},{"level":2,"heading":"Common Adverb Patterns","content":"","children":[{"level":3,"heading":"Speed/Manner","content":"速く (hayaku) - fast, quickly 遅く (osoku) - slowly, late ゆっくり (yukkuri) - slowly, leisurely 急いで (isoide) - hurriedly"},{"level":3,"heading":"Degree","content":"高く (takaku) - highly 低く (hikuku) - lowly 大きく (ookiku) - greatly 小さく (chiisaku) - slightly 多く (ooku) - much, many 少なく (sukunaku) - few, little"},{"level":3,"heading":"Quality","content":"美しく (utsukushiku) - beautifully きれいに (kirei ni) - beautifully, cleanly 上手に (jouzu ni) - skillfully 下手に (heta ni) - poorly"},{"level":3,"heading":"Manner","content":"静かに (shizuka ni) - quietly にぎやかに (nigiyaka ni) - lively 丁寧に (teinei ni) - politely, carefully 親切に (shinsetsu ni) - kindly"}]},{"level":2,"heading":"Conversation Examples","content":"","children":[{"level":3,"heading":"Example 1: Instructions","content":"A: どう書けばいいですか？ \"How should I write it?\" B: もっと大きく書いてください \"Please write it bigger\" A: こうですか？ \"Like this?\" B: はい、そしてきれいに書いてください \"Yes, and please write it neatly\""},{"level":3,"heading":"Example 2: Daily Actions","content":"A: 今朝早く起きた？ \"Did you wake up early this morning?\" B: うん、6時に起きた \"Yeah, I woke up at 6\" A: すごい！ \"Amazing!\" B: でも、ゆっくり朝ご飯を食べた \"But I ate breakfast slowly\""},{"level":3,"heading":"Example 3: Requests","content":"A: ちょっと静かに話してください \"Please speak a little quietly\" B: すみません \"Sorry\" A: あと、もっとゆっくり話してください \"Also, please speak more slowly\" B: 分かりました \"Understood\""}]},{"level":2,"heading":"Using with する (to do/make)","content":"Many adverbs work with する:\n\n大きくする - make big, enlarge 小さくする - make small, reduce きれいにする - make clean, tidy up 静かにする - make quiet, be quiet 元気にする - cheer up, invigorate","children":[{"level":3,"heading":"Examples","content":"部屋をきれいにする \"clean/tidy up the room\" 音を小さくしてください \"Please turn down the volume\" みんなを元気にする \"cheer everyone up\""}]},{"level":2,"heading":"Using with なる (to become)","content":"Adverbs work with なる to express change:\n\n大きくなる - become big, grow 小さくなる - become small, shrink きれいになる - become clean/beautiful 静かになる - become quiet 元気になる - become healthy/energetic","children":[{"level":3,"heading":"Examples","content":"子供が大きくなった \"The child grew up\" 部屋がきれいになりました \"The room became clean\" 病気が治って元気になった \"I recovered from illness and became healthy\""}]},{"level":2,"heading":"Comparing Adjectives and Adverbs","content":"| Adjective | Adverb | Usage | | ---------- | ------------ | ------------------------------------ | | 速い車 | 速く走る | fast car / run fast | | 高い山 | 高く飛ぶ | tall mountain / fly high | | 静かな部屋 | 静かに話す | quiet room / speak quietly | | きれいな花 | きれいに咲く | beautiful flower / bloom beautifully |","children":[{"level":3,"heading":"Side by Side","content":"Adjective modifying noun: 速い車 \"fast car\" 静かな部屋 \"quiet room\" Adverb modifying verb: 速く走る \"run fast\" 静かに話す \"speak quietly\""}]},{"level":2,"heading":"Common Adverb Expressions","content":"","children":[{"level":3,"heading":"Daily Activities","content":"早く起きる - wake up early 遅く寝る - sleep late よく食べる - eat well ゆっくり歩く - walk slowly 速く走る - run fast"},{"level":3,"heading":"Speaking/Communication","content":"静かに話す - speak quietly 大きく話す - speak loudly ゆっくり話す - speak slowly 丁寧に説明する - explain politely 親切に教える - teach kindly"},{"level":3,"heading":"Writing/Creating","content":"きれいに書く - write neatly 大きく書く - write big 小さく書く - write small 美しく描く - draw beautifully"}]},{"level":2,"heading":"Degree Modifiers with Adverbs","content":"","children":[{"level":3,"heading":"もっと (more)","content":"もっと速く走って \"Run faster\" もっと静かにしてください \"Please be quieter\" もっときれいに書いて \"Write more neatly\""},{"level":3,"heading":"とても / すごく (very)","content":"とても速く走る \"run very fast\" すごく静かに話す \"speak very quietly\""},{"level":3,"heading":"あまり～ない (not very)","content":"あまり速く走らない \"don''t run very fast\" あまり大きく書かない \"don''t write very big\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using Adjective Instead of Adverb","content":"Wrong: 速い走る ❌ Right: 速く走る ✓ \"run fast\" Wrong: 静かな話す ❌ Right: 静かに話す ✓ \"speak quietly\""},{"level":3,"heading":"Mistake 2: Wrong Adverb Form","content":"Wrong: 静かく話す ❌ Right: 静かに話す ✓ Wrong: きれいく書く ❌ Right: きれいに書く ✓"},{"level":3,"heading":"Mistake 3: Using に with I-adjectives","content":"Wrong: 速いに走る ❌ Right: 速く走る ✓ Wrong: 大きいに書く ❌ Right: 大きく書く ✓"},{"level":3,"heading":"Mistake 4: Using く with Na-adjectives","content":"Wrong: 静かく話す ❌ Right: 静かに話す ✓ Wrong: きれいく書く ❌ Right: きれいに書く ✓"}]},{"level":2,"heading":"Multiple Adverbs","content":"早く静かに起きる \"wake up early and quietly\" きれいに丁寧に書く \"write neatly and carefully\" ゆっくり大きく話す \"speak slowly and loudly\""},{"level":2,"heading":"Negative Adverb Forms","content":"","children":[{"level":3,"heading":"I-adjectives","content":"高く → 高くなく (not highly) 速く → 速くなく (not fast) 高くなく飛ぶ \"fly not high\" 速くなく走る \"run not fast\""},{"level":3,"heading":"Na-adjectives","content":"静かに → 静かにせず / 静かでなく きれいに → きれいにせず / きれいでなく"}]},{"level":2,"heading":"Idiomatic Adverb Expressions","content":"","children":[{"level":3,"heading":"よく (well, often)","content":"よく分かる \"understand well\" よく来る \"come often\" よく寝る \"sleep well\""},{"level":3,"heading":"早く (early, quickly)","content":"早く来て \"Come quickly\" 早く帰る \"go home early\""},{"level":3,"heading":"遠く (far)","content":"遠くから来る \"come from far away\" 遠くに見える \"can see far away\""}]},{"level":2,"heading":"Practice Patterns","content":"","children":[{"level":3,"heading":"Transformation Practice","content":"速い → 速く → 速く走る 高い → 高く → 高く飛ぶ 美しい → 美しく → 美しく咲く 静か → 静かに → 静かに話す きれい → きれいに → きれいに書く 丁寧 → 丁寧に → 丁寧に説明する"}]},{"level":2,"heading":"Using in Sentences","content":"","children":[{"level":3,"heading":"Commands","content":"もっと大きく書いて \"Write bigger\" 静かにしてください \"Please be quiet\" ゆっくり話してください \"Please speak slowly\""},{"level":3,"heading":"Descriptions","content":"彼は速く走る \"He runs fast\" 彼女はきれいに歌う \"She sings beautifully\" 先生は丁寧に教える \"The teacher teaches carefully\""},{"level":3,"heading":"Changes","content":"だんだん大きくなる \"gradually become big\" 急に静かになった \"suddenly became quiet\" 少しずつきれいになる \"gradually become clean/beautiful\""}]},{"level":2,"heading":"Key Takeaways","content":"✓ I-adjectives: い → く ✓ Na-adjectives: add に ✓ いい → よく (irregular) ✓ Modify verbs, not nouns ✓ Work with する/なる ✓ Can be negated ✓ Stack multiple adverbs ✓ Essential for natural expression\n\nMaster adverb forms - make your Japanese more dynamic!\n\nNext: Adjective Comparisons (より)\n\nRelated:\n\nI-adjective Conjugation Na-adjective Conjugation Adjective Present Tense","children":[]}]'::jsonb,
  '["I-adjective Conjugation","Na-adjective Conjugation","Adjective Present Tense"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'adjective-basics',
  'Japanese Adjective Basics',
  'https://practice-japanese.com/docs/adjective-basics/',
  'adjective-conjugation',
  'N5',
  '[{"level":2,"heading":"Two Types of Adjectives","content":"Unlike English, Japanese has two completely different types of adjectives that conjugate differently:\n\nI-adjectives (い形容詞) - end in い, conjugate like verbs Na-adjectives (な形容詞) - need な before nouns, conjugate with だ"},{"level":2,"heading":"I-Adjectives (い形容詞)","content":"","children":[{"level":3,"heading":"Identification","content":"Always end in い in dictionary form:\n\n大きい (ookii) - big 小さい (chiisai) - small 高い (takai) - tall/expensive 安い (yasui) - cheap 美しい (utsukushii) - beautiful"},{"level":3,"heading":"Key Feature","content":"Conjugate like verbs - change their ending directly:\n\n大きい → 大きかった (was big) 大きい → 大きくない (not big) 大きい → 大きくて (big and...)"},{"level":3,"heading":"Using I-Adjectives","content":"Before nouns (no particle needed):\n\n大きい犬 ookii inu \"big dog\" 高い車 takai kuruma \"expensive car\"\n\nAs predicate (sentence ending):\n\nこの犬は大きい kono inu wa ookii \"This dog is big\" その車は高い sono kuruma wa takai \"That car is expensive\""},{"level":3,"heading":"Basic Conjugation","content":"Present affirmative:\n\n大きい (ookii) - is big\n\nPast affirmative:\n\n大きかった (ookikatta) - was big\n\nPresent negative:\n\n大きくない (ookikunai) - is not big 大きくありません (ookiku arimasen) - is not big (polite)\n\nPast negative:\n\n大きくなかった (ookirunakatta) - was not big 大きくありませんでした (ookiku arimasendeshita) - was not big (polite)"},{"level":3,"heading":"Adverb Form","content":"Drop い, add く:\n\n早い (hayai) - early/fast → 早く (hayaku) - early/quickly 静かい? NO - 静か is na-adjective!\n\n早く走る hayaku hashiru \"run quickly\" 早く来て hayaku kite \"come early\""}]},{"level":2,"heading":"Na-Adjectives (な形容詞)","content":"","children":[{"level":3,"heading":"Identification","content":"Don''t end in い (usually). Need な before nouns:\n\n静か (shizuka) - quiet 綺麗 (kirei) - beautiful/clean 便利 (benri) - convenient 好き (suki) - likeable 有名 (yuumei) - famous"},{"level":3,"heading":"Key Feature","content":"Behave like nouns - need な to modify nouns, conjugate with だ/です:\n\n静かな部屋 (with な before noun) 部屋は静かだ (with だ as predicate)"},{"level":3,"heading":"Using Na-Adjectives","content":"Before nouns (need な):\n\n静かな部屋 shizuka na heya \"quiet room\" 綺麗な花 kirei na hana \"beautiful flower\"\n\nAs predicate (with だ/です):\n\nこの部屋は静かだ kono heya wa shizuka da \"This room is quiet\" その花は綺麗です sono hana wa kirei desu \"That flower is beautiful\""},{"level":3,"heading":"Basic Conjugation","content":"Present affirmative:\n\n静かだ (shizuka da) - is quiet 静かです (shizuka desu) - is quiet (polite)\n\nPast affirmative:\n\n静かだった (shizuka datta) - was quiet 静かでした (shizuka deshita) - was quiet (polite)\n\nPresent negative:\n\n静かじゃない (shizuka janai) - is not quiet 静かではありません (shizuka dewa arimasen) - is not quiet (polite)\n\nPast negative:\n\n静かじゃなかった (shizuka janakatta) - was not quiet 静かではありませんでした (shizuka dewa arimasendeshita) - was not quiet (polite)"},{"level":3,"heading":"Adverb Form","content":"Add に:\n\n静か (shizuka) - quiet → 静かに (shizuka ni) - quietly 綺麗 (kirei) - beautiful → 綺麗に (kirei ni) - beautifully\n\n静かに話す shizuka ni hanasu \"speak quietly\""}]},{"level":2,"heading":"Tricky Cases: いい","content":"いい (ii) means \"good\" but conjugates irregularly using よ:\n\nPresent: いい (ii) / 良い (yoi) - good Past: 良かった (yokatta) - was good Negative: 良くない (yokunai) - not good Adverb: 良く (yoku) - well\n\nDon''t say: いかった ❌ Say: 良かった ✓"},{"level":2,"heading":"Confusing Na-adjectives","content":"Some na-adjectives end in い, confusing learners:\n\n嫌い (kirai) - dislikeable → na-adjective 綺麗 (kirei) - beautiful → na-adjective 有名 (yuumei) - famous → na-adjective\n\nHow to tell:\n\nMost dictionary entries mark them as な形容詞 They need な before nouns They use だ/です as predicate\n\n嫌いな食べ物 (kirai na tabemono) - disliked food 綺麗な人 (kirei na hito) - beautiful person"},{"level":2,"heading":"Connecting Adjectives","content":"","children":[{"level":3,"heading":"I-adjectives: て-form","content":"Drop い, add くて:\n\n大きくて白い犬 ookikute shiroi inu \"a big and white dog\" この部屋は広くて明るい \"This room is spacious and bright\""},{"level":3,"heading":"Na-adjectives: で","content":"Use で to connect:\n\n静かで便利な場所 shizuka de benri na basho \"a quiet and convenient place\" 彼女は親切で優しい \"She is kind and gentle\""}]},{"level":2,"heading":"Common I-Adjectives","content":"大きい (ookii) - big 小さい (chiisai) - small 高い (takai) - tall/expensive 低い (hikui) - low/short 広い (hiroi) - spacious 狭い (semai) - narrow 新しい (atarashii) - new 古い (furui) - old 良い (yoi/ii) - good 悪い (warui) - bad 多い (ooi) - many 少ない (sukunai) - few"},{"level":2,"heading":"Common Na-Adjectives","content":"静か (shizuka) - quiet 綺麗 (kirei) - beautiful/clean 便利 (benri) - convenient 不便 (fuben) - inconvenient 好き (suki) - likeable 嫌い (kirai) - dislikeable 有名 (yuumei) - famous 親切 (shinsetsu) - kind 元気 (genki) - energetic/healthy 簡単 (kantan) - simple/easy"},{"level":2,"heading":"Side-by-Side Comparison","content":"| Feature | I-adjectives | Na-adjectives | | ---------------- | ------------ | --------------------- | | Ending | い | Various (often kanji) | | Before noun | 大きい犬 | 静かな犬 | | As predicate | 大きい | 静かだ/です | | Past | 大きかった | 静かだった | | Negative | 大きくない | 静かじゃない | | Adverb | 大きく | 静かに | | Connect | 大きくて | 静かで |"},{"level":2,"heading":"Degrees of Intensity","content":"","children":[{"level":3,"heading":"Very / Quite","content":"Use とても (totemo) or very casual すごく (sugoku):\n\nとても大きい totemo ookii \"very big\" すごく綺麗です sugoku kirei desu \"super beautiful\""},{"level":3,"heading":"A Little","content":"Use ちょっと (chotto) or 少し (sukoshi):\n\nちょっと高い chotto takai \"a little expensive\" 少し寒い sukoshi samui \"a bit cold\""},{"level":3,"heading":"Too Much","content":"Use すぎる (sugiru) - attach to stem:\n\nI-adjectives: Drop い, add すぎる\n\n高すぎる (takasugiru) - too expensive 大きすぎる (ookisugiru) - too big\n\nNa-adjectives: Add すぎる directly\n\n静かすぎる (shizukasugiru) - too quiet 便利すぎる (benrisugiru) - too convenient"}]},{"level":2,"heading":"Comparative Patterns","content":"","children":[{"level":3,"heading":"More than (より)","content":"A は B より 形容詞 東京はロンドンより大きい \"Tokyo is bigger than London\" この部屋はあの部屋より静かです \"This room is quieter than that room\""},{"level":3,"heading":"Most (一番)","content":"A は 一番 形容詞 富士山は一番高い \"Mt. Fuji is the tallest\" これが一番好きです \"I like this the most\"\n\nSee: Comparisons"}]},{"level":2,"heading":"Adjectives as Nouns","content":"","children":[{"level":3,"heading":"I-adjectives: Drop い, add さ","content":"高い (takai) → 高さ (takasa) - height 大きい (ookii) → 大きさ (ookisa) - size"},{"level":3,"heading":"Na-adjectives: Add さ","content":"静か (shizuka) → 静かさ (shizukasa) - quietness 便利 (benri) → 便利さ (benrisa) - convenience"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using い-adjective Pattern with Na-adjectives","content":"Wrong: 綺麗い部屋 ❌ Right: 綺麗な部屋 ✓"},{"level":3,"heading":"Mistake 2: Forgetting な Before Nouns","content":"Wrong: 静か部屋 ❌ Right: 静かな部屋 ✓"},{"level":3,"heading":"Mistake 3: Wrong Conjugation","content":"Wrong: 大きいだった ❌ Right: 大きかった ✓\n\nI-adjectives don''t need だ/です for past."},{"level":3,"heading":"Mistake 4: Mixing Politeness","content":"Wrong: 大きいです → 大きかったです (past) ✓ Not: 大きいでした ❌\n\nI-adjectives: adjective + です Not: adjective + でした"},{"level":3,"heading":"Mistake 5: Wrong Adverb Formation","content":"Wrong: 早いに ❌ Right: 早く ✓\n\nI-adjectives use く, not に."}]},{"level":2,"heading":"Special Cases","content":"","children":[{"level":3,"heading":"Colors","content":"Most are na-adjectives, except three:\n\nI-adjectives:\n\n赤い (akai) - red 青い (aoi) - blue 黄色い (kiiroi) - yellow\n\nNa-adjectives:\n\n白 (shiro) - white → 白い服 (shiroi fuku) 黒 (kuro) - black → 黒い車 (kuroi kuruma) 茶色 (chairo) - brown → 茶色な目 (chairo na me)\n\nWait, 白い and 黒い are actually i-adjectives! They end in い and conjugate like i-adjectives."},{"level":3,"heading":"Emotions/States","content":"Often na-adjectives:\n\n元気 (genki) - energetic → 元気な人 幸せ (shiawase) - happy → 幸せな時間 大変 (taihen) - tough/terrible → 大変な仕事"}]},{"level":2,"heading":"Politeness Levels","content":"","children":[{"level":3,"heading":"Casual (Plain)","content":"大きい (i-adj) - big 静かだ (na-adj) - quiet"},{"level":3,"heading":"Polite","content":"大きいです (i-adj) - big 静かです (na-adj) - quiet"},{"level":3,"heading":"Very Polite (Formal)","content":"大きゅうございます (i-adj) - very rare, archaic 静かでございます (na-adj) - formal service language\n\nMost situations need only plain or です form."}]},{"level":2,"heading":"Practice Framework","content":"","children":[{"level":3,"heading":"Step 1: Identify Type","content":"Does it end in い and conjugate? → I-adjective Does it need な before nouns? → Na-adjective"},{"level":3,"heading":"Step 2: Choose Form","content":"Before noun? → Add な for na-adjectives End of sentence? → Conjugate appropriately"},{"level":3,"heading":"Step 3: Conjugate","content":"I-adjectives: Change い ending Na-adjectives: Use だ/です forms"}]},{"level":2,"heading":"Quick Reference Chart","content":"","children":[{"level":3,"heading":"I-adjective (大きい)","content":"Present: 大きい Past: 大きかった Negative: 大きくない Past Neg: 大きくなかった Adverb: 大きく Connect: 大きくて Before noun: 大きい犬 (no particle)"},{"level":3,"heading":"Na-adjective (静か)","content":"Present: 静かだ/です Past: 静かだった/でした Negative: 静かじゃない/ではありません Past Neg: 静かじゃなかった/ではありませんでした Adverb: 静かに Connect: 静かで Before noun: 静かな犬 (need な)"}]},{"level":2,"heading":"Key Takeaways","content":"✓ Two types: I-adjectives (い) and Na-adjectives (な) ✓ I-adjectives conjugate directly, like verbs ✓ Na-adjectives need な before nouns, conjugate with だ/です ✓ Both can modify nouns or be predicates ✓ Some na-adjectives end in い (嫌い, 綺麗) ✓ Adverbs: I-adjectives use く, Na-adjectives use に\n\nUnderstanding both adjective types is essential - they''re used constantly in Japanese and follow completely different patterns.\n\nNext: Noun Basics\n\nDeep Dive:\n\nI-adjective Conjugation Na-adjective Conjugation Adjective Comparisons\n\nRelated:\n\nParts of Speech Verb Basics","children":[]}]'::jsonb,
  '["I-adjectives (い形容詞) - end in い, conjugate like verbs","Na-adjectives (な形容詞) - need な before nouns, conjugate with だ","Most dictionary entries mark them as な形容詞","They need な before nouns","They use だ/です as predicate","I-adjective Conjugation","Na-adjective Conjugation","Adjective Comparisons","Parts of Speech","Verb Basics"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'adjective-comparison',
  'Adjective Comparisons (より)',
  'https://practice-japanese.com/docs/adjective-comparison/',
  'adjective-conjugation',
  NULL,
  '[{"level":2,"heading":"Comparison Overview","content":"Japanese comparisons use より (yori) meaning \"than.\" Unlike English, adjectives don''t change form - you add より to show comparison.\n\nA は B より 高い \"A is more expensive than B\" 東京は大阪より大きい \"Tokyo is bigger than Osaka\""},{"level":2,"heading":"Basic Comparison Pattern","content":"","children":[{"level":3,"heading":"Structure","content":"A は B より [adjective]\n\n[Subject] は [Comparison] より [Adjective] A は B より 高い \"A is more expensive than B\""},{"level":3,"heading":"Word Order","content":"私は兄より背が高い \"I am taller than my older brother\" この車はあの車より速い \"This car is faster than that car\""}]},{"level":2,"heading":"Using より (than)","content":"","children":[{"level":3,"heading":"I-adjectives","content":"この本はあの本より面白い \"This book is more interesting than that book\" 今日は昨日より暑い \"Today is hotter than yesterday\" 日本語は英語より難しい \"Japanese is more difficult than English\""},{"level":3,"heading":"Na-adjectives","content":"東京は京都より便利だ \"Tokyo is more convenient than Kyoto\" この部屋はあの部屋より静かです \"This room is quieter than that room\" 彼は私より上手です \"He is better/more skilled than me\""}]},{"level":2,"heading":"Question Form","content":"","children":[{"level":3,"heading":"どちら/どっち (which)","content":"A と B と、どちらが [adjective] ですか？ \"Between A and B, which is [adjective]?\" コーヒーと紅茶と、どちらが好きですか？ \"Between coffee and tea, which do you like?\" 富士山とエベレストと、どちらが高いですか？ \"Between Mt. Fuji and Everest, which is taller?\""},{"level":3,"heading":"Answering","content":"Q: 犬と猫と、どちらが好きですか？ \"Which do you like, dogs or cats?\" A: 犬の方が好きです \"I like dogs better\" A: 猫より犬の方が好きです \"I like dogs better than cats\""}]},{"level":2,"heading":"の方が (no hou ga) - \"more\"","content":"の方が emphasizes \"the one that is more...\"","children":[{"level":3,"heading":"Pattern","content":"A の方が B より [adjective]\n\nこの車の方があの車より速い \"This car is faster than that car\" (emphasis on \"this car\") 東京の方が大阪より大きい \"Tokyo is bigger than Osaka\""},{"level":3,"heading":"Common Usage","content":"こっちの方がいい \"This one is better\" あっちの方が安い \"That one is cheaper\" 日本の方が好きです \"I like Japan better\""}]},{"level":2,"heading":"Comparing Three or More","content":"Use 一番 (ichiban) for superlatives:\n\nA と B と C の中で、どれが一番 [adjective] ですか？ \"Among A, B, and C, which is the most [adjective]?\"\n\n(See Superlatives for more)"},{"level":2,"heading":"Common Comparison Patterns","content":"","children":[{"level":3,"heading":"Places","content":"東京は大阪より大きい \"Tokyo is bigger than Osaka\" 京都は東京より静かです \"Kyoto is quieter than Tokyo\" 日本はアメリカより小さい \"Japan is smaller than America\""},{"level":3,"heading":"Time","content":"今日は昨日より暑い \"Today is hotter than yesterday\" 今年は去年より忙しい \"This year is busier than last year\" 夏は冬より暑いです \"Summer is hotter than winter\""},{"level":3,"heading":"People","content":"私は兄より背が高い \"I am taller than my older brother\" 彼は私より日本語が上手です \"He is better at Japanese than me\" 田中さんは鈴木さんより年上だ \"Tanaka is older than Suzuki\""},{"level":3,"heading":"Things","content":"この本はあの本より面白い \"This book is more interesting than that book\" 新しい車は古い車より速い \"The new car is faster than the old car\" リンゴはバナナより高いです \"Apples are more expensive than bananas\""}]},{"level":2,"heading":"Degree of Difference","content":"","children":[{"level":3,"heading":"もっと (motto) - \"more, even more\"","content":"もっと大きい \"bigger\" / \"even bigger\" もっと安い \"cheaper\" もっと静かな場所 \"a quieter place\""},{"level":3,"heading":"ずっと (zutto) - \"much more\"","content":"これはあれよりずっと高い \"This is much more expensive than that\" 東京は私の町よりずっと大きい \"Tokyo is much bigger than my town\" 彼は私よりずっと上手です \"He is much better than me\""},{"level":3,"heading":"少し (sukoshi) - \"a little more\"","content":"これはあれより少し高い \"This is a little more expensive than that\" 今日は昨日より少し暑い \"Today is a little hotter than yesterday\""}]},{"level":2,"heading":"Negative Comparisons","content":"","children":[{"level":3,"heading":"ほど～ない (hodo~nai) - \"not as... as\"","content":"A は B ほど [adjective]-くない / [adjective]じゃない 私は兄ほど背が高くない \"I''m not as tall as my older brother\" この本はあの本ほど面白くない \"This book is not as interesting as that book\" 京都は東京ほど大きくないです \"Kyoto is not as big as Tokyo\""},{"level":3,"heading":"Examples","content":"犬は猫ほど小さくない \"Dogs are not as small as cats\" 今日は昨日ほど暑くない \"Today is not as hot as yesterday\" この店はあの店ほど便利じゃない \"This store is not as convenient as that store\""}]},{"level":2,"heading":"Conversation Examples","content":"","children":[{"level":3,"heading":"Example 1: Shopping","content":"A: このシャツとあのシャツと、どちらがいいですか？ \"Which is better, this shirt or that shirt?\" B: こっちの方がいいです \"This one is better\" A: でも、高いですよ \"But it''s expensive\" B: あっちより少し高いだけです \"It''s only a little more expensive than that one\""},{"level":3,"heading":"Example 2: Comparing Cities","content":"A: 東京と大阪と、どちらが好きですか？ \"Which do you like better, Tokyo or Osaka?\" B: 大阪の方が好きです \"I like Osaka better\" A: どうしてですか？ \"Why?\" B: 東京より静かだし、食べ物が美味しいです \"It''s quieter than Tokyo, and the food is delicious\""},{"level":3,"heading":"Example 3: Weather","content":"A: 今日は暑いね \"It''s hot today\" B: うん、昨日より暑い \"Yeah, it''s hotter than yesterday\" A: 明日はもっと暑いらしいよ \"I heard tomorrow will be even hotter\" B: 本当？嫌だな \"Really? That''s awful\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using より After Subject","content":"Wrong: 私より兄は背が高い ❌ Right: 私は兄より背が高い ✓ Right: 兄は私より背が高い ✓ \"My brother is taller than me\""},{"level":3,"heading":"Mistake 2: Adding -er to Adjectives","content":"Wrong: この本はあの本よりもっと面白いer ❌ Right: この本はあの本より面白い ✓ Right: この本はあの本よりもっと面白い ✓ \"This book is more interesting than that book\""},{"level":3,"heading":"Mistake 3: Wrong Particle","content":"Wrong: これはあれから高い ❌ Right: これはあれより高い ✓ \"This is more expensive than that\""},{"level":3,"heading":"Mistake 4: Confusing より and ほど","content":"より = positive comparison (more than) ほど = negative comparison (not as... as) 私は兄より背が高い ✓ (I''m taller) 私は兄ほど背が高くない ✓ (I''m not as tall)"}]},{"level":2,"heading":"Summary Patterns","content":"","children":[{"level":3,"heading":"Basic Comparison","content":"A は B より [adjective] \"A is more [adjective] than B\""},{"level":3,"heading":"With Emphasis","content":"A の方が B より [adjective] \"A is more [adjective] than B\" (emphasis on A)"},{"level":3,"heading":"Negative Comparison","content":"A は B ほど [adjective]-くない \"A is not as [adjective] as B\""},{"level":3,"heading":"Question","content":"A と B と、どちらが [adjective] ですか？ \"Which is more [adjective], A or B?\""}]},{"level":2,"heading":"Practice Sentences","content":"この車はあの車より速い \"This car is faster than that car\" 日本語は英語より難しいです \"Japanese is more difficult than English\" 今日は昨日より暑い \"Today is hotter than yesterday\" 東京の方が大阪より大きい \"Tokyo is bigger than Osaka\" 私は兄ほど背が高くない \"I''m not as tall as my older brother\" コーヒーと紅茶と、どちらが好きですか？ \"Which do you like better, coffee or tea?\" こっちの方がずっと安い \"This one is much cheaper\" あの店はこの店ほど便利じゃない \"That store is not as convenient as this store\""},{"level":2,"heading":"Key Takeaways","content":"✓ Use より for \"than\" ✓ Adjectives don''t change form ✓ Pattern: A は B より [adjective] ✓ の方が adds emphasis ✓ ほど～ない for \"not as... as\" ✓ もっと = more, ずっと = much more ✓ どちら for comparing two things ✓ No comparative -er endings!\n\nMaster comparisons - essential for expressing preferences!\n\nNext: Adjective Superlatives (一番)\n\nRelated:\n\nAdjective Superlatives Adjective Equivalence I-adjectives","children":[]}]'::jsonb,
  '["Adjective Superlatives","Adjective Equivalence","I-adjectives"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'adjective-equivalence',
  'Adjective Equivalence (同じくらい)',
  'https://practice-japanese.com/docs/adjective-equivalence/',
  'adjective-conjugation',
  NULL,
  '[{"level":2,"heading":"Equivalence Overview","content":"Japanese expresses equivalence (as... as) using 同じくらい (onaji kurai) or ほど (hodo). These show that two things are equal or similar in degree.\n\nA は B と同じくらい高い \"A is as expensive as B\" A は B と同じくらい大きい \"A is as big as B\""},{"level":2,"heading":"Basic Equivalence Pattern","content":"","children":[{"level":3,"heading":"Using 同じくらい (onaji kurai)","content":"A は B と同じくらい [adjective]\n\n[Subject] は [Comparison] と 同じくらい [Adjective] A は B と 同じくらい 高い \"A is as expensive as B\""},{"level":3,"heading":"Examples","content":"この車はあの車と同じくらい速い \"This car is as fast as that car\" 東京は大阪と同じくらい暑い \"Tokyo is as hot as Osaka\" 私は兄と同じくらい背が高い \"I am as tall as my older brother\""}]},{"level":2,"heading":"Using くらい/ぐらい (about, approximately)","content":"A は B くらい/ぐらい [adjective]\n\nSimpler, more casual form:\n\nこの本はあの本くらい面白い \"This book is about as interesting as that book\" 彼は私くらい背が高い \"He is about as tall as me\" 今日は昨日ぐらい暑い \"Today is about as hot as yesterday\""},{"level":2,"heading":"Using ほど (hodo)","content":"A は B ほど [adjective]\n\nOften used in negative comparisons but can be positive:","children":[{"level":3,"heading":"Positive Equivalence","content":"この車はあの車ほど速い \"This car is as fast as that car\" 彼は私ほど日本語が上手だ \"He is as good at Japanese as me\""},{"level":3,"heading":"Negative (Not as... as)","content":"More common usage:\n\n私は兄ほど背が高くない \"I''m not as tall as my older brother\" この本はあの本ほど面白くない \"This book is not as interesting as that book\""}]},{"level":2,"heading":"Comparison of Equivalence Forms","content":"| Form | Usage | Nuance | | ------------- | ----------------------- | --------------------- | | 同じくらい | Positive equivalence | \"just as, equally\" | | くらい/ぐらい | Approximate equivalence | \"about as\" | | ほど | Often negative | \"as... as\" (emphasis) |","children":[{"level":3,"heading":"Examples Side by Side","content":"同じくらい: 東京は大阪と同じくらい大きい \"Tokyo is just as big as Osaka\" (equal) くらい: 東京は大阪くらい大きい \"Tokyo is about as big as Osaka\" (approximate) ほど (positive): 東京は大阪ほど大きい \"Tokyo is as big as Osaka\" ほど (negative): 大阪は東京ほど大きくない \"Osaka is not as big as Tokyo\""}]},{"level":2,"heading":"Common Equivalence Patterns","content":"","children":[{"level":3,"heading":"Size","content":"この部屋はあの部屋と同じくらい広い \"This room is as spacious as that room\" 私の家は彼の家くらい大きい \"My house is about as big as his house\""},{"level":3,"heading":"Price","content":"この車はあの車と同じくらい高い \"This car is as expensive as that car\" リンゴはバナナくらい高い \"Apples are about as expensive as bananas\""},{"level":3,"heading":"Quality","content":"この映画はあの映画と同じくらい面白い \"This movie is as interesting as that movie\" 彼女は私くらい日本語が上手だ \"She is about as good at Japanese as me\""},{"level":3,"heading":"Time/Duration","content":"今日は昨日と同じくらい忙しい \"Today is as busy as yesterday\" 今年は去年くらい暑い \"This year is about as hot as last year\""}]},{"level":2,"heading":"Question Forms","content":"","children":[{"level":3,"heading":"同じくらいですか？","content":"A と B は同じくらい高いですか？ \"Are A and B equally expensive?\" この2つは同じくらいですか？ \"Are these two about the same?\""},{"level":3,"heading":"どのくらい (how much)","content":"A は B とどのくらい違いますか？ \"How different are A and B?\" これはあれとどのくらい似ていますか？ \"How similar is this to that?\""}]},{"level":2,"heading":"Answering Equivalence Questions","content":"Q: 東京と大阪はどちらが大きいですか？ \"Which is bigger, Tokyo or Osaka?\" A: 同じくらいです \"They''re about the same\" A: 東京の方が少し大きいです \"Tokyo is a little bigger\" A: ほとんど同じです \"They''re almost the same\""},{"level":2,"heading":"Conversation Examples","content":"","children":[{"level":3,"heading":"Example 1: Comparing Items","content":"A: この2つのパソコン、どちらがいいですか？ \"Which of these two computers is better?\" B: 性能は同じくらいです \"The performance is about the same\" A: 値段は？ \"How about the price?\" B: こっちの方が少し安いです \"This one is a little cheaper\""},{"level":3,"heading":"Example 2: Personal Comparison","content":"A: 日本語と中国語、どちらが難しいですか？ \"Which is more difficult, Japanese or Chinese?\" B: 同じくらい難しいと思います \"I think they''re equally difficult\" A: 本当？ \"Really?\" B: はい、どちらも漢字がたくさんあります \"Yes, both have many kanji\""},{"level":3,"heading":"Example 3: Weather","content":"A: 今日は暑いね \"It''s hot today\" B: 昨日と同じくらいだよ \"It''s about the same as yesterday\" A: そう？もっと暑く感じる \"Really? It feels hotter\" B: 湿度が高いからかもね \"Maybe because the humidity is high\""}]},{"level":2,"heading":"Advanced Equivalence Patterns","content":"","children":[{"level":3,"heading":"～と同じくらい～ (as... as with details)","content":"彼は私と同じくらい一生懸命勉強する \"He studies as hard as me\" この店はあの店と同じくらいよく売れている \"This store sells about as well as that store\""},{"level":3,"heading":"～も～も同じくらい (both equally)","content":"犬も猫も同じくらい好きです \"I like both dogs and cats equally\" 春も秋も同じくらい好きだ \"I like both spring and autumn equally\""},{"level":3,"heading":"ほぼ同じ (almost the same)","content":"ほぼ同じくらい高い \"almost equally expensive\" ほぼ同じサイズです \"almost the same size\""}]},{"level":2,"heading":"Degree Modifiers","content":"","children":[{"level":3,"heading":"ちょうど (exactly)","content":"ちょうど同じくらい \"exactly the same\" ちょうど同じ大きさ \"exactly the same size\""},{"level":3,"heading":"だいたい (about, roughly)","content":"だいたい同じくらい \"about the same\" だいたい同じ値段 \"about the same price\""},{"level":3,"heading":"ほとんど (almost)","content":"ほとんど同じくらい \"almost the same\" ほとんど同じ速さ \"almost the same speed\""}]},{"level":2,"heading":"Negative Equivalence Detailed","content":"","children":[{"level":3,"heading":"～ほど～ない (not as... as)","content":"私は兄ほど背が高くない \"I''m not as tall as my older brother\" この本はあの本ほど面白くない \"This book is not as interesting as that book\" 今日は昨日ほど暑くないです \"Today is not as hot as yesterday\" 彼女は私ほど日本語が上手じゃない \"She''s not as good at Japanese as me\""},{"level":3,"heading":"それほど～ない (not that much)","content":"それほど高くない \"not that expensive\" それほど難しくないです \"not that difficult\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using より with 同じくらい","content":"Wrong: A は B より同じくらい高い ❌ Right: A は B と同じくらい高い ✓ \"A is as expensive as B\""},{"level":3,"heading":"Mistake 2: Wrong Particle","content":"Wrong: A は B で同じくらい ❌ Right: A は B と同じくらい ✓ Wrong: A は B に同じくらい ❌ Right: A は B と同じくらい ✓"},{"level":3,"heading":"Mistake 3: Confusing Positive and Negative ほど","content":"Positive: 彼は私ほど速い \"He is as fast as me\" Negative: 彼は私ほど速くない \"He is not as fast as me\""},{"level":3,"heading":"Mistake 4: Adding くらい Twice","content":"Wrong: 同じくらいくらい ❌ Right: 同じくらい ✓ Right: くらい ✓"}]},{"level":2,"heading":"Expressing Similarity","content":"","children":[{"level":3,"heading":"似ている (similar to)","content":"この車はあの車に似ている \"This car is similar to that car\" 彼は父親に似ています \"He resembles his father\""},{"level":3,"heading":"ような (like, similar to)","content":"あの車のような車 \"a car like that car\" これと同じような物 \"something similar to this\""}]},{"level":2,"heading":"Summary Patterns","content":"","children":[{"level":3,"heading":"Positive Equivalence","content":"A は B と同じくらい [adjective] \"A is as [adjective] as B\" A は B くらい [adjective] \"A is about as [adjective] as B\""},{"level":3,"heading":"Negative Equivalence","content":"A は B ほど [adjective]-くない \"A is not as [adjective] as B\""},{"level":3,"heading":"Question","content":"A と B は同じくらいですか？ \"Are A and B about the same?\""}]},{"level":2,"heading":"Practice Sentences","content":"この本はあの本と同じくらい面白い \"This book is as interesting as that book\" 私は兄くらい背が高い \"I''m about as tall as my older brother\" 今日は昨日ほど暑くない \"Today is not as hot as yesterday\" 東京と大阪は同じくらい大きいです \"Tokyo and Osaka are about equally big\" この店はあの店と同じくらい人気がある \"This store is as popular as that store\" 彼女は私ほど日本語が上手じゃない \"She''s not as good at Japanese as me\" ほぼ同じくらいの値段です \"They''re almost the same price\" どちらも同じくらい好きです \"I like both about equally\""},{"level":2,"heading":"Key Takeaways","content":"✓ 同じくらい = \"equally, just as\" ✓ くらい/ぐらい = \"about as\" (casual) ✓ ほど = \"as\" (often with negative) ✓ Use と particle with comparison ✓ ～ほど～ない = \"not as... as\" ✓ 同じ = \"same\" ✓ Can modify with degree words ✓ Essential for expressing equality\n\nMaster equivalence - compare things accurately!\n\nCongratulations! You''ve completed all adjective lessons!\n\nRelated:\n\nAdjective Comparisons (より) Adjective Superlatives (一番) I-adjectives Na-adjectives","children":[]}]'::jsonb,
  '["Adjective Comparisons (より)","Adjective Superlatives (一番)","I-adjectives","Na-adjectives"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'adjective-negation',
  'Adjective Negation',
  'https://practice-japanese.com/docs/adjective-negation/',
  'adjective-conjugation',
  NULL,
  '[{"level":2,"heading":"Adjective Negation","content":"Adjective negation differs between い-adjectives and な-adjectives . Each type follows distinct patterns for expressing \"not [adjective].\"\n\nい-adjectives: Change い to くない 高い → 高くない \"not expensive\" な-adjectives: Add じゃない/ではない 静か → 静かじゃない \"not quiet\" Different types Different patterns"},{"level":2,"heading":"い-Adjective Negation","content":"","children":[{"level":3,"heading":"Change い to くない","content":"高い → 高くない \"expensive\" → \"not expensive\" 大きい → 大きくない \"big\" → \"not big\" 面白い → 面白くない \"interesting\" → \"not interesting\" 美味しい → 美味しくない \"delicious\" → \"not delicious\" 難しい → 難しくない \"difficult\" → \"not difficult\" Replace final い Add くない Regular pattern"}]},{"level":2,"heading":"Special Case: いい","content":"","children":[{"level":3,"heading":"Irregular adjective","content":"いい (good) uses よい stem: いい → よくない \"good\" → \"not good\" NOT いくない Unique exception Common adjective よくありません (polite) \"not good\""}]},{"level":2,"heading":"い-Adjective Past Negative","content":"","children":[{"level":3,"heading":"Was not","content":"高くなかった \"wasn''t expensive\" 面白くなかった \"wasn''t interesting\" 美味しくなかった \"wasn''t delicious\" くない → くなかった Regular conjugation"}]},{"level":2,"heading":"Polite い-Adjective Negative","content":"","children":[{"level":3,"heading":"Formal negation","content":"Casual: 高くない Polite: 高くないです More formal: 高くありません Casual: 面白くない Polite: 面白くないです More formal: 面白くありません Add です for politeness Or use ありません Context determines"}]},{"level":2,"heading":"な-Adjective Negation","content":"","children":[{"level":3,"heading":"Add じゃない/ではない","content":"Casual: 静か → 静かじゃない \"quiet\" → \"not quiet\" 便利 → 便利じゃない \"convenient\" → \"not convenient\" Formal: 静か → 静かではない 便利 → 便利ではない じゃない = casual ではない = formal Same structure as nouns"}]},{"level":2,"heading":"な-Adjective Past Negative","content":"","children":[{"level":3,"heading":"Was not","content":"Casual: 静かじゃなかった \"wasn''t quiet\" Formal: 静かではなかった 便利ではありませんでした \"wasn''t convenient\" Past tense added Regular pattern"}]},{"level":2,"heading":"Noun Negation (Same as な)","content":"","children":[{"level":3,"heading":"Not a [noun]","content":"学生じゃない / 学生ではない \"not a student\" 先生じゃない / 先生ではない \"not a teacher\" 日本人じゃない \"not Japanese (person)\" Same pattern as な-adjectives Uses copula negation"}]},{"level":2,"heading":"じゃ vs では","content":"","children":[{"level":3,"heading":"Formality difference","content":"じゃない: - More casual - Common in speech - Natural conversation ではない: - More formal - Written language - Official contexts Both correct Choose by formality Context determines"}]},{"level":2,"heading":"Polite Forms","content":"","children":[{"level":3,"heading":"です/ます level","content":"い-adjectives: 高くないです (casual polite) 高くありません (formal polite) な-adjectives: 静かじゃないです (casual polite) 静かではありません (formal polite) Nouns: 学生じゃないです (casual polite) 学生ではありません (formal polite) Multiple politeness levels Adjust to situation"}]},{"level":2,"heading":"Te-Form Negatives","content":"","children":[{"level":3,"heading":"Not...and","content":"い-adjectives: 高くなくて \"not expensive and...\" 面白くなくて \"not interesting, so...\" な-adjectives: 静かじゃなくて \"not quiet and...\" 便利じゃなくて \"not convenient, so...\" Connecting ideas Showing reason Common pattern"}]},{"level":2,"heading":"Adverb Form","content":"","children":[{"level":3,"heading":"In a not-[adj] way","content":"い-adjectives: 高くなく \"not expensively\" 大きくなく \"not largely\" Used before する verbs: 高くなく買う \"buy not expensively\" 大きくなく作る \"make not largely\" Adverbial usage Less common Formal feel"}]},{"level":2,"heading":"Conditional Negative","content":"","children":[{"level":3,"heading":"If not","content":"い-adjectives: 高くなければ \"if not expensive\" 面白くなければ \"if not interesting\" な-adjectives: 静かでなければ \"if not quiet\" 便利でなければ \"if not convenient\" Conditional form \"If not\" meaning Common in conditions"}]},{"level":2,"heading":"Emphasis Patterns","content":"","children":[{"level":3,"heading":"Very not / not at all","content":"全然高くない \"not expensive at all\" 全く面白くない \"not interesting at all\" あまり大きくない \"not very big\" Emphatic negation Strengthening denial Common intensifiers"}]},{"level":2,"heading":"Double Adjectives","content":"","children":[{"level":3,"heading":"Both negative","content":"高くなくて、不便 \"not expensive and inconvenient\" 面白くなくて、つまらない \"not interesting and boring\" Combining adjectives Listing qualities Natural flow"}]},{"level":2,"heading":"Questions with Negatives","content":"","children":[{"level":3,"heading":"Isn''t it?","content":"高くない？ \"Isn''t it expensive?\" 面白くないですか？ \"Isn''t it interesting?\" 静かじゃない？ \"Isn''t it quiet?\" Seeking confirmation Expecting agreement Common pattern"}]},{"level":2,"heading":"Negative Comparisons","content":"","children":[{"level":3,"heading":"Not as...as","content":"これは高くない \"This is not expensive\" あれほど高くない \"Not as expensive as that\" そんなに大きくない \"Not that big\" Relative negation Comparison implied"}]},{"level":2,"heading":"Softening Statements","content":"","children":[{"level":3,"heading":"Not very","content":"あまり高くない \"not very expensive\" それほど難しくない \"not that difficult\" そんなに遠くない \"not so far\" Moderate negation Softening Diplomatic"}]},{"level":2,"heading":"With すぎる","content":"","children":[{"level":3,"heading":"Not too much","content":"Affirmative: 高すぎる \"too expensive\" Negative: 高すぎない \"not too expensive\" Negating excess Appropriate level Common expression"}]},{"level":2,"heading":"Written Style","content":"","children":[{"level":3,"heading":"Formal negation","content":"Written Japanese often uses: 高くない (plain) 高くありません (polite) Literary: 高からず (classical) Modern writing standard Polite forms common"}]},{"level":2,"heading":"Casual Contractions","content":"","children":[{"level":3,"heading":"Spoken shortcuts","content":"ない → ねえ (dialectal) 高くない → 高くねえ じゃない → じゃん (casual) 学生じゃない → 学生じゃん Very casual Regional variations Not standard"}]},{"level":2,"heading":"Past Polite Forms","content":"","children":[{"level":3,"heading":"Was not (polite)","content":"い-adjectives: 高くなかったです 高くありませんでした な-adjectives: 静かじゃなかったです 静かではありませんでした Polite past negative Formal contexts Standard forms"}]},{"level":2,"heading":"Common Mistakes","content":"❌ Wrong い-adjective negation ✗ 高いない ✗ 高いじゃない ✓ Correct form ✓ 高くない ❌ Wrong な-adjective negation ✗ 静かくない ✓ Correct form ✓ 静かじゃない ❌ Forgetting いい exception ✗ いくない ✓ Irregular form ✓ よくない ❌ Mixing types Don''t use い-adjective pattern for な ✓ Learn which type Use correct negation Master distinction Practice both types Know exceptions"},{"level":2,"heading":"Practical Examples","content":"","children":[{"level":3,"heading":"Real-life usage","content":"この店は高くない \"This store is not expensive\" 映画は面白くなかった \"Movie wasn''t interesting\" 部屋は静かじゃない \"Room is not quiet\" 今日は忙しくありません \"Not busy today\" 彼は親切じゃない \"He''s not kind\" 天気はよくない \"Weather is not good\" ここは便利じゃなかった \"This place wasn''t convenient\" あまり難しくないです \"Not very difficult\" 全然怖くない \"Not scary at all\" そんなに遠くないよ \"Not that far\" Daily descriptions Natural speech Common patterns Essential expressions Real contexts"}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'adjective-negative',
  'Adjective Negative Forms',
  'https://practice-japanese.com/docs/adjective-negative/',
  'adjective-conjugation',
  NULL,
  '[{"level":2,"heading":"Negative Forms Overview","content":"Negative forms express what something is not . Both i-adjectives and na-adjectives have different negative patterns.\n\nI-adjective: 高くない - is not expensive Na-adjective: 静かじゃない - is not quiet"},{"level":2,"heading":"I-adjective Negative","content":"","children":[{"level":3,"heading":"Plain Form","content":"Change い to くない\n\n高い → 高くない - is not expensive 大きい → 大きくない - is not big 美味しい → 美味しくない - is not delicious 難しい → 難しくない - is not difficult 楽しい → 楽しくない - is not fun"},{"level":3,"heading":"Polite Form","content":"Change い to くないです or くありません\n\n高くないです / 高くありません \"is not expensive\" (polite) 大きくないです / 大きくありません \"is not big\" (polite) 美味しくないです / 美味しくありません \"is not delicious\" (polite)\n\nくないです = casual polite くありません = formal polite"}]},{"level":2,"heading":"Na-adjective Negative","content":"","children":[{"level":3,"heading":"Plain Form","content":"Na-adjective + じゃない or ではない\n\n静かじゃない / 静かではない - is not quiet 便利じゃない / 便利ではない - is not convenient きれいじゃない / きれいではない - is not beautiful 元気じゃない / 元気ではない - is not energetic\n\nじゃない = casual ではない = formal"},{"level":3,"heading":"Polite Form","content":"Na-adjective + じゃないです or じゃありません\n\n静かじゃないです / 静かじゃありません \"is not quiet\" (polite) 便利じゃないです / 便利じゃありません \"is not convenient\" (polite) きれいじゃないです / きれいじゃありません \"is not beautiful\" (polite)"}]},{"level":2,"heading":"Complete Negative Chart","content":"| Type | Plain | Polite (Casual) | Polite (Formal) | | --------------- | ------------ | ---------------- | ------------------ | | I-adj | 高くない | 高くないです | 高くありません | | Na-adj | 静かじゃない | 静かじゃないです | 静かじゃありません | | Na-adj (formal) | 静かではない | 静かではないです | 静かではありません |"},{"level":2,"heading":"Special Case: いい/よい","content":"Good has irregular negative:\n\nPresent: いい / よい Negative: よくない (NOT いくない!) Polite negative: よくないです / よくありません 天気がよくない \"The weather is not good\" 映画はよくなかったです \"The movie was not good\""},{"level":2,"heading":"When to Use Negative Forms","content":"","children":[{"level":3,"heading":"1. Denying Characteristics","content":"この車は高くない \"This car is not expensive\" その部屋は静かじゃないです \"That room is not quiet\" 彼は親切じゃない \"He is not kind\""},{"level":3,"heading":"2. Correcting Information","content":"A: 日本語は難しいですか？ \"Is Japanese difficult?\" B: いいえ、難しくないです \"No, it''s not difficult\""},{"level":3,"heading":"3. Expressing Mild Disagreement","content":"それはあまり面白くない \"That''s not very interesting\" そんなに高くないよ \"It''s not that expensive\""}]},{"level":2,"heading":"Comparison: Affirmative vs Negative","content":"","children":[{"level":3,"heading":"I-adjectives","content":"高い → 高くない \"is expensive\" → \"is not expensive\" 大きい → 大きくない \"is big\" → \"is not big\" 美味しい → 美味しくない \"is delicious\" → \"is not delicious\""},{"level":3,"heading":"Na-adjectives","content":"静かだ → 静かじゃない \"is quiet\" → \"is not quiet\" 便利だ → 便利じゃない \"is convenient\" → \"is not convenient\" きれいだ → きれいじゃない \"is beautiful\" → \"is not beautiful\""}]},{"level":2,"heading":"Degree Expressions with Negatives","content":"","children":[{"level":3,"heading":"あまり～ない (not very)","content":"あまり高くない \"not very expensive\" あまり静かじゃない \"not very quiet\" あまり美味しくないです \"not very delicious\""},{"level":3,"heading":"全然～ない (not at all)","content":"全然高くない \"not expensive at all\" 全然難しくない \"not difficult at all\" 全然静かじゃないです \"not quiet at all\""},{"level":3,"heading":"それほど～ない (not that much)","content":"それほど大きくない \"not that big\" それほど便利じゃない \"not that convenient\""}]},{"level":2,"heading":"Question Forms","content":"","children":[{"level":3,"heading":"I-adjectives","content":"高くないですか？ \"Isn''t it expensive?\" 美味しくないですか？ \"Isn''t it delicious?\" 難しくないですか？ \"Isn''t it difficult?\""},{"level":3,"heading":"Na-adjectives","content":"静かじゃないですか？ \"Isn''t it quiet?\" 便利じゃないですか？ \"Isn''t it convenient?\" 元気じゃないですか？ \"Aren''t you well?\""}]},{"level":2,"heading":"Answering Negative Questions","content":"Tricky! Japanese yes/no logic:\n\nQ: 高くないですか？ \"Isn''t it expensive?\" A: はい、高くないです \"Right, it''s not expensive\" (agreeing with negative) A: いいえ、高いです \"No, it IS expensive\" (disagreeing with negative)"},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Soft Negation","content":"あまり好きじゃない \"don''t like it very much\" そんなに悪くない \"not that bad\" それほど難しくないです \"not that difficult\""},{"level":3,"heading":"Emphatic Negation","content":"全然面白くない \"not interesting at all\" 全く美味しくない \"not delicious at all\" 本当に静かじゃないです \"really not quiet\""}]},{"level":2,"heading":"Conversation Examples","content":"","children":[{"level":3,"heading":"Example 1: Shopping","content":"A: この服はどうですか？ \"How is this clothing?\" B: 悪くないですね \"It''s not bad\" A: 高くないですか？ \"Isn''t it expensive?\" B: いいえ、そんなに高くないです \"No, it''s not that expensive\""},{"level":3,"heading":"Example 2: Restaurant","content":"A: この店は美味しいですか？ \"Is this restaurant delicious?\" B: うーん、あまり美味しくない \"Hmm, it''s not very delicious\" A: 高いですか？ \"Is it expensive?\" B: 高くないけど、美味しくない \"It''s not expensive, but it''s not delicious\""},{"level":3,"heading":"Example 3: Place Description","content":"A: その町は静かですか？ \"Is that town quiet?\" B: いいえ、静かじゃないです \"No, it''s not quiet\" A: にぎやかですか？ \"Is it lively?\" B: はい、とてもにぎやかです \"Yes, it''s very lively\""}]},{"level":2,"heading":"Multiple Negatives","content":"高くなくて、美味しくない \"not expensive and not delicious\" 静かじゃなくて、便利じゃない \"not quiet and not convenient\" 大きくなくて、きれいじゃないです \"not big and not beautiful\""},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Wrong I-adjective Negative","content":"Wrong: 高いない ❌ Right: 高くない ✓ Wrong: 美味しいじゃない ❌ Right: 美味しくない ✓"},{"level":3,"heading":"Mistake 2: Wrong Na-adjective Negative","content":"Wrong: 静かくない ❌ Right: 静かじゃない ✓ Wrong: きれいない ❌ Right: きれいじゃない ✓"},{"level":3,"heading":"Mistake 3: いい Negative","content":"Wrong: いくない ❌ Right: よくない ✓ Wrong: いくないです ❌ Right: よくないです ✓"},{"level":3,"heading":"Mistake 4: Double Negative Marker","content":"Wrong: 高くないじゃない ❌ Right: 高くない ✓ Wrong: 静かじゃないくない ❌ Right: 静かじゃない ✓"}]},{"level":2,"heading":"Modifying Nouns with Negative","content":"","children":[{"level":3,"heading":"I-adjectives","content":"高くない車 \"car that is not expensive\" 美味しくない料理 \"food that is not delicious\" 大きくない部屋 \"room that is not big\""},{"level":3,"heading":"Na-adjectives","content":"Use relative clause:\n\n静かじゃない部屋 \"room that is not quiet\" 便利じゃない場所 \"place that is not convenient\" きれいじゃない花 \"flower that is not beautiful\""}]},{"level":2,"heading":"Comparing Politeness Levels","content":"","children":[{"level":3,"heading":"I-adjectives","content":"高くない (plain) 高くないです (casual polite) 高くありません (formal polite)"},{"level":3,"heading":"Na-adjectives","content":"静かじゃない (plain casual) 静かではない (plain formal) 静かじゃないです (polite casual) 静かじゃありません (polite formal) 静かではありません (most formal)"}]},{"level":2,"heading":"Conjugation Practice","content":"","children":[{"level":3,"heading":"新しい (new)","content":"新しい → 新しくない 新しくないです / 新しくありません"},{"level":3,"heading":"古い (old)","content":"古い → 古くない 古くないです / 古くありません"},{"level":3,"heading":"簡単 (simple)","content":"簡単だ → 簡単じゃない / 簡単ではない 簡単じゃないです / 簡単じゃありません"},{"level":3,"heading":"複雑 (complex)","content":"複雑だ → 複雑じゃない / 複雑ではない 複雑じゃないです / 複雑じゃありません"}]},{"level":2,"heading":"Using in Context","content":"","children":[{"level":3,"heading":"Describing Things","content":"この問題は難しくない \"This problem is not difficult\" その答えは正しくないです \"That answer is not correct\" 彼の説明は明確じゃない \"His explanation is not clear\""},{"level":3,"heading":"Expressing Opinions","content":"私はその意見に賛成じゃない \"I don''t agree with that opinion\" この方法はよくないと思う \"I think this method is not good\" そのアイデアは悪くないです \"That idea is not bad\""}]},{"level":2,"heading":"Key Takeaways","content":"✓ I-adjectives: い → くない ✓ Na-adjectives: じゃない (casual) / ではない (formal) ✓ いい → よくない (irregular) ✓ Add です for politeness ✓ Use with あまり, 全然, それほど ✓ Negative questions need careful answers ✓ Can modify nouns ✓ Multiple politeness levels available\n\nMaster negative forms - essential for denying and correcting!\n\nNext: Adjective Past Negative\n\nRelated:\n\nAdjective Present Tense I-adjective Conjugation Na-adjective Conjugation","children":[]}]'::jsonb,
  '["Adjective Present Tense","I-adjective Conjugation","Na-adjective Conjugation"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'adjective-past-negative',
  'Adjective Past Negative',
  'https://practice-japanese.com/docs/adjective-past-negative/',
  'adjective-conjugation',
  NULL,
  '[{"level":2,"heading":"Past Negative Overview","content":"Past negative forms express what something was not in the past. Both i-adjectives and na-adjectives have distinct past negative patterns.\n\nI-adjective: 高くなかった - was not expensive Na-adjective: 静かじゃなかった - was not quiet"},{"level":2,"heading":"I-adjective Past Negative","content":"","children":[{"level":3,"heading":"Plain Form","content":"Change い to くなかった\n\n高い → 高くなかった - was not expensive 大きい → 大きくなかった - was not big 美味しい → 美味しくなかった - was not delicious 難しい → 難しくなかった - was not difficult 楽しい → 楽しくなかった - was not fun"},{"level":3,"heading":"Polite Form","content":"Add です to くなかった or use くありませんでした\n\n高くなかったです / 高くありませんでした \"was not expensive\" (polite) 大きくなかったです / 大きくありませんでした \"was not big\" (polite) 美味しくなかったです / 美味しくありませんでした \"was not delicious\" (polite)"}]},{"level":2,"heading":"Na-adjective Past Negative","content":"","children":[{"level":3,"heading":"Plain Form","content":"Na-adjective + じゃなかった or ではなかった\n\n静かじゃなかった / 静かではなかった \"was not quiet\" 便利じゃなかった / 便利ではなかった \"was not convenient\" きれいじゃなかった / きれいではなかった \"was not beautiful\" 元気じゃなかった / 元気ではなかった \"was not energetic\""},{"level":3,"heading":"Polite Form","content":"Na-adjective + じゃなかったです or じゃありませんでした\n\n静かじゃなかったです / 静かじゃありませんでした \"was not quiet\" (polite) 便利じゃなかったです / 便利じゃありませんでした \"was not convenient\" (polite) きれいじゃなかったです / きれいじゃありませんでした \"was not beautiful\" (polite)"}]},{"level":2,"heading":"Complete Past Negative Chart","content":"| Type | Plain | Polite (Casual) | Polite (Formal) | | --------------- | ---------------- | -------------------- | ------------------------ | | I-adj | 高くなかった | 高くなかったです | 高くありませんでした | | Na-adj | 静かじゃなかった | 静かじゃなかったです | 静かじゃありませんでした | | Na-adj (formal) | 静かではなかった | 静かではなかったです | 静かではありませんでした |"},{"level":2,"heading":"Special Case: いい/よい","content":"Good has irregular past negative:\n\nPresent: いい / よい Past negative: よくなかった (NOT いくなかった!) Polite: よくなかったです / よくありませんでした 天気がよくなかった \"The weather was not good\" 映画はよくなかったです \"The movie was not good\""},{"level":2,"heading":"Formation Pattern","content":"Think of it as: Present negative + past tense","children":[{"level":3,"heading":"I-adjectives","content":"高い → 高くない → 高くなかった (present → negative → past negative)"},{"level":3,"heading":"Na-adjectives","content":"静かだ → 静かじゃない → 静かじゃなかった (present → negative → past negative)"}]},{"level":2,"heading":"When to Use Past Negative","content":"","children":[{"level":3,"heading":"1. Denying Past States","content":"昨日は暑くなかった \"Yesterday was not hot\" その部屋は静かじゃなかったです \"That room was not quiet\" 試験は難しくなかった \"The exam was not difficult\""},{"level":3,"heading":"2. Correcting Past Information","content":"A: 映画は面白かったですか？ \"Was the movie interesting?\" B: いいえ、面白くなかったです \"No, it was not interesting\""},{"level":3,"heading":"3. Describing Past Experiences","content":"子供の時、野菜は好きじゃなかった \"When I was a child, I didn''t like vegetables\" 昔この町は便利じゃなかったです \"Long ago, this town was not convenient\""}]},{"level":2,"heading":"All Four Forms Comparison","content":"","children":[{"level":3,"heading":"I-adjective (高い)","content":"Present affirmative: 高い / 高いです Present negative: 高くない / 高くないです Past affirmative: 高かった / 高かったです Past negative: 高くなかった / 高くなかったです"},{"level":3,"heading":"Na-adjective (静か)","content":"Present affirmative: 静かだ / 静かです Present negative: 静かじゃない / 静かじゃないです Past affirmative: 静かだった / 静かでした Past negative: 静かじゃなかった / 静かじゃなかったです"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"With Time Expressions","content":"昨日は寒くなかった \"Yesterday was not cold\" 先週は忙しくなかったです \"Last week was not busy\" 去年の夏は暑くなかった \"Last summer was not hot\" 子供の時は背が高くなかった \"When I was a child, I was not tall\""},{"level":3,"heading":"Comparing Then and Now","content":"昔は便利じゃなかったけど、今は便利だ \"It wasn''t convenient before, but now it is\" 昔は高くなかったけど、今は高いです \"It wasn''t expensive before, but now it is\""}]},{"level":2,"heading":"Degree Expressions","content":"","children":[{"level":3,"heading":"あまり～なかった (wasn''t very)","content":"あまり高くなかった \"wasn''t very expensive\" あまり美味しくなかったです \"wasn''t very delicious\" あまり静かじゃなかった \"wasn''t very quiet\""},{"level":3,"heading":"全然～なかった (wasn''t at all)","content":"全然難しくなかった \"wasn''t difficult at all\" 全然面白くなかったです \"wasn''t interesting at all\" 全然便利じゃなかった \"wasn''t convenient at all\""}]},{"level":2,"heading":"Question Forms","content":"","children":[{"level":3,"heading":"I-adjectives","content":"高くなかったですか？ \"Wasn''t it expensive?\" 美味しくなかったですか？ \"Wasn''t it delicious?\" 難しくなかったですか？ \"Wasn''t it difficult?\""},{"level":3,"heading":"Na-adjectives","content":"静かじゃなかったですか？ \"Wasn''t it quiet?\" 便利じゃなかったですか？ \"Wasn''t it convenient?\" 元気じゃなかったですか？ \"Weren''t you well?\""}]},{"level":2,"heading":"Answering Past Negative Questions","content":"Q: 高くなかったですか？ \"Wasn''t it expensive?\" A: はい、高くなかったです \"Right, it wasn''t expensive\" (agreeing) A: いいえ、高かったです \"No, it WAS expensive\" (disagreeing)"},{"level":2,"heading":"Conversation Examples","content":"","children":[{"level":3,"heading":"Example 1: After Movie","content":"A: 映画はどうだった？ \"How was the movie?\" B: あまり面白くなかった \"It wasn''t very interesting\" A: 長くなかった？ \"Wasn''t it long?\" B: うん、3時間だったから長かった \"Yeah, it was 3 hours so it was long\""},{"level":3,"heading":"Example 2: Past Experience","content":"A: 日本旅行はどうでしたか？ \"How was your Japan trip?\" B: よかったです \"It was good\" A: 高くなかったですか？ \"Wasn''t it expensive?\" B: そんなに高くなかったです \"It wasn''t that expensive\""},{"level":3,"heading":"Example 3: Childhood Memory","content":"A: 子供の時、野菜は好きだった？ \"When you were a child, did you like vegetables?\" B: いいえ、好きじゃなかった \"No, I didn''t like them\" A: 今は？ \"How about now?\" B: 今は好き \"I like them now\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Wrong I-adjective Form","content":"Wrong: 高いなかった ❌ Right: 高くなかった ✓ Wrong: 美味しいじゃなかった ❌ Right: 美味しくなかった ✓"},{"level":3,"heading":"Mistake 2: Wrong Na-adjective Form","content":"Wrong: 静かくなかった ❌ Right: 静かじゃなかった ✓ Wrong: きれいかなかった ❌ Right: きれいじゃなかった ✓"},{"level":3,"heading":"Mistake 3: いい Past Negative","content":"Wrong: いくなかった ❌ Right: よくなかった ✓ Wrong: いかなかった ❌ Right: よくなかった ✓"},{"level":3,"heading":"Mistake 4: Double Past Marker","content":"Wrong: 高くなかったでした ❌ Right: 高くなかったです ✓ Right: 高くありませんでした ✓ Wrong: 静かじゃなかったでした ❌ Right: 静かじゃなかったです ✓"}]},{"level":2,"heading":"Multiple Past Negatives","content":"高くなくて、美味しくなかった \"wasn''t expensive and wasn''t delicious\" 静かじゃなくて、便利じゃなかったです \"wasn''t quiet and wasn''t convenient\""},{"level":2,"heading":"Contrasting with Affirmative","content":"昔は高かったけど、今は高くない \"It was expensive before, but now it''s not\" 昔は静かじゃなかったけど、今は静かです \"It wasn''t quiet before, but now it is\""},{"level":2,"heading":"Conjugation Practice","content":"","children":[{"level":3,"heading":"新しい (new)","content":"新しい → 新しくない → 新しかった → 新しくなかった 新しくなかったです / 新しくありませんでした"},{"level":3,"heading":"古い (old)","content":"古い → 古くない → 古かった → 古くなかった 古くなかったです / 古くありませんでした"},{"level":3,"heading":"簡単 (simple)","content":"簡単だ → 簡単じゃない → 簡単だった → 簡単じゃなかった 簡単じゃなかったです / 簡単じゃありませんでした"},{"level":3,"heading":"複雑 (complex)","content":"複雑だ → 複雑じゃない → 複雑だった → 複雑じゃなかった 複雑じゃなかったです / 複雑じゃありませんでした"}]},{"level":2,"heading":"Using in Narratives","content":"昔々、ある所に小さな村がありました。 その村は大きくなくて、有名じゃなかったです。 でも、人々はとても親切でした。 生活は便利じゃなかったけど、幸せでした。 \"Long ago, there was a small village. That village wasn''t big and wasn''t famous. But the people were very kind. Life wasn''t convenient, but they were happy.\""},{"level":2,"heading":"Key Takeaways","content":"✓ I-adjectives: い → くなかった ✓ Na-adjectives: じゃなかった / ではなかった ✓ いい → よくなかった (irregular) ✓ Add です for politeness ✓ Formation: negative + past ✓ Use with past time expressions ✓ Essential for past narratives ✓ Multiple politeness levels\n\nMaster past negative - complete your adjective conjugation!\n\nNext: Adjective Adverb Forms\n\nRelated:\n\nAdjective Negative Adjective Past Tense I-adjective Conjugation","children":[]}]'::jsonb,
  '["Adjective Negative","Adjective Past Tense","I-adjective Conjugation"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'adjective-past',
  'Adjective Past Tense',
  'https://practice-japanese.com/docs/adjective-past/',
  'adjective-conjugation',
  NULL,
  '[{"level":2,"heading":"Past Tense Overview","content":"Past tense adjectives describe past states and completed conditions . Both i-adjectives and na-adjectives have past forms.\n\nI-adjective: 高かった - was expensive Na-adjective: 静かだった - was quiet"},{"level":2,"heading":"I-adjective Past","content":"","children":[{"level":3,"heading":"Plain Form","content":"Change い to かった\n\n高い → 高かった - was expensive 大きい → 大きかった - was big 美味しい → 美味しかった - was delicious 難しい → 難しかった - was difficult 楽しい → 楽しかった - was fun"},{"level":3,"heading":"Polite Form","content":"Add です to かった\n\n高かったです - was expensive (polite) 大きかったです - was big (polite) 美味しかったです - was delicious (polite) 難しかったです - was difficult (polite) 楽しかったです - was fun (polite)"}]},{"level":2,"heading":"Na-adjective Past","content":"","children":[{"level":3,"heading":"Plain Form","content":"Na-adjective + だった\n\n静かだった - was quiet 便利だった - was convenient きれいだった - was beautiful 元気だった - was energetic 有名だった - was famous"},{"level":3,"heading":"Polite Form","content":"Na-adjective + でした\n\n静かでした - was quiet (polite) 便利でした - was convenient (polite) きれいでした - was beautiful (polite) 元気でした - was energetic (polite) 有名でした - was famous (polite)"}]},{"level":2,"heading":"When to Use Past Tense","content":"","children":[{"level":3,"heading":"1. Describing Past States","content":"昨日は暑かった \"Yesterday was hot\" その部屋は静かでした \"That room was quiet\" 子供の時、私は元気だった \"When I was a child, I was energetic\""},{"level":3,"heading":"2. Past Experiences","content":"その映画は面白かった \"That movie was interesting\" 日本旅行は楽しかったです \"The Japan trip was fun\" 昔この町は有名だった \"This town used to be famous\""},{"level":3,"heading":"3. Past Conditions","content":"10年前、この店は安かった \"10 years ago, this store was cheap\" 昔はここは静かでした \"Long ago, this place was quiet\""}]},{"level":2,"heading":"Comparison: Present vs Past","content":"| Type | Present | Past | | ------------- | -------- | ------------ | | I-adj plain | 高い | 高かった | | I-adj polite | 高いです | 高かったです | | Na-adj plain | 静かだ | 静かだった | | Na-adj polite | 静かです | 静かでした |","children":[{"level":3,"heading":"Side by Side Examples","content":"Present: 今日は暑い Past: 昨日は暑かった \"Today is hot\" / \"Yesterday was hot\" Present: この部屋は静かです Past: あの部屋は静かでした \"This room is quiet\" / \"That room was quiet\""}]},{"level":2,"heading":"Special Case: いい/よい","content":"Good conjugates from よい:\n\nPresent: いい / よい Past: よかった (NOT いかった!) Past polite: よかったです 天気がよかった \"The weather was good\" 映画がよかったです \"The movie was good\""},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Describing Past Events","content":"パーティーは楽しかった \"The party was fun\" 試験は難しかったです \"The exam was difficult\" 昨日の会議は長かった \"Yesterday''s meeting was long\""},{"level":3,"heading":"Describing Past Weather","content":"昨日は寒かった \"Yesterday was cold\" 先週は暑かったです \"Last week was hot\" 今朝は涼しかった \"This morning was cool\""},{"level":3,"heading":"Describing Past Places","content":"その店は静かだった \"That store was quiet\" 東京はにぎやかでした \"Tokyo was lively\" 昔この町は小さかった \"This town used to be small\""},{"level":3,"heading":"Describing Past Food","content":"そのラーメンは美味しかった \"That ramen was delicious\" 料理は美味しかったです \"The food was delicious\" ケーキは甘かった \"The cake was sweet\""}]},{"level":2,"heading":"Time Expressions","content":"","children":[{"level":3,"heading":"Common Time Words","content":"昨日 (kinou) - yesterday 今朝 (kesa) - this morning 先週 (senshuu) - last week 去年 (kyonen) - last year 昔 (mukashi) - long ago さっき (sakki) - just now 先月 (sengetsu) - last month 子供の時 (kodomo no toki) - when (I) was a child"},{"level":3,"heading":"Examples","content":"昨日は暑かった \"Yesterday was hot\" 先週は忙しかったです \"Last week was busy\" 去年の夏は楽しかった \"Last summer was fun\" 子供の時は元気だった \"When I was a child, I was energetic\""}]},{"level":2,"heading":"Question Forms","content":"","children":[{"level":3,"heading":"I-adjectives","content":"昨日は暑かったですか？ \"Was yesterday hot?\" 映画は面白かったですか？ \"Was the movie interesting?\" 試験は難しかったですか？ \"Was the exam difficult?\""},{"level":3,"heading":"Na-adjectives","content":"部屋は静かでしたか？ \"Was the room quiet?\" 旅行は楽しかったですか？ \"Was the trip fun?\" 町は便利でしたか？ \"Was the town convenient?\""}]},{"level":2,"heading":"Answering Questions","content":"","children":[{"level":3,"heading":"Affirmative","content":"Q: 映画は面白かったですか？ \"Was the movie interesting?\" A: はい、面白かったです \"Yes, it was interesting\" Q: 部屋は静かでしたか？ \"Was the room quiet?\" A: はい、静かでした \"Yes, it was quiet\""},{"level":3,"heading":"Negative","content":"Q: 高かったですか？ \"Was it expensive?\" A: いいえ、高くなかったです \"No, it was not expensive\""}]},{"level":2,"heading":"Conversation Examples","content":"","children":[{"level":3,"heading":"Example 1: After Watching Movie","content":"A: 映画はどうだった？ \"How was the movie?\" B: とても面白かった！ \"It was very interesting!\" A: 長かった？ \"Was it long?\" B: うん、3時間だった \"Yeah, it was 3 hours\""},{"level":3,"heading":"Example 2: Travel Experience","content":"A: 日本旅行はどうでしたか？ \"How was your Japan trip?\" B: すごく楽しかったです \"It was very fun\" A: 天気はよかったですか？ \"Was the weather good?\" B: はい、毎日よかったです \"Yes, it was good every day\""},{"level":3,"heading":"Example 3: Past Experience","content":"A: 子供の時、どんな子だった？ \"What kind of child were you?\" B: 元気な子だった \"I was an energetic child\" A: 勉強は好きだった？ \"Did you like studying?\" B: いいえ、あまり好きじゃなかった \"No, I didn''t like it much\""}]},{"level":2,"heading":"Comparing Then and Now","content":"昔は小さかったけど、今は大きい \"It used to be small, but now it''s big\" 昔は静かだったけど、今はにぎやかです \"It used to be quiet, but now it''s lively\" 昔は不便だったけど、今は便利だ \"It used to be inconvenient, but now it''s convenient\""},{"level":2,"heading":"Multiple Adjectives in Past","content":"","children":[{"level":3,"heading":"Connecting with て-form","content":"その映画は面白くて感動的だった \"That movie was interesting and moving\" 部屋は広くてきれいでした \"The room was spacious and clean\" 彼は親切で優しかった \"He was kind and gentle\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Wrong Past Form for I-adjectives","content":"Wrong: 高いた ❌ Right: 高かった ✓ Wrong: 大きいでした ❌ Right: 大きかったです ✓"},{"level":3,"heading":"Mistake 2: Wrong Past Form for Na-adjectives","content":"Wrong: 静かかった ❌ Right: 静かだった ✓ Wrong: きれいかった ❌ Right: きれいだった ✓"},{"level":3,"heading":"Mistake 3: いい Conjugation","content":"Wrong: いかった ❌ Right: よかった ✓ Wrong: いかったです ❌ Right: よかったです ✓"},{"level":3,"heading":"Mistake 4: Double Past Marker","content":"Wrong: 高かったでした ❌ Right: 高かったです ✓ Wrong: 静かだったでした ❌ Right: 静かでした ✓"}]},{"level":2,"heading":"Modifying Past-tense Nouns","content":"Use relative clause structure:\n\n昨日食べた料理は美味しかった \"The food I ate yesterday was delicious\" 買った車は高かった \"The car I bought was expensive\" 子供の時住んでいた町は静かだった \"The town I lived in as a child was quiet\""},{"level":2,"heading":"Degree Expressions in Past","content":"とても楽しかった \"was very fun\" すごく美味しかった \"was extremely delicious\" 本当に難しかったです \"was really difficult\" かなり高かった \"was quite expensive\" ちょっと寒かった \"was a little cold\""},{"level":2,"heading":"Practice Patterns","content":"","children":[{"level":3,"heading":"Weather","content":"今日は暑い → 昨日は暑かった \"Today is hot\" → \"Yesterday was hot\" 今は涼しい → さっきは暑かった \"It''s cool now\" → \"It was hot just now\""},{"level":3,"heading":"Food","content":"美味しい → 美味しかった \"is delicious\" → \"was delicious\" 甘い → 甘かった \"is sweet\" → \"was sweet\""},{"level":3,"heading":"Places","content":"静かだ → 静かだった \"is quiet\" → \"was quiet\" 便利です → 便利でした \"is convenient\" → \"was convenient\""}]},{"level":2,"heading":"Key Takeaways","content":"✓ I-adjectives: い → かった ✓ Na-adjectives: だった (plain) / でした (polite) ✓ いい → よかった (irregular) ✓ Add です for politeness to かった ✓ Describes past states and experiences ✓ Use with time expressions ✓ Connect multiple past adjectives with て ✓ Essential for past narratives\n\nMaster past tense - tell stories about the past!\n\nNext: Adjective Negative Forms\n\nRelated:\n\nAdjective Present Tense Adjective Past Negative I-adjective Conjugation","children":[]}]'::jsonb,
  '["Adjective Present Tense","Adjective Past Negative","I-adjective Conjugation"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'adjective-present',
  'Adjective Present Tense',
  'https://practice-japanese.com/docs/adjective-present/',
  'adjective-conjugation',
  NULL,
  '[{"level":2,"heading":"Present Tense Overview","content":"Present tense adjectives describe current states , general truths , and characteristics . Both i-adjectives and na-adjectives have present forms.\n\nI-adjective: 高い - is expensive Na-adjective: 静かだ - is quiet"},{"level":2,"heading":"I-adjective Present","content":"","children":[{"level":3,"heading":"Plain Form","content":"Dictionary form = Present tense\n\n高い - is expensive 大きい - is big 美味しい - is delicious 難しい - is difficult"},{"level":3,"heading":"Polite Form","content":"Add です\n\n高いです - is expensive (polite) 大きいです - is big (polite) 美味しいです - is delicious (polite) 難しいです - is difficult (polite)"}]},{"level":2,"heading":"Na-adjective Present","content":"","children":[{"level":3,"heading":"Plain Form","content":"Na-adjective + だ\n\n静かだ - is quiet 便利だ - is convenient きれいだ - is beautiful 元気だ - is energetic"},{"level":3,"heading":"Polite Form","content":"Na-adjective + です\n\n静かです - is quiet (polite) 便利です - is convenient (polite) きれいです - is beautiful (polite) 元気です - is energetic (polite)"}]},{"level":2,"heading":"When to Use Present Tense","content":"","children":[{"level":3,"heading":"1. Current State","content":"今日は暑い \"Today is hot\" この部屋は静かです \"This room is quiet\" 私は元気だ \"I am healthy/fine\""},{"level":3,"heading":"2. General Truth","content":"富士山は高い \"Mt. Fuji is tall\" この町は便利です \"This town is convenient\" 日本語は難しい \"Japanese is difficult\""},{"level":3,"heading":"3. Permanent Characteristics","content":"彼は背が高い \"He is tall\" この車は速いです \"This car is fast\" 彼女はきれいだ \"She is beautiful\""},{"level":3,"heading":"4. Habitual States","content":"私は毎日忙しい \"I am busy every day\" この店はいつも静かです \"This store is always quiet\""}]},{"level":2,"heading":"Comparison: I-adj vs Na-adj Present","content":"| Type | Plain | Polite | Example | | ------ | ----- | ------ | ----------------- | | I-adj | い | いです | 高い / 高いです | | Na-adj | だ | です | 静かだ / 静かです |","children":[{"level":3,"heading":"Side by Side","content":"I-adjective: この本は高い \"This book is expensive\" この本は高いです \"This book is expensive\" (polite) Na-adjective: この部屋は静かだ \"This room is quiet\" この部屋は静かです \"This room is quiet\" (polite)"}]},{"level":2,"heading":"Modifying Nouns (Present)","content":"","children":[{"level":3,"heading":"I-adjectives","content":"Modify directly:\n\n高い本 - expensive book 大きい家 - big house 美味しい料理 - delicious food 新しい車 - new car"},{"level":3,"heading":"Na-adjectives","content":"Add な:\n\n静かな部屋 - quiet room 便利な店 - convenient store きれいな花 - beautiful flower 元気な子供 - energetic child"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Describing Objects","content":"この車は高い \"This car is expensive\" そのカバンは大きいです \"That bag is big\" あの建物はきれいだ \"That building is beautiful\""},{"level":3,"heading":"Describing Places","content":"東京は大きい \"Tokyo is big\" この町は静かです \"This town is quiet\" あの場所は便利だ \"That place is convenient\""},{"level":3,"heading":"Describing People","content":"彼は親切だ \"He is kind\" 先生は優しいです \"The teacher is gentle\" 田中さんは元気です \"Tanaka is energetic\""},{"level":3,"heading":"Describing Weather","content":"今日は暑い \"Today is hot\" 外は寒いです \"Outside is cold\" 天気がいい \"The weather is good\""}]},{"level":2,"heading":"Question Forms","content":"","children":[{"level":3,"heading":"I-adjectives","content":"高いですか？ \"Is it expensive?\" 美味しいですか？ \"Is it delicious?\" 難しいですか？ \"Is it difficult?\""},{"level":3,"heading":"Na-adjectives","content":"静かですか？ \"Is it quiet?\" 便利ですか？ \"Is it convenient?\" 元気ですか？ \"Are you well?\""}]},{"level":2,"heading":"Answering Questions","content":"","children":[{"level":3,"heading":"Affirmative","content":"Q: 高いですか？ \"Is it expensive?\" A: はい、高いです \"Yes, it is expensive\" Q: 静かですか？ \"Is it quiet?\" A: はい、静かです \"Yes, it is quiet\""},{"level":3,"heading":"Negative","content":"Q: 難しいですか？ \"Is it difficult?\" A: いいえ、難しくないです \"No, it is not difficult\" Q: 便利ですか？ \"Is it convenient?\" A: いいえ、便利じゃないです \"No, it is not convenient\""}]},{"level":2,"heading":"Conversation Examples","content":"","children":[{"level":3,"heading":"Example 1: Shopping","content":"A: このシャツはどうですか？ \"How is this shirt?\" B: いいですね。でも高いです \"It''s good. But it''s expensive\" A: 本当ですね。あれは？ \"That''s true. How about that one?\" B: あれは安くていいです \"That one is cheap and good\""},{"level":3,"heading":"Example 2: Describing a Place","content":"A: この町はどうですか？ \"How is this town?\" B: とても静かです \"It''s very quiet\" A: 便利ですか？ \"Is it convenient?\" B: はい、駅も近くて便利です \"Yes, the station is also close and it''s convenient\""},{"level":3,"heading":"Example 3: Asking About Food","content":"A: この店の料理は美味しいですか？ \"Is the food at this restaurant delicious?\" B: はい、とても美味しいです \"Yes, it''s very delicious\" A: 高くないですか？ \"Isn''t it expensive?\" B: 少し高いですが、美味しいです \"It''s a little expensive, but it''s delicious\""}]},{"level":2,"heading":"Multiple Adjectives","content":"","children":[{"level":3,"heading":"Connecting with て-form","content":"この部屋は広くて明るい \"This room is spacious and bright\" 彼女はきれいで親切です \"She is beautiful and kind\" この本は面白くて安い \"This book is interesting and cheap\""}]},{"level":2,"heading":"Degree Expressions","content":"","children":[{"level":3,"heading":"Intensifiers","content":"とても高い \"very expensive\" すごく大きい \"extremely big\" 本当に美味しい \"really delicious\" かなり難しい \"quite difficult\" ちょっと高い \"a little expensive\""},{"level":3,"heading":"Reducing Intensity","content":"あまり高くない \"not very expensive\" それほど大きくない \"not that big\" 全然難しくない \"not difficult at all\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Wrong Polite Form","content":"Wrong: 高いはです ❌ Right: 高いです ✓ Wrong: 静かなです ❌ Right: 静かです ✓"},{"level":3,"heading":"Mistake 2: Mixing I-adj and Na-adj Patterns","content":"Wrong: 高いだ ❌ Right: 高い ✓ (i-adjective doesn''t need だ) Wrong: 静か ❌ (as predicate) Right: 静かだ ✓ (na-adjective needs だ)"},{"level":3,"heading":"Mistake 3: Using だ in Polite Speech","content":"Wrong: 高いだです ❌ Right: 高いです ✓ Wrong: 静かだです ❌ Right: 静かです ✓"}]},{"level":2,"heading":"Present Tense vs Other Tenses","content":"| Tense | I-adjective | Na-adjective | Meaning | | -------- | ----------- | ------------ | ------------------------------- | | Present | 高い | 静かだ | is expensive / is quiet | | Past | 高かった | 静かだった | was expensive / was quiet | | Negative | 高くない | 静かじゃない | is not expensive / is not quiet |"},{"level":2,"heading":"Key Differences: Plain vs Polite","content":"","children":[{"level":3,"heading":"Plain (Casual)","content":"高い - expensive 大きい - big 静かだ - quiet 便利だ - convenient\n\nUse with: friends, family, casual situations"},{"level":3,"heading":"Polite (Formal)","content":"高いです - expensive 大きいです - big 静かです - quiet 便利です - convenient\n\nUse with: strangers, superiors, formal situations"}]},{"level":2,"heading":"Practice Patterns","content":"","children":[{"level":3,"heading":"Describing Items","content":"この車は速い \"This car is fast\" その時計は高いです \"That watch is expensive\" あのカバンは軽い \"That bag is light\""},{"level":3,"heading":"Describing States","content":"私は今忙しい \"I am busy now\" 彼は元気です \"He is well\" この問題は簡単だ \"This problem is simple\""}]},{"level":2,"heading":"Key Takeaways","content":"✓ I-adjectives: dictionary form = present ✓ Na-adjectives: need だ (plain) or です (polite) ✓ Add です for politeness to i-adjectives ✓ Use for current states and general truths ✓ Modify nouns directly (i-adj) or with な (na-adj) ✓ Can combine multiple adjectives ✓ Questions add か ✓ Present describes now and always\n\nMaster present tense - most common adjective form!\n\nNext: Adjective Past Tense\n\nRelated:\n\nI-adjective Conjugation Na-adjective Conjugation Adjective Negative","children":[]}]'::jsonb,
  '["I-adjective Conjugation","Na-adjective Conjugation","Adjective Negative"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'adjective-superlative',
  'Adjective Superlatives (一番)',
  'https://practice-japanese.com/docs/adjective-superlative/',
  'adjective-conjugation',
  NULL,
  '[{"level":2,"heading":"Superlative Overview","content":"Japanese superlatives use 一番 (ichiban) meaning \"number one\" or \"the most.\" Unlike English, adjectives don''t change - just add 一番.\n\n一番 + [adjective] \"the most [adjective]\" 一番高い - the most expensive / the highest 一番大きい - the biggest 一番好き - like the most / favorite"},{"level":2,"heading":"Basic Superlative Pattern","content":"","children":[{"level":3,"heading":"Structure","content":"[Group] の中で A が一番 [adjective]\n\n[Group] の中で [Subject] が 一番 [Adjective] \"Among [group], [subject] is the most [adjective]\" クラスの中で田中さんが一番背が高い \"Among the class, Tanaka is the tallest\""}]},{"level":2,"heading":"Using 一番 (ichiban)","content":"","children":[{"level":3,"heading":"I-adjectives","content":"一番高い \"the most expensive / the highest\" 一番大きい \"the biggest\" 一番美味しい \"the most delicious\" 一番難しい \"the most difficult\" 一番楽しい \"the most fun\""},{"level":3,"heading":"Na-adjectives","content":"一番便利だ / 一番便利です \"the most convenient\" 一番静かだ / 一番静かです \"the quietest\" 一番きれいだ / 一番きれいです \"the most beautiful / the prettiest\" 一番有名だ / 一番有名です \"the most famous\""}]},{"level":2,"heading":"Question Forms","content":"","children":[{"level":3,"heading":"何が一番 (what is the most)","content":"何が一番好きですか？ \"What do you like the most?\" 何が一番美味しいですか？ \"What is the most delicious?\" 何が一番難しいですか？ \"What is the most difficult?\""},{"level":3,"heading":"どれが一番 (which is the most)","content":"A と B と C の中で、どれが一番好きですか？ \"Among A, B, and C, which do you like the most?\" この中で、どれが一番高いですか？ \"Among these, which is the most expensive?\""},{"level":3,"heading":"誰が一番 (who is the most)","content":"クラスで誰が一番背が高いですか？ \"Who is the tallest in the class?\" 誰が一番日本語が上手ですか？ \"Who is the best at Japanese?\""},{"level":3,"heading":"どこが一番 (where is the most)","content":"日本でどこが一番好きですか？ \"Where in Japan do you like the most?\" どこが一番きれいですか？ \"Where is the most beautiful?\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"の中で (among, within)","content":"クラスの中で一番 \"the most in the class\" 家族の中で一番 \"the most in the family\" 日本の中で一番 \"the most in Japan\" 世界の中で一番 \"the most in the world\" この中で一番 \"the most among these\""},{"level":3,"heading":"で (place/scope marker)","content":"クラスで一番 \"the most in the class\" 日本で一番 \"the most in Japan\" 世界で一番 \"the most in the world\""}]},{"level":2,"heading":"Common Superlatives","content":"","children":[{"level":3,"heading":"Favorites","content":"一番好き \"like the most / favorite\" 一番嫌い \"dislike the most / least favorite\" 一番好きな食べ物 \"favorite food\" 一番好きな映画 \"favorite movie\""},{"level":3,"heading":"Size","content":"一番大きい \"the biggest\" 一番小さい \"the smallest\" 一番高い (tall) \"the tallest\" 一番低い \"the lowest / shortest\""},{"level":3,"heading":"Quality","content":"一番良い / 一番いい \"the best\" 一番悪い \"the worst\" 一番美味しい \"the most delicious\" 一番面白い \"the most interesting\""},{"level":3,"heading":"Difficulty","content":"一番難しい \"the most difficult\" 一番易しい / 一番簡単 \"the easiest\" 一番複雑 \"the most complex\""}]},{"level":2,"heading":"Answering Superlative Questions","content":"Q: 何が一番好きですか？ \"What do you like the most?\" A: 寿司が一番好きです \"I like sushi the most\" Q: どの季節が一番好きですか？ \"Which season do you like the most?\" A: 春が一番好きです \"I like spring the most\" Q: 誰が一番速いですか？ \"Who is the fastest?\" A: 田中さんが一番速いです \"Tanaka is the fastest\""},{"level":2,"heading":"Conversation Examples","content":"","children":[{"level":3,"heading":"Example 1: Favorites","content":"A: 何の食べ物が一番好きですか？ \"What food do you like the most?\" B: ラーメンが一番好きです \"I like ramen the most\" A: どこのラーメンが一番美味しいですか？ \"Where has the most delicious ramen?\" B: 博多のラーメンが一番美味しいと思います \"I think Hakata ramen is the most delicious\""},{"level":3,"heading":"Example 2: Comparisons","content":"A: この3つの中で、どれが一番安いですか？ \"Among these three, which is the cheapest?\" B: これが一番安いです \"This one is the cheapest\" A: 一番高いのは？ \"Which is the most expensive?\" B: あれが一番高いです \"That one is the most expensive\""},{"level":3,"heading":"Example 3: Personal Questions","content":"A: 日本で一番好きな場所はどこですか？ \"What''s your favorite place in Japan?\" B: 京都が一番好きです \"I like Kyoto the most\" A: どうしてですか？ \"Why?\" B: 一番きれいだし、一番静かだから \"Because it''s the most beautiful and the most quiet\""}]},{"level":2,"heading":"Common Mistake Patterns","content":"","children":[{"level":3,"heading":"Mistake 1: Forgetting が Particle","content":"Wrong: 寿司一番好きです ❌ Right: 寿司が一番好きです ✓ \"I like sushi the most\""},{"level":3,"heading":"Mistake 2: Using より with 一番","content":"Wrong: 一番より高い ❌ Right: 一番高い ✓ \"the most expensive\""},{"level":3,"heading":"Mistake 3: Adding -est to Adjectives","content":"Wrong: 一番高いest ❌ Right: 一番高い ✓ \"the highest\""},{"level":3,"heading":"Mistake 4: Wrong Word Order","content":"Wrong: 高い一番 ❌ Right: 一番高い ✓ \"the most expensive\""}]},{"level":2,"heading":"Special Expressions","content":"","children":[{"level":3,"heading":"一番上 (ichiban ue) - the top","content":"一番上の本 \"the top book\" クラスで一番上 \"the top of the class\""},{"level":3,"heading":"一番下 (ichiban shita) - the bottom","content":"一番下の引き出し \"the bottom drawer\""},{"level":3,"heading":"一番前 (ichiban mae) - the very front","content":"一番前の席 \"the front-most seat\""},{"level":3,"heading":"一番後ろ (ichiban ushiro) - the very back","content":"一番後ろの列 \"the back-most row\""}]},{"level":2,"heading":"With Time Expressions","content":"一年で一番暑い月 \"the hottest month of the year\" 今まで一番楽しかった \"the most fun I''ve ever had\" 人生で一番大切なもの \"the most important thing in life\" 今まで一番美味しい料理 \"the most delicious food I''ve ever had\""},{"level":2,"heading":"Multiple Superlatives","content":"一番大きくて一番高い \"the biggest and the most expensive\" 一番きれいで一番親切 \"the most beautiful and the kindest\" 世界で一番高くて一番有名な山 \"the world''s tallest and most famous mountain\""},{"level":2,"heading":"Regional Superlatives","content":"","children":[{"level":3,"heading":"Countries/Cities","content":"日本で一番大きい都市 \"the biggest city in Japan\" 世界で一番高い山 \"the world''s highest mountain\" アジアで一番人口が多い国 \"the most populated country in Asia\""},{"level":3,"heading":"Examples","content":"東京は日本で一番大きい都市です \"Tokyo is the biggest city in Japan\" 富士山は日本で一番高い山です \"Mt. Fuji is the highest mountain in Japan\" この店はこの町で一番有名です \"This store is the most famous in this town\""}]},{"level":2,"heading":"Negative Superlatives","content":"","children":[{"level":3,"heading":"一番～ない (least)","content":"一番好きじゃない \"like the least\" 一番高くない \"the least expensive\" 一番面白くない \"the least interesting\""},{"level":3,"heading":"Examples","content":"これが一番好きじゃない \"I like this the least\" 冬が一番好きじゃないです \"I like winter the least\""}]},{"level":2,"heading":"Practice Sentences","content":"何が一番好きですか？ \"What do you like the most?\" 日本語で一番難しいのは何ですか？ \"What is the most difficult thing about Japanese?\" 世界で一番高い山はエベレストです \"The world''s highest mountain is Everest\" クラスで田中さんが一番背が高いです \"Tanaka is the tallest in the class\" 今まで一番楽しかった旅行 \"The most fun trip I''ve ever had\" これが一番美味しいです \"This is the most del","children":[]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'adverb-formation',
  'Adverb Formation',
  'https://practice-japanese.com/docs/adverb-formation/',
  'verb-conjugation',
  NULL,
  '[{"level":2,"heading":"What is Adverb Formation?","content":"Adverb formation in Japanese turns adjectives into adverbs that modify verbs, adjectives, or sentences. い-adjectives change to く form, while な-adjectives take に. These adverbs describe how, when, or to what degree an action occurs.\n\n速い (fast - adjective) → 速く (fast - adverb) 速く走る \"run fast\" 静か (quiet - adjective) → 静かに (quietly - adverb) 静かに話す \"speak quietly\""},{"level":2,"heading":"い-Adjective Adverbs","content":"","children":[{"level":3,"heading":"Formation: drop い, add く","content":"高い → 高く \"highly/expensive-ly\" 速い → 速く \"fast\" 強い → 強く \"strongly\" 早い → 早く \"early\" 多い → 多く \"much/many\" 高く飛ぶ \"fly high\" 速く走る \"run fast\" 強く押す \"push strongly\""},{"level":3,"heading":"Usage with verbs","content":"早く起きる \"Wake up early\" 大きく開ける \"Open widely\" 小さく書く \"Write small\" Modifies the verb"}]},{"level":2,"heading":"な-Adjective Adverbs","content":"","children":[{"level":3,"heading":"Formation: adjective + に","content":"静か → 静かに \"quietly\" きれい → きれいに \"beautifully/cleanly\" 元気 → 元気に \"energetically\" 丁寧 → 丁寧に \"politely/carefully\" 真面目 → 真面目に \"seriously\" 静かに話す \"speak quietly\" きれいに書く \"write neatly\" 元気に遊ぶ \"play energetically\""},{"level":3,"heading":"Usage","content":"丁寧に教える \"Teach carefully\" 真面目に勉強する \"Study seriously\" Describes manner"}]},{"level":2,"heading":"Common い-Adjective Adverbs","content":"","children":[{"level":3,"heading":"Degree and extent","content":"多く \"much/many\" 少なく \"few/little\" 長く \"long (time/length)\" 短く \"short\" 広く \"widely\" 深く \"deeply\" 多く食べる \"eat a lot\" 長く続ける \"continue for long\""},{"level":3,"heading":"Speed and intensity","content":"速く \"fast\" 遅く \"slow\" 強く \"strongly\" 弱く \"weakly\" 激しく \"intensely\" 速く走る \"run fast\" 強く打つ \"hit strongly\""},{"level":3,"heading":"Quality","content":"良く/よく \"well\" 悪く \"badly\" 正しく \"correctly\" 詳しく \"in detail\" よく分かる \"understand well\" 正しく使う \"use correctly\""}]},{"level":2,"heading":"Common な-Adjective Adverbs","content":"","children":[{"level":3,"heading":"Manner","content":"静かに \"quietly\" 丁寧に \"carefully/politely\" 簡単に \"easily\" 楽に \"comfortably/easily\" 自由に \"freely\" 静かに歩く \"walk quietly\" 簡単に解ける \"solve easily\""},{"level":3,"heading":"State","content":"元気に \"energetically\" 健康に \"healthily\" 幸せに \"happily\" 安全に \"safely\" 元気に挨拶する \"greet energetically\" 安全に運転する \"drive safely\""}]},{"level":2,"heading":"Special Cases","content":"","children":[{"level":3,"heading":"いい/よい → よく","content":"いい → よく (irregular) \"good\" → \"well\" よく分かる \"understand well\" よく見る \"look carefully\" よく寝る \"sleep well\" Irregular formation Most common adverb"},{"level":3,"heading":"同じ → 同じように","content":"同じ → 同じように \"same\" → \"in the same way\" 同じように作る \"Make in the same way\" Pre-noun adjective Needs ように"}]},{"level":2,"heading":"Modifying Other Adjectives","content":"","children":[{"level":3,"heading":"Adverb + adjective","content":"とても大きい \"Very big\" すごく美味しい \"Extremely delicious\" 少し難しい \"A little difficult\" Degree modification"}]},{"level":2,"heading":"Negative Adverbs","content":"","children":[{"level":3,"heading":"With ない","content":"高くない \"Not expensive\" 静かじゃない/静かでない \"Not quiet\" Used with negative forms"}]},{"level":2,"heading":"Adverbs Modifying Adverbs","content":"とても速く走る \"Run very fast\" もっと静かに話す \"Speak more quietly\" かなり詳しく説明する \"Explain quite in detail\" Layered modification"},{"level":2,"heading":"Position in Sentence","content":"","children":[{"level":3,"heading":"Before what it modifies","content":"速く走る \"Run fast\" (modifies verb) とても美味しい \"Very delicious\" (modifies adjective) Usually precedes"}]},{"level":2,"heading":"Contractions in Speech","content":"","children":[{"level":3,"heading":"Casual forms","content":"すごく → すげー (very casual) めっちゃ (Kansai: very) ちょー (slang: super) Informal speech Regional variations"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"よく + verb","content":"よく分かる \"understand well\" よく知る \"know well\" よく見る \"often see/look carefully\" よく食べる \"eat often/well\" Multiple meanings Context dependent"},{"level":3,"heading":"早く + verb","content":"早く起きる \"wake up early\" 早く来る \"come early\" 早く終わる \"finish early\" Time-related"}]},{"level":2,"heading":"Adverbs from Nouns","content":"","children":[{"level":3,"heading":"Noun + に","content":"毎日 → 毎日に (rare) Usually: 毎日 (functions as adverb) 一生懸命 → 一生懸命に \"with all one''s might\" 一緒 → 一緒に \"together\" Some nouns become adverbs"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Wrong ending","content":"Wrong: 速いに ⚠️ Correct: 速く ✓ \"Fast\" い-adjectives use く"},{"level":3,"heading":"Forgetting に","content":"Wrong: 静か話す ⚠️ Correct: 静かに話す ✓ \"Speak quietly\" な-adjectives need に"},{"level":3,"heading":"Using adjective form with verbs","content":"Wrong: 速い走る ⚠️ Correct: 速く走る ✓ \"Run fast\" Must convert to adverb"}]},{"level":2,"heading":"Multiple Adverbs","content":"","children":[{"level":3,"heading":"Stacking","content":"とても速く走る \"Run very fast\" もっときれいに書く \"Write more neatly\" Can layer adverbs"}]},{"level":2,"heading":"Intensifiers","content":"","children":[{"level":3,"heading":"Common intensifying adverbs","content":"とても \"very\" すごく \"extremely\" 非常に \"extremely\" (formal) かなり \"quite/considerably\" ちょっと/少し \"a little\" あまり \"not very\" (with negative) Modify degree"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"速く走る \"Run fast\" 静かに話す \"Speak quietly\" よく分かる \"Understand well\" 早く起きる \"Wake up early\" きれいに書く \"Write neatly\" 丁寧に教える \"Teach carefully\""},{"level":3,"heading":"Intermediate","content":"もっと速く走らないと、 間に合わない。 \"If you don''t run faster, you won''t make it.\" 静かに話してください。 赤ちゃんが寝ています。 \"Please speak quietly. The baby is sleeping.\" この仕事は丁寧にやらなければ ならない。時間をかけて、 正しく進めましょう。 \"This work must be done carefully. Let''s take our time and proceed correctly.\" 毎日一生懸命に日本語を 勉強している。少しずつだが、 よく分かるようになってきた。 \"I''m studying Japanese hard every day. Little by little, I''ve come to understand well.\""}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'adversity-passive',
  'Adversity Passive',
  'https://practice-japanese.com/docs/adversity-passive/',
  'other',
  NULL,
  '[{"level":2,"heading":"Adversity Passive","content":"Adversity passive (迷惑の受身) emphasizes the negative or unfortunate nature of passive constructions. While indirect passive inherently carries adversity, even direct passive can express misfortune. This highlights how passive voice in Japanese often conveys victim perspective.\n\nJapanese passive often implies: - Negative impact - Speaker affected negatively - Unfortunate situation - Victim''s viewpoint Cultural and linguistic feature Important nuance"},{"level":2,"heading":"Adversity in Direct Passive","content":"","children":[{"level":3,"heading":"Negative situations","content":"財布を盗まれた \"Had wallet stolen\" 試験に落とされた \"Was failed on exam\" 会社をクビにされた \"Was fired from company\" Direct passive But clearly unfortunate Victim perspective Negative events"}]},{"level":2,"heading":"Adversity in Indirect Passive","content":"","children":[{"level":3,"heading":"Inherently negative","content":"雨に降られた \"Was rained on\" 子供に泣かれた \"Child cried (on me)\" 犬に死なれた \"Dog died (on me)\" Indirect passive Always adversity Built-in negative nuance Inconvenience expressed"}]},{"level":2,"heading":"Neutral Events Made Negative","content":"","children":[{"level":3,"heading":"Perspective shift","content":"見られた can mean: \"Was seen\" (neutral) OR \"Was watched/seen\" (uncomfortable, privacy invaded) 聞かれた can mean: \"Was asked\" (neutral) OR \"Was asked\" (bothersome, intrusive) Context reveals Often negative interpretation Passive adds victim feeling"}]},{"level":2,"heading":"Personal Loss","content":"","children":[{"level":3,"heading":"Possessions and relationships","content":"車を壊された \"Had car broken\" 自転車を盗まれた \"Had bike stolen\" 恋人に振られた \"Was dumped by lover\" Personal loss Negative impact Victim perspective Unfortunate events"}]},{"level":2,"heading":"Unwanted Actions","content":"","children":[{"level":3,"heading":"Things done to you","content":"無視された \"Was ignored\" 笑われた \"Was laughed at\" 批判された \"Was criticized\" 馬鹿にされた \"Was made fun of\" Unwanted treatment Negative social actions Hurt feelings"}]},{"level":2,"heading":"Betrayal and Disappointment","content":"","children":[{"level":3,"heading":"Relationship problems","content":"騙された \"Was deceived\" 裏切られた \"Was betrayed\" 嘘をつかれた \"Was lied to\" 約束を破られた \"Had promise broken\" Trust violated Disappointment Emotional harm"}]},{"level":2,"heading":"Physical Harm","content":"","children":[{"level":3,"heading":"Being hurt","content":"殴られた \"Was hit\" 蹴られた \"Was kicked\" 噛まれた \"Was bitten\" 刺された \"Was stabbed\" Physical violence Clear adversity Victim of action"}]},{"level":2,"heading":"Inconvenience","content":"","children":[{"level":3,"heading":"Troublesome situations","content":"待たされた \"Was made to wait\" 急かされた \"Was rushed\" 起こされた \"Was woken up\" 邪魔された \"Was disturbed/interrupted\" Inconvenient actions Disruption Bothered by others"}]},{"level":2,"heading":"Privacy Invasion","content":"","children":[{"level":3,"heading":"Unwanted attention","content":"写真を撮られた \"Had photo taken (without permission)\" 見られた \"Was seen/watched (uncomfortably)\" 覗かれた \"Was peeped at\" Privacy concerns Uncomfortable situations Unwanted observation"}]},{"level":2,"heading":"When Adversity NOT Present","content":"","children":[{"level":3,"heading":"Neutral or positive passives","content":"褒められた \"Was praised\" (Can be neutral or positive) 招待された \"Was invited\" (Usually positive) 選ばれた \"Was chosen\" (Usually positive) Some passives not adversative Context matters But less common pattern"}]},{"level":2,"heading":"Cultural Aspect","content":"","children":[{"level":3,"heading":"Japanese perspective","content":"Japanese passive emphasizes: - Receiving end experience - Impact on self - Often negative - Victim''s viewpoint Different from English English passive more neutral Japanese adds emotional layer Cultural difference"}]},{"level":2,"heading":"Emotional Expression","content":"","children":[{"level":3,"heading":"Showing feelings","content":"困った \"troubled\" 嫌だった \"unpleasant\" 悲しかった \"sad\" 腹が立った \"angry\" Often accompany adversity passive Express emotional impact Natural combination 雨に降られて困った \"Was rained on and troubled\""}]},{"level":2,"heading":"Complaint Function","content":"","children":[{"level":3,"heading":"Expressing grievances","content":"Adversity passive common in: - Complaints - Venting frustrations - Sharing troubles - Describing misfortune Natural vehicle for complaints Socially acceptable expression Indirect criticism"}]},{"level":2,"heading":"With てしまう","content":"","children":[{"level":3,"heading":"Emphasizing regret","content":"財布を盗まれてしまった \"Ended up having wallet stolen\" 騙されてしまった \"Ended up being deceived\" Double adversity feeling Regret added Unfortunate completion"}]},{"level":2,"heading":"Responsibility Shift","content":"","children":[{"level":3,"heading":"Not my fault","content":"Using passive can imply: \"It happened to me\" \"I''m not responsible\" \"I''m the victim\" Removes agency Emphasizes receiving Absolves self Victim status"}]},{"level":2,"heading":"In Storytelling","content":"","children":[{"level":3,"heading":"Describing hardships","content":"色々な困難に遭われた \"Encountered various difficulties\" 多くの人に批判された \"Was criticized by many\" Narrative device Showing struggle Building sympathy Dramatic effect"}]},{"level":2,"heading":"Comparison to Active","content":"","children":[{"level":3,"heading":"Feeling difference","content":"Active: 犬が死んだ \"Dog died\" Neutral statement Passive: 犬に死なれた \"Dog died on me\" Emotional impact Personal loss expressed Active = factual Passive = emotional Different perspectives"}]},{"level":2,"heading":"Verb Choice Matters","content":"","children":[{"level":3,"heading":"Some verbs inherently negative","content":"盗まれる \"be stolen\" - always bad 殺される \"be killed\" - always bad 騙される \"be deceived\" - always bad Others depend on context: 見られる - can be neutral or negative 呼ばれる - usually neutral 使われる - can be neutral Lexical meaning plus passive Combined effect"}]},{"level":2,"heading":"Learning to Recognize","content":"","children":[{"level":3,"heading":"Adversity signals","content":"Look for: - Negative verbs (steal, break, etc.) - Personal loss (wallet, bike, etc.) - Emotional context - Complaint situations - Indirect passive structure Context clues Pattern recognition Understanding speaker''s perspective"}]},{"level":2,"heading":"Common Mistakes","content":"❌ Using passive for positive self-actions ✗ 褒められました (when bragging) ✓ Better to be modest ✓ Or use different structure ❌ Missing negative nuance Passive isn''t neutral in Japanese ✓ Recognize feeling Usually implies difficulty ❌ Overusing in neutral contexts Not every passive needs adversity ✓ Choose appropriately Context determines Some passives neutral Understanding nuance crucial Cultural awareness needed","children":[]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'age-speech',
  'Age-related Speech',
  'https://practice-japanese.com/docs/age-speech/',
  'other',
  NULL,
  '[{"level":2,"heading":"Understanding Age-Related Speech","content":"Japanese speech patterns vary significantly across age groups, reflecting generational differences in education, social values, and cultural exposure. Understanding these differences helps you speak age-appropriately and communicate effectively across generations.\n\nWhy age affects speech:\n\nDifferent historical language education Generational slang and trends Changing social norms and formality Technological and cultural influences Shifting attitudes toward hierarchy\n\nKey principle: Adjust your speech level based on the age and status of the person you''re speaking with, not just your own age."},{"level":2,"heading":"Children''s Speech (0-12 years)","content":"","children":[{"level":3,"heading":"Characteristics","content":"Simple vocabulary:\n\nおなか (onaka) instead of 腹 (hara) - \"stomach\" ごはん (gohan) instead of 食事 (shokuji) - \"meal\" おうち (ouchi) instead of 家 (ie/uchi) - \"home\"\n\nCute/childish expressions:\n\nばいばい (baibai) - \"Bye bye\" わんわん (wanwan) - \"doggy\" ぶーぶー (bu-bu-) - \"car\" (from vroom vroom) ねんね (nenne) - \"sleep/bedtime\" まんま (manma) - \"food\" (baby talk)\n\nIncomplete sentences:\n\nおなかすいた！ (Onaka suita!) - \"I''m hungry!\" [Instead of: 私はお腹がすきました] これほしい！ (Kore hoshii!) - \"I want this!\" [Instead of: 私はこれが欲しいです]\n\nParent-child language:\n\nParents to children: 〜なさい (nasai) - Command form Example: 食べなさい (Tabenasai) - \"Eat!\" ダメ (dame) - \"No!\" / \"Don''t!\" いい子 (ii ko) - \"Good child\""},{"level":3,"heading":"Common expressions","content":"やった！ (Yatta!) - \"Yay!\" / \"I did it!\" えーん (e-n) - Crying sound ママ (mama) / パパ (papa) - \"Mommy/Daddy\" 先生 (sensei) - \"Teacher\" (used as address)"}]},{"level":2,"heading":"Teenagers/High School Students (13-18 years)","content":"","children":[{"level":3,"heading":"Characteristics","content":"Heavy slang usage:\n\nやばい (yabai) - \"Amazing/terrible\" マジ (maji) - \"Seriously\" ウケる (ukeru) - \"That''s funny\" キモい (kimoi) - \"Gross\" ダサい (dasai) - \"Lame\"\n\nShortened words:\n\nスタバ (sutaba) - Starbucks マック (makku) - McDonald''s テスト (tesuto) - Test ディズニー → ディズニ (dizuni)\n\nSocial media influence:\n\nバズる (bazuru) - \"Go viral\" 映える (baeru) - \"Instagram-worthy\" リア充 (riajuu) - \"Fulfilled in real life\" 陰キャ (in-kya) - \"Introvert/quiet person\" 陽キャ (you-kya) - \"Extrovert/popular person\"\n\nCasual, informal speech:\n\n〜じゃん (jan) - \"Isn''t it?\" Example: いいじゃん (Ii jan) - \"That''s good!\" 超 (chou) - \"Super\" Example: 超かわいい (Chou kawaii) - \"Super cute!\"\n\nFriend group language:\n\nおまえ (omae) / あんた (anta) - \"You\" (casual) あいつ (aitsu) - \"That person\" 〜くん (kun) / 〜ちゃん (chan) - Name suffixes"},{"level":3,"heading":"Common expressions","content":"うざい (uzai) - \"Annoying\" ムカつく (mukatsuku) - \"Irritating\" ガチ (gachi) - \"Serious/for real\" エモい (emoi) - \"Emotional/nostalgic\" ぴえん (pien) - \"Sad\" (from crying emoji)"}]},{"level":2,"heading":"University Students/Young Adults (19-25 years)","content":"","children":[{"level":3,"heading":"Characteristics","content":"More polished casual speech:\n\nMaintains slang but more controlled: - やばい (yabai) - Still frequent - マジ (maji) - Common - ウケる (ukeru) - Used but less frequent\n\nWorkplace preparation:\n\nLearning keigo (polite language): - です/ます forms consistently - お〜になる (o ~ ni naru) - Honorific forms - Basic business expressions\n\nUniversity culture language:\n\nゼミ (zemi) - \"Seminar\" サークル (sa-kuru) - \"Club/circle\" バイト (baito) - \"Part-time job\" 就活 (shuukatsu) - \"Job hunting\" 単位 (tan''i) - \"Credits\"\n\nSocial drinking culture:\n\n飲み会 (nomikai) - \"Drinking party\" 先輩 (senpai) - \"Senior\" 後輩 (kouhai) - \"Junior\""},{"level":3,"heading":"Common expressions","content":"お疲れ様です (Otsukaresama desu) - \"Good work\" (entering workplace) すみません (Sumimasen) - More frequent than すいません よろしくお願いします (Yoroshiku onegaishimasu) - Proper use"}]},{"level":2,"heading":"Working Adults (26-50 years)","content":"","children":[{"level":3,"heading":"Characteristics","content":"Balanced formality:\n\nWorkplace: Formal keigo Friends: Casual but mature Family: Relaxed but proper\n\nProfessional vocabulary:\n\nお世話になっております (Osewa ni natte orimasu) - \"Thank you for your continued support\" 承知しました (Shouchi shimashita) - \"Understood\" (formal) 恐れ入ります (Osore irimasu) - \"I''m sorry to trouble you\"\n\nLess trendy slang:\n\nAware of youth slang but don''t overuse: - Understand やばい but use less - May say すごい (sugoi) instead - Moderate language overall\n\nRespectful speech:\n\nProper use of: - さん (san) suffix consistently - です/ます forms in semi-formal situations - Appropriate humility expressions"},{"level":3,"heading":"Common expressions","content":"お疲れ様です (Otsukaresama desu) - \"Good work\" (daily) 申し訳ございません (Moushiwake gozaimasen) - \"I deeply apologize\" いかがでしょうか (Ikaga deshou ka) - \"How about...?\" / \"Would you...?\" よろしいでしょうか (Yoroshii deshou ka) - \"Would that be alright?\""}]},{"level":2,"heading":"Middle-Aged Adults (51-65 years)","content":"","children":[{"level":3,"heading":"Characteristics","content":"More formal baseline:\n\nEven casual speech more formal than youth: - Less particle dropping - More complete sentences - Traditional expressions\n\nTraditional values reflected:\n\nStronger hierarchy awareness: - Careful with honorifics - Age-based respect - Traditional gender roles (may vary)\n\nGeneration-specific references:\n\nShowa era (1926-1989) cultural references Different idioms and expressions Less English loanword usage\n\nTechnology gap language:\n\nMay use older terms: - 携帯 (keitai) instead of スマホ (sumaho) - \"Mobile phone\" - メール (me-ru) for email (vs younger LINE, etc.)"},{"level":3,"heading":"Common expressions","content":"どうも (doumo) - Multi-purpose greeting/thanks いやいや (iyaiya) - \"No, no\" (humble) そうですか (Sou desu ka) - \"Is that so?\" (polite)"}]},{"level":2,"heading":"Elderly (65+ years)","content":"","children":[{"level":3,"heading":"Characteristics","content":"Formal and traditional:\n\nConsistently formal speech: - Full です/ます usage - Complete sentences - Traditional vocabulary\n\nRegional dialect stronger:\n\nMore likely to use: - Local dialect at home - Traditional expressions - Regional vocabulary\n\nPre-war education influence:\n\nDifferent kanji preferences Classical expressions More formal writing style\n\nGenerational vocabulary:\n\nWords that younger generations don''t use: - お前さん (omae-san) - \"You\" (old-fashioned) - あなた様 (anata-sama) - Very polite \"you\" - Traditional counters and expressions"},{"level":3,"heading":"Common expressions","content":"おはようございます (Ohayou gozaimasu) - Always full form ありがとうございます (Arigatou gozaimasu) - Never shortened いただきます (Itadakimasu) - Consistently used before meals ごちそうさまでした (Gochisousama deshita) - Full form after meals\n\nTraditional wisdom expressions:\n\nもったいない (mottainai) - \"What a waste!\" お陰様で (okage-sama de) - \"Thanks to you/fortune\" ご無沙汰しております (Gobusata shite orimasu) - \"It''s been a long time\""}]},{"level":2,"heading":"Age-Based Hierarchical Language","content":"","children":[{"level":3,"heading":"Speaking to Older People (敬語 keigo)","content":"Humble forms (謙譲語):\n\n行く (iku) → 参る (mairu) - \"go\" (humble) 言う (iu) → 申す (mousu) - \"say\" (humble) いる (iru) → おる (oru) - \"be\" (humble)\n\nHonorific forms (尊敬語):\n\n行く (iku) → いらっしゃる (irassharu) - \"go\" (respectful) 言う (iu) → おっしゃる (ossharu) - \"say\" (respectful) いる (iru) → いらっしゃる (irassharu) - \"be\" (respectful)\n\nPolite prefixes:\n\nお待ちください (Omachi kudasai) - \"Please wait\" ご覧ください (Goran kudasai) - \"Please look\""},{"level":3,"heading":"Speaking to Younger People","content":"Less formal but respectful:\n\nFriends'' children: 〜くん/〜ちゃん suffixes Own children: Plain forms, commands Students (as teacher): 〜さん or 〜くん"},{"level":3,"heading":"Peer Communication","content":"Same age, same status:\n\nUse casual forms: - Mutual plain speech - Shared slang and expressions - Relaxed formality\n\nSame age, different status:\n\nStill requires some formality: - Colleague but different rank - Acquaintance vs close friend - Professional context"}]},{"level":2,"heading":"Generational Slang Timeline","content":"","children":[{"level":3,"heading":"Showa Era (1926-1989)","content":"Older generations remember:\n\nナウい (naui) - \"Cool/trendy\" (from English \"now\") チョベリバ (choberiba) - \"Super bad\" (1990s high school girls) アベック (abekku) - \"Couple\" (from French \"avec\")"},{"level":3,"heading":"Heisei Era (1989-2019)","content":"Middle-aged adults used:\n\nキモい (kimoi) - \"Gross\" (still used) ウザい (uzai) - \"Annoying\" (still used) ダサい (dasai) - \"Lame\" (still used) ムカつく (mukatsuku) - \"Irritating\" (still used)"},{"level":3,"heading":"Reiwa Era (2019-present)","content":"Current youth language:\n\nぴえん (pien) - \"Sad\" エモい (emoi) - \"Emotional\" ガチ (gachi) - \"Serious\" バズる (bazuru) - \"Go viral\" 推し (oshi) - \"Favorite\" (idol/character)"}]},{"level":2,"heading":"Technology and Age","content":"","children":[{"level":3,"heading":"Digital Native (Gen Z, younger millennials)","content":"Comfortable with:\n\nSNS用語 (SNS yougo) - Social media terms ネット用語 (netto yougo) - Internet slang 絵文字 (emoji) / スタンプ (sutanpu) - Heavy use\n\nAbbreviations:\n\nりょ (ryo) - \"OK\" (from 了解) おk (ok) - \"OK\" www - \"lol\" 草 (kusa) - \"lol\" (grass, looks like www)"},{"level":3,"heading":"Digital Immigrants (Older millennials, Gen X)","content":"Mixed comfort:\n\nUses technology but more formal: - Proper sentences in messages - Less emoji usage - Standard abbreviations"},{"level":3,"heading":"Limited Digital (Older generations)","content":"Traditional communication:\n\nPrefer phone calls to messages Formal email language Limited social media"}]},{"level":2,"heading":"Common Mistakes by Age Group","content":"","children":[{"level":3,"heading":"Young Learners","content":"❌ Using overly casual speech with elders: おばあちゃん、これやばくない？\n\n✓ おばあさん、これすごいですね。"},{"level":3,"heading":"Adult Learners","content":"❌ Trying to use teen slang: 50-year-old: マジやばくね？\n\n✓ 50-year-old: 本当にすごいですね。"},{"level":3,"heading":"All Learners","content":"❌ Ignoring age hierarchy: Using plain forms with obviously older person\n\n✓ Using です/ます and respectful language with elders"}]},{"level":2,"heading":"Speaking Appropriately Across Ages","content":"","children":[{"level":3,"heading":"To Children (as adult)","content":"Simple vocabulary 〜なさい commands Encouraging words: - いい子だね (Ii ko da ne) - \"Good child\" - すごいね (Sugoi ne) - \"Amazing!\" - がんばって (Ganbatte) - \"Do your best!\""},{"level":3,"heading":"To Teenagers (as adult)","content":"Respectful but not condescending Avoid trying to use their slang Clear です/ます forms Listen more than lecture"},{"level":3,"heading":"To Peers","content":"Match their formality level Share generational references Casual but respectful Build on common ground"},{"level":3,"heading":"To Elders","content":"Always use respectful language Full honorifics and humble forms Listen attentively Traditional expressions welcome"}]},{"level":2,"heading":"Summary","content":"Key Points:\n\nJapanese speech varies significantly across age groups Hierarchy and respect are age-based Slang and casual language are youth-dominated Formality increases with age Technology creates generational language divides Always err on the side of respect","children":[]}]'::jsonb,
  '["Different historical language education","Generational slang and trends","Changing social norms and formality","Technological and cultural influences","Shifting attitudes toward hierarchy","Japanese speech varies significantly across age groups","Hierarchy and respect are age-based","Slang and casual language are youth-dominated","Formality increases with age","Technology creates generational language divides","Always err on the side of respect"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'apologies',
  'Apologies',
  'https://practice-japanese.com/docs/apologies/',
  'other',
  NULL,
  '[{"level":2,"heading":"Apologies","content":"Apologies are deeply embedded in Japanese culture and communication. Japanese people apologize more frequently than English speakers, using apologies not just for mistakes but also to show consideration, humility, and social awareness.\n\nEnglish: Apologize for clear wrongs Japanese: Apologize frequently Shows consideration Maintains harmony Cultural importance More than just \"sorry\""},{"level":2,"heading":"Basic Apology Levels","content":"","children":[{"level":3,"heading":"Casual apologies","content":"ごめん \"Sorry\" (casual) ごめんね \"Sorry\" (casual, softer) 悪い \"My bad\" (very casual, masculine) わりい \"Sorry\" (very casual, masculine) Friends/family only Informal settings Not for strangers"},{"level":3,"heading":"Polite apologies","content":"ごめんなさい \"I''m sorry\" (polite) すみません \"Excuse me / Sorry\" すいません \"Excuse me / Sorry\" (casual pronunciation) Most common Everyday use Safe choice General purpose Widely appropriate"},{"level":3,"heading":"Formal apologies","content":"申し訳ございません \"I sincerely apologize\" 申し訳ありません \"I apologize\" 大変申し訳ございません \"I deeply apologize\" Business settings Serious mistakes Formal situations Maximum respect Heavy apology"},{"level":3,"heading":"Most formal","content":"誠に申し訳ございません \"I sincerely and deeply apologize\" 謹んでお詫び申し上げます \"I humbly apologize\" 深くお詫び申し上げます \"I deeply apologize\" Public apologies Major errors Official statements Extreme formality Company/public figures Written apologies"}]},{"level":2,"heading":"すみません Usage","content":"","children":[{"level":3,"heading":"Multiple meanings","content":"すみません as apology: \"I''m sorry\" すみません as attention: \"Excuse me\" すみません as thanks: \"Thank you / Sorry for the trouble\" Most versatile phrase Context determines meaning Learn all uses"},{"level":3,"heading":"Getting attention","content":"すみません、ちょっといいですか \"Excuse me, do you have a moment?\" すみません、これください \"Excuse me, I''ll take this\" 店員さん、すみません \"Waiter, excuse me\" No apology intended Just getting attention Common usage"},{"level":3,"heading":"Expressing gratitude","content":"すみません、ありがとうございます \"Sorry (for the trouble), thank you\" わざわざすみません \"Sorry for going out of your way\" Japanese: Feel indebted Apologize for burden Even when helped Cultural difference Shows consideration"}]},{"level":2,"heading":"Degrees of Sorry","content":"","children":[{"level":3,"heading":"Light apologies","content":"ごめん \"Oops / Sorry\" Use: Small mistakes Minor bumps Casual settings Light tone Quick apology Between friends"},{"level":3,"heading":"Medium apologies","content":"ごめんなさい \"I''m sorry\" すみません \"I''m sorry / Excuse me\" Use: Standard mistakes Everyday errors General situations Most common level Appropriate broadly Go-to apology"},{"level":3,"heading":"Heavy apologies","content":"本当にごめんなさい \"I''m really sorry\" 本当に申し訳ございません \"I''m truly sorry\" Use: Serious mistakes Hurt feelings Major errors Shows sincerity Deep regret Genuine remorse"}]},{"level":2,"heading":"Specific Situations","content":"","children":[{"level":3,"heading":"Being late","content":"遅れてごめん \"Sorry I''m late\" (casual) 遅れてすみません \"Sorry I''m late\" (polite) 遅刻して申し訳ございません \"I apologize for being late\" (formal) Common scenario Acknowledge lateness Show respect for time"},{"level":3,"heading":"Causing trouble","content":"ご迷惑をおかけしました \"I caused you trouble / inconvenience\" お手数をおかけしました \"I caused you trouble / effort\" ご心配をおかけしました \"I made you worry\" Acknowledging impact Taking responsibility Formal phrasing"},{"level":3,"heading":"Mistakes at work","content":"申し訳ございません \"I sincerely apologize\" 不注意でした \"It was my carelessness\" 以後気をつけます \"I''ll be careful from now on\" Professional setting Take responsibility Show improvement intent"},{"level":3,"heading":"Bumping into someone","content":"すみません \"Sorry / Excuse me\" ごめんなさい \"I''m sorry\" 失礼しました \"Excuse me / I was rude\" Physical contact Public spaces Quick apology Immediate response Keep moving Brief acknowledgment"}]},{"level":2,"heading":"Adding Emphasis","content":"","children":[{"level":3,"heading":"Making it stronger","content":"本当に + apology \"Really/truly sorry\" 大変 + apology \"Very/extremely sorry\" 心から + apology \"From my heart sorry\" 誠に + apology \"Sincerely sorry\" Intensifiers Shows depth Genuine emotion"},{"level":3,"heading":"Examples with emphasis","content":"本当にごめんなさい \"I''m really sorry\" 大変申し訳ございません \"I''m extremely sorry\" 心からお詫びします \"I apologize from my heart\" Deeper remorse Serious situations Heartfelt apology"}]},{"level":2,"heading":"Explaining the Apology","content":"","children":[{"level":3,"heading":"Giving reasons","content":"すみません、[reason]で \"Sorry, because of [reason]\" 申し訳ございません。[explanation] \"I apologize. [explanation]\" Example: すみません、寝坊してしまいました \"Sorry, I overslept\" Context provided Taking responsibility Honest explanation"},{"level":3,"heading":"Without excuse","content":"言い訳はありません \"I have no excuse\" 弁解の余地もありません \"There''s no room for excuses\" 完全に私のミスです \"It''s completely my mistake\" No deflection Full responsibility Humble acceptance"}]},{"level":2,"heading":"Apologizing for Others","content":"","children":[{"level":3,"heading":"On behalf of someone","content":"彼の代わりに謝ります \"I apologize on his behalf\" 申し訳ございません（代表して） \"I apologize (representing them)\" Use: Boss for employee Use: Parent for child Taking responsibility Group responsibility Hierarchical duty"}]},{"level":2,"heading":"Refusing Apology","content":"","children":[{"level":3,"heading":"Accepting apology","content":"いいよ \"It''s okay\" (casual) 大丈夫です \"It''s alright\" 気にしないで \"Don''t worry about it\" 謝らなくてもいいよ \"You don''t need to apologize\" Gracious acceptance Dismissing need Moving forward"},{"level":3,"heading":"When no apology needed","content":"謝る必要ないよ \"You don''t need to apologize\" 全然問題ないです \"It''s not a problem at all\" そんなことで謝らないで \"Don''t apologize for that\" Reassuring speaker No harm done Being gracious"}]},{"level":2,"heading":"Customer Service Apologies","content":"","children":[{"level":3,"heading":"Store/restaurant staff","content":"申し訳ございません \"I sincerely apologize\" 大変お待たせいたしました \"Thank you for waiting\" (apologetic tone) ご不便をおかけして申し訳ございません \"I apologize for the inconvenience\" Professional standard Maximum politeness Service industry"},{"level":3,"heading":"Company apologies","content":"この度は誠に申し訳ございませんでした \"We sincerely apologize for this matter\" 深くお詫び申し上げます \"We deeply apologize\" Official statements Public apologies Corporate responsibility Formal written Press releases Major issues"}]},{"level":2,"heading":"Cultural Nuances","content":"","children":[{"level":3,"heading":"Preventive apologies","content":"すみません、質問があるんですが \"Sorry, I have a question\" 申し訳ないんですけど \"I''m sorry but...\" Japanese: Apologize before asking English: Just ask Cultural difference Showing consideration Not wanting to burden Politeness strategy"},{"level":3,"heading":"Apologizing for existing","content":"すみません (when being helped) \"Sorry (for the trouble)\" 恐れ入ります \"I''m grateful / Sorry to trouble you\" Japanese mindset: Being burden to others Should apologize Even when helped Cultural perspective Group harmony"}]},{"level":2,"heading":"Apology Body Language","content":"","children":[{"level":3,"heading":"Physical expression","content":"Bowing deeply Looking down Humble posture Serious expression Physical apology Shows sincerity Cultural expectation Depth of bow = seriousness Multiple bows = deep remorse"}]},{"level":2,"heading":"Repeated Apologies","content":"","children":[{"level":3,"heading":"Multiple sorry","content":"すみません、すみません \"Sorry, sorry\" 本当にごめんなさい、ごめんなさい \"I''m really sorry, sorry\" Japanese: Often repeat Shows sincerity Emphasizes remorse Not annoying Culturally normal Expected behavior"}]},{"level":2,"heading":"Email Apologies","content":"","children":[{"level":3,"heading":"Written apologies","content":"大変申し訳ございません \"I sincerely apologize\" 深くお詫び申し上げます \"I deeply apologize\" ご迷惑をおかけして申し訳ございません \"I apologize for any inconvenience\" Formal writing Business emails Professional tone More formal in writing Complete sentences Respectful language"}]},{"level":2,"heading":"Apology and Responsibility","content":"","children":[{"level":3,"heading":"Taking blame","content":"私の責任です \"It''s my responsibility\" 私のせいです \"It''s my fault\" 私が悪かったです \"I was wrong\" Clear ownership No deflection Accepting fault Honest admission Shows maturity Earns respect"},{"level":3,"heading":"Promising improvement","content":"二度とこのようなことがないよう気をつけます \"I''ll be careful so this never happens again\" 今後気をつけます \"I''ll be careful from now on\" 反省しています \"I''m reflecting on this\" Future action Showing growth Commitment to change"}]},{"level":2,"heading":"Regional Variations","content":"","children":[{"level":3,"heading":"Kansai dialect","content":"すんません \"Sorry\" (Kansai pronunciation) わるいな \"My bad\" ごめんやで \"Sorry\" (with Kansai ending) Regional flavor Dialect differences Local speech patterns"}]},{"level":2,"heading":"When NOT to Apologize","content":"","children":[{"level":3,"heading":"Unnecessary apologies","content":"Don''t apologize for: - Other''s mistakes (unless responsible) - Things beyond your control - Being right Balance needed Not everything requires sorry Overdoing it weakens Choose appropriately"}]},{"level":2,"heading":"Apology Combinations","content":"","children":[{"level":3,"heading":"Complex apologies","content":"本当に申し訳ございません。私の不注意でした。 \"I''m truly sorry. It was my carelessness.\" 遅れてすみません。言い訳になりますが、電車が遅れまして。 \"Sorry I''m late. This is an excuse, but the train was delayed.\" 申し訳ございません。すぐに対応いたします。 \"I apologize. I''ll handle it immediately.\" Full structure Explanation + action Professional apology"}]},{"level":2,"heading":"Apologizing to Specific People","content":"","children":[{"level":3,"heading":"To superiors","content":"申し訳ございません \"I sincerely apologize\" 大変失礼いたしました \"I was very rude\" Maximum politeness Respectful language Humble stance"},{"level":3,"heading":"To customers","content":"大変申し訳ございませんでした \"We sincerely apologize\" ご迷惑をおかけしました \"We caused you inconvenience\" Business relationship Professional standard Customer service"},{"level":3,"heading":"To friends","content":"ごめん \"Sorry\" 悪かった \"My bad\" Casual and brief No formality needed Direct and honest"}]},{"level":2,"heading":"Accepting vs Giving Apology","content":"","children":[{"level":3,"heading":"When someone apologizes to you","content":"いいよ \"It''s okay\" 気にしないで \"Don''t worry\" 大丈夫 \"It''s alright\" 全然 \"Not at all\" Gracious response Dismissing issue Restoring harmony Cultural expectation Minimize conflict"}]},{"level":2,"heading":"Serious Apologies","content":"","children":[{"level":3,"heading":"Major mistakes","content":"深くお詫び申し上げます \"I deeply apologize\" 取り返しのつかないことをしてしまいました \"I''ve done something irreparable\" どうか許してください \"Please forgive me\" Grave errors Deep regret Seeking forgiveness Heavy emotional weight Serious consequences"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Errors to avoid","content":"❌ Using ごめん to strangers ✓ すみません or ごめんなさい ❌ Not apologizing when late ✓ Always apologize for lateness ❌ Using すみません only as sorry ✓ Remember multiple meanings ❌ Not bowing when serious ✓ Physical apology needed Learn cultural context Match formality level"}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'ba-conditional',
  'ば (ba) - If/When (general)',
  'https://practice-japanese.com/docs/ba-conditional/',
  'conditionals',
  NULL,
  '[{"level":2,"heading":"What is ば Conditional?","content":"ば expresses general conditions, requirements, or hypothetical situations. It means \"if\" or \"when\" in a general sense, often implying \"if X, then Y is possible/will happen.\"\n\n安ければ、買います \"If it''s cheap, I''ll buy it\" 頑張れば、できる \"If you try hard, you can do it\" 時間があれば、手伝います \"If I have time, I''ll help\""},{"level":2,"heading":"Formation","content":"","children":[{"level":3,"heading":"With verbs: Change final う sound to え + ば","content":"| Dictionary | ば-form | Rule | | ---------- | ------- | --------- | | 書く | 書けば | く → けば | | 読む | 読めば | む → めば | | 話す | 話せば | す → せば | | 待つ | 待てば | つ → てば | | 死ぬ | 死ねば | ぬ → ねば | | 買う | 買えば | う → えば | | 飲む | 飲めば | む → めば | | 遊ぶ | 遊べば | ぶ → べば | | 行く | 行けば | く → けば |"},{"level":3,"heading":"Irregular verbs","content":"する → すれば 来る → 来れば (くれば)"},{"level":3,"heading":"With い-adjectives: Remove い + ければ","content":"高い → 高ければ (If it''s expensive) 安い → 安ければ (If it''s cheap) いい/良い → 良ければ (よければ) (If it''s good) ない → なければ (If there isn''t)"},{"level":3,"heading":"With な-adjectives and nouns: + であれば / ならば","content":"静か → 静かであれば / 静かならば (If it''s quiet) 学生 → 学生であれば / 学生ならば (If (someone is) a student) 雨 → 雨であれば / 雨ならば (If it''s rain) Often shortened to なら in modern Japanese"}]},{"level":2,"heading":"Basic Usage","content":"","children":[{"level":3,"heading":"General conditions","content":"時間があれば、行きます \"If I have time, I''ll go\" 安ければ、買います \"If it''s cheap, I''ll buy it\" 分からなければ、聞いてください \"If you don''t understand, please ask\""},{"level":3,"heading":"Requirements/prerequisites","content":"勉強すれば、合格できる \"If you study, you can pass\" 頑張れば、夢は叶う \"If you try hard, dreams come true\" 練習すれば、上手になる \"If you practice, you''ll improve\""},{"level":3,"heading":"Hypothetical situations","content":"お金があれば、旅行したい \"If I had money, I''d want to travel\" もっと時間があれば、もっと勉強できるのに \"If I had more time, I could study more\" 彼がいれば、楽しいだろう \"If he were here, it would be fun\""}]},{"level":2,"heading":"Key Characteristics","content":"","children":[{"level":3,"heading":"Can use with commands/requests","content":"暇なら、手伝ってください ✓ \"If you''re free, please help\" 分からなければ、聞いてね ✓ \"If you don''t understand, ask me\" Commands are OK after ば"},{"level":3,"heading":"Cannot use with past tense results","content":"Wrong: 行けば、いなかった ❌ Right: 行ったら、いなかった ✓ \"When I went, he wasn''t there\" Cannot describe past discoveries with ば"},{"level":3,"heading":"Often with potential form","content":"頑張れば、できる \"If you try hard, you can do it\" 練習すれば、上手になれる \"If you practice, you can improve\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Making Plans","content":"A: 明日、映画に行かない？ \"Won''t you go to a movie tomorrow?\" B: 時間があれば、行きたいな \"If I have time, I''d like to go\" A: じゃあ、連絡してね \"Then, let me know\""},{"level":3,"heading":"Example 2: Asking for Help","content":"A: この問題が分からないんだけど \"I don''t understand this problem\" B: よければ、教えるよ \"If you''d like, I''ll teach you\" A: 本当？ありがとう！ \"Really? Thank you!\""},{"level":3,"heading":"Example 3: Giving Advice","content":"A: 試験、心配だな \"I''m worried about the exam\" B: もっと勉強すれば、大丈夫だよ \"If you study more, you''ll be fine\" A: そうだね、頑張る \"You''re right, I''ll try hard\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using with past results","content":"Wrong: 家に帰れば、猫が寝ていた ❌ Right: 家に帰ったら、猫が寝ていた ✓ \"When I got home, the cat was sleeping\" Use たら for past discoveries"},{"level":3,"heading":"Mistake 2: Wrong conjugation","content":"Wrong: 行きば ❌ Right: 行けば ✓ \"If (you) go\" Change う sound to え, then add ば"},{"level":3,"heading":"Mistake 3: Overusing in casual speech","content":"Too formal: 暇なら、遊ぼう ⚠️ More natural: 暇だったら、遊ぼう ✓ \"If you''re free, let''s hang out\" たら is more common in casual speech"}]},{"level":2,"heading":"ば in Set Phrases","content":"","children":[{"level":3,"heading":"～ば～ほど (the more... the more...)","content":"見れば見るほど、好きになる \"The more I look, the more I like it\" 練習すれば練習するほど、上手になる \"The more you practice, the better you become\""},{"level":3,"heading":"～ばいい / ～ばよかった","content":"どうすればいい？ \"What should I do?\" (lit: If I do what, is it good?) もっと勉強すればよかった \"I should have studied more\""},{"level":3,"heading":"～なければならない (must)","content":"行かなければならない \"I must go\" 勉強しなければならない \"I must study\" Obligation pattern"}]},{"level":2,"heading":"Expressing Requirements","content":"","children":[{"level":3,"heading":"Prerequisites","content":"会員になれば、割引がもらえる \"If you become a member, you get a discount\" 申請すれば、許可される \"If you apply, it will be approved\" 登録すれば、使える \"If you register, you can use it\""}]},{"level":2,"heading":"Giving Conditions","content":"","children":[{"level":3,"heading":"For achieving goals","content":"毎日練習すれば、必ず上達する \"If you practice every day, you''ll definitely improve\" 諦めなければ、いつか成功する \"If you don''t give up, you''ll succeed someday\" 早く寝れば、朝すっきり起きられる \"If you sleep early, you can wake up refreshed\""}]},{"level":2,"heading":"Hypothetical Wishes","content":"","children":[{"level":3,"heading":"Expressing desires","content":"お金があれば、世界中を旅行したい \"If I had money, I''d want to travel around the world\" 時間があれば、もっと勉強できるのに \"If I had time, I could study more\" 彼がいれば、寂しくないのに \"If he were here, I wouldn''t be lonely\""}]},{"level":2,"heading":"ば vs たら","content":"","children":[{"level":3,"heading":"ば - General/formal","content":"安ければ、買います \"If it''s cheap, I''ll buy it\" (general condition) More formal feeling Abstract condition"},{"level":3,"heading":"たら - Specific/casual","content":"安かったら、買います \"If it''s cheap, I''ll buy it\" (specific instance) More casual Concrete situation"},{"level":3,"heading":"Both often work","content":"時間があれば、行きます ✓ 時間があったら、行きます ✓ \"If I have time, I''ll go\" Slight nuance difference"}]},{"level":2,"heading":"Negative Form","content":"","children":[{"level":3,"heading":"～なければ (if not)","content":"急がなければ、遅れる \"If you don''t hurry, you''ll be late\" 勉強しなければ、合格できない \"If you don''t study, you can''t pass\" 行かなければならない \"(I) must go\" (if I don''t go, it''s not good)"}]},{"level":2,"heading":"Formal Patterns","content":"","children":[{"level":3,"heading":"～であれば (formal)","content":"学生であれば、割引があります \"If you''re a student, there''s a discount\" 可能であれば、お願いします \"If it''s possible, please (do it)\" More formal than なら"}]},{"level":2,"heading":"Common Set Expressions","content":"","children":[{"level":3,"heading":"どうすればいい","content":"どうすればいいですか \"What should I do?\" 何を食べればいい？ \"What should I eat?\" Asking for advice"},{"level":3,"heading":"～ば～ほど","content":"考えれば考えるほど、分からなくなる \"The more I think, the less I understand\" 勉強すれば勉強するほど、面白くなる \"The more I study, the more interesting it becomes\""},{"level":3,"heading":"あれば／いれば","content":"お金があれば - If there''s money 時間があれば - If there''s time 人がいれば - If there are people"}]},{"level":2,"heading":"Politeness Levels","content":"","children":[{"level":3,"heading":"Casual: plain + ば","content":"安ければ、買う \"If it''s cheap, I''ll buy\""},{"level":3,"heading":"Polite: plain + ば + polite ending","content":"安ければ、買います \"If it''s cheap, I''ll buy\" Common pattern"}]},{"level":2,"heading":"With Different Verb Types","content":"","children":[{"level":3,"heading":"Godan verbs","content":"書く → 書けば (if you write) 読む → 読めば (if you read) 話す → 話せば (if you speak)"},{"level":3,"heading":"Ichidan verbs","content":"食べる → 食べれば (if you eat) 見る → 見れば (if you see) 起きる → 起きれば (if you wake up)"},{"level":3,"heading":"Irregular verbs","content":"する → すれば (if you do) 来る → 来れば (if you come)"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"時間があれば、行きます \"If I have time, I''ll go\" 分からなければ、聞いてください \"If you don''t understand, please ask\" 安ければ、買いたい \"If it''s cheap, I want to buy it\""},{"level":3,"heading":"Intermediate","content":"もっと勉強すれば、試験に合格できると思います \"If you study more, I think you can pass the exam\" よければ、一緒に食事に行きませんか \"If you''d like, won''t you go to dinner together?\" 毎日練習すれば、必ず上手になります \"If you practice every day, you''ll definitely improve\""},{"level":3,"heading":"Advanced","content":"この問題を解決したければ、まず原因を 特定しなければならない。原因が分かれば、 対策を立てることができる。 \"If you want to solve this problem, first you must identify the cause. If you understand the cause, you can formulate countermeasures.\" もし私にもっと時間とお金があれば、 世界中を旅して、様々な文化を学びたい。 そうすれば、もっと広い視野を持てるだろう。 \"If I had more time and money, I''d want to travel around the world and learn about various cultures. If I did that, I could have a broader perspective.\""}]},{"level":2,"heading":"Related Grammar Points","content":"たら conditional - Hypothetical/sequential と conditional - Natural consequence なら conditional - Contextual Conditionals overview - All conditional forms 〜ば〜ほど - The more... the more 〜なければならない - Must/have to","children":[]}]'::jsonb,
  '["たら conditional - Hypothetical/sequential","と conditional - Natural consequence","なら conditional - Contextual","Conditionals overview - All conditional forms","〜ば〜ほど - The more... the more","〜なければならない - Must/have to"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'bakari-detail',
  'ばかり (Only/Just)',
  'https://practice-japanese.com/docs/bakari-detail/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is ばかり?","content":"ばかり (bakari) is a versatile particle with several related meanings, all centered around limitation or focus. Its primary meanings are \"only,\" \"nothing but,\" \"just,\" \"about,\" or \"just finished.\"\n\nMain uses:\n\nLimitation - \"only,\" \"nothing but\" (similar to だけ) Approximation - \"about,\" \"approximately\" Repetition - \"always,\" \"keeps doing\" Recent completion - \"just finished\"\n\nCasual form: っぱり (ppari) or ばっか (bakka)\n\n遊んでばかりいる。 Asonde bakari iru. \"All I do is play.\" / \"I do nothing but play.\" 買ったばかりだ。 Katta bakari da. \"I just bought it.\""},{"level":2,"heading":"Usage 1: Limitation (Only/Nothing but)","content":"","children":[{"level":3,"heading":"Basic limitation pattern","content":"Similar to だけ but with stronger nuance of excess or criticism:\n\nStructure: [Noun/Verb-て form] + ばかり\n\nWith nouns:\n\n肉ばかり食べる。 Niku bakari taberu. \"I eat only meat.\" / \"I eat nothing but meat.\" 文句ばかり言う。 Monku bakari iu. \"He does nothing but complain.\" お金のことばかり考える。 Okane no koto bakari kangaeru. \"All I think about is money.\"\n\nWith verbs (て-form):\n\n寝てばかりいる。 Nete bakari iru. \"All you do is sleep.\" 遊んでばかりいる。 Asonde bakari iru. \"You do nothing but play.\" 食べてばかりいる。 Tabete bakari iru. \"All I do is eat.\""},{"level":3,"heading":"Emphasizing repetition or excess","content":"同じことばかり言う。 Onaji koto bakari iu. \"You keep saying the same thing.\" 失敗ばかりする。 Shippai bakari suru. \"I keep making mistakes.\" / \"I do nothing but fail.\" 文句ばかり言っている。 Monku bakari itte iru. \"All you do is complain.\""}]},{"level":2,"heading":"Usage 2: Approximation (About/Around)","content":"","children":[{"level":3,"heading":"Indicating approximate amounts","content":"Structure: [Number/Quantity] + ばかり\n\n10個ばかり買った。 Jukko bakari katta. \"I bought about 10.\" 一時間ばかり待った。 Ichi-jikan bakari matta. \"I waited about an hour.\" 100人ばかり集まった。 Hyaku-nin bakari atsumatta. \"About 100 people gathered.\" 50万円ばかりかかった。 Gojuu man-en bakari kakatta. \"It cost about 500,000 yen.\"\n\nNote: This usage is somewhat formal/literary."}]},{"level":2,"heading":"Usage 3: Just Finished (Recently Completed)","content":"","children":[{"level":3,"heading":"たばかり (ta-bakari) - Just did","content":"Structure: [Past tense verb] + ばかり\n\n食べたばかりだ。 Tabeta bakari da. \"I just ate.\" 来たばかりです。 Kita bakari desu. \"I just came.\" / \"I just arrived.\" 買ったばかりなのに壊れた。 Katta bakari na noni kowareta. \"Even though I just bought it, it broke.\" 始めたばかりです。 Hajimeta bakari desu. \"I just started.\" 会ったばかりの人。 Atta bakari no hito. \"A person I just met.\""},{"level":3,"heading":"Emphasizing recency","content":"昨日買ったばかりだ。 Kinou katta bakari da. \"I just bought it yesterday.\" 今起きたばかり。 Ima okita bakari. \"I just woke up now.\" 引っ越したばかりです。 Hikkoshita bakari desu. \"I just moved.\""}]},{"level":2,"heading":"Usage 4: About to (Formal/Literary)","content":"","children":[{"level":3,"heading":"ばかりだ - On the verge of","content":"Structure: [Verb dictionary form] + ばかりだ\n\n泣かんばかりの顔。 Nakan bak"}]}]'::jsonb,
  '["Limitation - \"only,\" \"nothing but\" (similar to だけ)","Approximation - \"about,\" \"approximately\"","Repetition - \"always,\" \"keeps doing\"","Recent completion - \"just finished\""]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'basic-patterns',
  'Basic Japanese Sentence Patterns',
  'https://practice-japanese.com/docs/basic-patterns/',
  'quotation',
  'N5',
  '[{"level":2,"heading":"Pattern 1: X は Y です (X is Y)","content":"Structure: [Topic] は [Noun/Adjective] です Meaning: \"X is Y\" or \"As for X, it''s Y\"\n\nThe most basic pattern for stating facts and introducing yourself.","children":[{"level":3,"heading":"Examples","content":"私は学生です。 Watashi wa gakusei desu. I (topic) student am. \"I am a student.\" これは本です。 Kore wa hon desu. This (topic) book is. \"This is a book.\" 東京は大きいです。 Toukyou wa ookii desu. Tokyo (topic) big is. \"Tokyo is big.\""},{"level":3,"heading":"Making it Negative","content":"Add じゃないです (janai desu) or じゃありません (ja arimasen):\n\n私は学生じゃないです。 Watashi wa gakusei janai desu. \"I''m not a student.\" これは本じゃありません。 Kore wa hon ja arimasen. \"This is not a book.\""},{"level":3,"heading":"Making it Past Tense","content":"Change です to でした (deshita):\n\n私は学生でした。 Watashi wa gakusei deshita. \"I was a student.\" 東京は寒かったです。 Toukyou wa samukatta desu. \"Tokyo was cold.\""}]},{"level":2,"heading":"Pattern 2: X は Y を V (X does V to Y)","content":"Structure: [Topic] は [Object] を [Verb] Meaning: \"X does [verb] to Y\"\n\nThis pattern describes actions with direct objects.","children":[{"level":3,"heading":"Examples","content":"私は本を読みます。 Watashi wa hon wo yomimasu. I (topic) book (object) read. \"I read books.\" 田中さんはコーヒーを飲みます。 Tanaka-san wa ko-hi- wo nomimasu. \"Tanaka drinks coffee.\" 学生は日本語を勉強します。 Gakusei wa nihongo wo benkyou shimasu. \"Students study Japanese.\""},{"level":3,"heading":"Common Verbs","content":"食べる (taberu) - to eat 飲む (nomu) - to drink 見る (miru) - to see/watch 聞く (kiku) - to listen/hear 読む (yomu) - to read 書く (kaku) - to write 買う (kau) - to buy"},{"level":3,"heading":"Making it Negative","content":"Change ます to ません:\n\n私は本を読みません。 Watashi wa hon wo yomimasen. \"I don''t read books.\" コーヒーを飲みません。 Ko-hi- wo nomimasen. \"I don''t drink coffee.\""},{"level":3,"heading":"Making it Past Tense","content":"Change ます to ました:\n\n私は本を読みました。 Watashi wa hon wo yomimashita. \"I read a book.\""}]},{"level":2,"heading":"Pattern 3: X に Y があります/います (Y exists at X)","content":"Structure: [Location] に [Thing] が あります/います Meaning: \"There is Y at/in X\" or \"Y exists at X\"\n\nUse あります (arimasu) for inanimate objects, います (imasu) for living things.","children":[{"level":3,"heading":"Examples - Inanimate (あります)","content":"机の上に本があります。 Tsukue no ue ni hon ga arimasu. Desk top on book exists. \"There is a book on the desk.\" 冷蔵庫に牛乳があります。 Reizouko ni gyuunyuu ga arimasu. \"There is milk in the refrigerator.\" 駅の近くにコンビニがあります。 Eki no chikaku ni konbini ga arimasu. \"There''s a convenience store near the station.\""},{"level":3,"heading":"Examples - Animate (います)","content":"部屋に猫がいます。 Heya ni neko ga imasu. Room in cat exists. \"There is a cat in the room.\" 公園に子供がいます。 Kouen ni kodomo ga imasu. \"There are children in the park.\" 教室に先生がいます。 Kyoushitsu ni sensei ga imasu. \"There is a teacher in the classroom.\""},{"level":3,"heading":"Common Location Words","content":"上 (ue) - on top, above 下 (shita) - under, below 中 (naka) - inside 外 (soto) - outside 前 (mae) - in front 後ろ (ushiro) - behind 隣 (tonari) - next to 近く (chikaku) - near"}]},{"level":2,"heading":"Pattern 4: X は Y が好きです (X likes Y)","content":"Structure: [Topic] は [Thing] が 好きです Meaning: \"X likes Y\"\n\nUse が (ga) to mark what you like, not を (wo).","children":[{"level":3,"heading":"Examples","content":"私は寿司が好きです。 Watashi wa sushi ga suki desu. I (topic) sushi (subject) like. \"I like sushi.\" 田中さんは音楽が好きです。 Tanaka-san wa ongaku ga suki desu. \"Tanaka likes music.\" 猫が好きです。 Neko ga suki desu. \"I like cats.\" [Subject implied]"},{"level":3,"heading":"Dislike - 嫌い (kirai)","content":"私は野菜が嫌いです。 Watashi wa yasai ga kirai desu. \"I dislike vegetables.\""},{"level":3,"heading":"Degrees of Like/Dislike","content":"大好き (daisuki) - love, really like 好き (suki) - like 嫌い (kirai) - dislike 大嫌い (daikirai) - hate, really dislike"}]},{"level":2,"heading":"Pattern 5: X は Y ができます (X can do Y)","content":"Structure: [Topic] は [Ability] が できます Meaning: \"X can do Y\" or \"X is able to Y\"","children":[{"level":3,"heading":"Examples","content":"私は日本語ができます。 Watashi wa nihongo ga dekimasu. I (topic) Japanese (subject) can. \"I can speak Japanese.\" 田中さんは料理ができます。 Tanaka-san wa ryouri ga dekimasu. \"Tanaka can cook.\" 泳ぐことができます。 Oyogu koto ga dekimasu. \"I can swim.\" [Literally: \"swimming thing can do\"]"},{"level":3,"heading":"Negative - Cannot","content":"私は運転ができません。 Watashi wa unten ga dekimasen. \"I cannot drive.\""}]},{"level":2,"heading":"Pattern 6: X へ/に 行きます (Go to X)","content":"Structure: [Place] へ/に 行きます/来ます Meaning: \"Go to X\" or \"Come to X\"\n\nUse へ (e) or に (ni) for destination.","children":[{"level":3,"heading":"Examples","content":"学校に行きます。 Gakkou ni ikimasu. School to go. \"I go to school.\" 日本へ行きます。 Nihon e ikimasu. \"I will go to Japan.\" 家に帰ります。 Ie ni kaerimasu. \"I return home.\" ここに来ます。 Koko ni kimasu. \"I come here.\""},{"level":3,"heading":"Common Verbs of Movement","content":"行く (iku) - to go 来る (kuru) - to come 帰る (kaeru) - to return home 戻る (modoru) - to return, go back"}]},{"level":2,"heading":"Pattern 7: X で Y をします (Do Y at/with X)","content":"Structure: [Place/Means] で [Action] をします Meaning: \"Do Y at X\" or \"Do Y by means of X\"\n\nThe particle で marks location of action or means.","children":[{"level":3,"heading":"Examples - Location","content":"図書館で勉強します。 Toshokan de benkyou shimasu. Library at study do. \"I study at the library.\" レストランで食べます。 Resutoran de tabemasu. \"I eat at a restaurant.\""},{"level":3,"heading":"Examples - Means/Tool","content":"ペンで書きます。 Pen de kakimasu. Pen with write. \"I write with a pen.\" 日本語で話します。 Nihongo de hanashimasu. \"I speak in Japanese.\" 電車で行きます。 Densha de ikimasu. \"I go by train.\""}]},{"level":2,"heading":"Pattern 8: X は Y に Z をあげます (X gives Z to Y)","content":"Structure: [Giver] は [Receiver] に [Thing] を あげます Meaning: \"X gives Z to Y\"","children":[{"level":3,"heading":"Examples","content":"私は友達にプレゼントをあげます。 Watashi wa tomodachi ni purezento wo agemasu. I (topic) friend (to) present (object) give. \"I give a present to my friend.\" 先生は学生に本をあげます。 Sensei wa gakusei ni hon wo agemasu. \"The teacher gives books to students.\""},{"level":3,"heading":"Receiving - もらいます (moraimasu)","content":"私は友達にプレゼントをもらいます。 Watashi wa tomodachi ni purezento wo moraimasu. \"I receive a present from my friend.\"\n\nSee: Giving and Receiving Verbs for more detail"}]},{"level":2,"heading":"Pattern 9: X は Y が欲しいです (X wants Y)","content":"Structure: [Topic] は [Thing] が 欲しいです Meaning: \"X wants Y\" (for things)","children":[{"level":3,"heading":"Examples","content":"私は新しい車が欲しいです。 Watashi wa atarashii kuruma ga hoshii desu. I (topic) new car (subject) want. \"I want a new car.\" 水が欲しいです。 Mizu ga hoshii desu. \"I want water.\""},{"level":3,"heading":"Want to DO something - たい (tai)","content":"For actions, use verb stem + たい:\n\n寿司を食べたいです。 Sushi wo tabetai desu. \"I want to eat sushi.\" 日本に行きたいです。 Nihon ni ikitai desu. \"I want to go to Japan.\"\n\nSee: Expressing Desires for complete guide"}]},{"level":2,"heading":"Pattern 10: Question Pattern","content":"Structure: [Statement] + か Meaning: Turns statement into question\n\nSimply add か (ka) to the end.","children":[{"level":3,"heading":"Examples","content":"これは本ですか？ Kore wa hon desu ka? \"Is this a book?\" 日本語が分かりますか？ Nihongo ga wakarimasu ka? \"Do you understand Japanese?\" どこに行きますか？ Doko ni ikimasu ka? \"Where are you going?\""},{"level":3,"heading":"Question Words","content":"何 (nani/nan) - what 誰 (dare) - who どこ (doko) - where いつ (itsu) - when どうして/なぜ (doushite/naze) - why どう (dou) - how\n\nSee: Question Patterns for detailed guide"}]},{"level":2,"heading":"Combining Patterns","content":"Real sentences often combine multiple patterns:\n\n私は図書館で日本語の本を読みました。 Watashi wa toshokan de nihongo no hon wo yomimashita. \"I read a Japanese book at the library.\" [Pattern 2: を Verb] + [Pattern 7: で location]\n\n田中さんは寿司が好きですが、私は嫌いです。 Tanaka-san wa sushi ga suki desu ga, watashi wa kirai desu. \"Tanaka likes sushi, but I dislike it.\" [Pattern 4: Likes] + が (but) + [Pattern 4: Dislikes]"},{"level":2,"heading":"Practice Exercises","content":"Try creating sentences with these patterns:","children":[{"level":3,"heading":"Exercise 1: Introduction","content":"Use Pattern 1 to introduce yourself:\n\nI am [your name] I am a student I am from [country]"},{"level":3,"heading":"Exercise 2: Daily Actions","content":"Use Pattern 2 with these verbs:\n\n食べる (eat) 見る (watch/see) 勉強する (study)"},{"level":3,"heading":"Exercise 3: Locations","content":"Use Pattern 3 to describe your room:\n\nThere is a desk There is a computer There is a chair"},{"level":3,"heading":"Exercise 4: Preferences","content":"Use Pattern 4 for likes/dislikes:\n\nI like coffee I dislike vegetables I love music"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Wrong Particle","content":"Wrong: 私は本が読みます ❌ Right: 私は本を読みます ✓\n\nUse を for direct objects of action verbs."},{"level":3,"heading":"Mistake 2: Missing Topic","content":"Wrong: 学生です ❌ (without context) Right: 私は学生です ✓\n\nInclude は topic marker when introducing new information."},{"level":3,"heading":"Mistake 3: が vs を with 好き","content":"Wrong: 寿司を好きです ❌ Right: 寿司が好きです ✓\n\n好き、嫌い、上手、下手 always use が."},{"level":3,"heading":"Mistake 4: あります vs います","content":"Wrong: 部屋に猫があります ❌ Right: 部屋に猫がいます ✓\n\nLiving things use います, inanimate objects use あります."}]},{"level":2,"heading":"Summary Chart","content":"| Pattern | Structure | Example | Meaning | | ------- | -------------------- | ---------------- | ------------- | | 1 | X は Y です | 私は学生です | X is Y | | 2 | X は Y を V | 本を読みます | Do V to Y | | 3 | X に Y があります | 机に本がある | Y exists at X | | 4 | X は Y が好き | 寿司が好き | X likes Y | | 5 | X は Y ができる | 日本語ができる | X can do Y | | 6 | X へ/に 行く | 学校に行く | Go to X | | 7 | X で Y をする | 図書館で勉強 | Do Y at X | | 8 | X は Y に Z をあげる | 友達に本をあげる | Give Z to Y | | 9 | X は Y が欲しい | 車が欲しい | Want Y | | 10 | Statement + か | 学生ですか | Question |","children":[]}]'::jsonb,
  '["食べる (taberu) - to eat","飲む (nomu) - to drink","見る (miru) - to see/watch","聞く (kiku) - to listen/hear","読む (yomu) - to read","書く (kaku) - to write","買う (kau) - to buy","上 (ue) - on top, above","下 (shita) - under, below","中 (naka) - inside","外 (soto) - outside","前 (mae) - in front","後ろ (ushiro) - behind","隣 (tonari) - next to","近く (chikaku) - near","大好き (daisuki) - love, really like","好き (suki) - like","嫌い (kirai) - dislike","大嫌い (daikirai) - hate, really dislike","行く (iku) - to go","来る (kuru) - to come","帰る (kaeru) - to return home","戻る (modoru) - to return, go back","何 (nani/nan) - what","誰 (dare) - who","どこ (doko) - where","いつ (itsu) - when","どうして/なぜ (doushite/naze) - why","どう (dou) - how","I am [your name]","I am a student","I am from [country]","食べる (eat)","見る (watch/see)","勉強する (study)","There is a desk","There is a computer","There is a chair","I like coffee","I dislike vegetables","I love music"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'before-after',
  'Before/After (前、後)',
  'https://practice-japanese.com/docs/before-after/',
  'other',
  NULL,
  '[{"level":2,"heading":"Overview","content":"Japanese uses 前 (まえ - mae) for \"before\" and 後 (あと - ato) for \"after\" to express temporal relationships between events or actions.\n\n食べる前 \"before eating\" 食べた後 \"after eating\""},{"level":2,"heading":"前 (mae) - Before","content":"","children":[{"level":3,"heading":"Formation with Verbs","content":"Use the dictionary form (present tense) before 前:\n\nVerb (dictionary form) + 前 食べる前 - before eating 行く前 - before going 寝る前 - before sleeping"},{"level":3,"heading":"Formation with Nouns","content":"Noun + の + 前 食事の前 - before the meal 会議の前 - before the meeting 学校の前 - before school"},{"level":3,"heading":"Common Patterns with 前","content":"寝る前に歯を磨く \"I brush my teeth before sleeping\" 出かける前に電話した \"I called before going out\" テストの前に勉強する \"I study before the test\" 食事の前に手を洗う \"I wash my hands before meals\""}]},{"level":2,"heading":"後 (ato) - After","content":"","children":[{"level":3,"heading":"Formation with Verbs","content":"Use the た form (past tense) before 後:\n\nVerb (た form) + 後 食べた後 - after eating 行った後 - after going 見た後 - after seeing"},{"level":3,"heading":"Formation with Nouns","content":"Noun + の + 後 食事の後 - after the meal 会議の後 - after the meeting 学校の後 - after school"},{"level":3,"heading":"Common Patterns with 後","content":"食べた後、歯を磨く \"After eating, I brush my teeth\" 帰った後、電話してください \"Please call after you get home\" 映画を見た後、レストランに行った \"After watching the movie, we went to a restaurant\" 会議の後で話しましょう \"Let''s talk after the meeting\""}]},{"level":2,"heading":"Important Differences","content":"","children":[{"level":3,"heading":"Verb Forms","content":"前: Dictionary form (present) 食べる前 - before eating 後: た form (past) 食べた後 - after eating\n\nThis is consistent - even for future actions:\n\n明日行く前に電話する \"I''ll call before going tomorrow\" 明日行った後で会おう \"Let''s meet after going tomorrow\""}]},{"level":2,"heading":"Particles: に vs で","content":"","children":[{"level":3,"heading":"に (more common)","content":"寝る前に \"before sleeping\" 食べた後に \"after eating\""},{"level":3,"heading":"で (also acceptable)","content":"寝る前で \"before sleeping\" 食べた後で \"after eating\"\n\nNote: Both are correct, but に is more common. で can emphasize the timing more."}]},{"level":2,"heading":"Time Expressions","content":"","children":[{"level":3,"heading":"[Time] + 前","content":"3時間前 - 3 hours ago 2日前 - 2 days ago 1週間前 - 1 week ago 1年前 - 1 year ago 1時間前に電話した \"I called 1 hour ago\" 2日前に会った \"I met (them) 2 days ago\""},{"level":3,"heading":"[Time] + 後","content":"3時間後 - 3 hours later 2日後 - 2 days later 1週間後 - 1 week later 1年後 - 1 year later 1時間後に来る \"I''ll come 1 hour later\" 2日後に会いましょう \"Let''s meet 2 days later\""}]},{"level":2,"heading":"Before/After Places","content":"","children":[{"level":3,"heading":"〜の前 (in front of)","content":"駅の前 - in front of the station 家の前 - in front of the house 学校の前 - in front of the school 駅の前で待っている \"I''m waiting in front of the station\"\n\nContext determines meaning:\n\nTemporal: 学校の前に勉強する \"study before school\" Spatial: 学校の前に車がある \"there''s a car in front of the school\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Daily Routine","content":"A: いつ歯を磨きますか？ \"When do you brush your teeth?\" B: 寝る前と、起きた後に磨きます \"I brush before sleeping and after waking up\" A: 朝ごはんの前？後？ \"Before or after breakfast?\" B: 朝ごはんの後です \"After breakfast\""},{"level":3,"heading":"Example 2: Meeting Plans","content":"A: いつ会いましょうか？ \"When shall we meet?\" B: 会議の後はどうですか？ \"How about after the meeting?\" A: 何時に終わりますか？ \"What time does it end?\" B: 3時に終わります。3時半に会いましょう \"It ends at 3. Let''s meet at 3:30\""},{"level":3,"heading":"Example 3: Past Events","content":"A: いつ日本に来ましたか？ \"When did you come to Japan?\" B: 1年前に来ました \"I came 1 year ago\" A: 来る前に日本語を勉強しましたか？ \"Did you study Japanese before coming?\" B: はい、2年間勉強しました \"Yes, I studied for 2 years\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Wrong verb form","content":"Wrong: 食べた前 ❌ Right: 食べる前 ✓ \"before eating\" Wrong: 食べる後 ❌ Right: 食べた後 ✓ \"after eating\""},{"level":3,"heading":"Mistake 2: Using wrong form for future","content":"Wrong: 明日行った前 ❌ Right: 明日行く前 ✓ \"before going tomorrow\" Note: Use dictionary form for 前 even for future"},{"level":3,"heading":"Mistake 3: Confusing spatial and temporal","content":"学校の前に勉強する \"study before school\" (temporal) 学校の前に車がある \"there''s a car in front of school\" (spatial) Context makes the meaning clear!"}]},{"level":2,"heading":"Special Expressions","content":"","children":[{"level":3,"heading":"〜する前に (before doing)","content":"出かける前に \"before going out\" 寝る前に \"before sleeping\" 食べる前に \"before eating\""},{"level":3,"heading":"〜した後で (after doing)","content":"帰った後で \"after returning\" 見た後で \"after seeing\" 聞いた後で \"after hearing\""},{"level":3,"heading":"〜の前に (before [noun])","content":"試験の前に \"before the exam\" パーティーの前に \"before the party\" 出発の前に \"before departure\""},{"level":3,"heading":"〜の後で (after [noun])","content":"授業の後で \"after class\" 仕事の後で \"after work\" 食事の後で \"after the meal\""}]},{"level":2,"heading":"Comparing Before and After","content":"","children":[{"level":3,"heading":"Sequential Actions","content":"Before then after: 勉強する前に準備して、勉強した後で休む \"I prepare before studying, and rest after studying\""},{"level":3,"heading":"Time Sequence","content":"1週間前に予約して、1週間後に行く \"I''ll make a reservation 1 week before and go 1 week later\""}]},{"level":2,"heading":"Relative Time References","content":"","children":[{"level":3,"heading":"前 (ago)","content":"Looking back from now:\n\n2日前 - 2 days ago (from now) 1時間前 - 1 hour ago 昨日の3時間前 - 3 hours before yesterday"},{"level":3,"heading":"後 (later)","content":"Looking forward from a point:\n\n2日後 - 2 days later (from a reference point) 1時間後 - 1 hour later 明日の3時間後 - 3 hours after tomorrow"}]},{"level":2,"heading":"Politeness Levels","content":"","children":[{"level":3,"heading":"Casual","content":"食べる前 \"before eating\" 食べた後 \"after eating\""},{"level":3,"heading":"Polite","content":"食べる前に \"before eating\" 食べた後で \"after eating\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Before (前)","content":"寝る前に歯を磨く \"I brush my teeth before sleeping\" 出かける前に電話する \"I''ll call before going out\" 食事の前に手を洗う \"I wash my hands before meals\" テストの前に勉強した \"I studied before the test\""},{"level":3,"heading":"After (後)","content":"食べた後、歯を磨く \"I brush my teeth after eating\" 映画を見た後、レストランに行った \"After watching the movie, we went to a restaurant\" 帰った後で電話してください \"Please call after you get home\" 授業の後で会いましょう \"Let''s meet after class\""},{"level":3,"heading":"Time Ago (前)","content":"3時間前に電話した \"I called 3 hours ago\" 1週間前に日本に来た \"I came to Japan 1 week ago\" 2日前に会った \"I met (them) 2 days ago\""},{"level":3,"heading":"Time Later (後)","content":"2時間後に来る \"I''ll come 2 hours later\" 3日後に出発する \"I''ll depart 3 days later\" 1年後に結婚した \"I got married 1 year later\""},{"level":3,"heading":"Sequential Actions","content":"朝起きた後、シャワーを浴びる前に運動する \"After waking up in the morning, I exercise before taking a shower\" 勉強する前に準備して、勉強した後で休む \"I prepare before studying and rest after studying\""}]},{"level":2,"heading":"Related Grammar Points","content":"〜てから (te-kara) - After doing Time Words - Time expressions Duration - Time periods Sequential Actions - Ordering events While/During - Simultaneous actions Te-form - Connecting actions","children":[]}]'::jsonb,
  '["Temporal: 学校の前に勉強する \"study before school\"","Spatial: 学校の前に車がある \"there''s a car in front of the school\"","〜てから (te-kara) - After doing","Time Words - Time expressions","Duration - Time periods","Sequential Actions - Ordering events","While/During - Simultaneous actions","Te-form - Connecting actions"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'beki-detail',
  '〜べきだ (Should) - Detailed',
  'https://practice-japanese.com/docs/beki-detail/',
  'other',
  NULL,
  '[{"level":2,"heading":"Understanding べきだ","content":"〜べきだ expresses moral obligation, duty, or strong advice, meaning \"should,\" \"ought to,\" or \"must.\" It''s stronger than ほうがいい and indicates what is morally/socially right or expected.\n\n真実を話すべきだ \"You should tell the truth\" (moral obligation) 休んだほうがいい \"You should rest\" (advice for your benefit) べきだ = moral/social obligation ほうがいい = practical advice"},{"level":2,"heading":"Formation","content":"","children":[{"level":3,"heading":"Dictionary form + べきだ","content":"Regular verbs:\n\n行く → 行くべきだ (should go) 食べる → 食べるべきだ (should eat) 勉強する → 勉強するべきだ (should study)\n\nSpecial case: する\n\nする → するべきだ Or: すべきだ (contracted) Both acceptable すべきだ more common\n\nNegative:\n\n行くべきではない (shouldn''t go) Or: 行くべきじゃない (casual)"}]},{"level":2,"heading":"Strength of Obligation","content":"","children":[{"level":3,"heading":"Obligation scale","content":"Weakest: ～といい (it would be good if) ↓ ～たほうがいい (had better) ↓ ～なければならない (must/have to) ↓ Strongest: ～べきだ (should/ought to - moral duty) べきだ = moral/ethical obligation"}]},{"level":2,"heading":"べきだ vs ほうがいい","content":"","children":[{"level":3,"heading":"べきだ - Moral/social obligation","content":"謝るべきだ \"You should apologize\" (it''s the right thing to do) Moral duty What society expects Ethical imperative"},{"level":3,"heading":"ほうがいい - Practical advice","content":"謝ったほうがいい \"You should apologize\" (it would be better for you) For your benefit Practical suggestion Personal advantage"},{"level":3,"heading":"Clear comparison","content":"べきだ: 真実を話すべきだ \"You should tell the truth\" (moral obligation) ほうがいい: 早く寝たほうがいい \"You should sleep early\" (for your health) Different motivations"}]},{"level":2,"heading":"Usage Contexts","content":"","children":[{"level":3,"heading":"Moral/ethical duties","content":"約束は守るべきだ \"You should keep promises\" 人に親切にするべきだ \"You should be kind to people\" 正直であるべきだ \"You should be honest\" Universal moral principles"},{"level":3,"heading":"Social expectations","content":"時間を守るべきだ \"You should be punctual\" 礼儀正しくするべきだ \"You should be polite\" Social norms and manners"},{"level":3,"heading":"Professional obligations","content":"報告すべきだ \"You should report it\" 責任を取るべきだ \"You should take responsibility\" Workplace duties"},{"level":3,"heading":"Logical necessities","content":"この問題は解決すべきだ \"This problem should be solved\" 改善すべき点がある \"There are points that should be improved\" Necessary actions"}]},{"level":2,"heading":"Polite Forms","content":"","children":[{"level":3,"heading":"べきだ (plain)","content":"行くべきだ \"Should go\""},{"level":3,"heading":"べきです (polite)","content":"行くべきです \"Should go\" Standard polite"},{"level":3,"heading":"べきである (formal/written)","content":"対策を取るべきである \"Measures should be taken\" Formal documents Academic writing"}]},{"level":2,"heading":"Negative Forms","content":"","children":[{"level":3,"heading":"べきではない (shouldn''t)","content":"嘘をつくべきではない \"You shouldn''t lie\" 諦めるべきではない \"You shouldn''t give up\" Moral prohibition"},{"level":3,"heading":"べきじゃない (casual negative)","content":"そんなこと言うべきじゃない \"You shouldn''t say such things\" More casual"}]},{"level":2,"heading":"べきだった (Should have)","content":"","children":[{"level":3,"heading":"Past regret","content":"もっと勉強するべきだった \"I should have studied more\" 謝るべきだった \"I should have apologized\" Expressing regret What was the right thing to do"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"～すべきだ (Should do)","content":"努力すべきだ \"You should make an effort\" 挑戦すべきだ \"You should challenge\" Common with する verbs Often contracted to すべき"},{"level":3,"heading":"～べきではない (Shouldn''t)","content":"差別すべきではない \"You shouldn''t discriminate\" 無視すべきではない \"You shouldn''t ignore\" Moral prohibition"},{"level":3,"heading":"～べきか (Should I/we)","content":"どうすべきか \"What should I/we do?\" 行くべきか、行かないべきか \"Should I go or not go?\" Questioning duty"}]},{"level":2,"heading":"べき alone (What should be done)","content":"","children":[{"level":3,"heading":"As a noun","content":"やるべきこと \"Things that should be done\" 考えるべき問題 \"Problems that should be considered\" するべきことがたくさんある \"There are many things to do\" Nominalized form"}]},{"level":2,"heading":"In Formal Contexts","content":"","children":[{"level":3,"heading":"Reports and proposals","content":"この問題は早急に対処すべきである \"This problem should be addressed urgently\" 改善策を検討すべきだ \"Improvement measures should be considered\" Professional writing"},{"level":3,"heading":"Academic writing","content":"この理論は再検討されるべきである \"This theory should be reconsidered\" 更なる研究が必要とされるべきだ \"Further research should be required\""}]},{"level":2,"heading":"Softening べきだ","content":"","children":[{"level":3,"heading":"と思う (I think)","content":"謝るべきだと思う \"I think you should apologize\" Softer than bare べきだ Personal opinion marker"},{"level":3,"heading":"のではないか (Don''t you think?)","content":"考え直すべきではないか \"Don''t you think you should reconsider?\" More tentative Questioning form"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Using for trivial matters","content":"Unnatural: 水を飲むべきだ ⚠️ Natural: 水を飲んだほうがいい ✓ べきだ too strong for trivial advice"},{"level":3,"heading":"Confusing with しなければならない","content":"べきだ: Moral should (what''s right) 真実を話すべきだ \"You should tell the truth\" なければならない: Practical must (necessity) 働かなければならない \"I must work\" (no choice) Different nuances"}]},{"level":2,"heading":"べきだ in Different Situations","content":"","children":[{"level":3,"heading":"Giving advice to others","content":"もっと自分を大切にすべきだよ \"You should value yourself more\" 夢を諦めるべきじゃない \"You shouldn''t give up on your dreams\" Strong advice"},{"level":3,"heading":"Self-reflection","content":"もっと頑張るべきだった \"I should have tried harder\" 考えるべきだった \"I should have thought about it\" Regret, self-criticism"},{"level":3,"heading":"Social commentary","content":"政府は対策を取るべきだ \"The government should take measures\" 社会は変わるべきだ \"Society should change\" Expressing opinions on social issues"}]},{"level":2,"heading":"べき vs other obligation forms","content":"","children":[{"level":3,"heading":"べきだ (moral should)","content":"環境を守るべきだ \"We should protect the environment\" What''s morally right"},{"level":3,"heading":"なければならない (practical must)","content":"会社に行かなければならない \"I must go to work\" Practical necessity"},{"level":3,"heading":"はずだ (logical should)","content":"彼は来るはずだ \"He should come\" (expected) Based on facts/plans"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"真実を話すべきだ \"You should tell the truth\" 約束を守るべきです \"You should keep promises\" 諦めるべきではない \"You shouldn''t give up\""},{"level":3,"heading":"Intermediate","content":"この問題については、専門家に 相談すべきだと思います \"Regarding this problem, I think you should consult an expert\" 若いうちに、いろいろな経験を するべきだ。後悔しないように \"While you''re young, you should have various experiences. So you won''t regret it\" 環境問題は、一人一人が 真剣に考えるべき課題だ \"Environmental problems are issues each person should seriously consider\""},{"level":3,"heading":"Advanced","content":"リーダーは、常に冷静で公平であるべきだ。 感情的になってはいけない。チーム全体の ことを考え、最善の決断を下すべきである。 それがリーダーの責任だ。 \"A leader should always be calm and fair. They shouldn''t become emotional. They should consider the entire team and make the best decisions. That''s a leader''s responsibility.\" 教育は全ての人に平等に与えられるべきである。 経済的な理由で学べない人がいてはならない。 社会全体で支援する仕組みを作るべきだ。 これは人権の問題として考えるべきである。 \"Education should be given equally to all people. There shouldn''t be people who can''t learn for economic reasons. We should create a system to support them as a society. This should be considered as a human rights issue.\""}]},{"level":2,"heading":"Related Grammar Points","content":"〜ほうがいい - Had better (practical advice) 〜なければならない - Must/have to (necessity) 〜べきではない - Shouldn''t (moral prohibition) 〜べきだった - Should have (regret) Obligation expressions - Expressing duty Strong advice - Giving recommendations","children":[]}]'::jsonb,
  '["〜ほうがいい - Had better (practical advice)","〜なければならない - Must/have to (necessity)","〜べきではない - Shouldn''t (moral prohibition)","〜べきだった - Should have (regret)","Obligation expressions - Expressing duty","Strong advice - Giving recommendations"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'beki',
  '〜べき (Should/Ought to)',
  'https://practice-japanese.com/docs/beki/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 〜べき?","content":"〜べき expresses moral obligation , duty , or strong advice , meaning \"should,\" \"ought to,\" or \"supposed to.\" It''s more formal and carries a sense of what is right or proper.\n\n約束は守るべきだ \"You should keep promises\" 真実を話すべきです \"You ought to tell the truth\""},{"level":2,"heading":"Formation","content":"","children":[{"level":3,"heading":"Pattern: Verb dictionary form + べき(だ/です)","content":"| Verb | + べき | | ------ | ---------- | | 行く | 行くべき | | 食べる | 食べるべき | | する | するべき | | 来る | 来るべき |\n\nException: する can also be すべき\n\n勉強するべき ✓ 勉強すべき ✓ Both mean: \"should study\""}]},{"level":2,"heading":"Basic Usage","content":"","children":[{"level":3,"heading":"Moral advice/obligation","content":"正直であるべきだ \"You should be honest\" 健康に気をつけるべきです \"You should take care of your health\" 感謝の気持ちを持つべきだ \"You should have a sense of gratitude\""},{"level":3,"heading":"Social expectations","content":"時間を守るべきです \"You should be punctual\" 年上の人を敬うべきだ \"You should respect your elders\" 法律を守るべきです \"You should obey the law\""}]},{"level":2,"heading":"べき vs Alternatives","content":"| Expression | Meaning | Nuance | | ---------------- | -------------- | --------------- | | べき | Should (moral) | Duty/obligation | | ほうがいい | Had better | Personal advice | | なければならない | Must | Necessity | | たほうがいい | Should have | Past advice |\n\n勉強するべきだ \"You should study\" (it''s right/proper) 勉強したほうがいい \"You''d better study\" (practical advice) 勉強しなければならない \"You must study\" (necessity)"},{"level":2,"heading":"Politeness Levels","content":"","children":[{"level":3,"heading":"Casual","content":"行くべきだ \"Should go\" やるべきだ \"Should do\""},{"level":3,"heading":"Polite","content":"行くべきです \"Should go\" やるべきです \"Should do\""},{"level":3,"heading":"Formal Writing","content":"考慮すべきである \"Should be considered\" 実施すべきである \"Should be implemented\""}]},{"level":2,"heading":"Negative Form","content":"","children":[{"level":3,"heading":"べきではない / べきでない","content":"嘘をつくべきではない \"You shouldn''t lie\" 諦めるべきではありません \"You shouldn''t give up\" 怒るべきでない \"You shouldn''t get angry\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Advice","content":"A: 彼に謝ったほうがいいかな？ \"Should I apologize to him?\" B: うん、謝るべきだと思うよ \"Yeah, I think you should apologize\" A: そうだよね... \"You''re right...\""},{"level":3,"heading":"Example 2: Discussion","content":"A: この問題、どうすべきでしょうか？ \"What should we do about this problem?\" B: まず原因を調べるべきです \"First, we should investigate the cause\""},{"level":3,"heading":"Example 3: Opinion","content":"A: もっと環境に配慮すべきだと思います \"I think we should be more environmentally conscious\" B: 賛成です \"I agree\""}]},{"level":2,"heading":"Past: べきだった","content":"","children":[{"level":3,"heading":"Should have done","content":"もっと勉強するべきだった \"I should have studied more\" 早く言うべきでした \"I should have said it earlier\" 諦めるべきではなかった \"I shouldn''t have given up\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"べきだと思う (I think should)","content":"謝るべきだと思います \"I think you should apologize\" 行くべきだと思う \"I think you should go\""},{"level":3,"heading":"べきか (whether should)","content":"行くべきかどうか迷っています \"I''m wondering whether I should go\" 何をすべきか分かりません \"I don''t know what I should do\""},{"level":3,"heading":"べきではないか (shouldn''t we?)","content":"もっと努力すべきではないか \"Shouldn''t we try harder?\" 考え直すべきではないでしょうか \"Shouldn''t we reconsider?\""}]},{"level":2,"heading":"Common Verbs with べき","content":"","children":[{"level":3,"heading":"Moral actions","content":"守るべき - should protect/keep 従うべき - should obey/follow 尊重すべき - should respect 考えるべき - should think/consider"},{"level":3,"heading":"Social duties","content":"謝るべき - should apologize 感謝すべき - should be grateful 努力すべき - should make effort 反省すべき - should reflect"},{"level":3,"heading":"Decisions","content":"決めるべき - should decide 選ぶべき - should choose 行動すべき - should act 変えるべき - should change"}]},{"level":2,"heading":"In Writing","content":"","children":[{"level":3,"heading":"Formal documents/essays","content":"政府は対策を取るべきである \"The government should take measures\" 我々は環境を守るべきだ \"We should protect the environment\" 教育を重視すべきである \"Education should be prioritized\""}]},{"level":2,"heading":"Questions with べき","content":"","children":[{"level":3,"heading":"〜べきですか？","content":"今すぐ行くべきですか？ \"Should I go right now?\" 何をすべきでしょうか？ \"What should I do?\" どちらを選ぶべきですか？ \"Which should I choose?\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Confusing with ほうがいい","content":"べき = moral should (what''s right) ほうがいい = practical had better (advice) Context matters!"},{"level":3,"heading":"Mistake 2: Using べき too casually","content":"Too strong: 早く寝るべきだよ ⚠️ Better: 早く寝たほうがいいよ ✓ \"You should sleep early\" べき can sound preachy in casual contexts!"},{"level":3,"heading":"Mistake 3: Wrong form","content":"Wrong: 行くべきない ❌ Right: 行くべきではない ✓ \"Shouldn''t go\""}]},{"level":2,"heading":"Expressing Strong Opinion","content":"","children":[{"level":3,"heading":"Adding emphasis","content":"絶対に諦めるべきではない \"You absolutely shouldn''t give up\" 必ず守るべきです \"You must definitely keep it\" 当然やるべきだ \"You should naturally do it\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"約束は守るべきです \"You should keep promises\" 嘘をつくべきではない \"You shouldn''t lie\" 健康に気をつけるべきだ \"You should take care of your health\""},{"level":3,"heading":"Intermediate","content":"もっと真剣に将来のことを考えるべきだと思います \"I think you should think more seriously about your future\" この問題について話し合うべきではないでしょうか \"Shouldn''t we discuss this problem?\" 彼に謝るべきだったと後悔しています \"I regret that I should have apologized to him\""},{"level":3,"heading":"Advanced","content":"社会の一員として、責任を持って行動すべきである \"As a member of society, one should act responsibly\" 環境保護のために、私たち全員が努力すべきだと考えます \"I believe we all should make an effort for environmental protection\" 過去の失敗から学び、同じ過ちを繰り返すべきではない \"We should learn from past failures and not repeat the same mistakes\""}]},{"level":2,"heading":"Related Grammar Points","content":"〜ほうがいい - Had better 〜なければならない - Must 〜べきではない - Shouldn''t 〜べきだった - Should have Obligation expressions - Various must/should forms","children":[]}]'::jsonb,
  '["〜ほうがいい - Had better","〜なければならない - Must","〜べきではない - Shouldn''t","〜べきだった - Should have","Obligation expressions - Various must/should forms"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'buru',
  '〜ぶる (Act like)',
  'https://practice-japanese.com/docs/buru/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 〜ぶる?","content":"〜ぶる (buru) expresses pretending to be something or acting like something one is not. It usually has negative connotations of pretense or putting on airs.\n\nEnglish equivalent: \"act like,\" \"pretend to be,\" \"put on airs\"\n\nStructure: [Noun] + ぶる\n\n先生 (sensei - teacher) + ぶる = 先生ぶる \"act like a teacher\" 知識 (chishiki - knowledge) + ぶる = 知識ぶる \"act knowledgeable\" 偉い (erai - great) + ぶる = 偉ぶる \"act important/superior\""},{"level":2,"heading":"Basic Formation","content":"","children":[{"level":3,"heading":"With nouns","content":"[Noun] + ぶる\n\n先生ぶる (sensei buru) - \"act like a teacher\" 親ぶる (oya buru) - \"act like a parent\" 大人ぶる (otona buru) - \"act mature/grown-up\" 上司ぶる (joushi buru) - \"act like a boss\""}]},{"level":2,"heading":"Common Expressions","content":"","children":[{"level":3,"heading":"Typical usage","content":"偉ぶる (era buru) - \"act important\" / \"put on airs\" 知ったかぶる (shittaka buru) - \"pretend to know\" 分かったかぶる (wakattaka buru) - \"pretend to understand\" いい子ぶる (iiko buru) - \"act like a good child\" 金持ちぶる (kanemochi buru) - \"act rich\""}]},{"level":2,"heading":"Pretending to Know","content":"","children":[{"level":3,"heading":"False knowledge","content":"知ったかぶる。 Shittaka buru. \"Pretend to know.\" 知ったかぶって話す。 Shittaka butte hanasu. \"Talk as if one knows.\" 分かったかぶるな。 Wakattaka buru na. \"Don''t pretend you understand.\" 彼はいつも知ったかぶる。 Kare wa itsumo shittaka buru. \"He always pretends to know.\""}]},{"level":2,"heading":"Acting Superior","content":"","children":[{"level":3,"heading":"Putting on airs","content":"偉ぶる態度。 Era buru taido. \"An attitude of acting important.\" 彼は偉ぶっている。 Kare wa era butte iru. \"He''s acting important.\" 偉ぶった言い方。 Era butta iikata. \"A way of speaking that puts on airs.\" 偉ぶるな！ Era buru na! \"Don''t act so important!\""}]},{"level":2,"heading":"Acting Mature","content":"","children":[{"level":3,"heading":"Beyond one''s age/position","content":"大人ぶる。 Otona buru. \"Act mature\" / \"Act grown-up.\" 大人ぶった子供。 Otona butta kodomo. \"A child acting mature.\" 彼女は大人ぶっている。 Kanojo wa otona butte iru. \"She''s acting mature.\""}]},{"level":2,"heading":"Professional Pretense","content":"","children":[{"level":3,"heading":"Acting the part","content":"先生ぶる。 Sensei buru. \"Act like a teacher.\" 社長ぶる。 Shachou buru. \"Act like a company president.\" 専門家ぶって説明する。 Senmonka butte setsumei suru. \"Explain as if one''s an expert.\" 上司ぶった態度。 Joushi butta taido. \"Acting like a boss.\""}]},{"level":2,"heading":"Acting Good","content":"","children":[{"level":3,"heading":"Fake innocence","content":"いい子ぶる。 Iiko buru. \"Act like a good child.\" いい人ぶる。 Ii hito buru. \"Act like a good person.\" いい子ぶっている。 Iiko butte iru. \"Acting like a good child.\" いい子ぶるのはやめて。 Iiko buru no wa yamete. \"Stop acting innocent.\""}]},{"level":2,"heading":"Conjugation as る-verb","content":"","children":[{"level":3,"heading":"Regular verb conjugations","content":"Present: ぶる Past: ぶった Te-form: ぶって Negative: ぶらない 偉ぶる (era buru) - \"act important\" 偉ぶった (era butta) - \"acted important\" 偉ぶっている (era butte iru) - \"is acting important\" 偉ぶらない (era buranai) - \"doesn''t act important\""}]},{"level":2,"heading":"With ている","content":"","children":[{"level":3,"heading":"Ongoing pretense","content":"知ったかぶっている。 Shittaka butte iru. \"Is pretending to know.\" 彼は大人ぶっている。 Kare wa otona butte iru. \"He''s acting mature.\" 偉ぶっている人。 Era butte iru hito. \"A person who acts important.\""}]},{"level":2,"heading":"Negative Connotation","content":"","children":[{"level":3,"heading":"Usually critical","content":"ぶる almost always implies criticism or disapproval:\n\n偉ぶる = acting important (negative - shouldn''t do it) 知ったかぶる = pretending to know (negative - dishonest) 大人ぶる = acting mature (negative - inappropriate)"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Frequently used expressions","content":"知ったかぶる (shittaka buru) - \"pretend to know\" - Most common expression with ぶる 偉ぶる (era buru) - \"act superior\" - Very common criticism いい子ぶる (iiko buru) - \"act innocent\" - Common for children or insincere adults"}]},{"level":2,"heading":"Modifying Nouns","content":"","children":[{"level":3,"heading":"Using た-form","content":"偉ぶった態度 (era butta taido) - \"a superior attitude\" 大人ぶった話し方 (otona butta hanashikata) - \"a mature way of speaking\" 知ったかぶった説明 (shittaka butta setsumei) - \"a know-it-all explanation\" 彼の偉ぶった態度が嫌いだ。 Kare no era butta taido ga kirai da. \"I hate his attitude of acting important.\""}]},{"level":2,"heading":"Polite Forms","content":"Casual: 偉ぶる Polite: 偉ぶります Negative: 偉ぶらない / 偉ぶりません Past: 偉ぶった / 偉ぶりました 知ったかぶらないでください。 Shittaka buranai de kudasai. \"Please don''t pretend to know.\" 彼は偉ぶっていました。 Kare wa era butte imashita. \"He was acting important.\""},{"level":2,"heading":"Commands and Warnings","content":"","children":[{"level":3,"heading":"Telling someone to stop","content":"偉ぶるな！ Era buru na! \"Don''t act so important!\" 知ったかぶるな。 Shittaka buru na. \"Don''t pretend you know.\" 大人ぶらないで。 Otona buranai de. \"Don''t act mature.\" いい子ぶるのをやめなさい。 Iiko buru no wo yamenasai. \"Stop acting innocent.\""}]},{"level":2,"heading":"Related Words","content":"","children":[{"level":3,"heading":"Similar concepts","content":"気取る (kidoru) - \"put on airs\" (similar meaning) ふりをする (furi wo suru) - \"pretend\" (neutral) 演じる (enjiru) - \"act/perform\" (neutral) 偉ぶる = 偉そうにする = 気取る (all similar)"}]},{"level":2,"heading":"Comparing ぶる Patterns","content":"","children":[{"level":3,"heading":"Different pretense words","content":"ぶる - Pretending/acting:\n\n偉ぶる (era buru) \"act important\" (negative)\n\nらしい - Typical of:\n\n先生らしい (sensei rashii) \"teacher-like\" (neutral/positive)\n\nっぽい - Has characteristics:\n\n子供っぽい (kodomoppoi) \"childish\" (neutral/negative)\n\n| Pattern | Nuance | Example | | ------- | --------------------- | ---------- | | ぶる | Pretending (negative) | 偉ぶる | | らしい | Typical (neutral) | 先生らしい | | っぽい | Has traits | 子供っぽい |"}]},{"level":2,"heading":"Less Common Uses","content":"","children":[{"level":3,"heading":"With adjective stems","content":"偉ぶる (from 偉い - erai) \"act important\" 賢ぶる (from 賢い - kashikoi) \"act smart/wise\" These are less common and somewhat dated."}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using with verbs","content":"❌ 食べぶる ✓ 食べるふりをする\n\nぶる attaches to nouns, not verbs."},{"level":3,"heading":"Mistake 2: Missing negative connotation","content":"ぶる is almost always critical/negative.\n\n✓ 偉ぶる (negative - shouldn''t do it) ❌ Using it as neutral description"},{"level":3,"heading":"Mistake 3: Wrong formation","content":"❌ 先生をぶる ✓ 先生ぶる\n\nNo particle を needed."},{"level":3,"heading":"Mistake 4: Confusing with other patterns","content":"大人ぶる = pretend to be mature (negative) 大人らしい = mature (positive) 大人っぽい = mature-seeming (neutral)"}]},{"level":2,"heading":"Summary","content":"Key Points:\n\nぶる expresses pretending or acting like something Formation: [Noun] + ぶる Almost always has negative connotations Conjugates as regular る-verb Most common: 知ったかぶる, 偉ぶる, いい子ぶる Often used with ている for ongoing state Different from らしい (typical) and っぽい (characteristic) Essential for criticizing pretentious behavior","children":[]}]'::jsonb,
  '["ぶる expresses pretending or acting like something","Formation: [Noun] + ぶる","Almost always has negative connotations","Conjugates as regular る-verb","Most common: 知ったかぶる, 偉ぶる, いい子ぶる","Often used with ている for ongoing state","Different from らしい (typical) and っぽい (characteristic)","Essential for criticizing pretentious behavior"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'business-keigo',
  'Business Keigo',
  'https://practice-japanese.com/docs/business-keigo/',
  'keigo',
  NULL,
  '[{"level":2,"heading":"Business Keigo","content":"Business keigo is formal honorific language essential for professional Japanese communication. It combines respectful, humble, and polite forms for emails, meetings, phone calls, and client interactions. Proper business keigo shows professionalism and maintains smooth workplace relationships.\n\nCasual: すみません Business: 申し訳ございません \"I''m sorry\" (business formal) Casual: 分かった Business: 承知いたしました \"I understand\" (business formal)"},{"level":2,"heading":"Essential Business Phrases","content":"","children":[{"level":3,"heading":"Greetings and openings","content":"お世話になっております \"Thank you for your continued support\" (Standard business greeting) いつもお世話になっております \"Thank you always for your support\" お忙しいところ恐れ入ります \"Sorry to bother you when you''re busy\" Email/call openings"},{"level":3,"heading":"Understanding and acknowledgment","content":"承知いたしました \"I understand\" (humble + formal) かしこまりました \"Certainly\" (very formal) 承りました \"I''ve received/understood\" Confirming understanding"},{"level":3,"heading":"Apologizing","content":"申し訳ございません \"I''m very sorry\" (formal) 大変申し訳ございません \"I''m extremely sorry\" 失礼いたしました \"I was rude/I apologize\" Business apologies"},{"level":3,"heading":"Thanking","content":"ありがとうございます \"Thank you\" 誠にありがとうございます \"Thank you very much\" 感謝申し上げます \"I express my gratitude\" (very formal) お礼申し上げます \"I offer my thanks\" Expressing gratitude"},{"level":3,"heading":"Requesting","content":"お願いいたします \"Please\" (humble) お願い申し上げます \"I humbly request\" (very formal) 〜していただけますでしょうか \"Could you please...?\" 〜していただけますと幸いです \"I would be grateful if you could...\" Polite requests"}]},{"level":2,"heading":"Email Keigo","content":"","children":[{"level":3,"heading":"Email opening","content":"〇〇様 お世話になっております。 ABC株式会社の田中でございます。 \"Dear Mr./Ms. ○○, Thank you for your continued support. This is Tanaka from ABC Corporation.\" Standard format"},{"level":3,"heading":"Email closing","content":"何卒よろしくお願いいたします。 \"I humbly request your favorable consideration.\" 今後ともよろしくお願いいたします。 \"I look forward to our continued relationship.\" 引き続きよろしくお願いいたします。 \"I continue to ask for your support.\" Formal endings"}]},{"level":2,"heading":"Meeting Language","content":"","children":[{"level":3,"heading":"Starting meetings","content":"本日はお時間をいただき、 ありがとうございます。 \"Thank you for your time today.\" それでは、始めさせていただきます。 \"Well then, let me begin.\" Meeting openings"},{"level":3,"heading":"Presenting opinions","content":"私の考えを申し上げますと... \"If I may express my thoughts...\" 僭越ながら申し上げますと... \"Humbly speaking...\" Offering opinions"},{"level":3,"heading":"Concluding","content":"以上でございます。 \"That is all.\" ご清聴ありがとうございました。 \"Thank you for your attention.\" Meeting closings"}]},{"level":2,"heading":"Phone Keigo","content":"","children":[{"level":3,"heading":"Answering calls","content":"お電話ありがとうございます。 ABC株式会社でございます。 \"Thank you for calling. This is ABC Corporation.\" いつもお世話になっております。 \"Thank you for your continued support.\" Phone greeting"},{"level":3,"heading":"Transferring calls","content":"少々お待ちください。 \"Please wait a moment.\" 担当者におつなぎいたします。 \"I''ll transfer you to the person in charge.\" 恐れ入りますが、もう一度お願いできますか。 \"I''m sorry, but could you repeat that?\" Phone handling"}]},{"level":2,"heading":"Visiting Clients","content":"","children":[{"level":3,"heading":"Arrival","content":"お約束の時間に参りました。 \"I''ve arrived at our appointed time.\" 本日はお時間をいただき、 ありがとうございます。 \"Thank you for your time today.\" Visiting greeting"},{"level":3,"heading":"Leaving","content":"本日はありがとうございました。 \"Thank you for today.\" 失礼いたします。 \"I''ll take my leave.\" Departure phrases"}]},{"level":2,"heading":"Internal vs External Keigo","content":"","children":[{"level":3,"heading":"Speaking to outsiders about your company","content":"Lower your side: 弊社 \"our company\" (humble) 当社 \"our company\" (neutral) 弊社の山田が参ります。 \"Our Yamada will come.\" Humble your company"},{"level":3,"heading":"Speaking about other companies","content":"Elevate them: 御社 \"your company\" (respectful) 貴社 \"your company\" (written) 御社の新製品を拝見いたしました。 \"I''ve seen your company''s new product.\" Respectful to others"}]},{"level":2,"heading":"Common Business Patterns","content":"","children":[{"level":3,"heading":"〜させていただく (humble permission)","content":"説明させていただきます \"I will explain\" (humbly) 確認させていただきます \"I will confirm\" (humbly) Asking permission humbly"},{"level":3,"heading":"〜いただけますでしょうか (polite request)","content":"ご確認いただけますでしょうか \"Could you please confirm?\" お時間をいただけますでしょうか \"Could I have some of your time?\" Very polite asking"},{"level":3,"heading":"〜申し上げる (humble saying)","content":"お礼申し上げます \"I offer thanks\" 報告申し上げます \"I will report\" Humble reporting"}]},{"level":2,"heading":"Receiving Instructions","content":"","children":[{"level":3,"heading":"Acknowledging orders","content":"承知いたしました \"I understand\" かしこまりました \"Certainly\" 早速対応いたします \"I''ll handle it right away\" Receiving tasks"}]},{"level":2,"heading":"Declining Politely","content":"","children":[{"level":3,"heading":"Saying no formally","content":"恐れ入りますが、難しいかと存じます \"I''m afraid it might be difficult\" 申し訳ございませんが、お受けいたしかねます \"I''m sorry, but I''m unable to accept\" 大変申し訳ございませんが、今回は見送らせていただきます \"I''m very sorry, but we''ll pass this time\" Polite refusals"}]},{"level":2,"heading":"Offering Help","content":"何かお手伝いできることがございましたら、 お申し付けください \"If there''s anything I can help with, please let me know\" ご不明な点がございましたら、 お気軽にお問い合わせください \"If you have any questions, please feel free to contact us\" Offering assistance"},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Too casual in business","content":"Wrong: 分かった ⚠️ Correct: 承知いたしました ✓ \"I understand\" Wrong: すみません ⚠️ Correct: 申し訳ございません ✓ \"I''m sorry\" Maintain formality"},{"level":3,"heading":"Elevating your own company","content":"Wrong: 弊社の社長がおっしゃっています ⚠️ Correct: 弊社の社長が申しております ✓ \"Our president says\" To outsiders, humble your side"},{"level":3,"heading":"Using である調 (plain form)","content":"Wrong in business emails: これは重要だ ⚠️ Correct: これは重要でございます ✓ \"This is important\" Use です/ます forms"}]},{"level":2,"heading":"Quick Reference","content":"","children":[{"level":3,"heading":"Essential vocabulary","content":"弊社/当社 - our company 御社/貴社 - your company (respectful) 参る - go/come (humble) 申す - say (humble) 承る - receive/hear (humble) いたす - do (humble) いただく - receive (humble) 存じる - know (humble) 伺う - visit/ask (humble) 拝見する - see (humble)"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"お世話になっております。 \"Thank you for your support.\" 承知いたしました。 \"I understand.\" 申し訳ございません。 \"I''m very sorry.\" ご確認いただけますでしょうか。 \"Could you please confirm?\" よろしくお願いいたします。 \"I humbly request your support.\""},{"level":3,"heading":"Intermediate","content":"いつもお世話になっております。 ABC株式会社の山田でございます。 先日ご依頼いただきました資料を お送りいたします。ご確認の上、 ご不明な点がございましたら、 お気軽にお問い合わせください。 \"Thank you for your continued support. This is Yamada from ABC Corporation. I''m sending the documents you requested the other day. After confirming them, if you have any questions, please feel free to contact us.\" 本日はお忙しいところお時間を いただき、誠にありがとうございます。 弊社の新商品についてご説明 させていただきます。何かご質問が ございましたら、遠慮なく お申し付けください。 \"Thank you very much for your time today despite being busy. I will explain about our company''s new product. If you have any questions, please don''t hesitate to let me know.\""}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'business-writing',
  'Business Writing',
  'https://practice-japanese.com/docs/business-writing/',
  'other',
  NULL,
  '[{"level":2,"heading":"Business Writing","content":"Business writing in Japanese (ビジネス文書) requires specific formats, honorific language (keigo), and formal expressions. It includes reports, proposals, memos, and various corporate documents.\n\nKey features: - です・ます style (mostly) - Keigo (honorific language) - Fixed formats - Formal vocabulary - Clear structure Professional tone Respectful language"},{"level":2,"heading":"です・ます vs である","content":"","children":[{"level":3,"heading":"Style choice","content":"Internal documents: である style common Reports, analysis External documents: です・ます style Letters, proposals to clients Context determines Audience matters Company culture varies"}]},{"level":2,"heading":"Document Structure","content":"","children":[{"level":3,"heading":"Standard format","content":"Title: 件名 Date: 日付 To: 宛先 From: 発信者 Subject: 標題 Body: - 前文 (opening) - 主文 (main content) - 末文 (closing) Signature: 署名 Fixed structure Professional appearance Standard across companies"}]},{"level":2,"heading":"Opening Phrases","content":"","children":[{"level":3,"heading":"Standard beginnings","content":"拝啓 \"Dear Sir/Madam\" (formal letter) 謹啓 \"Respectfully\" (very formal) いつもお世話になっております \"Thank you for your continued support\" 平素は格別のお引き立てを賜り、厚く御礼申し上げます \"We deeply appreciate your continued patronage\" Standard greetings Professional courtesy Relationship acknowledgment"}]},{"level":2,"heading":"Closing Phrases","content":"","children":[{"level":3,"heading":"Document endings","content":"敬具 \"Respectfully yours\" (Pairs with 拝啓) 謹言 \"Respectfully\" (Pairs with 謹啓) 何卒よろしくお願い申し上げます \"We humbly ask for your kind consideration\" 今後ともよろしくお願いいたします \"We look forward to your continued support\" Formal closings Professional courtesy Standard endings"}]},{"level":2,"heading":"Honorific Language","content":"","children":[{"level":3,"heading":"Respect for clients","content":"Humble (謙譲語): 申し上げる \"say (humble)\" いたす \"do (humble)\" 拝見する \"see (humble)\" Respectful (尊敬語): いらっしゃる \"be/go/come (respect)\" おっしゃる \"say (respect)\" ご覧になる \"see (respect)\" Keigo essential Shows respect Professional standard"}]},{"level":2,"heading":"Request Expressions","content":"","children":[{"level":3,"heading":"Asking politely","content":"〜いただけますでしょうか \"Could you possibly...?\" ご確認いただければ幸いです \"We would be grateful if you could confirm\" お手数ですが、〜 \"Sorry for the trouble, but...\" ご検討のほどよろしくお願いいたします \"Please consider this matter\" Maximum politeness Business courtesy Professional requests"}]},{"level":2,"heading":"Apology Phrases","content":"","children":[{"level":3,"heading":"Business apologies","content":"申し訳ございません \"We sincerely apologize\" お詫び申し上げます \"We offer our apologies\" ご迷惑をおかけして申し訳ございません \"We apologize for any inconvenience\" 深くお詫び申し上げます \"We deeply apologize\" Taking responsibility Professional accountability Sincere apology"}]},{"level":2,"heading":"Report Writing","content":"","children":[{"level":3,"heading":"Internal reports","content":"Title: 報告書 \"Report\" 件名: 〇〇に関する報告 \"Regarding: Report on 〇〇\" 概要 \"Overview\" 詳細 \"Details\" 結論 \"Conclusion\" 提案 \"Proposal\" である or です・ます Clear sections Objective tone"}]},{"level":2,"heading":"Proposal Format","content":"","children":[{"level":3,"heading":"提案書 structure","content":"提案の背景 \"Background\" 目的 \"Objective\" 提案内容 \"Proposal content\" 期待される効果 \"Expected effects\" 予算 \"Budget\" スケジュール \"Schedule\" Persuasive writing Clear benefits Professional presentation"}]},{"level":2,"heading":"Meeting Minutes","content":"","children":[{"level":3,"heading":"議事録 format","content":"日時: Date/time 場所: Location 出席者: Attendees 議題: Agenda 討議内容: Discussion 決定事項: Decisions 次回予定: Next meeting Factual recording Clear documentation Action items"}]},{"level":2,"heading":"Memo Writing","content":"","children":[{"level":3,"heading":"社内メモ","content":"To: 〇〇部 各位 \"To: All in 〇〇 dept\" From: 総務部 \"From: General Affairs\" Date: 令和〇年〇月〇日 Re: 〇〇の件 \"Re: Regarding 〇〇\" Brief and clear Internal communication Informative"}]},{"level":2,"heading":"Formal Vocabulary","content":"","children":[{"level":3,"heading":"Business terms","content":"検討する \"examine, consider\" 実施する \"implement, carry out\" 報告する \"report\" 確認する \"confirm\" 承認する \"approve\" 対応する \"deal with, respond\" ご連絡する \"contact (humble)\" お知らせする \"inform (humble)\" Professional vocabulary Precise meaning Standard usage"}]},{"level":2,"heading":"Numbers and Dates","content":"","children":[{"level":3,"heading":"Formal notation","content":"Numbers in kanji for formal: 一万円 \"10,000 yen\" 三千人 \"3,000 people\" Dates: 令和6年10月4日 \"Reiwa 6, October 4\" Or: 2024年10月4日 Consistent format Professional appearance"}]},{"level":2,"heading":"Avoiding Ambiguity","content":"","children":[{"level":3,"heading":"Clear communication","content":"明確に述べる Be explicit 具体的に書く Write specifically 誤解を避ける表現 Avoid ambiguous expressions 数値で示す Show with numbers Precision valued Clarity essential Professional standard"}]},{"level":2,"heading":"Attachments","content":"","children":[{"level":3,"heading":"Referencing enclosures","content":"別紙をご参照ください \"Please refer to attached document\" 添付ファイルをご確認ください \"Please check attached file\" 資料を同封いたします \"We enclose the materials\" Clear reference Professional handling"}]},{"level":2,"heading":"Confirmation Requests","content":"","children":[{"level":3,"heading":"Seeking verification","content":"ご確認のほどよろしくお願いいたします \"Please confirm\" ご査収ください \"Please review and accept\" ご検討いただけますでしょうか \"Could you please consider?\" Professional requests Respectful tone Standard phrases"}]},{"level":2,"heading":"Deadline Expressions","content":"","children":[{"level":3,"heading":"Time constraints","content":"〇月〇日までに \"By (date)\" お早めにご返信ください \"Please reply as soon as possible\" 期限は〇日です \"Deadline is (date)\" なるべく早くご対応ください \"Please respond as soon as possible\" Clear timeframes Professional urgency Respectful requests"}]},{"level":2,"heading":"Notification Language","content":"","children":[{"level":3,"heading":"Informing formally","content":"お知らせいたします \"We inform you\" ご案内申し上げます \"We would like to inform you\" ご報告いたします \"We report\" お伝えいたします \"We convey\" Formal notification Professional courtesy Standard announcements"}]},{"level":2,"heading":"Response Acknowledgment","content":"","children":[{"level":3,"heading":"Confirming receipt","content":"ご連絡ありがとうございます \"Thank you for contacting us\" メールを拝受いたしました \"We received your email\" 承知いたしました \"Understood / Acknowledged\" かしこまりました \"Certainly\" Professional acknowledgment Prompt response Courtesy"}]},{"level":2,"heading":"Refusal Language","content":"","children":[{"level":3,"heading":"Declining politely","content":"誠に申し訳ございませんが \"We are truly sorry, but...\" 残念ながら〜できかねます \"Unfortunately we cannot...\" ご期待に沿えず申し訳ございません \"We apologize for not meeting your expectations\" Diplomatic refusal Maintaining relationship Professional courtesy"}]},{"level":2,"heading":"Contract Language","content":"","children":[{"level":3,"heading":"Legal documents","content":"甲 \"Party A\" 乙 \"Party B\" 以下の通り契約する \"Contract as follows\" 本契約は〜 \"This contract...\" である style Legal precision Formal structure"}]},{"level":2,"heading":"Common Mistakes","content":"❌ Using casual language ✗ ちょっと待ってください ✓ Formal language ✓ 少々お待ちください ❌ Insufficient keigo Must use appropriate honorifics ✓ Proper keigo Client = 尊敬語 Self = 謙譲語 ❌ Unclear structure Proper formatting essential ✓ Standard format Clear sections and headings ❌ Ambiguous expressions Precision required ✓ Clear, specific language Avoid misunderstanding Professional tone essential Respect crucial Format matters"},{"level":2,"heading":"Practical Examples","content":"","children":[{"level":3,"heading":"Business email","content":"件名: 会議日程のご確認 〇〇株式会社 営業部 田中様 いつもお世話になっております。 株式会社△△の山田でございます。 先日ご提案いただきました会議の件ですが、 以下の日程でご都合はいかがでしょうか。 日時: 10月15日（火）14:00-15:00 場所: 弊社会議室 ご確認のほどよろしくお願いいたします。 何かご不明な点がございましたら、 お気軽にお問い合わせください。 今後ともよろしくお願いいたします。 株式会社△△ 営業部 山田太郎 Tel: 03-1234-5678 Email: [email protected] Standard format Polite language Clear content Professional tone Contact information"},{"level":3,"heading":"Internal report","content":"報告書 件名: 第3四半期売上報告 日付: 2024年10月4日 報告者: 営業部 佐藤 1. 概要 第3四半期の売上は前年比10%増となった。 2. 詳細 - 新規顧客: 50社 - 売上高: 5000万円 - 主要製品: A製品が好調 3. 課題 競合他社の攻勢が激しく、価格競争が激化している。 4. 今後の対策 新製品の開発を加速し、差別化を図る必要がある。 以上 Clear structure Objective tone Factual presentation Actionable conclusions Professional format"}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'casual-speech',
  'Casual Speech Patterns',
  'https://practice-japanese.com/docs/casual-speech/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is Casual Speech?","content":"Casual speech (タメ口 tameguchi or 普通体 futsuu-tai) is the informal way Japanese people speak with friends, family, and close colleagues. It''s characterized by:\n\nUsing plain verb forms instead of polite ます/です forms Dropping particles Shortened expressions More direct language\n\nWhen to use casual speech:\n\nWith close friends With family members Among people of similar age and status In casual settings like bars, cafes with friends In internal monologue\n\nWhen NOT to use casual speech:\n\nWith strangers In business settings With teachers, bosses, or superiors With customers or clients When meeting someone for the first time"},{"level":2,"heading":"Basic Transformation: Polite → Casual","content":"","children":[{"level":3,"heading":"Verbs","content":"Present affirmative:\n\nPolite: 行きます (ikimasu) → Casual: 行く (iku) \"go/will go\" Polite: 食べます (tabemasu) → Casual: 食べる (taberu) \"eat/will eat\" Polite: します (shimasu) → Casual: する (suru) \"do/will do\"\n\nPresent negative:\n\nPolite: 行きません (ikimasen) → Casual: 行かない (ikanai) \"don''t go/won''t go\" Polite: 食べません (tabemasen) → Casual: 食べない (tabenai) \"don''t eat/won''t eat\" Polite: しません (shimasen) → Casual: しない (shinai) \"don''t do/won''t do\"\n\nPast affirmative:\n\nPolite: 行きました (ikimashita) → Casual: 行った (itta) \"went\" Polite: 食べました (tabemashita) → Casual: 食べた (tabeta) \"ate\" Polite: しました (shimashita) → Casual: した (shita) \"did\"\n\nPast negative:\n\nPolite: 行きませんでした (ikimasen deshita) → Casual: 行かなかった (ikanakatta) \"didn''t go\" Polite: 食べませんでした (tabemasen deshita) → Casual: 食べなかった (tabenakatta) \"didn''t eat\" Polite: しませんでした (shimasen deshita) → Casual: しなかった (shinakatta) \"didn''t do\""},{"level":3,"heading":"Copula (to be)","content":"Present affirmative:\n\nPolite: 学生です (gakusei desu) → Casual: 学生だ (gakusei da) \"is a student\" Even more casual: 学生 (gakusei) Just drop だ entirely in conversation\n\nPresent negative:\n\nPolite: 学生じゃないです (gakusei janai desu) → Casual: 学生じゃない (gakusei janai) \"is not a student\" Alternative: 学生じゃありません → 学生じゃない\n\nPast affirmative:\n\nPolite: 学生でした (gakusei deshita) → Casual: 学生だった (gakusei datta) \"was a student\"\n\nPast negative:\n\nPolite: 学生じゃなかったです → Casual: 学生じゃなかった (gakusei janakatta) \"was not a student\""},{"level":3,"heading":"Adjectives","content":"い-adjectives:\n\nPolite: おいしいです (oishii desu) → Casual: おいしい (oishii) \"is delicious\" Polite: 高いです (takai desu) → Casual: 高い (takai) \"is expensive\"\n\nNote: い-adjectives don''t change much; just drop です.\n\nな-adjectives:\n\nPolite: 静かです (shizuka desu) → Casual: 静かだ (shizuka da) \"is quiet\" Polite: 便利です (benri desu) → Casual: 便利だ (benri da) \"is convenient\""}]},{"level":2,"heading":"Dropping Particles","content":"In casual speech, particles are often dropped, especially を, に, and へ.","children":[{"level":3,"heading":"を Dropping","content":"Formal: ご飯を食べる (gohan wo taberu) Casual: ご飯食べる (gohan taberu) \"eat rice/meal\" Formal: テレビを見る (terebi wo miru) Casual: テレビ見る (terebi miru) \"watch TV\""},{"level":3,"heading":"に Dropping","content":"Formal: 学校に行く (gakkou ni iku) Casual: 学校行く (gakkou iku) \"go to school\" Formal: 家に帰る (ie ni kaeru) Casual: 家帰る (ie kaeru) \"go home\""},{"level":3,"heading":"Particles that STAY","content":"は, が, and で are usually kept, though は can be dropped in very casual speech:\n\n私は学生 → 学生 (just \"student\" when obvious) これが好き (kore ga suki) - が usually stays 図書館で勉強 (toshokan de benkyou) - で usually stays"}]},{"level":2,"heading":"Casual Question Forms","content":"","children":[{"level":3,"heading":"Dropping か","content":"The question particle か is usually dropped in casual speech. Use rising intonation instead.\n\nFormal: 行きますか？ (ikimasu ka?) Casual: 行く？ (iku?) \"Are you going?\" Formal: これは何ですか？ (kore wa nan desu ka?) Casual: これ何？ (kore nani?) \"What''s this?\" Formal: 分かりますか？ (wakarimasu ka?) Casual: 分かる？ (wakaru?) \"Do you understand?\""},{"level":3,"heading":"の for Questions","content":"Add の (no) at the end for a softer, more casual question:\n\n行くの？ (iku no?) \"Are you going?\" 何してるの？ (nani shiteru no?) \"What are you doing?\" 本当なの？ (hontou na no?) \"Is it true?\"\n\nMore common in feminine speech, but used by everyone."}]},{"level":2,"heading":"Common Casual Expressions","content":"","children":[{"level":3,"heading":"Greetings","content":"おはよう (ohayou) - \"Good morning\" (casual) おはようございます (ohayou gozaimasu) - polite version やあ / よう (yaa / you) - \"Hey\" 元気？ (genki?) - \"How are you?\" / \"You good?\" 元気ですか？ (genki desu ka?) - polite version"},{"level":3,"heading":"Agreement/Acknowledgment","content":"うん (un) - \"Yeah\" (casual) はい (hai) - \"Yes\" (polite) そうそう (sou sou) - \"Yeah, yeah\" / \"Exactly\" だよね (da yo ne) - \"Right?\" / \"Isn''t it?\" まあね (maa ne) - \"Well, yeah\" / \"I guess so\""},{"level":3,"heading":"Negative Responses","content":"ううん (uun) - \"Nah\" / \"No\" (casual) いいえ (iie) - \"No\" (polite) ちがう (chigau) - \"That''s wrong\" / \"No, that''s not it\" やだ (yada) - \"I don''t want to\" / \"No way\""},{"level":3,"heading":"Casual Invitations","content":"行こう (ikou) - \"Let''s go\" 行きましょう (ikimashou) - polite version 食べよう (tabeyou) - \"Let''s eat\" 映画見ない？ (eiga minai?) - \"Wanna watch a movie?\" 映画を見ませんか？ (eiga wo mimasen ka?) - polite version"}]},{"level":2,"heading":"Sentence-Ending Patterns","content":"","children":[{"level":3,"heading":"よ (yo) - Emphasis/Information","content":"Adds emphasis or provides new information:\n\nこれ、おいしいよ。 Kore, oishii yo. \"This is delicious!\" (You should know) もう帰るよ。 Mou kaeru yo. \"I''m leaving now.\" (FYI)"},{"level":3,"heading":"ね (ne) - Confirmation/Agreement","content":"Seeks confirmation or agreement:\n\nいい天気だね。 Ii tenki da ne. \"Nice weather, isn''t it?\" 難しいね。 Muzukashii ne. \"It''s difficult, huh?\""},{"level":3,"heading":"よね (yo ne) - Combination","content":"Expects agreement about information:\n\n明日は休みだよね。 Ashita wa yasumi da yo ne. \"Tomorrow''s a day off, right?\" 彼は学生だよね。 Kare wa gakusei da yo ne. \"He''s a student, right?\""},{"level":3,"heading":"な (na) - Reflection","content":"Masculine casual reflection (softer than よ):\n\nいい天気だな。 Ii tenki da na. \"Nice weather.\" (to oneself or casual company) 難しいな。 Muzukashii na. \"This is tough, man.\""}]},{"level":2,"heading":"んだ/んです Forms in Casual Speech","content":"","children":[{"level":3,"heading":"の/ん for Explanation","content":"In casual speech, のだ becomes んだ:\n\nFormal: 明日は忙しいんです。 Casual: 明日は忙しいんだ。 Asu wa isogashii n da. \"The thing is, I''m busy tomorrow.\" Formal: どうしてですか？ Casual: どうしてなの？ Doushite na no? \"Why is that?\""},{"level":3,"heading":"Question form: の？","content":"どこ行くの？ Doko iku no? \"Where are you going?\" (explanatory) 何してるの？ Nani shiteru no? \"What are you doing?\" (seeking explanation)"}]},{"level":2,"heading":"Contractions in Casual Speech","content":"","children":[{"level":3,"heading":"ている → てる","content":"Formal: 何をしていますか？ Casual: 何してる？ Nani shiteru? \"What are you doing?\" Formal: 食べています Casual: 食べてる Tabeteru \"I''m eating\""},{"level":3,"heading":"ては → ちゃ / では → じゃ","content":"Formal: 食べてはいけません Casual: 食べちゃダメ Tabecha dame \"You can''t eat that\" Formal: 見てはいけない Casual: 見ちゃダメ Micha dame \"You can''t look\""},{"level":3,"heading":"ておく → とく","content":"Formal: 読んでおきます Casual: 読んどく Yondoku \"I''ll read it (in advance)\" Formal: やっておいて Casual: やっといて Yattoite \"Do it (in advance)\"\n\nSee: Contractions for complete list"}]},{"level":2,"heading":"Real Conversation Examples","content":"","children":[{"level":3,"heading":"Example 1: Making Plans","content":"A: 明日暇？ Ashita hima? \"Are you free tomorrow?\" B: うん、暇だよ。 Un, hima da yo. \"Yeah, I''m free.\" A: 映画見に行かない？ Eiga mi ni ikanai? \"Wanna go see a movie?\" B: いいね！何時にする？ Ii ne! Nanji ni suru? \"Sounds good! What time?\" A: 3時でどう？ Sanji de dou? \"How about 3 o''clock?\" B: オッケー。駅で会おう。 OK. Eki de aou. \"OK. Let''s meet at the station.\""},{"level":3,"heading":"Example 2: Talking About Food","content":"A: お腹すいた。 Onaka suita. \"I''m hungry.\" B: 何食べたい？ Nani tabetai? \"What do you want to eat?\" A: ラーメンがいいな。 Ra-men ga ii na. \"Ramen would be good.\" B: あそこの店、おいしいよ。 Asoko no mise, oishii yo. \"That shop over there is delicious.\" A: じゃ、そこ行こう！ Ja, soko ikou! \"Then let''s go there!\""},{"level":3,"heading":"Example 3: Casual Chat","content":"A: 昨日何してた？ Kinou nani shiteta? \"What were you doing yesterday?\" B: ずっと家にいた。 Zutto ie ni ita. \"I was home the whole time.\" A: 暇だったでしょ？ Hima datta desho? \"You were bored, right?\" B: まあね。でも映画見たから大丈夫。 Maa ne. Demo eiga mita kara daijoubu. \"Well, yeah. But I watched a movie, so it''s fine.\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using Casual Speech Too Early","content":"❌ Meeting someone: よう！元気？ ✓ First meeting: はじめまして。元気ですか？\n\nWait until the relationship develops or until invited to use casual speech."},{"level":3,"heading":"Mistake 2: Mixing Polite and Casual","content":"❌ 今日は暇ですから、映画見る？ (です + casual verb)\n\n✓ 今日は暇だから、映画見る？ (all casual) ✓ 今日は暇ですから、映画を見ませんか？ (all polite)\n\nKeep the same level throughout."},{"level":3,"heading":"Mistake 3: Dropping Essential Particles","content":"❌ 私友達話す (watashi tomodachi hanasu) Confusing without particles\n\n✓ 私は友達と話す (watashi wa tomodachi to hanasu) \"I talk with my friend\"\n\nSome particles (は、が、と) are usually kept."},{"level":3,"heading":"Mistake 4: Forgetting Rising Intonation","content":"❌ 行く. (iku.) - Statement: \"I''m going\" ✓ 行く？ (iku?) - Question: \"Are you going?\"\n\nIn casual questions without か, intonation is crucial."}]},{"level":2,"heading":"Practice Exercises","content":"","children":[{"level":3,"heading":"Exercise 1: Convert to Casual","content":"Transform these polite sentences:\n\n今日は忙しいですか？ コーヒーを飲みますか？ 昨日映画を見ました。 明日学校に行きません。\n\nAnswers:\n\n今日忙しい？ コーヒー飲む？ 昨日映画見た。 明日学校行かない。"},{"level":3,"heading":"Exercise 2: Add Ending Particles","content":"Add よ, ね, or よね to these sentences:\n\nおいしい＿＿ 難しい＿＿ 明日は休み＿＿ もう帰る＿＿\n\nPossible answers:\n\nおいしいよ (telling new info) 難しいね (seeking agreement) 明日は休みだよね (confirming) もう帰るよ (announcing)"},{"level":3,"heading":"Exercise 3: Create Casual Conversations","content":"Make short dialogues about:\n\nAsking a friend to hang out Talking about what you did today Discussing food preferences"}]},{"level":2,"heading":"Levels of Casualness","content":"Even within casual speech, there are levels:\n\nLevel 1: Semi-casual\n\n今日暇？ 映画見に行かない？ Keeps some structure, suitable for acquaintances\n\nLevel 2: Casual\n\n暇？ 映画行く？ Drops more particles, close friends\n\nLevel 3: Very casual\n\n暇？ 映画？ Minimal structure, very close friends/family\n\nAdjust based on relationship closeness and context."},{"level":2,"heading":"Summary","content":"| Aspect | Polite | Casual | | --------- | ------------ | --------------- | | Verbs | 行きます | 行く | | Copula | です | だ (or drop) | | Questions | か？ | ？ (intonation) | | Particles | Usually kept | Often dropped | | -ている | -ています | -てる | | Tone | Formal | Direct |\n\nKey Points:\n\nUse plain forms (dictionary form, ない, た, etc.) Drop を, に, へ particles often Use rising intonation for questions Add よ, ね for nuance Match casualness level to relationship Never use with superiors or in formal settings\n\nMastering casual speech is essential for natural Japanese conversation, but always be aware of social context and use appropriate formality levels.","children":[]}]'::jsonb,
  '["今日は忙しいですか？","コーヒーを飲みますか？","昨日映画を見ました。","明日学校に行きません。","今日忙しい？","コーヒー飲む？","昨日映画見た。","明日学校行かない。","おいしい＿＿","難しい＿＿","明日は休み＿＿","もう帰る＿＿","おいしいよ (telling new info)","難しいね (seeking agreement)","明日は休みだよね (confirming)","もう帰るよ (announcing)","Using plain verb forms instead of polite ます/です forms","Dropping particles","Shortened expressions","More direct language","With close friends","With family members","Among people of similar age and status","In casual settings like bars, cafes with friends","In internal monologue","With strangers","In business settings","With teachers, bosses, or superiors","With customers or clients","When meeting someone for the first time","Asking a friend to hang out","Talking about what you did today","Discussing food preferences","Use plain forms (dictionary form, ない, た, etc.)","Drop を, に, へ particles often","Use rising intonation for questions","Add よ, ね for nuance","Match casualness level to relationship","Never use with superiors or in formal settings"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'causative-formation',
  'Causative Formation',
  'https://practice-japanese.com/docs/causative-formation/',
  'other',
  NULL,
  '[{"level":2,"heading":"Causative Formation","content":"Causative verbs (使役形 - shieki-kei) express making or letting someone do something. The formation follows regular patterns based on verb groups.\n\nBasic meaning: \"Make someone do\" \"Let someone do\" Formation adds: せる/させる Context determines \"make\" vs \"let\""},{"level":2,"heading":"る-Verbs (Group 2)","content":"","children":[{"level":3,"heading":"Drop る, add させる","content":"食べる → 食べさせる \"eat\" → \"make/let eat\" 見る → 見させる \"watch\" → \"make/let watch\" 寝る → 寝させる \"sleep\" → \"make/let sleep\" 起きる → 起きさせる \"wake up\" → \"make/let wake up\" 教える → 教えさせる \"teach\" → \"make/let teach\" Simple pattern Drop final る Add させる"}]},{"level":2,"heading":"う-Verbs (Group 1)","content":"","children":[{"level":3,"heading":"Change to あ-stem, add せる","content":"書く → 書かせる (kaku → kakaseru) \"write\" → \"make/let write\" 読む → 読ませる (yomu → yomaseru) \"read\" → \"make/let read\" 話す → 話させる (hanasu → hanasaseru) \"speak\" → \"make/let speak\" 待つ → 待たせる (matsu → mataseru) \"wait\" → \"make/let wait\" 行く → 行かせる (iku → ikaseru) \"go\" → \"make/let go\" 飲む → 飲ませる (nomu → nomaseru) \"drink\" → \"make/let drink\" 泳ぐ → 泳がせる (oyogu → oyogaseru) \"swim\" → \"make/let swim\" Change to あ-form Add せる"}]},{"level":2,"heading":"Irregular Verbs","content":"","children":[{"level":3,"heading":"Special forms","content":"する → させる \"do\" → \"make/let do\" 勉強する → 勉強させる \"study\" → \"make/let study\" 来る → 来させる (こさせる) \"come\" → \"make/let come\" Only two irregular Learn these separately"}]},{"level":2,"heading":"Conjugation Type","content":"","children":[{"level":3,"heading":"Causative = る-verb","content":"All causatives conjugate as る-verbs: 食べさせる (ru-verb pattern): Present: 食べさせる Past: 食べさせた Negative: 食べさせない Te-form: 食べさせて 行かせる (also ru-verb): Present: 行かせる Past: 行かせた Negative: 行かせない Te-form: 行かせて Regardless of original verb type All causatives become ru-verbs Consistent conjugation"}]},{"level":2,"heading":"Formation Summary Table","content":"","children":[{"level":3,"heading":"All patterns","content":"る-verbs: 食べる → 食べさせる 見る → 見させる う-verbs: 書く → 書かせる 読む → 読ませる 話す → 話させる 待つ → 待たせる 死ぬ → 死なせる 遊ぶ → 遊ばせる 飲む → 飲ませる 帰る → 帰らせる 泳ぐ → 泳がせる 買う → 買わせる Irregular: する → させる 来る → 来させる Memorize patterns Practice formation"}]},{"level":2,"heading":"Common Formation Mistakes","content":"","children":[{"level":3,"heading":"Errors to avoid","content":"❌ Wrong stem ✗ 食べるさせる (keeping る) ✓ Correct ✓ 食べさせる ❌ Wrong ending for う-verbs ✗ 書きせる ✓ Correct あ-stem ✓ 書かせる ❌ Irregular confusion ✗ するさせる ✓ Correct irregular ✓ させる Practice prevents errors Know your verb groups"}]},{"level":2,"heading":"Recognition Pattern","content":"","children":[{"level":3,"heading":"Spotting causatives","content":"If you see: 〜させる ending 〜せる ending Likely causative form Check verb stem Identify meaning 食べさせる → causative of 食べる 行かせる → causative of 行く"}]},{"level":2,"heading":"Multiple Meanings","content":"","children":[{"level":3,"heading":"Context determines","content":"Same form, different meanings: 子供を遊ばせる \"Make child play\" (permission) OR \"Let child play\" (permission) 親が子供を勉強させる \"Parent makes child study\" (force) Context shows which Both use same form Natural interpretation"}]},{"level":2,"heading":"Potential Confusion","content":"","children":[{"level":3,"heading":"させる vs させられる","content":"させる = causative \"make/let someone do\" させられる = causative-passive \"be made to do\" 食べさせる \"make (someone) eat\" 食べさせられる \"be made to eat\" Different meanings Don''t confuse Extra られる = passive"}]},{"level":2,"heading":"With Different Verbs","content":"","children":[{"level":3,"heading":"Transitive vs intransitive","content":"Intransitive base: 行く → 行かせる \"make/let go\" 泣く → 泣かせる \"make cry\" 笑う → 笑わせる \"make laugh\" Transitive base: 食べる → 食べさせる \"make/let eat\" 読む → 読ませる \"make/let read\" 書く → 書かせる \"make/let write\" Both types form causatives Pattern stays same"}]},{"level":2,"heading":"Honorific Confusion","content":"","children":[{"level":3,"heading":"Different from させていただく","content":"Causative: させる \"make/let do\" Humble request: させていただく \"humbly do / be allowed to do\" 勉強させる \"make (someone) study\" 勉強させていただく \"humbly study / be allowed to study\" Different functions Different meanings Don''t mix up"}]},{"level":2,"heading":"Formality","content":"","children":[{"level":3,"heading":"Neutral formation","content":"Causative form itself neutral: Casual: 食べさせた Polite: 食べさせました Formal: 食べさせました Formality from conjugation Not from causative itself Adjust ending as needed"}]},{"level":2,"heading":"Length Consideration","content":"","children":[{"level":3,"heading":"Long forms","content":"Causative adds length: 食べる (3 mora) → 食べさせる (5 mora) 見る (2 mora) → 見させる (4 mora) Longer forms Can sound heavy Natural in context"}]},{"level":2,"heading":"Practice Conjugation","content":"","children":[{"level":3,"heading":"Drill examples","content":"Convert to causative: 飲む → 飲ませる 書く → 書かせる 来る → 来させる する → させる 食べる → 食べさせる 見る → 見させる 行く → 行かせる 帰る → 帰らせる 寝る → 寝させる 起きる → 起きさせる Regular practice Builds automaticity Master patterns"}]},{"level":2,"heading":"Special Readings","content":"","children":[{"level":3,"heading":"Pronunciation notes","content":"Some require attention: 話す → 話させる (hanasaseru) Three さ sounds 待つ → 待たせる (mataseru) Not \"machiseru\" 死ぬ → 死なせる (shinaseru) な appears Learn correct readings Practice pronunciation"}]},{"level":2,"heading":"In Context","content":"","children":[{"level":3,"heading":"Seeing causatives used","content":"親が子供に野菜を食べさせる \"Parent makes child eat vegetables\" 先生が生徒に本を読ませる \"Teacher has students read book\" 子供を公園で遊ばせる \"Let child play in park\" Natural usage Common patterns Real examples"}]},{"level":2,"heading":"Building Complexity","content":"","children":[{"level":3,"heading":"Causative as foundation","content":"Once you know causative formation: Can add passive: させられる Can add てもらう: させてもらう Can add てください: させてください Foundation for advanced forms Master formation first Expand from there"}]},{"level":2,"heading":"Common Mistakes","content":"❌ Using wrong group pattern ✗ 食べるせる ✓ Know verb groups ✓ 食べさせる ❌ Forgetting irregular forms ✗ 来るさせる ✓ Memorize exceptions ✓ 来させる ❌ Wrong conjugation of causative ✗ 食べさせます (should be 食べさせます is correct, but thinking it conjugates as う-verb) ✓ All causatives are る-verbs ✓ 食べさせます Formation is mechanical Follow patterns exactly Practice makes perfect"},{"level":2,"heading":"Practical Examples","content":"","children":[{"level":3,"heading":"Formation practice","content":"Base → Causative: 歩く → 歩かせる \"make/let walk\" 立つ → 立たせる \"make/let stand\" 座る → 座らせる \"make/let sit\" 泣く → 泣かせる \"make cry\" 笑う → 笑わせる \"make laugh\" 休む → 休ませる \"make/let rest\" 働く → 働かせる \"make/let work\" 遊ぶ → 遊ばせる \"make/let play\" Daily verbs Common causatives Practice these Master formation Natural usage follows"}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'causative-passive-detail',
  'Causative-Passive',
  'https://practice-japanese.com/docs/causative-passive-detail/',
  'other',
  NULL,
  '[{"level":2,"heading":"Causative-Passive","content":"Causative-passive (使役受身 - shieki ukemi) combines causative and passive to express being made to do something . This form emphasizes the speaker as victim of someone else''s force or authority.\n\nFormation: Causative + Passive 食べさせる (causative) + られる (passive) = 食べさせられる \"be made to eat\" Victim perspective Being forced Against will"},{"level":2,"heading":"Formation","content":"","children":[{"level":3,"heading":"Two-step process","content":"Step 1: Make causative 食べる → 食べさせる Step 2: Make passive of causative 食べさせる → 食べさせられる 読む → 読ませる → 読ませられる 行く → 行かせる → 行かせられる する → させる → させられる Causative first Then passive"}]},{"level":2,"heading":"Short Form (Colloquial)","content":"","children":[{"level":3,"heading":"For う-verbs only","content":"Standard: 読ませられる Short: 読まされる Standard: 行かせられる Short: 行かされる Standard: 書かせられる Short: 書かされる Drop せ from させられる Common in speech More natural sounding Only for う-verbs"}]},{"level":2,"heading":"る-Verbs (No Short Form)","content":"","children":[{"level":3,"heading":"Keep full form","content":"食べさせられる (No short form) 見させられる (No short form) 起きさせられる (No short form) Cannot shorten Must use full form Remember distinction"}]},{"level":2,"heading":"Core Meaning","content":"","children":[{"level":3,"heading":"Being forced","content":"宿題をさせられた \"Was made to do homework\" Implies: Against will Forced by authority No choice 残業させられた \"Was made to work overtime\" Unwilling compliance Victim of authority"}]},{"level":2,"heading":"Victim Perspective","content":"","children":[{"level":3,"heading":"Speaker as victim","content":"親に野菜を食べさせられた \"Was made to eat vegetables by parents\" 先生に掃除をさせられた \"Was made to clean by teacher\" Focus on being forced Speaker didn''t want to Authority imposed"}]},{"level":2,"heading":"Complaint Function","content":"","children":[{"level":3,"heading":"Expressing grievance","content":"毎日残業させられる \"Am made to work overtime every day\" 無理やり飲ませられた \"Was forcibly made to drink\" Common in complaints Venting frustration Sharing hardship"}]},{"level":2,"heading":"Authority Contexts","content":"","children":[{"level":3,"heading":"Hierarchical situations","content":"上司に報告書を書かせられた \"Was made to write report by boss\" 親に塾に行かせられた \"Was made to go to cram school by parents\" 先生に宿題をやらせられた \"Was made to do homework by teacher\" Work hierarchies Parent-child Teacher-student"}]},{"level":2,"heading":"Unwanted Tasks","content":"","children":[{"level":3,"heading":"Unpleasant duties","content":"嫌な仕事をさせられた \"Was made to do unpleasant work\" 掃除をさせられた \"Was made to clean\" 待たされた \"Was made to wait\" Negative tasks Unwilling performance Frustration expressed"}]},{"level":2,"heading":"With に (Agent)","content":"","children":[{"level":3,"heading":"Who forced you","content":"親に勉強させられる \"Be made to study by parents\" 会社に残業させられる \"Be made to work overtime by company\" に marks the forcer Agent of causation Who made you do it"}]},{"level":2,"heading":"Negative Form","content":"","children":[{"level":3,"heading":"Not being made to","content":"宿題をさせられなかった \"Wasn''t made to do homework\" 行かせられなかった \"Wasn''t made to go\" Escaped obligation Avoided being forced Lucky exception"}]},{"level":2,"heading":"Past Tense","content":"","children":[{"level":3,"heading":"Was made to","content":"昨日残業させられた \"Was made to work overtime yesterday\" 子供の時よく勉強させられた \"Was often made to study as a child\" Past forced actions Completed unwilling acts Historical complaints"}]},{"level":2,"heading":"てしまう Addition","content":"","children":[{"level":3,"heading":"Regret emphasis","content":"変な物を食べさせられてしまった \"Ended up being made to eat strange thing\" 無理やり歌わされてしまった \"Ended up being forced to sing\" Double regret Unfortunate completion Emphasized victimhood"}]},{"level":2,"heading":"Continuous State","content":"","children":[{"level":3,"heading":"Ongoing force","content":"毎日残業させられている \"Am being made to work overtime every day\" ずっと待たされている \"Am being made to wait forever\" Current ongoing Continuing victimhood Still being forced"}]},{"level":2,"heading":"Physical Actions","content":"","children":[{"level":3,"heading":"Bodily force","content":"立たされた \"Was made to stand\" 走らされた \"Was made to run\" 座らされた \"Was made to sit\" Physical compliance Body control Often punishment"}]},{"level":2,"heading":"Childhood Memories","content":"","children":[{"level":3,"heading":"Common reminiscence","content":"子供の時、よく勉強させられた \"As a child, was often made to study\" ピアノを習わされた \"Was made to learn piano\" 野菜を食べさせられた \"Was made to eat vegetables\" Childhood experiences Parental authority Common stories"}]},{"level":2,"heading":"Work Complaints","content":"","children":[{"level":3,"heading":"Professional grievances","content":"無理な仕事をさせられる \"Am made to do unreasonable work\" 休日出勤させられた \"Was made to come to work on day off\" 夜遅くまで働かされた \"Was made to work until late at night\" Workplace complaints Authority abuse Common frustrations"}]},{"level":2,"heading":"Polite Contexts","content":"","children":[{"level":3,"heading":"Formal complaints","content":"お客様を待たせてしまい、申し訳ございません \"Apologies for making customer wait\" Even in formal language Causative-passive appears Though softer phrasing"}]},{"level":2,"heading":"Short Form Usage","content":"","children":[{"level":3,"heading":"Casual speech","content":"Standard long: 読ませられた Casual short: 読まされた Standard long: 行かせられた Casual short: 行かされた Standard long: 待たせられた Casual short: 待たされた Shorter = more casual Common in conversation Natural speech Only う-verbs"}]},{"level":2,"heading":"Emotional Weight","content":"","children":[{"level":3,"heading":"Feeling forced","content":"Using causative-passive shows: - Resentment - Unwillingness - Victimhood - Complaint Strong emotional content Not neutral description Implies negative feeling"}]},{"level":2,"heading":"Avoiding Responsibility","content":"","children":[{"level":3,"heading":"Not my choice","content":"させられた implies: \"It wasn''t my decision\" \"I had no choice\" \"Authority made me\" \"Not my fault\" Shifts responsibility Away from self Onto authority"}]},{"level":2,"heading":"Common Verbs","content":"","children":[{"level":3,"heading":"Frequently used","content":"待たされる/待たされる \"be made to wait\" 働かされる/働かせられる \"be made to work\" 勉強させられる \"be made to study\" 食べさせられる \"be made to eat\" やらされる/やらせられる \"be made to do\" 行かされる/行かせられる \"be made to go\" 立たされる/立たせられる \"be made to stand\" 書かされる/書かせられる \"be made to write\" Master these patterns Common complaints Daily usage"}]},{"level":2,"heading":"Nuance Difference","content":"","children":[{"level":3,"heading":"Causative vs causative-passive","content":"Causative (active): 親が子供を勉強させる \"Parent makes child study\" Parent''s perspective Causative-passive: 子供が親に勉強させられる \"Child is made to study by parent\" Child''s (victim''s) perspective Different viewpoints Different implications Choose by perspective"}]},{"level":2,"heading":"In Questions","content":"","children":[{"level":3,"heading":"Asking about being forced","content":"何をさせられたの？ \"What were you made to do?\" 誰に行かされたの？ \"Who made you go?\" なぜそんなことをさせられたの？ \"Why were you made to do such a thing?\" Sympathetic questions Understanding situation"}]},{"level":2,"heading":"Formal Situations","content":"","children":[{"level":3,"heading":"Professional language","content":"長時間労働をさせられております \"Am being made to work long hours\" 過重な業務をさせられています \"Am being made to do excessive work\" More formal conjugation Same victim feeling Professional complaints"}]},{"level":2,"heading":"Cultural Context","content":"","children":[{"level":3,"heading":"Japanese work culture","content":"Causative-passive common in: - Work complaints - School memories - Parental authority stories - Service industry Reflects hierarchical society Power dynamics Authority structures"}]},{"level":2,"heading":"てもらう Alternative","content":"","children":[{"level":3,"heading":"When not victim","content":"If willing: ❌ 手伝わされた (sounds unwilling) ✓ 手伝ってもらった \"Had (someone) help me\" If not victim perspective Use different structure てもらう for cooperation"}]},{"level":2,"heading":"Common Mistakes","content":"❌ Using when you wanted to do it Causative-passive implies unwillingness ✓ Only use when forced ❌ Confusing short and long forms ✗ 食べさされる (wrong for る-verb) ✓ Know which verbs can shorten ✓ 食べさせられる (る-verbs stay long) ❌ Wrong particle for agent ✗ 親が勉強させられた ✓ Use に for agent ✓ 親に勉強させられた Victim perspective crucial Implies negative feeling Use appropriately"},{"level":2,"heading":"Practical Examples","content":"","children":[{"level":3,"heading":"Real-life complaints","content":"会議で発表させられた \"Was made to present at meeting\" 一晩中勉強させられた \"Was made to study all night\" 嫌いな食べ物を食べさせられた \"Was made to eat food I hate\" 雨の中、外で待たされた \"Was made to wait outside in rain\" 理不尽な仕事をやらされている \"Am being made to do unreasonable work\" 子供の頃、ピアノを習わされた \"Was made to learn piano as a child\" 上司に休日出勤させられた \"Was made to work on holiday by boss\" 無理やり飲まされた \"Was forcibly made to drink\" Daily complaints Work frustrations Childhood memories Unwilling compliance Victim stories Authority grievances Natural expression Common in conversation"}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'causative-relationships',
  'Causative Relationships',
  'https://practice-japanese.com/docs/causative-relationships/',
  'other',
  NULL,
  '[{"level":2,"heading":"Causative Relationships","content":"Understanding the relationship between transitive verbs, intransitive verbs, and causative forms helps you express causation correctly in Japanese. Sometimes a transitive verb itself expresses causation, making a causative form unnecessary.\n\nThree ways to express causation: 1. Transitive verb (direct causation) 2. Intransitive + causative (make it happen) 3. Transitive + causative (make someone do) Choose based on meaning needed"},{"level":2,"heading":"Transitive as Causative","content":"","children":[{"level":3,"heading":"Already expresses causation","content":"開ける \"open (something)\" Already means \"make it open\" ドアを開ける \"Open the door\" = \"Make door open\" No need for causative Transitive is sufficient Direct causation built in"},{"level":3,"heading":"Common transitive verbs","content":"消す \"turn off\" → make go out つける \"turn on\" → make turn on 始める \"start\" → make begin 終える \"finish\" → make end 止める \"stop\" → make stop These already causative in meaning Using causative would be redundant Transitive = direct control"}]},{"level":2,"heading":"When to Use Causative","content":"","children":[{"level":3,"heading":"With intransitive verbs","content":"Intransitive doesn''t have agent control: 行く \"go\" (intransitive) 行かせる \"make/let go\" (causative needed) 笑う \"laugh\" (intransitive) 笑わせる \"make laugh\" (causative needed) Can''t directly control Need causative form Indirect causation"},{"level":3,"heading":"Making someone do transitive action","content":"Transitive verb + causative: Make someone perform action 食べる \"eat\" 食べさせる \"make/let (someone) eat\" 読む \"read\" 読ませる \"make/let (someone) read\" Causative = making agent do it"}]},{"level":2,"heading":"Redundancy to Avoid","content":"","children":[{"level":3,"heading":"Don''t double up","content":"❌ Wrong: ドアを開けさせる (When YOU are opening it) ✓ Correct: ドアを開ける \"Open the door\" ❌ Wrong: 電気を消させる ✓ Correct: 電気を消す \"Turn off light\" Transitive already implies causation Causative redundant for direct action"}]},{"level":2,"heading":"Three-Level System","content":"","children":[{"level":3,"heading":"Complete relationship","content":"1. Intransitive (happens): 開く \"opens\" ドアが開く \"door opens\" 2. Transitive (direct cause): 開ける \"open (it)\" ドアを開ける \"(I) open door\" 3. Causative (indirect cause): 開けさせる \"make (someone) open\" 子供にドアを開けさせる \"make child open door\" Each level adds agency layer Different control degrees"}]},{"level":2,"heading":"Causative with Intransitive","content":"","children":[{"level":3,"heading":"Expressing indirect causation","content":"行く → 行かせる \"go\" → \"make go\" 子供を学校に行かせる \"Make child go to school\" 泣く → 泣かせる \"cry\" → \"make cry\" 彼女を泣かせた \"Made her cry\" Intransitive needs causative To express causing action"}]},{"level":2,"heading":"Causative with Transitive","content":"","children":[{"level":3,"heading":"Making someone perform action","content":"食べる → 食べさせる \"eat\" → \"make/let eat\" 子供に野菜を食べさせる \"Make child eat vegetables\" 読む → 読ませる \"read\" → \"make/let read\" 生徒に本を読ませる \"Make students read book\" Focus on making person do action Not on making thing undergo action"}]},{"level":2,"heading":"Particle Patterns","content":"","children":[{"level":3,"heading":"を vs に with causatives","content":"Intransitive verb causative: 子供を行かせる \"make child go\" を marks person caused to act Transitive verb causative: 子供に本を読ませる \"make child read book\" に marks person caused を marks direct object Pattern changes with transitivity Important distinction"}]},{"level":2,"heading":"Natural Alternatives","content":"","children":[{"level":3,"heading":"Often simpler options exist","content":"Instead of causative: 開けさせる → 開けてもらう \"make open\" → \"have (them) open\" 読ませる → 読んでもらう \"make read\" → \"have (them) read\" てもらう more natural Less forceful More polite implication"}]},{"level":2,"heading":"Transitive = Direct Control","content":"","children":[{"level":3,"heading":"Physical causation","content":"When you physically cause: ボタンを押す \"press button\" Makes button go down Direct physical causation スイッチを入れる \"turn on switch\" Makes device activate Direct control Use transitive, not causative Direct action suffices"}]},{"level":2,"heading":"Causative = Indirect Control","content":"","children":[{"level":3,"heading":"Through agency of others","content":"When causing through someone else: 子供にボタンを押させる \"Make child press button\" 生徒に本を読ませる \"Make students read\" Through another person Indirect causation Causative needed"}]},{"level":2,"heading":"State Changes","content":"","children":[{"level":3,"heading":"Transitive causes change","content":"色を変える \"change color\" Direct causation of change 気持ちを変える \"change feelings\" Direct causation of change 温度を上げる \"raise temperature\" Direct causation of change Transitive handles state changes No causative needed Direct control of change"}]},{"level":2,"heading":"Permissive Causative","content":"","children":[{"level":3,"heading":"\"Let\" meaning","content":"行かせる can mean \"let go\" 子供を遊びに行かせる \"Let child go play\" Permission sense Not forceful Context determines Same form, different meaning Causative covers both"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"With intransitive verbs","content":"Must use causative: 泣く → 泣かせる \"make cry\" 笑う → 笑わせる \"make laugh\" 寝る → 寝させる \"make sleep\" 起きる → 起きさせる \"make wake up\" 帰る → 帰らせる \"make return\" These need causative form Can''t use transitive alternative"}]},{"level":2,"heading":"Semantic Difference","content":"","children":[{"level":3,"heading":"Focus changes","content":"ドアを開ける \"Open door\" Focus: door opening 子供にドアを開けさせる \"Make child open door\" Focus: child''s action Different emphasis Different sentence structure Different particles"}]},{"level":2,"heading":"Making vs Letting","content":"","children":[{"level":3,"heading":"Context clarifies","content":"行かせる: 子供を学校に行かせる \"make/let child go to school\" 行かせない: 子供を外に行かせない \"don''t let child go outside\" Affirmative = often \"make\" Negative = often \"don''t let\" Context crucial for interpretation"}]},{"level":2,"heading":"Learning Strategy","content":"","children":[{"level":3,"heading":"Decision tree","content":"Ask yourself: 1. Am I directly doing action to object? → Use transitive verb 2. Am I making someone/something do action? AND is base verb intransitive? → Use intransitive + causative 3. Am I making someone perform transitive action? → Use transitive + causative 4. Can I use てもらう instead? → Consider it for politeness Practice with this logic Builds correct usage"}]},{"level":2,"heading":"Common Mistakes","content":"❌ Unnecessary causative ✗ ドアを開けさせる (when you open it yourself) ✓ Direct transitive ✓ ドアを開ける ❌ Missing causative ✗ 子供を学校に行く ✓ Need causative ✓ 子供を学校に行かせる ❌ Wrong particle ✗ 子供を本を読ませる ✓ Correct particles ✓ 子供に本を読ませる Understanding verb type helps Choose appropriate form Consider agency"},{"level":2,"heading":"Practical Examples","content":"","children":[{"level":3,"heading":"Natural usage","content":"Direct causation (transitive): 窓を開けた \"Opened window\" 電気をつけた \"Turned on light\" 授業を始めた \"Started class\" Indirect causation (causative): 子供を寝かせた \"Put child to sleep\" 学生に本を読ませた \"Had students read book\" 彼を笑わせた \"Made him laugh\" Different situations Different forms needed Both common in daily speech Natural Japanese requires distinction"}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
