  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'common-expressions',
  'Common Expressions',
  'https://practice-japanese.com/docs/common-expressions/',
  'other',
  NULL,
  '[{"level":2,"heading":"Common Expressions","content":"Common expressions are the building blocks of natural Japanese conversation. These phrases go beyond literal translations and capture the essence of how native speakers actually communicate in everyday situations.\n\nTextbook: 今何時ですか Common: 今何時？ \"What time is it now?\" Natural speech patterns Contextual appropriateness"},{"level":2,"heading":"Agreement and Acknowledgment","content":"","children":[{"level":3,"heading":"Showing understanding","content":"そうですね \"That''s right / I see\" なるほど \"I see / That makes sense\" 確かに \"Certainly / Indeed\" まあね \"Well, yeah\" Understanding and agreement Common in conversation"},{"level":3,"heading":"Strong agreement","content":"本当に \"Really / Truly\" 全くその通り \"Exactly / Absolutely right\" 間違いない \"No doubt / For sure\" だよね \"Right? / Isn''t it?\" Emphatic agreement Casual to formal"}]},{"level":2,"heading":"Expressing Surprise","content":"","children":[{"level":3,"heading":"Mild surprise","content":"えっ \"Eh? / What?\" へえ \"Huh / Oh really?\" そうなんだ \"Is that so?\" マジで \"Seriously?\" (casual) Natural reactions Common interjections"},{"level":3,"heading":"Strong surprise","content":"本当に？ \"Really?\" 嘘！ \"No way! / Literally: Lie!\" 信じられない \"I can''t believe it\" びっくりした \"I was surprised\" Genuine shock Everyday usage"}]},{"level":2,"heading":"Hesitation and Thinking","content":"","children":[{"level":3,"heading":"Filler words","content":"えーと \"Um / Let me see\" あのー \"Well / Um\" そうですね \"Let''s see\" ちょっと \"A moment\" Natural pauses Thinking time"},{"level":3,"heading":"Expressing uncertainty","content":"どうかな \"I wonder / Not sure\" 分からない \"I don''t know\" 多分 \"Maybe / Probably\" かもしれない \"Might be\" Showing doubt Avoiding assertion"}]},{"level":2,"heading":"Making Requests","content":"","children":[{"level":3,"heading":"Polite requests","content":"すみません \"Excuse me\" お願いします \"Please\" ～てもらえますか \"Could you...?\" ～ていただけますか \"Would you kindly...?\" Polite and practical Daily necessity"},{"level":3,"heading":"Casual requests","content":"ちょっと \"Hey / Quick favor\" 悪いけど \"Sorry but\" ～てくれる？ \"Can you...?\" 頼むよ \"Come on / Please\" Among friends Informal settings"}]},{"level":2,"heading":"Declining and Refusing","content":"","children":[{"level":3,"heading":"Soft refusals","content":"ちょっと... \"Well... (implied no)\" 難しいです \"It''s difficult\" 遠慮します \"I''ll refrain\" 今日はちょっと \"Today is a bit...\" Indirect rejection Culturally appropriate"},{"level":3,"heading":"Direct refusals","content":"無理です \"It''s impossible\" できません \"I can''t\" ごめんなさい \"I''m sorry (no)\" やめとく \"I''ll pass\" (casual) More direct Still polite"}]},{"level":2,"heading":"Checking Understanding","content":"","children":[{"level":3,"heading":"Confirming comprehension","content":"分かりますか \"Do you understand?\" 大丈夫ですか \"Are you okay? / Is it alright?\" いいですか \"Is it good? / Okay?\" ～ですよね \"...right?\" Ensuring clarity Common in explanations"},{"level":3,"heading":"Asking for repetition","content":"もう一度お願いします \"Once more please\" すみません、何ですか \"Sorry, what was that?\" 聞こえませんでした \"I couldn''t hear\" ゆっくり話してください \"Please speak slowly\" No shame in asking Better than misunderstanding"}]},{"level":2,"heading":"Showing Interest","content":"","children":[{"level":3,"heading":"Encouraging continuation","content":"それで？ \"And then?\" へえ、そうなんだ \"Oh really?\" 続けて \"Go on / Continue\" もっと教えて \"Tell me more\" Active listening Engagement signals"},{"level":3,"heading":"Expressing interest","content":"面白い \"Interesting\" いいね \"That''s nice / Cool\" すごい \"Amazing / Wow\" さすが \"As expected (of you)\" Positive reactions Genuine interest"}]},{"level":2,"heading":"Time-Related Expressions","content":"","children":[{"level":3,"heading":"Temporal markers","content":"ちょうど \"Just now / Exactly\" さっき \"A moment ago\" もうすぐ \"Soon / Almost\" 今ちょっと \"Right now (busy)\" Time context Frequently used"},{"level":3,"heading":"Duration and frequency","content":"いつも \"Always\" たまに \"Sometimes\" 最近 \"Recently\" この間 \"The other day\" Temporal relationship Natural conversation"}]},{"level":2,"heading":"Degree and Extent","content":"","children":[{"level":3,"heading":"Intensifiers","content":"とても \"Very\" かなり \"Quite / Pretty\" めちゃくちゃ \"Extremely\" (casual) 超 \"Super\" (casual) Degree markers Common modifiers"},{"level":3,"heading":"Minimizers","content":"ちょっと \"A little / Somewhat\" あまり～ない \"Not very\" 全然～ない \"Not at all\" ほとんど～ない \"Hardly / Barely\" Downplaying Modest expression"}]},{"level":2,"heading":"Reactions to Bad News","content":"","children":[{"level":3,"heading":"Sympathy expressions","content":"それは大変ですね \"That''s tough\" お気の毒に \"I''m sorry to hear that\" 残念ですね \"That''s unfortunate\" 困りましたね \"That''s troubling\" Showing empathy Appropriate response"},{"level":3,"heading":"Offering support","content":"何かできることは？ \"Is there anything I can do?\" 大丈夫？ \"Are you okay?\" 頑張って \"Hang in there\" 心配しないで \"Don''t worry\" Supportive phrases Caring response"}]},{"level":2,"heading":"Phone and Communication","content":"","children":[{"level":3,"heading":"Phone-specific","content":"もしもし \"Hello\" (phone only) お電話変わりました \"This is [name] now\" 少々お待ちください \"Please wait a moment\" また後でかけます \"I''ll call again later\" Phone etiquette Specific context"},{"level":3,"heading":"Messaging conventions","content":"了解 \"Roger / Understood\" おつかれさま \"Good work\" (common message) よろしく \"Please / Thanks\" OK \"Okay\" (borrowed) Text and chat Modern usage"}]},{"level":2,"heading":"Shopping and Service","content":"","children":[{"level":3,"heading":"Customer phrases","content":"見てるだけです \"Just looking\" これください \"I''ll take this\" いくらですか \"How much?\" 袋いりません \"I don''t need a bag\" Shopping essentials Practical daily use"},{"level":3,"heading":"Staff responses","content":"いらっしゃいませ \"Welcome\" かしこまりました \"Certainly\" 少々お待ちください \"One moment please\" ありがとうございました \"Thank you very much\" Service industry Standard phrases"}]},{"level":2,"heading":"Changing Topics","content":"","children":[{"level":3,"heading":"Topic transitions","content":"ところで \"By the way\" そういえば \"Speaking of which\" 話は変わるけど \"Changing the subject\" あ、そうだ \"Oh, that reminds me\" Smooth transitions Natural flow"}]},{"level":2,"heading":"Expressing Opinions","content":"","children":[{"level":3,"heading":"Softening opinions","content":"個人的には \"Personally\" 私の意見では \"In my opinion\" と思います \"I think\" ～じゃないかな \"Don''t you think...?\" Humble expression Not imposing"},{"level":3,"heading":"Showing preference","content":"～の方がいい \"... is better\" どちらかというと \"If I had to choose\" ～が好き \"I like...\" できれば \"If possible / Preferably\" Personal taste Diplomatic phrasing"}]},{"level":2,"heading":"Common Casual Expressions","content":"","children":[{"level":3,"heading":"Among friends","content":"マジ \"Seriously / Really\" ヤバイ \"Crazy / Awesome / Bad\" うざい \"Annoying\" ダサい \"Uncool / Lame\" Youth language Casual only"},{"level":3,"heading":"Exclamations","content":"やった！ \"Yes! / I did it!\" しまった！ \"Oh no! / Darn!\" よかった \"I''m glad / That''s good\" 最高 \"The best / Awesome\" Emotional reactions Natural outbursts"}]},{"level":2,"heading":"Context-Dependent Phrases","content":"","children":[{"level":3,"heading":"Situation-specific","content":"At work: お疲れ様です At home: ただいま / おかえり Eating: いただきます / ごちそうさま Meeting: よろしくお願いします Each has its place Cultural rituals"}]},{"level":2,"heading":"Understanding Nuance","content":"","children":[{"level":3,"heading":"Same word, different feelings","content":"そうですね (Agreeing): \"Yes, that''s right\" (Hesitating): \"Well, let me think\" (Dismissing): \"Yeah, whatever\" Context is everything Tone matters"},{"level":3,"heading":"Reading between lines","content":"ちょっと... = Usually means \"no\" 考えておきます = \"I''ll think about it\" (soft no) そうですね... = May indicate disagreement Indirect communication Cultural awareness needed"}]},{"level":2,"heading":"Regional Variations","content":"","children":[{"level":3,"heading":"Common dialect phrases","content":"じゃん (Kanto): \"isn''t it?\" やん (Kansai): \"isn''t it?\" ばい (Kyushu): sentence ending だべ (Tohoku): \"right?\" Regional flavor Understanding variety"}]},{"level":2,"heading":"Borrowed Expressions","content":"","children":[{"level":3,"heading":"English influence","content":"OK / オーケー \"Okay\" ナイス \"Nice\" グッド \"Good\" チェック \"Check\" Modern Japanese International words"}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'comparative-adjectives',
  'Comparative Adjectives',
  'https://practice-japanese.com/docs/comparative-adjectives/',
  'adjective-conjugation',
  NULL,
  '[{"level":2,"heading":"What are Comparative Adjectives?","content":"Unlike English where adjectives change form (big → bigger → biggest), Japanese adjectives don''t change for comparisons. Instead, Japanese uses particles and adverbs like より (than), のほうが (more than), and 一番 (most) to express comparative and superlative meanings.\n\nEnglish: bigger, biggest Japanese: 大きい (same form always) Use particles to show comparison"},{"level":2,"heading":"Key Principle","content":"","children":[{"level":3,"heading":"Adjectives stay the same","content":"大きい → 大きい → 大きい \"Big\" (base/comparative/superlative) Add particles, not suffixes No form changes"}]},{"level":2,"heading":"Basic Comparison Structure","content":"","children":[{"level":3,"heading":"A は B より [adjective]","content":"東京は大阪より大きい \"Tokyo is bigger than Osaka\" 犬は猫より大きい \"Dogs are bigger than cats\" Standard comparative"},{"level":3,"heading":"A より B のほうが [adjective]","content":"大阪より東京のほうが大きい \"Tokyo is bigger than Osaka\" Emphasis on preferred item"}]},{"level":2,"heading":"い-Adjectives in Comparisons","content":"","children":[{"level":3,"heading":"Present affirmative","content":"高い → より高い \"Expensive → more expensive\" 難しい → より難しい \"Difficult → more difficult\" Form unchanged"},{"level":3,"heading":"Negative comparisons","content":"高くない → より高くない \"Not expensive → less expensive\" 難しくない → より難しくない \"Not difficult → less difficult\" Negative stem + ない"},{"level":3,"heading":"Past comparisons","content":"高かった → より高かった \"Was expensive → was more expensive\" Past form + comparison"}]},{"level":2,"heading":"な-Adjectives in Comparisons","content":"","children":[{"level":3,"heading":"Present affirmative","content":"便利 → より便利 \"Convenient → more convenient\" きれい → よりきれい \"Pretty → prettier\" No change to adjective"},{"level":3,"heading":"With だ/です","content":"便利だ → より便利だ \"Is convenient → is more convenient\" Copula added as needed"}]},{"level":2,"heading":"Superlative with 一番","content":"","children":[{"level":3,"heading":"一番 + adjective","content":"一番高い \"Most expensive / The most expensive\" 一番難しい \"Most difficult / The most difficult\" 一番 precedes adjective"},{"level":3,"heading":"[Group] で一番","content":"クラスで一番背が高い \"The tallest in the class\" 日本で一番大きい \"The biggest in Japan\" Specifies comparison group"}]},{"level":2,"heading":"Comparative Patterns Summary","content":"","children":[{"level":3,"heading":"Three main structures","content":"Comparative (2 items): A は B より 大きい \"A is bigger than B\" Emphatic comparative: B より A のほうが 大きい \"A is bigger than B\" Superlative (3+ items): A は 一番 大きい \"A is the biggest\" Different structures, same adjective"}]},{"level":2,"heading":"もっと (Even more)","content":"","children":[{"level":3,"heading":"Adding emphasis","content":"もっと大きい \"Even bigger / Bigger (than that)\" もっと速く \"Even faster\" Emphasizes comparison"},{"level":3,"heading":"もっと vs より","content":"より: marks standard AよりB \"B than A\" もっと: intensifier もっと大きい \"even bigger\" Different functions"}]},{"level":2,"heading":"ずっと (Much more)","content":"","children":[{"level":3,"heading":"Significant difference","content":"ずっと大きい \"Much bigger\" ずっと難しい \"Much more difficult\" Large gap emphasized"},{"level":3,"heading":"Combining patterns","content":"AよりBのほうがずっと大きい \"B is much bigger than A\" Multiple markers together"}]},{"level":2,"heading":"さらに (Even more/Furthermore)","content":"","children":[{"level":3,"heading":"Formal emphasis","content":"さらに速い \"Even faster\" さらに高度な \"Even more advanced\" Formal/written style"}]},{"level":2,"heading":"Common Comparative Adjectives","content":"","children":[{"level":3,"heading":"Size comparisons","content":"大きい → より大きい \"Bigger\" 小さい → より小さい \"Smaller\" 長い → より長い \"Longer\" 短い → より短い \"Shorter\""},{"level":3,"heading":"Quality comparisons","content":"いい/よい → よりいい \"Better\" 悪い → より悪い \"Worse\" 新しい → より新しい \"Newer\" 古い → より古い \"Older\""},{"level":3,"heading":"Difficulty comparisons","content":"難しい → より難しい \"More difficult\" 簡単 → より簡単 \"Easier\" 複雑 → より複雑 \"More complex\""},{"level":3,"heading":"Speed and intensity","content":"速い → より速い \"Faster\" 遅い → より遅い \"Slower\" 強い → より強い \"Stronger\" 弱い → より弱い \"Weaker\""}]},{"level":2,"heading":"Negative Comparisons","content":"","children":[{"level":3,"heading":"\"Less\" expressions","content":"高くない → より高くない \"Less expensive\" 難しくない → より難しくない \"Less difficult\" Use negative form"},{"level":3,"heading":"\"Not as...as\"","content":"AほどBじゃない \"Not as B as A\" 東京ほど大きくない \"Not as big as Tokyo\" ほど with negative"}]},{"level":2,"heading":"Special Cases","content":"","children":[{"level":3,"heading":"いい/よい (good/better)","content":"Base: いい/よい Comparative: よりいい/よりよい Superlative: 一番いい Same form, add markers"},{"level":3,"heading":"大きい vs 大きな","content":"大きい: regular い-adjective より大きい \"bigger\" 大きな: pre-noun only No comparative form Different types"}]},{"level":2,"heading":"Multiple Comparisons","content":"","children":[{"level":3,"heading":"Ranking three or more","content":"A、B、Cの中で、Aが一番大きい \"Among A, B, and C, A is the biggest\" Use 一番 for 3+ items"}]},{"level":2,"heading":"Equal Comparisons","content":"","children":[{"level":3,"heading":"同じくらい (about the same)","content":"AはBと同じくらい大きい \"A is about as big as B\" Equal degree"},{"level":3,"heading":"A も B も同じ","content":"どちらも同じくらい高い \"Both are about equally expensive\" No significant difference"}]},{"level":2,"heading":"Incremental Comparisons","content":"","children":[{"level":3,"heading":"だんだん/徐々に (gradually)","content":"だんだん難しくなる \"Gradually becomes more difficult\" 徐々に速くなる \"Gradually becomes faster\" Progressive change"},{"level":3,"heading":"ますます (more and more)","content":"ますます難しい \"More and more difficult\" ますます人気 \"More and more popular\" Increasing degree"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Adding English-style suffixes","content":"Wrong: 大きいer ⚠️ Wrong: 大きiest ⚠️ Correct: より大きい ✓ Correct: 一番大きい ✓ Japanese doesn''t inflect adjectives"},{"level":3,"heading":"Wrong particle choice","content":"Wrong: 東京が大阪より大きい ⚠️ Better: 東京は大阪より大きい ✓ Use は for subject in comparisons"},{"level":3,"heading":"Forgetting の in のほうが","content":"Wrong: Aよりほうが ⚠️ Correct: Aよりのほうが ✓ の is required"},{"level":3,"heading":"Using 一番 for two items","content":"Unnatural: AとBで一番好き ⚠️ Natural: AよりBのほうが好き ✓ Use comparative for two items"}]},{"level":2,"heading":"Polite Forms","content":"","children":[{"level":3,"heading":"です/ます endings","content":"Casual: より大きい \"Bigger\" Polite: より大きいです \"Is bigger\" Add polite copula"}]},{"level":2,"heading":"Formal Comparisons","content":"","children":[{"level":3,"heading":"より for written style","content":"この方法はより効果的です \"This method is more effective\" Business/academic contexts"},{"level":3,"heading":"一層/更に (even more - formal)","content":"一層困難な \"Even more difficult\" 更なる発展 \"Further development\" Formal writing"}]},{"level":2,"heading":"Practical Comparison Patterns","content":"","children":[{"level":3,"heading":"Shopping comparisons","content":"これとそれ、どちらが安いですか \"Which is cheaper, this or that?\" こちらのほうが安いです \"This one is cheaper\" Consumer contexts"},{"level":3,"heading":"Preference statements","content":"夏より冬のほうが好き \"I like winter more than summer\" 肉より魚のほうがいい \"Fish is better than meat\" Personal choices"}]},{"level":2,"heading":"Temporal Comparisons","content":"","children":[{"level":3,"heading":"前より/昨日より","content":"前より上手になった \"Became better than before\" 昨日より寒い \"Colder than yesterday\" Time-based standards"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"東京は大阪より大きい \"Tokyo is bigger than Osaka\" これのほうが安い \"This one is cheaper\" 日本で一番高い山 \"The highest mountain in Japan\""},{"level":3,"heading":"Intermediate","content":"日本語は英語より文法が複雑だと思います \"I think Japanese grammar is more complex than English\" この問題は思ったより難しくなかった \"This problem wasn''t more difficult than I thought\" 世界で一番美しい景色を見た \"I saw the most beautiful scenery in the world\""},{"level":3,"heading":"Advanced","content":"技術が進歩すればするほど、私たちの生活は より便利になるが、同時により複雑にもなる。 50年前と比べて、現代の生活は はるかに快適になった。しかし、 一番大切なのは、便利さではなく、 心の豊かさだと思う。昔の人々のほうが、 精神的にはより充実していたかもしれない。 \"The more technology progresses, the more convenient our lives become, but at the same time they also become more complex. Compared to 50 years ago, modern life has become far more comfortable. However, I think what''s most important is not convenience, but richness of heart. People of the past may have been more spiritually fulfilled.\" 語学学習において、最初は簡単に感じても、 進めば進むほどより難しくなる。しかし、 諦めずに続けることが一番重要だ。 努力すればするほど、より深く理解できる ようになる。完璧を目指すよりも、 少しずつでも前より上達することを 目標にしたほうがいい。 \"In language learning, even if it feels easy at first, the more you progress, the more difficult it becomes. However, continuing without giving up is most important. The more effort you make, the more deeply you can understand. Rather than aiming for perfection, it''s better to aim for improving little by little compared to before.\""}]},{"level":2,"heading":"Related Grammar Points","content":"より - Than (comparison marker) のほうが - More than (emphatic) 一番 - Most (superlative) ほど - As...as (extent) くらい/ぐらい - About (degree) 同じ - Same (equality) もっと - Even more (intensifier) ずっと - Much more (large difference)","children":[]}]'::jsonb,
  '["より - Than (comparison marker)","のほうが - More than (emphatic)","一番 - Most (superlative)","ほど - As...as (extent)","くらい/ぐらい - About (degree)","同じ - Same (equality)","もっと - Even more (intensifier)","ずっと - Much more (large difference)"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'comparing-conditionals',
  'Comparing Conditionals',
  'https://practice-japanese.com/docs/comparing-conditionals/',
  'conditionals',
  NULL,
  '[{"level":2,"heading":"Overview of Four Conditionals","content":"Japanese has four main conditional forms, each with specific uses and restrictions. Understanding when to use each is key to natural Japanese.\n\nと - Natural consequence, habitual ば - General condition, requirement たら - Most versatile, hypothetical なら - Context-based, advice"},{"level":2,"heading":"Side-by-Side Comparison","content":"","children":[{"level":3,"heading":"Same sentence, different conditionals","content":"と: 春になると、花が咲く \"When spring comes, flowers bloom\" (every year, natural) ば: 春になれば、花が咲く \"If spring comes, flowers will bloom\" (general statement) たら: 春になったら、花見に行こう \"When spring comes, let''s go flower viewing\" (specific plan) なら: 春なら、桜が綺麗だよ \"If it''s spring, cherry blossoms are beautiful\" (advice/info)"}]},{"level":2,"heading":"Detailed Comparison Table","content":"| Feature | と | ば | たら | なら | | -------------------- | ------------------- | ----------------- | ----------------------- | ---------- | | Main use | Natural consequence | General condition | Hypothetical/sequential | Contextual | | Commands after | ✗ No | ✓ Yes | ✓ Yes | ✓ Yes | | Volitional after | ✗ No | ✓ Yes | ✓ Yes | ✓ Yes | | Past result | ✓ Yes | ✗ No | ✓ Yes | Sometimes | | Discovery | ✓ Yes | ✗ No | ✓ Yes | ✗ No | | Formality | Neutral | Formal | Casual | Casual | | Versatility | Limited | Moderate | High | Moderate |"},{"level":2,"heading":"と (Natural Consequence)","content":"","children":[{"level":3,"heading":"When to use","content":"✓ Natural/automatic results ✓ Habitual actions ✓ Scientific facts ✓ Directions ✓ General truths このボタンを押すと、開く \"When you press this button, it opens\""},{"level":3,"heading":"When NOT to use","content":"✗ With commands/requests ✗ With volitional (~よう) ✗ Hypothetical one-time events ✗ Based on given context Wrong: 暇だと、手伝って ❌ Right: 暇なら、手伝って ✓"}]},{"level":2,"heading":"ば (General Condition)","content":"","children":[{"level":3,"heading":"When to use","content":"✓ General conditions ✓ Requirements ✓ Hypothetical (formal) ✓ Prerequisites ✓ Conditional abilities 勉強すれば、合格できる \"If you study, you can pass\""},{"level":3,"heading":"When NOT to use","content":"✗ With past tense results ✗ For discoveries ✗ Very casual conversation Wrong: 行けば、いなかった ❌ Right: 行ったら、いなかった ✓"}]},{"level":2,"heading":"たら (Hypothetical/Sequential)","content":"","children":[{"level":3,"heading":"When to use","content":"✓ Hypothetical situations ✓ Sequential actions (when/after) ✓ Discoveries ✓ Specific future plans ✓ Most situations! 家に着いたら、電話して \"When you get home, call me\""},{"level":3,"heading":"When NOT to use","content":"Honestly, たら works almost everywhere! It''s the safest choice when unsure. Only slight awkwardness in: ⚠️ Very formal writing (use ば) ⚠️ Scientific facts (use と)"}]},{"level":2,"heading":"なら (Contextual)","content":"","children":[{"level":3,"heading":"When to use","content":"✓ Based on given information ✓ Giving advice ✓ Topic-oriented statements ✓ \"If that''s the case...\" ✓ Responding to statements 東京なら、知ってる \"If it''s Tokyo, I know about it\""},{"level":3,"heading":"When NOT to use","content":"✗ Hypothetical future (unclear) ✗ Natural consequences ✗ Discoveries ✗ Scientific facts Unclear: 明日雨なら、中止 ⚠️ Clear: 明日雨だったら、中止 ✓"}]},{"level":2,"heading":"Commands and Requests","content":"","children":[{"level":3,"heading":"Can you use commands after?","content":"と: ❌ 春になると、花見に行こう (unnatural) ば: ✓ 時間があれば、手伝って \"If you have time, help me\" たら: ✓ 終わったら、教えて \"When you''re done, let me know\" なら: ✓ 暇なら、来てね \"If you''re free, come over\""}]},{"level":2,"heading":"Past Tense Results","content":"","children":[{"level":3,"heading":"Can you describe past discoveries?","content":"と: ✓ 家に帰ると、猫が寝ていた \"When I got home, the cat was sleeping\" ば: ❌ 家に帰れば、猫が寝ていた (wrong) たら: ✓ 家に帰ったら、猫が寝ていた \"When I got home, the cat was sleeping\" なら: ❌ 家に帰るなら、猫が寝ていた (wrong)"}]},{"level":2,"heading":"Making Plans","content":"","children":[{"level":3,"heading":"Which sounds most natural?","content":"Less natural: 明日晴れると、行く More natural: 明日晴れれば、行く Most natural: 明日晴れたら、行こう ✓✓✓ Context-based: 明日晴れなら、行けるよ For future plans → Use たら!"}]},{"level":2,"heading":"Giving Directions","content":"","children":[{"level":3,"heading":"Which is best?","content":"Best: まっすぐ行くと、駅がある ✓✓✓ \"When you go straight, there''s a station\" OK: まっすぐ行けば、駅がある OK: まっすぐ行ったら、駅がある Odd: まっすぐ行くなら、駅がある For directions → Use と!"}]},{"level":2,"heading":"Offering Advice","content":"","children":[{"level":3,"heading":"Which sounds natural?","content":"Odd: 痩せたいと、運動して Wrong: 痩せたければ、運動して OK: 痩せたかったら、運動して Best: 痩せたいなら、運動したほうがいい ✓✓✓ For advice → Use なら!"}]},{"level":2,"heading":"Expressing Requirements","content":"","children":[{"level":3,"heading":"Which emphasizes condition?","content":"Neutral: 会員になると、安くなる Best: 会員になれば、安くなる ✓✓✓ OK: 会員になったら、安くなる Topic: 会員なら、安い For requirements → Use ば!"}]},{"level":2,"heading":"Habitual Actions","content":"","children":[{"level":3,"heading":"Which for habits?","content":"Best: 朝起きると、コーヒーを飲む ✓✓✓ \"When I wake up, I drink coffee\" OK: 朝起きれば、コーヒーを飲む OK: 朝起きたら、コーヒーを飲む Odd: 朝起きるなら、コーヒーを飲む For habits → Use と!"}]},{"level":2,"heading":"Multiple Conditionals Accepted","content":"","children":[{"level":3,"heading":"Many situations accept multiple forms","content":"時間があると、散歩する (habitual) 時間があれば、散歩します (general) 時間があったら、散歩しよう (specific plan) 時間があるなら、散歩しない？ (suggestion) All grammatically correct! Nuance differs slightly"}]},{"level":2,"heading":"Formality Levels","content":"","children":[{"level":3,"heading":"From casual to formal","content":"Most casual: 暇だったら、遊ぼう (たら) Casual-neutral: 暇なら、遊ぼう (なら) 暇だと、暇だ (と) - stating fact Formal: お時間があれば、ご連絡ください (ば)"}]},{"level":2,"heading":"Decision Flowchart","content":"1. Is it automatic/natural/habitual? → YES: Use と → NO: Go to 2 2. Is it a general condition/requirement? → YES: Use ば → NO: Go to 3 3. Is it based on given context/advice? → YES: Use なら → NO: Go to 4 4. When in doubt or for hypothetical → Use たら (safest choice!)"},{"level":2,"heading":"Common Mistakes to Avoid","content":"","children":[{"level":3,"heading":"Mistake 1: Using と with commands","content":"Wrong: 時間があると、来て ❌ Right: 時間があったら、来て ✓ Right: 時間があれば、来て ✓ Right: 時間があるなら、来て ✓"},{"level":3,"heading":"Mistake 2: Using ば with past","content":"Wrong: ドアを開ければ、誰もいなかった ❌ Right: ドアを開けたら、誰もいなかった ✓ Right: ドアを開けると、誰もいなかった ✓"},{"level":3,"heading":"Mistake 3: Using なら for hypothetical","content":"Unclear: 明日雨なら、中止 ⚠️ Clear: 明日雨だったら、中止 ✓ Clear: 明日雨なら中止だよ (if told it will rain) ✓"}]},{"level":2,"heading":"Real-World Examples","content":"","children":[{"level":3,"heading":"Example 1: Weather and plans","content":"と: 雨が降ると、中止になる (always/rule) ば: 雨が降れば、中止です (general condition) たら: 雨が降ったら、中止だ (specific situation) ✓ Most common なら: 雨なら、中止だよね (based on forecast)"},{"level":3,"heading":"Example 2: Asking for help","content":"と: ❌ (can''t use with requests) ば: 時間があれば、手伝って ✓ たら: 時間があったら、手伝って ✓ Most common なら: 時間があるなら、手伝って ✓"},{"level":3,"heading":"Example 3: Giving directions","content":"と: 右に曲がると、駅がある ✓ Most natural ば: 右に曲がれば、駅がある ✓ たら: 右に曲がったら、駅がある ✓ なら: 右に曲がるなら、駅がある (odd)"}]},{"level":2,"heading":"When たら is Best","content":"","children":[{"level":3,"heading":"たら works in most situations","content":"✓ Future plans ✓ Hypothetical ✓ Sequential actions ✓ Discoveries ✓ Casual conversation ✓ Commands/requests ✓ With volitional If unsure → Use たら!"}]},{"level":2,"heading":"When Each is Preferred","content":"","children":[{"level":3,"heading":"Quick reference","content":"Natural facts: と \"When you heat water, it boils\" Requirements: ば \"If you''re a member, you get a discount\" Plans/hypothetical: たら \"If it''s sunny tomorrow, let''s go\" Advice/context: なら \"If you''re going to Tokyo, spring is good\""}]},{"level":2,"heading":"Summary: Key Differences","content":"**と** ✓ Natural, automatic, habitual ✗ No commands, no volitional Example: 春になると咲く **ば** ✓ General condition, requirement ✗ No past results Example: 頑張れば成功する **たら** ✓ Most versatile! Everything works ✗ (Almost no restrictions) Example: 終わったら教えて **なら** ✓ Context-based, advice ✗ Not for natural facts Example: 東京なら知ってる"},{"level":2,"heading":"Practice: Choose the Best","content":"1. \"When spring comes, flowers bloom\" Best: と ✓ 2. \"If you study, you can pass\" Best: ば ✓ 3. \"If it rains tomorrow, it''s cancelled\" Best: たら ✓ 4. \"If you''re going to Japan, spring is good\" Best: なら ✓ 5. \"When you get home, call me\" Best: たら ✓ 6. \"When you press this, it opens\" Best: と ✓"},{"level":2,"heading":"Related Grammar Points","content":"と conditional - Natural consequence ば conditional - General condition たら conditional - Hypothetical/sequential なら conditional - Contextual Conditionals overview - All forms Conditional mastery - Advanced usage","children":[]}]'::jsonb,
  '["と conditional - Natural consequence","ば conditional - General condition","たら conditional - Hypothetical/sequential","なら conditional - Contextual","Conditionals overview - All forms","Conditional mastery - Advanced usage"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'compound-verbs',
  'Compound Verb Formation',
  'https://practice-japanese.com/docs/compound-verbs/',
  'verb-conjugation',
  NULL,
  '[{"level":2,"heading":"Compound Verb Formation","content":"Compound verbs (複合動詞 - fukugou doushi) are created by combining two verbs to express a single, more specific action. The first verb (in stem form) provides the main action, while the second verb adds nuance about manner, direction, or aspect.\n\nStructure: Verb 1 (stem) + Verb 2 (conjugated) Example: 飲み (drink stem) + 始める (begin) = 飲み始める \"begin drinking\" First verb = main action Second verb = adds meaning"},{"level":2,"heading":"Basic Formation","content":"","children":[{"level":3,"heading":"Using verb stems","content":"Step 1: Get the stem of first verb 食べる → 食べ (tabe) 読む → 読み (yomi) 書く → 書き (kaki) Step 2: Add second verb 食べ + 始める = 食べ始める \"start eating\" 読み + 終わる = 読み終わる \"finish reading\" 書き + 続ける = 書き続ける \"continue writing\" Stem formation: る-verbs: Drop る う-verbs: Change to い-form"}]},{"level":2,"heading":"How They Work","content":"","children":[{"level":3,"heading":"Combined meaning","content":"The compound creates a single concept: 飲み始める Not: \"drink\" and \"begin\" separately But: \"begin drinking\" as one action 書き終わる Not: \"write\" and \"finish\" But: \"finish writing\" as complete idea Treated as one verb Conjugates as the second verb"}]},{"level":2,"heading":"Common Second Verbs","content":"","children":[{"level":3,"heading":"Aspectual verbs","content":"These add information about how/when action occurs: 始める - begin 終わる - finish 続ける - continue 出す - start suddenly 直す - redo かける - be in the middle of"},{"level":3,"heading":"Directional verbs","content":"These show direction of action: 上がる - go up 下がる - go down 入る - enter 出る - exit"},{"level":3,"heading":"Result verbs","content":"These show completion or result: 切る - completely 抜く - thoroughly 込む - into something"}]},{"level":2,"heading":"Stem Form Review","content":"","children":[{"level":3,"heading":"る-verbs","content":"食べる → 食べ 見る → 見 寝る → 寝 起きる → 起き Simply drop る Easy pattern"},{"level":3,"heading":"う-verbs","content":"飲む → 飲み (nomu → nomi) 読む → 読み (yomu → yomi) 書く → 書き (kaku → kaki) 話す → 話し (hanasu → hanashi) 待つ → 待ち (matsu → machi) Change to い-form Same as ます form without ます"},{"level":3,"heading":"Irregular","content":"する → し 来る (くる) → 来 (き) 勉強する → 勉強し 勉強し始める \"start studying\""}]},{"level":2,"heading":"Conjugation","content":"","children":[{"level":3,"heading":"The compound conjugates as the second verb","content":"飲み始める (ru-verb): Present: 飲み始める Past: 飲み始めた Negative: 飲み始めない Te-form: 飲み始めて 書き続ける (ru-verb): Present: 書き続ける Past: 書き続けた Negative: 書き続けない Te-form: 書き続けて Only second verb changes First verb stays in stem"}]},{"level":2,"heading":"Transitivity","content":"","children":[{"level":3,"heading":"Follows second verb","content":"If second verb is transitive, compound is transitive: 読み始める (始める is transitive) 本を読み始める \"start reading a book\" If second verb is intransitive, compound is intransitive: 降り出す (出す here becomes intransitive) 雨が降り出した \"rain started falling\" Second verb determines grammar"}]},{"level":2,"heading":"Multiple Compounds","content":"","children":[{"level":3,"heading":"Can stack sometimes","content":"Less common but possible: 読み始めている \"be in the state of having started reading\" 書き続けていた \"was continuing to write\" Adding ている to compounds Shows ongoing state"}]},{"level":2,"heading":"Not All Combinations Work","content":"","children":[{"level":3,"heading":"Natural vs unnatural","content":"Natural: 食べ始める ✓ 飲み続ける ✓ 書き終わる ✓ Unnatural: 食べ上がる ✗ (doesn''t make semantic sense) 飲み入る ✗ (illogical combination) Must make logical sense Some combinations established Others don''t exist"}]},{"level":2,"heading":"Semantic Relationship","content":"","children":[{"level":3,"heading":"First verb must be compatible","content":"The action of verb 1 must be something that can: - Begin (始める) - Continue (続ける) - Finish (終わる) - Be done excessively (すぎる) etc. 走り始める ✓ \"start running\" - running can begin 走り忘れる ✗ \"forget to run\" - use 走るのを忘れる instead"}]},{"level":2,"heading":"Writing","content":"","children":[{"level":3,"heading":"Usually written together","content":"一緒に書く: 飲み始める (not 飲み 始める) 食べ続ける (not 食べ 続ける) Written as one unit No space between Single compound verb"}]},{"level":2,"heading":"Formal vs Casual","content":"","children":[{"level":3,"heading":"Same in both registers","content":"Compound verbs used equally in: - Casual speech - Formal speech - Writing The formality comes from: - Conjugation (です/ます vs plain) - Context - Other grammar Not inherently formal or casual"}]},{"level":2,"heading":"Learning Strategy","content":"","children":[{"level":3,"heading":"Start with common patterns","content":"Most useful second verbs: 始める, 終わる, 続ける, 出す, 直す, すぎる Learn these combinations first Understand the pattern Gradually expand Focus on natural, common compounds Don''t try to force unnatural ones"}]},{"level":2,"heading":"Difference from Verb + の + Verb","content":"","children":[{"level":3,"heading":"Compare structures","content":"Compound: 食べ始める \"begin eating\" - single unified action Separate: 食べるのを始める \"begin the eating\" - more explicit Compound = more natural, concise Separate = more formal, explicit Both grammatically correct"}]},{"level":2,"heading":"Common Mistakes","content":"❌ Using dictionary form of first verb ✗ 食べる始める ✓ Using stem form ✓ 食べ始める ❌ Wrong stem form ✗ 飲む始める (should be 飲み) ✓ Correct stem ✓ 飲み始める ❌ Conjugating first verb ✗ 食べた続ける ✓ First verb stays in stem ✓ 食べ続けた Stem form is key Only second verb conjugates","children":[]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'conditionals-overview',
  'Conditional Forms Overview',
  'https://practice-japanese.com/docs/conditionals-overview/',
  'conditionals',
  NULL,
  '[{"level":2,"heading":"Japanese Conditionals Overview","content":"Japanese has four main conditional forms: と , ば , たら , and なら . Each has specific uses and nuances for expressing \"if\" and \"when\" conditions.\n\nと - Natural consequence, habitual actions ば - General conditions, requirements たら - After completion, hypothetical situations なら - Based on context, advice giving"},{"level":2,"heading":"The Four Conditionals","content":"","children":[{"level":3,"heading":"と (to) - Natural Consequence","content":"春になると、桜が咲く \"When spring comes, cherry blossoms bloom\" (always happens) このボタンを押すと、ドアが開く \"When you press this button, the door opens\" (automatic) Use: Natural results, scientific facts, habitual actions"},{"level":3,"heading":"ば (ba) - General Condition","content":"安ければ、買います \"If it''s cheap, I''ll buy it\" (general condition) 頑張れば、できる \"If you try hard, you can do it\" (requirement) Use: General conditions, requirements, hypothetical"},{"level":3,"heading":"たら (tara) - Hypothetical/Sequential","content":"雨が降ったら、行きません \"If it rains, I won''t go\" (hypothetical future) 家に着いたら、電話してね \"When you get home, call me\" (after completion) Use: Hypothetical situations, sequential actions, discovery"},{"level":3,"heading":"なら (nara) - Contextual","content":"A: 東京に行くんだ \"I''m going to Tokyo\" B: 東京なら、いいレストラン知ってるよ \"If it''s Tokyo, I know a good restaurant\" (based on context) Use: Based on given information, advice, suggestions"}]},{"level":2,"heading":"Quick Comparison Chart","content":"| Form | Main Use | Can use commands? | Can use past? | Example | | -------- | ----------------------- | ----------------- | ------------- | ---------------- | | と | Natural consequence | No ✗ | Yes ✓ | 春になると咲く | | ば | General condition | Yes ✓ | No ✗ | 安ければ買う | | たら | Hypothetical/sequential | Yes ✓ | Yes ✓ | 着いたら電話して | | なら | Contextual | Yes ✓ | Sometimes | 東京なら知ってる |"},{"level":2,"heading":"Basic Formation","content":"","children":[{"level":3,"heading":"と Formation","content":"Verb dictionary form + と い-adjective (remove い) + いと な-adjective/noun + だと 押すと開く \"When (you) press, it opens\""},{"level":3,"heading":"ば Formation","content":"Verb: Change final う sound to え + ば い-adjective: Remove い + ければ な-adjective: なら + ば Noun: なら + ば 読む → 読めば 高い → 高ければ"},{"level":3,"heading":"たら Formation","content":"Use た-form + ら 食べた → 食べたら 行った → 行ったら 高かった → 高かったら"},{"level":3,"heading":"なら Formation","content":"Verb dictionary/plain form + なら い-adjective plain form + なら な-adjective + なら Noun + なら 行くなら 安いなら"}]},{"level":2,"heading":"When to Use Which","content":"","children":[{"level":3,"heading":"Use と when:","content":"✓ Natural/automatic consequences ✓ Scientific facts ✓ Habitual actions ✓ General truths 水を熱すると、沸騰する \"When you heat water, it boils\" (always)"},{"level":3,"heading":"Use ば when:","content":"✓ General conditions ✓ Requirements/prerequisites ✓ Hypothetical situations (formal) ✓ Giving conditions for something 勉強すれば、合格できる \"If you study, you can pass\" (general condition)"},{"level":3,"heading":"Use たら when:","content":"✓ Hypothetical future situations ✓ Sequential actions (when/after) ✓ Discovery/unexpected situations ✓ Casual conditions ✓ Most versatile! 明日晴れたら、行こう \"If it''s sunny tomorrow, let''s go\" (hypothetical)"},{"level":3,"heading":"Use なら when:","content":"✓ Based on given information ✓ Giving advice/suggestions ✓ Topical conditions ✓ \"If that''s the case...\" 日本語を勉強するなら、毎日練習したほうがいい \"If you''re studying Japanese, you should practice daily\" (advice)"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"と - Automatic results","content":"右に曲がると、駅がある \"When you turn right, there''s a station\" ここを押すと、電気がつく \"When you press here, the light turns on\" Cannot use commands after と!"},{"level":3,"heading":"ば - Conditions for success","content":"早く起きれば、間に合う \"If you wake up early, you''ll make it in time\" もっと練習すれば、上手になる \"If you practice more, you''ll improve\" Can use commands after ば"},{"level":3,"heading":"たら - When/after/if","content":"駅に着いたら、電話してください \"When you arrive at the station, please call\" お金があったら、買いたい \"If I had money, I''d want to buy it\" Most flexible conditional"},{"level":3,"heading":"なら - If that''s the case","content":"A: 疲れた \"I''m tired\" B: 疲れたなら、休んだら？ \"If you''re tired, how about resting?\" Based on what was just said"}]},{"level":2,"heading":"Key Differences","content":"","children":[{"level":3,"heading":"Commands/Requests After Conditional","content":"と: ❌ Cannot use 春になると、花見に行って (unnatural) ば: ✓ Can use 暇なら、手伝って \"If you''re free, help me\" たら: ✓ Can use 着いたら、連絡して \"When you arrive, contact me\" なら: ✓ Can use 行くなら、気をつけて \"If you''re going, be careful\""},{"level":3,"heading":"Past Tense in Result","content":"と: ✓ Natural 家に帰ると、猫が寝ていた \"When I got home, the cat was sleeping\" ば: ❌ Cannot use past 家に帰れば、猫が寝ていた (wrong) たら: ✓ Can use 家に帰ったら、猫が寝ていた \"When I got home, the cat was sleeping\" なら: Sometimes"}]},{"level":2,"heading":"Choosing the Right Conditional","content":"","children":[{"level":3,"heading":"Decision Tree","content":"1. Is it automatic/natural? → Use と (ボタンを押すと開く) 2. Is it a general condition/requirement? → Use ば (頑張れば成功する) 3. Is it hypothetical or after completion? → Use たら (雨が降ったら中止) 4. Is it based on context/giving advice? → Use なら (東京なら知ってる) 5. When in doubt → Use たら (most versatile)"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using と with commands","content":"Wrong: 暇だと、手伝って ❌ Right: 暇なら、手伝って ✓ Or: 暇だったら、手伝って ✓ \"If you''re free, help me\""},{"level":3,"heading":"Mistake 2: Using ば with past results","content":"Wrong: 行けば、いなかった ❌ Right: 行ったら、いなかった ✓ \"When I went, he wasn''t there\""},{"level":3,"heading":"Mistake 3: Confusing なら with other forms","content":"Wrong: 明日雨なら、中止 (sounds off) Better: 明日雨だったら、中止 ✓ \"If it rains tomorrow, cancelled\" なら is better for context-based conditions"}]},{"level":2,"heading":"Overlapping Uses","content":"","children":[{"level":3,"heading":"Some situations accept multiple forms","content":"安いと買う (automatic tendency) \"When it''s cheap, I buy it\" 安ければ買う (general condition) \"If it''s cheap, I''ll buy it\" 安かったら買う (hypothetical) \"If it''s cheap, I''ll buy it\" 安いなら買う (based on information) \"If it''s cheap, I''ll buy it\" Nuance differs slightly!"}]},{"level":2,"heading":"Real-World Examples","content":"","children":[{"level":3,"heading":"Directions (と)","content":"まっすぐ行くと、コンビニがある \"When you go straight, there''s a convenience store\" Natural consequence"},{"level":3,"heading":"Requirements (ば)","content":"会員になれば、割引がもらえます \"If you become a member, you get a discount\" Condition for benefit"},{"level":3,"heading":"Plans (たら)","content":"週末晴れたら、ピクニックに行こう \"If it''s sunny this weekend, let''s go on a picnic\" Hypothetical future"},{"level":3,"heading":"Advice (なら)","content":"日本に行くなら、春がいいよ \"If you''re going to Japan, spring is good\" Based on given information"}]},{"level":2,"heading":"Summary","content":"","children":[{"level":3,"heading":"Quick Reference","content":"**と**: \"When X, always Y\" → Natural, automatic, habitual **ば**: \"If X, then Y (generally)\" → General conditions, requirements **たら**: \"If/when X, then Y\" → Most flexible, hypothetical, sequential **なら**: \"If it''s X, then Y\" → Contextual, advice, topical When unsure: Use たら!"}]},{"level":2,"heading":"Practice Recognition","content":"","children":[{"level":3,"heading":"Identify the best conditional","content":"1. \"When spring comes, flowers bloom\" → と (natural) 2. \"If you study hard, you''ll pass\" → ば (general condition) 3. \"If it rains tomorrow, I won''t go\" → たら (hypothetical future) 4. \"If you''re going to Tokyo, try this restaurant\" → なら (advice based on context)"}]},{"level":2,"heading":"Related Grammar Points","content":"と conditional - Natural consequence ば conditional - General condition たら conditional - Hypothetical/sequential なら conditional - Contextual Comparing conditionals - Detailed comparison 〜ても - Even if","children":[]}]'::jsonb,
  '["と conditional - Natural consequence","ば conditional - General condition","たら conditional - Hypothetical/sequential","なら conditional - Contextual","Comparing conditionals - Detailed comparison","〜ても - Even if"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'condolences',
  'Condolences',
  'https://practice-japanese.com/docs/condolences/',
  'other',
  NULL,
  '[{"level":2,"heading":"Condolences","content":"Expressing condolences in Japanese requires sensitivity and appropriate language. These phrases show sympathy during loss, difficulty, or unfortunate situations, with specific expressions for different contexts.\n\nBe careful with: - Level of formality - Relationship to person - Severity of situation Cultural sensitivity important"},{"level":2,"heading":"For Death and Loss","content":"","children":[{"level":3,"heading":"Standard condolence","content":"ご愁傷様です \"I''m sorry for your loss\" Most common phrase At funerals Expressing sympathy Formal and respectful Standard expression"},{"level":3,"heading":"Alternative expressions","content":"お悔やみ申し上げます \"I offer my condolences\" 心からお悔やみ申し上げます \"My heartfelt condolences\" Very formal Written condolences Official messages"},{"level":3,"heading":"Acknowledging grief","content":"ご冥福をお祈りします \"I pray for their peaceful rest\" 安らかにお眠りください \"May they rest in peace\" Said about the deceased Not directly to grieving person Respectful tribute"}]},{"level":2,"heading":"For Difficult Situations","content":"","children":[{"level":3,"heading":"General sympathy","content":"大変ですね \"That''s tough / difficult\" それは辛いですね \"That must be painful\" お気の毒に \"I feel sorry for you\" Everyday difficulties Showing empathy Acknowledging hardship"},{"level":3,"heading":"For illness","content":"お大事に \"Take care of yourself\" 早く良くなってください \"Please get better soon\" お見舞い申し上げます \"I wish you well\" (formal) Common and useful Showing concern Appropriate for various severity"},{"level":3,"heading":"For job loss/failure","content":"残念でしたね \"That''s unfortunate\" 次があるよ \"There''ll be a next time\" Encouraging Not too heavy Balance sympathy and hope"}]},{"level":2,"heading":"Offering Support","content":"","children":[{"level":3,"heading":"Practical help","content":"何かできることはありますか \"Is there anything I can do?\" 力になりたい \"I want to help\" いつでも連絡してね \"Contact me anytime\" Genuine offer Shows availability Practical support"},{"level":3,"heading":"Emotional support","content":"そばにいるよ \"I''m here for you\" 一人じゃないよ \"You''re not alone\" Comforting Close relationships Emotional presence"}]},{"level":2,"heading":"Responding to Bad News","content":"","children":[{"level":3,"heading":"Initial reaction","content":"それは大変でしたね \"That must have been terrible\" 信じられない \"I can''t believe it\" なんと言っていいか... \"I don''t know what to say...\" Honest response Appropriate shock Acknowledging severity"},{"level":3,"heading":"Continued support","content":"無理しないでね \"Don''t push yourself\" 休んでくださいね \"Please rest\" 時間がかかっても大丈夫 \"It''s okay to take time\" Understanding Patience No pressure"}]},{"level":2,"heading":"Business Context","content":"","children":[{"level":3,"heading":"Professional sympathy","content":"この度はお気の毒でございます \"I''m sorry for this matter\" 謹んでお悔やみ申し上げます \"I humbly offer my condolences\" Maximum formality Business relationships Official communications"}]},{"level":2,"heading":"What NOT to Say","content":"","children":[{"level":3,"heading":"Avoid","content":"❌ 頑張って (for serious grief) \"Do your best\" - too pushy ❌ すぐ忘れるよ \"You''ll forget soon\" - insensitive ❌ もっと大変な人もいる \"Others have it worse\" - dismissive Be sensitive Read the situation Don''t minimize pain"}]},{"level":2,"heading":"Cultural Considerations","content":"","children":[{"level":3,"heading":"Japanese approach","content":"Less verbal expression More through actions Showing up matters Silent presence valued Don''t feel need to fill silence Being there is enough Actions speak louder"},{"level":3,"heading":"Funeral etiquette","content":"Wear black Offer monetary gift (香典) Say ご愁傷様です Keep it brief Specific customs Follow protocol Respectful behavior"}]},{"level":2,"heading":"Quick Reference","content":"Death: ご愁傷様です Illness: お大事に General difficulty: 大変ですね Support: 何かできることはありますか Sympathy: お気の毒に Choose appropriate level Consider relationship Be genuine and brief","children":[]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'congratulations',
  'Congratulations',
  'https://practice-japanese.com/docs/congratulations/',
  'other',
  NULL,
  '[{"level":2,"heading":"Congratulations","content":"Congratulating in Japanese involves specific expressions for different types of achievements and celebrations. The most common word is おめでとう (omedetou), but usage varies by formality and occasion.\n\nおめでとう (casual) おめでとうございます (polite) Used for: - Achievements - Celebrations - Happy milestones"},{"level":2,"heading":"Basic Forms","content":"","children":[{"level":3,"heading":"Casual","content":"おめでとう \"Congrats\" Use with friends/family Informal celebrations Close relationships"},{"level":3,"heading":"Polite","content":"おめでとうございます \"Congratulations\" Standard form Most situations Safe choice"},{"level":3,"heading":"Very polite","content":"誠におめでとうございます \"Sincere congratulations\" 心よりお祝い申し上げます \"I offer my heartfelt congratulations\" Formal occasions Business settings Written messages"}]},{"level":2,"heading":"Specific Occasions","content":"","children":[{"level":3,"heading":"Birthdays","content":"お誕生日おめでとう(ございます) \"Happy birthday\" Most common celebration phrase Can add: 素敵な一年になりますように \"May you have a wonderful year\""},{"level":3,"heading":"Weddings","content":"ご結婚おめでとうございます \"Congratulations on your marriage\" 末永くお幸せに \"May you have eternal happiness\" Standard wedding greeting Always polite form"},{"level":3,"heading":"New Year","content":"あけましておめでとうございます \"Happy New Year\" 今年もよろしくお願いします \"Please treat me well this year too\" Most important Japanese greeting Used first days of January Cultural significance"},{"level":3,"heading":"Graduation","content":"ご卒業おめでとうございます \"Congratulations on your graduation\" 卒業おめでとう \"Congrats on graduating\" (casual)"},{"level":3,"heading":"New job/promotion","content":"就職おめでとう \"Congrats on the new job\" 昇進おめでとうございます \"Congratulations on your promotion\" ご栄転おめでとうございます \"Congratulations on your new position\" (formal)"},{"level":3,"heading":"New baby","content":"ご出産おめでとうございます \"Congratulations on the birth\" 赤ちゃんおめでとう \"Congrats on the baby\" (casual) お子様の誕生おめでとうございます \"Congratulations on your child''s birth\" (formal)"}]},{"level":2,"heading":"Achievement Congratulations","content":"","children":[{"level":3,"heading":"General success","content":"おめでとう \"Congrats\" やったね \"You did it!\" すごいね \"That''s amazing!\" Exams passed Goals achieved Competitions won"},{"level":3,"heading":"Specific achievements","content":"合格おめでとう \"Congrats on passing\" 優勝おめでとう \"Congrats on winning\" 成功おめでとう \"Congrats on your success\""}]},{"level":2,"heading":"Adding Personal Touch","content":"","children":[{"level":3,"heading":"Warm wishes","content":"おめでとう、本当に嬉しいよ \"Congrats, I''m really happy for you\" おめでとう、頑張ったね \"Congrats, you worked hard\" Shows personal connection Genuine happiness Acknowledges effort"}]},{"level":2,"heading":"Business Context","content":"","children":[{"level":3,"heading":"Professional congratulations","content":"この度はおめでとうございます \"Congratulations on this occasion\" ご就任おめでとうございます \"Congratulations on your appointment\" Formal business language Written communications Official announcements"}]},{"level":2,"heading":"Responding to Congratulations","content":"","children":[{"level":3,"heading":"Accepting","content":"ありがとう(ございます) \"Thank you\" みんなのおかげです \"It''s thanks to everyone\" まだまだです \"I still have a long way to go\" Humble response Sharing credit Showing modesty"}]},{"level":2,"heading":"Cultural Notes","content":"","children":[{"level":3,"heading":"When to congratulate","content":"Japanese culture: - More restrained than Western - Don''t overdo enthusiasm - Be appropriate to relationship Match the person''s energy Consider context"},{"level":3,"heading":"Group celebrations","content":"みんなおめでとう \"Congrats to everyone\" お疲れ様でした \"Good work everyone\" For team achievements Group efforts Shared success"}]},{"level":2,"heading":"Common Mistakes","content":"❌ Using おめでとう for condolences ✓ Only for happy occasions ❌ Too casual with superiors ✓ Always use おめでとうございます ❌ Forgetting 今年もよろしく after New Year greeting ✓ Complete the phrase properly Context matters Match formality"},{"level":2,"heading":"Quick Reference","content":"Birthday: お誕生日おめでとう Wedding: ご結婚おめでとうございます New Year: あけましておめでとうございます Graduation: ご卒業おめでとうございます Baby: ご出産おめでとうございます General: おめでとう(ございます) Always consider formality level Add personal touches when appropriate","children":[]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'contractions',
  'Contractions',
  'https://practice-japanese.com/docs/contractions',
  'other',
  NULL,
  '[{"level":2,"heading":"What Are Japanese Contractions?","content":"Contractions (縮約形 shukuyaku-kei) are shortened forms of expressions commonly used in casual speech. Just like English \"don''t\" instead of \"do not,\" Japanese speakers naturally contract certain combinations to speak more efficiently and casually.\n\nWhy learn contractions?\n\nSound more natural and fluent Understand native speakers better Essential for watching anime, dramas, movies Used constantly in daily conversation\n\nWhen NOT to use:\n\nFormal writing Business situations With superiors or strangers Official documents"},{"level":2,"heading":"Core Contractions","content":"","children":[{"level":3,"heading":"ている → てる (Progressive/State)","content":"The most common contraction. Drop the い from ている.\n\nStandard: 何をしていますか？ Contracted: 何してる？ Nani shiteru? \"What are you doing?\" Standard: 食べている Contracted: 食べてる Tabeteru \"eating\" / \"is eating\" Standard: 知っています Contracted: 知ってる Shitteru \"I know\" Standard: 住んでいます Contracted: 住んでる Sunderu \"living (somewhere)\"\n\nWith question:\n\n見てる？ (miteru?) - \"Are you watching?\" 知ってる？ (shitteru?) - \"Do you know?\" 寝てる？ (neteru?) - \"Are you sleeping?\""},{"level":3,"heading":"ては → ちゃ / では → じゃ","content":"Used with negative commands or conditional meanings.\n\nては → ちゃ:\n\nStandard: 食べてはいけない Contracted: 食べちゃいけない Tabecha ikenai \"You can''t eat (that)\" Standard: 見てはダメ Contracted: 見ちゃダメ Micha dame \"You can''t look\" Standard: 行ってはいけません Contracted: 行っちゃダメ Itcha dame \"You can''t go\"\n\nでは → じゃ:\n\nStandard: それではダメだ Contracted: それじゃダメだ Sore ja dame da \"That won''t do\" Standard: これでは無理 Contracted: これじゃ無理 Kore ja muri \"This is impossible\""},{"level":3,"heading":"てしまう → ちゃう (Completion/Regret)","content":"Express completion or unintended actions.\n\nてしまう → ちゃう:\n\nStandard: 食べてしまった Contracted: 食べちゃった Tabechatta \"I ate it (all up)\" / \"Oops, I ate it\" Standard: 忘れてしまった Contracted: 忘れちゃった Wasurechatta \"I forgot (unfortunately)\" Standard: 壊してしまった Contracted: 壊しちゃった Kowashichatta \"I broke it (regrettably)\"\n\nでしまう → じゃう:\n\nStandard: 死んでしまう Contracted: 死んじゃう Shinjau \"will die\" Standard: 読んでしまった Contracted: 読んじゃった Yonjatta \"I read it (completely)\""},{"level":3,"heading":"ておく → とく (Preparation)","content":"Do something in advance or for later.\n\nStandard: 買っておきます Contracted: 買っとく Kattoku \"I''ll buy it (in advance)\" Standard: 読んでおいて Contracted: 読んどいて Yondoite \"Read it (beforehand)\" Standard: 準備しておく Contracted: 準備しとく Junbi shitoku \"I''ll prepare (in advance)\" Standard: やっておこう Contracted: やっとこう Yattokou \"Let''s do it (now for later)\""},{"level":3,"heading":"ていく → てく (Direction Away)","content":"Going somewhere or doing something while going.\n\nStandard: 行っていきます Contracted: 行ってく Itteku \"I''m going (and going on)\" Standard: 持っていって Contracted: 持ってって Mottette \"Take it with you\" Standard: 帰っていく Contracted: 帰ってく Kaetteku \"going back home\""},{"level":3,"heading":"てくる → てくる (Direction Toward/Back)","content":"Coming or doing something and coming back. Note: This one doesn''t always contract, but can become っくる.\n\nStandard: 買ってきます Contracted: 買ってくる / 買ってくる Kattekuru \"I''ll go buy (and come back)\" Standard: 持ってきて Contracted: 持ってきて / 持ってって Mottekite / Mottette \"Bring it here\""}]},{"level":2,"heading":"Negative Form Contractions","content":"","children":[{"level":3,"heading":"ない → ん (Negative)","content":"Very casual negative contraction.\n\nStandard: 知らない Contracted: 知らん Shiran \"I don''t know\" Standard: 分からない Contracted: 分からん Wakaran \"I don''t understand\" Standard: やらない Contracted: やらん Yaran \"I won''t do it\"\n\nMore common in Western Japan (Kansai dialect)."},{"level":3,"heading":"なければ → なきゃ (Conditional Negative)","content":"\"If not\" or \"must\" constructions.\n\nStandard: 行かなければならない Contracted: 行かなきゃ(いけない) Ikanakya (ikenai) \"I have to go\" Standard: 勉強しなければ Contracted: 勉強しなきゃ Benkyou shinakya \"I have to study\" Standard: 食べなければダメ Contracted: 食べなきゃダメ Tabenakya dame \"You have to eat\""},{"level":3,"heading":"なくては → なくちゃ (Must/Have to)","content":"Similar to なきゃ but from なくては.\n\nStandard: 行かなくてはいけない Contracted: 行かなくちゃ Ikanakucha \"I have to go\" Standard: やらなくては Contracted: やらなくちゃ Yaranakucha \"I have to do it\""}]},{"level":2,"heading":"のだ/んだ Contractions","content":"","children":[{"level":3,"heading":"のです → んです → んだ/んす","content":"Explanatory form contractions.\n\nStandard: そうなのです Contracted: そうなんです → そうなんだ Sou na n da \"That''s how it is (you see)\" Standard: 忙しいのです Contracted: 忙しいんです → 忙しいんだ Isogashii n da \"I''m busy (that''s why)\" Standard: 学生なのです Contracted: 学生なんです → 学生なんだ Gakusei na n da \"I''m a student (explanation)\"\n\nSuper casual: んす\n\nそうなんす (sou nansu) - Very casual \"that''s right\""}]},{"level":2,"heading":"Conditional Contractions","content":"","children":[{"level":3,"heading":"れば → りゃ / ければ → きゃ","content":"Conditional form contractions.\n\nStandard: 見れば Contracted: 見りゃ Mirya \"if you look\" Standard: すれば Contracted: すりゃ Surya \"if you do\" Standard: 行けば Contracted: 行きゃ Ikya \"if you go\" Standard: よければ Contracted: よきゃ Yokya \"if it''s okay\""},{"level":3,"heading":"たら → た (Conditional)","content":"Very casual, especially in regions.\n\nStandard: 行ったら Contracted: 行った Itta \"if/when you go\" (context dependent) Standard: そうだったら Contracted: そうだった Sou datta \"if that''s the case\""}]},{"level":2,"heading":"Question Word Contractions","content":"","children":[{"level":3,"heading":"なにを → なにを/なんを → なに/なん","content":"Standard: 何をしているの？ Contracted: 何してるの？ Nani shiteru no? \"What are you doing?\" Standard: 何を食べる？ Contracted: 何食べる？ Nani taberu? \"What are you eating?\""},{"level":3,"heading":"どうして → どして","content":"Why/how come.\n\nStandard: どうしてですか？ Contracted: どしてですか？/ どして？ Doshite? \"Why?\" Standard: どうして来ないの？ Contracted: どして来ないの？ Doshite konai no? \"Why aren''t you coming?\""}]},{"level":2,"heading":"Word-Specific Contractions","content":"","children":[{"level":3,"heading":"これは → こりゃ / それは → そりゃ","content":"Standard: これはすごい Contracted: こりゃすごい Korya sugoi \"This is amazing!\" Standard: それは無理だ Contracted: そりゃ無理だ Sorya muri da \"That''s impossible\" Standard: あれは何？ Contracted: ありゃ何？ Arya nani? \"What''s that?\""},{"level":3,"heading":"という → って / といえば → ってば","content":"Quotation and emphasis.\n\nStandard: 田中さんという人 Contracted: 田中さんって人 Tanaka-san tte hito \"A person called Tanaka\" Standard: 何と言った？ Contracted: 何て言った？/ なんつった？ Nan te itta? / Nantsutta? \"What did you say?\" Standard: 聞いているの！ Contracted: 聞いてってば！ Kiite tteba! \"I''m telling you, listen!\""}]},{"level":2,"heading":"Polite Contractions (Yes, They Exist!)","content":"Even polite speech has some contractions.","children":[{"level":3,"heading":"ございます → ございます","content":"ありがとうございます → Standard ありがとございます → Sometimes heard but incorrect"},{"level":3,"heading":"です → っす (Young/Casual-Polite)","content":"Used by young people to be polite but casual.\n\nそうです → そうっす Sou ssu \"That''s right\" 学生です → 学生っす Gakusei ssu \"I''m a student\"\n\nCommon among male university students."}]},{"level":2,"heading":"Regional Variations","content":"","children":[{"level":3,"heading":"Kansai Contractions","content":"している → してる → しとる Shitoru \"doing\" ない → へん Hen \"not\" (Kansai negative) 知らない → 知らん → 知らへん Shirahen \"I don''t know\""},{"level":3,"heading":"Tokyo/Standard","content":"している → してる Shiteru \"doing\" のだ → んだ N da Explanatory"}]},{"level":2,"heading":"Combining Multiple Contractions","content":"Real speech often stacks contractions:\n\nStandard: 何をしているのですか？ First: 何をしているんですか？ Second: 何してるんですか？ Full casual: 何してんの？ Nani shiten no? \"What are you doing?\" Standard: 行っておかなければならない First: 行っとかなければならない Second: 行っとかなきゃならない Full casual: 行っとかなきゃ Ittokanakya \"I have to go (in advance)\" Standard: 食べてしまいました First: 食べちゃいました Second: 食べちゃった Full casual: 食べちった Tabechitта \"I ate it (all up)\""},{"level":2,"heading":"Contraction Reference Chart","content":"| Standard | Contraction | Example | | -------- | ----------- | ----------------------------- | | ている | てる | 食べてる (tabeteru) | | ては | ちゃ | 食べちゃ (tabecha) | | てしまう | ちゃう | 食べちゃう (tabechau) | | ておく | とく | 買っとく (kattoku) | | ていく | てく | 行ってく (itteku) | | なければ | なきゃ | 行かなきゃ (ikanakya) | | なくては | なくちゃ | やらなくちゃ (yaranakucha) | | のだ | んだ | そうなんだ (sou nan da) | | という | って | 田中さんって (Tanaka-san tte) | | これは | こりゃ | こりゃダメだ (korya dame da) |"},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using in Formal Situations","content":"❌ 会議で：レポート書いときました ✓ 会議で：レポートを書いておきました\n\nNever use contractions in business or formal settings."},{"level":3,"heading":"Mistake 2: Incorrect Contraction Forms","content":"❌ 食べている → 食べる (wrong - that''s just dictionary form) ✓ 食べている → 食べてる (correct contraction)\n\nDon''t confuse contractions with plain forms."},{"level":3,"heading":"Mistake 3: Over-Contracting","content":"❌ 行かなきゃならなくちゃいけない (too many contractions stacked) ✓ 行かなきゃいけない or 行かなくちゃ\n\nDon''t over-complicate with too many contractions."},{"level":3,"heading":"Mistake 4: Wrong Context","content":"❌ Teacher: 先生、これ知ってる？ ✓ Teacher: 先生、これを知っていますか？\n\nMatch formality to the situation and person."}]},{"level":2,"heading":"Listening Recognition","content":"When watching anime or dramas, listen for these contracted forms:\n\nCommon in anime:\n\nやっちゃった！ (Yacchatta!) - \"I did it!\" / \"Oops!\" 知らねー (Shirane-) - \"I don''t know\" (masculine, rough) わかんない (Wakannai) - \"I don''t understand\" しょうがない → しょーがない (Shou ga nai) - \"Can''t be helped\"\n\nCommon in daily conversation:\n\nそうなんだ (Sou nan da) - \"Oh, I see\" やっとく (Yattoku) - \"I''ll do it (in advance)\" 行かなきゃ (Ikanakya) - \"I gotta go\""},{"level":2,"heading":"Speed/Intensity Variations","content":"Some contractions have multiple levels:\n\nStandard: していない Level 1: してない (shitenai) Level 2: してへん (shitenai/shihen - Kansai) Level 3: してねー (shitene- - masculine/rough) Level 4: せーへん (se-hen - very Kansai)\n\nStandard: 分からない Level 1: 分かんない (wakannai) Level 2: 分からん (wakaran) Level 3: 分かんねー (wakanne- - rough)"},{"level":2,"heading":"Summary","content":"Most Important Contractions to Know:\n\nている → てる - Most common, essential ちゃう (てしまう) - Very common, shows completion/regret とく (ておく) - Common for preparation なきゃ/なくちゃ - Essential for \"have to\" んだ (のだ) - Explanatory sentences\n\nKey Points:\n\nContractions are mandatory for natural casual speech Never use in formal situations or with superiors Listen to native content to internalize patterns Start with the most common contractions (てる, ちゃう) Combine with particle dropping for full casual effect\n\nRemember:\n\nContractions don''t change meaning, only style They make you sound more natural and fluent Essential for understanding native speakers Practice listening recognition before production\n\nMastering contractions is a major step toward sounding like a native speaker and understanding real Japanese conversations!","children":[]}]'::jsonb,
  '["ている → てる - Most common, essential","ちゃう (てしまう) - Very common, shows completion/regret","とく (ておく) - Common for preparation","なきゃ/なくちゃ - Essential for \"have to\"","んだ (のだ) - Explanatory sentences","Sound more natural and fluent","Understand native speakers better","Essential for watching anime, dramas, movies","Used constantly in daily conversation","Formal writing","Business situations","With superiors or strangers","Official documents","Contractions are mandatory for natural casual speech","Never use in formal situations or with superiors","Listen to native content to internalize patterns","Start with the most common contractions (てる, ちゃう)","Combine with particle dropping for full casual effect","Contractions don''t change meaning, only style","They make you sound more natural and fluent","Essential for understanding native speakers","Practice listening recognition before production"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'copula-past',
  'Past Forms (だった/でした)',
  'https://practice-japanese.com/docs/copula-past/',
  'other',
  NULL,
  '[{"level":2,"heading":"Past Tense Copula Overview","content":"The past tense of the Japanese copula has two main forms corresponding to the present forms だ and です:\n\nPresent → Past だ → だった (plain) です → でした (polite) 学生だ → 学生だった \"was a student\" 学生です → 学生でした \"was a student\" (polite)"},{"level":2,"heading":"だった (datta) - Plain Past","content":"","children":[{"level":3,"heading":"Basic Formation","content":"だった is the plain past form, meaning \"was\" or \"were.\"\n\nNoun + だった Na-adjective + だった 学生だった \"was a student\" きれいだった \"was beautiful\""},{"level":3,"heading":"Basic Usage","content":"昨日は月曜日だった \"Yesterday was Monday\" 彼は学生だった \"He was a student\" 昔は静かだった \"It was quiet long ago\" 子供の頃は元気だった \"I was energetic when I was a child\" あの映画は面白かった \"That movie was interesting\""},{"level":3,"heading":"With Na-adjectives","content":"きれいだった \"was beautiful\" 静かだった \"was quiet\" 便利だった \"was convenient\" 有名だった \"was famous\" 親切だった \"was kind\""}]},{"level":2,"heading":"でした (deshita) - Polite Past","content":"","children":[{"level":3,"heading":"Basic Formation","content":"でした is the polite past form, meaning \"was\" or \"were.\"\n\nNoun + でした Na-adjective + でした 学生でした \"was a student\" (polite) きれいでした \"was beautiful\" (polite)"},{"level":3,"heading":"Basic Usage","content":"昨日は月曜日でした \"Yesterday was Monday\" 彼は学生でした \"He was a student\" 昔は静かでした \"It was quiet long ago\" とても便利でした \"It was very convenient\" いい天気でした \"It was nice weather\""},{"level":3,"heading":"With Na-adjectives","content":"きれいでした \"was beautiful\" 静かでした \"was quiet\" 便利でした \"was convenient\" 有名でした \"was famous\" 親切でした \"was kind\""}]},{"level":2,"heading":"Negative Past Forms","content":"","children":[{"level":3,"heading":"じゃなかった (Plain negative past)","content":"学生じゃなかった \"was not a student\" 本当じゃなかった \"was not true\" 簡単じゃなかった \"was not simple\" きれいじゃなかった \"was not beautiful\" 静かじゃなかった \"was not quiet\""},{"level":3,"heading":"じゃなかったです (Polite casual)","content":"学生じゃなかったです \"was not a student\" (polite) 簡単じゃなかったです \"was not simple\" (polite) 便利じゃなかったです \"was not convenient\" (polite)"},{"level":3,"heading":"ではありませんでした (Most polite)","content":"学生ではありませんでした \"was not a student\" (formal/polite) 簡単ではありませんでした \"was not simple\" (formal/polite) 適切ではありませんでした \"was not appropriate\" (formal/polite)"}]},{"level":2,"heading":"Complete Conjugation Chart","content":"","children":[{"level":3,"heading":"Affirmative Forms","content":"Present Plain: だ Present Polite: です Past Plain: だった Past Polite: でした 学生だ → 学生です 学生だった → 学生でした"},{"level":3,"heading":"Negative Forms","content":"Present Negative Plain: じゃない Present Negative Polite: じゃないです / ではありません Past Negative Plain: じゃなかった Past Negative Polite: じゃなかったです / ではありませんでした 学生じゃない → 学生じゃないです 学生じゃなかった → 学生じゃなかったです"}]},{"level":2,"heading":"Time Expressions with Past Copula","content":"","children":[{"level":3,"heading":"Yesterday/Past Time","content":"昨日は雨だった \"It was rainy yesterday\" 先週は忙しかった \"I was busy last week\" 去年の夏は暑かった \"Last summer was hot\" 10年前は学生だった \"I was a student 10 years ago\""},{"level":3,"heading":"Childhood/Long Ago","content":"子供の頃は元気だった \"I was energetic in my childhood\" 昔はここは畑だった \"This used to be a field long ago\" 若い時は細かった \"I was thin when I was young\""},{"level":3,"heading":"Specific Past Moments","content":"その時は大変だった \"It was tough at that time\" あの日は特別だった \"That day was special\" その瞬間は静かだった \"That moment was quiet\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"だったら (if it was/were)","content":"Conditional form of だった.\n\n学生だったら、割引がある \"If you were a student, there would be a discount\" 昨日だったら、行けた \"If it were yesterday, I could have gone\" もっと早かったら、間に合った \"If it were earlier, I would have made it\""},{"level":3,"heading":"だったのに (even though it was)","content":"Expresses regret or contrast.\n\n簡単だったのに \"Even though it was easy\" きれいだったのに、壊れた \"Even though it was beautiful, it broke\" 学生だったのに、覚えていない \"Even though I was a student, I don''t remember\""},{"level":3,"heading":"だったんだ (explanatory past)","content":"Explains past situations.\n\nそうだったんだ \"So that''s how it was\" 学生だったんだ \"Oh, you were a student (I see)\" 大変だったんだね \"So it was tough, huh\""},{"level":3,"heading":"だったっけ (was it...? reminder)","content":"Casual question about past.\n\n昨日だったっけ？ \"Was it yesterday?\" 田中さんだったっけ？ \"Was it Tanaka?\" 月曜日だったっけ？ \"Was it Monday?\""}]},{"level":2,"heading":"Question Forms","content":"","children":[{"level":3,"heading":"だった + か","content":"学生だったか？ \"Were you a student?\" 昨日だったか？ \"Was it yesterday?\" 本当だったか？ \"Was it true?\""},{"level":3,"heading":"でしたか (Polite question)","content":"学生でしたか？ \"Were you a student?\" 大変でしたか？ \"Was it difficult?\" きれいでしたか？ \"Was it beautiful?\""},{"level":3,"heading":"Answering Past Questions","content":"Q: 学生でしたか？ \"Were you a student?\" A: はい、学生でした \"Yes, I was a student\" A: いいえ、学生じゃなかったです \"No, I wasn''t a student\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Past Experience","content":"A: 昨日のパーティーはどうだった？ \"How was yesterday''s party?\" B: 楽しかった！みんな元気だった \"It was fun! Everyone was energetic\" A: 食べ物はおいしかった？ \"Was the food delicious?\" B: うん、とてもおいしかった \"Yeah, it was very delicious\""},{"level":3,"heading":"Example 2: Reminiscing","content":"A: 子供の頃、どんな子だった？ \"What kind of child were you in childhood?\" B: 静かな子だった \"I was a quiet child\" A: 今は全然違うね \"You''re totally different now\" B: そうだね。昔はシャイだったんだ \"That''s right. I was shy back then\""},{"level":3,"heading":"Example 3: Polite Inquiry","content":"A: 会議はいかがでしたか？ \"How was the meeting?\" B: とても有益でした \"It was very beneficial\" A: 長かったですか？ \"Was it long?\" B: いいえ、1時間ぐらいでした \"No, it was about an hour\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using でした with verbs","content":"Wrong: 行きましたでした ❌ Right: 行きました ✓ \"I went\" Note: Verbs have their own past forms"},{"level":3,"heading":"Mistake 2: Using だった with i-adjectives directly","content":"Wrong: 大きいだった ❌ Right: 大きかった ✓ \"It was big\" Note: I-adjectives conjugate differently"},{"level":3,"heading":"Mistake 3: Double past marking","content":"Wrong: 学生だったでした ❌ Right: 学生でした ✓ \"I was a student\""},{"level":3,"heading":"Mistake 4: Wrong negative past","content":"Wrong: 学生ないだった ❌ Right: 学生じゃなかった ✓ \"I wasn''t a student\""}]},{"level":2,"heading":"Comparison with Other Past Forms","content":"","children":[{"level":3,"heading":"I-adjectives Past","content":"I-adjective: おいしい → おいしかった \"delicious → was delicious\" NOT: おいしいだった ❌"},{"level":3,"heading":"Verbs Past","content":"Verb: 行く → 行った \"go → went\" NOT: 行くだった ❌"},{"level":3,"heading":"Na-adjectives and Nouns Past","content":"Na-adj: きれい → きれいだった Noun: 学生 → 学生だった \"beautiful → was beautiful\" \"student → was a student\""}]},{"level":2,"heading":"Formal Written Past: であった","content":"","children":[{"level":3,"heading":"In Formal Writing","content":"彼は学生であった \"He was a student\" (formal written) その結果は明らかであった \"The result was clear\" (formal written) これは事実であった \"This was a fact\" (formal written)"}]},{"level":2,"heading":"Special Expressions","content":"","children":[{"level":3,"heading":"だったら (conditional/advice)","content":"私だったら、行かない \"If it were me, I wouldn''t go\" 明日だったら、大丈夫 \"If it''s tomorrow, it''s okay\""},{"level":3,"heading":"そうだったんですか (I see)","content":"A: 学生でした \"I was a student\" B: そうだったんですか \"Oh, I see / Is that so\""},{"level":3,"heading":"だったんですね (confirmation with understanding)","content":"大変だったんですね \"So it was tough (I understand now)\" そうだったんですね \"So that''s how it was (I get it now)\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic Past Statements","content":"昨日は日曜日だった \"Yesterday was Sunday\" 彼は先生だった \"He was a teacher\" その映画は面白かった \"That movie was interesting\" 天気はよかった \"The weather was good\""},{"level":3,"heading":"Polite Past","content":"とてもきれいでした \"It was very beautiful\" 会議は長かったです \"The meeting was long\" 先生は親切でした \"The teacher was kind\""},{"level":3,"heading":"Negative Past","content":"簡単じゃなかった \"It wasn''t easy\" 学生じゃなかったです \"I wasn''t a student\" 本当ではありませんでした \"It was not true\""},{"level":3,"heading":"In Context","content":"子供の時は元気だった \"I was energetic when I was a child\" 10年前、ここは静かだった \"Ten years ago, this place was quiet\" あの人は有名な歌手だった \"That person was a famous singer\" 昔はもっと安かった \"It used to be cheaper long ago\" その時は大変だったんだ \"It was tough at that time (you see)\""}]},{"level":2,"heading":"Related Grammar Points","content":"だ (da) - Plain present copula です (desu) - Polite present copula である (de aru) - Formal copula じゃない (janai) - Negative copula Past Tense Usage - Using past tense in context Time Words (今、昨日、明日) - Time expressions","children":[]}]'::jsonb,
  '["だ (da) - Plain present copula","です (desu) - Polite present copula","である (de aru) - Formal copula","じゃない (janai) - Negative copula","Past Tense Usage - Using past tense in context","Time Words (今、昨日、明日) - Time expressions"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'counter-hai',
  'Cups/Glasses (杯)',
  'https://practice-japanese.com/docs/counter-hai/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 杯 (はい/ばい/ぱい)?","content":"杯 is the counter for cups, glasses, and bowls containing drinks or food. It follows the same sound change pattern as 本 and 匹.\n\nコーヒーを2杯飲みました \"I drank 2 cups of coffee\" ビールを3杯ください \"3 beers, please\""},{"level":2,"heading":"Complete 杯 Counter Chart","content":"| Number | Reading | Romaji | Sound | | ------ | ---------- | -------- | ----- | | 1 | いっぱい | ippai | ぱい | | 2 | にはい | ni-hai | はい | | 3 | さんばい | san-bai | ばい | | 4 | よんはい | yon-hai | はい | | 5 | ごはい | go-hai | はい | | 6 | ろっぱい | roppai | ぱい | | 7 | ななはい | nana-hai | はい | | 8 | はっぱい | happai | ぱい | | 9 | きゅうはい | kyuu-hai | はい | | 10 | じゅっぱい | juppai | ぱい |","children":[{"level":3,"heading":"Larger Numbers","content":"11杯 = じゅういっぱい 20杯 = にじゅっぱい 100杯 = ひゃっぱい"}]},{"level":2,"heading":"Sound Change Pattern","content":"","children":[{"level":3,"heading":"はい (hai) - Default","content":"2杯 (にはい) 4杯 (よんはい) 5杯 (ごはい) 7杯 (ななはい) 9杯 (きゅうはい)"},{"level":3,"heading":"ばい (bai) - After ん sound","content":"3杯 (さんばい) - after さん"},{"level":3,"heading":"ぱい (pai) - After っ sound","content":"1杯 (いっぱい) - いち becomes いっ 6杯 (ろっぱい) - ろく becomes ろっ 8杯 (はっぱい) - はち becomes はっ 10杯 (じゅっぱい) - じゅう becomes じゅっ"}]},{"level":2,"heading":"What to Count with 杯","content":"","children":[{"level":3,"heading":"Hot Drinks","content":"コーヒー - coffee お茶 - tea 紅茶 (こうちゃ) - black tea ココア - hot chocolate\n\nコーヒーを1杯ください \"One cup of coffee, please\" お茶を2杯飲みました \"I drank 2 cups of tea\""},{"level":3,"heading":"Cold Drinks","content":"水 (みず) - water ジュース - juice ビール - beer ワイン - wine ソーダ - soda 牛乳 (ぎゅうにゅう) - milk\n\nビールを3杯飲んだ \"I drank 3 beers\" 水を1杯ください \"A glass of water, please\""},{"level":3,"heading":"Bowls of Food","content":"ご飯 (ごはん) - rice ラーメン - ramen スープ - soup 味噌汁 (みそしる) - miso soup うどん - udon そば - soba\n\nご飯を2杯食べました \"I ate 2 bowls of rice\" ラーメンを1杯食べたい \"I want to eat a bowl of ramen\""}]},{"level":2,"heading":"Important: いっぱい Has Two Meanings!","content":"","children":[{"level":3,"heading":"1. One Cup/Glass (counter)","content":"コーヒーを一杯ください \"One coffee, please\" ビールが一杯飲みたい \"I want to drink one beer\""},{"level":3,"heading":"2. Full (adjective)","content":"コップがいっぱいです \"The glass is full\" お腹いっぱい \"I''m full\" (stomach) いっぱいある \"There''s a lot\"\n\nContext makes it clear which meaning!"}]},{"level":2,"heading":"Asking \"How Many?\"","content":"","children":[{"level":3,"heading":"何杯 (なんばい)","content":"何杯飲みましたか？ \"How many cups did you drink?\" コーヒーを何杯飲みますか？ \"How many cups of coffee do you drink?\" ご飯を何杯食べますか？ \"How many bowls of rice will you eat?\" ビールは何杯ですか？ \"How many beers?\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Drink + を + 杯 Counter","content":"コーヒーを2杯飲みました \"I drank 2 cups of coffee\" ビールを3杯ください \"3 beers, please\" 水を1杯持ってきて \"Bring me a glass of water\""},{"level":3,"heading":"Subject + が + 杯 Counter + Verb","content":"お茶が3杯あります \"There are 3 cups of tea\" ビールが2杯来ました \"2 beers have arrived\""},{"level":3,"heading":"杯 Counter + の + Noun","content":"2杯のコーヒー \"2 cups of coffee\" 3杯のビール \"3 beers\" 1杯のお茶 \"1 cup of tea\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Café","content":"A: 何を飲みますか？ \"What will you drink?\" B: コーヒーを2杯ください \"2 coffees, please\" A: サイズは？ \"What size?\" B: 両方ラージで \"Both large\""},{"level":3,"heading":"Example 2: Restaurant","content":"A: ビールは何杯お持ちしましょうか？ \"How many beers shall I bring?\" B: とりあえず3杯お願いします \"3 for now, please\" A: かしこまりました \"Certainly\""},{"level":3,"heading":"Example 3: At Home","content":"A: お茶をもう一杯いかがですか？ \"Would you like another cup of tea?\" B: いいえ、もうお腹いっぱいです \"No, I''m already full\" A: そうですか \"I see\""},{"level":3,"heading":"Example 4: Eating Rice","content":"A: ご飯のおかわりいかがですか？ \"Would you like another bowl of rice?\" B: はい、もう一杯ください \"Yes, one more bowl please\" A: どうぞ \"Here you go\""}]},{"level":2,"heading":"Special Expressions","content":"","children":[{"level":3,"heading":"もう一杯 (another cup)","content":"もう一杯ください \"One more, please\" もう一杯いかがですか？ \"Would you like another cup?\" もう一杯飲もう \"Let''s have another drink\""},{"level":3,"heading":"〜杯ずつ (each)","content":"一杯ずつ飲みましょう \"Let''s drink one each\" 2杯ずつ注いでください \"Please pour 2 cups each\""},{"level":3,"heading":"〜杯目 (ordinal)","content":"1杯目 (いっぱいめ) - the first cup 2杯目 (にはいめ) - the second cup 3杯目 (さんばいめ) - the third cup 2杯目のコーヒーが一番美味しかった \"The second cup of coffee was the most delicious\""},{"level":3,"heading":"〜杯も (as many as)","content":"5杯も飲んだ \"I drank as many as 5 cups\" 10杯も食べた \"I ate as many as 10 bowls\""},{"level":3,"heading":"〜杯しか (only)","content":"1杯しか飲んでいない \"I''ve only drunk 1 cup\" 2杯しか残っていない \"Only 2 cups remain\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Wrong sound changes","content":"Wrong: いちはい (1) ❌ Right: いっぱい ✓ Wrong: さんはい (3) ❌ Right: さんばい ✓ Wrong: ろくはい (6) ❌ Right: ろっぱい ✓"},{"level":3,"heading":"Mistake 2: Confusing いっぱい meanings","content":"一杯 = one cup (counter) いっぱい = full (adjective) Context is key!"},{"level":3,"heading":"Mistake 3: Using wrong counter for bottles","content":"Depends on context: ビール一杯 = one glass of beer ✓ ビール一本 = one bottle of beer ✓ Both can be correct!"},{"level":3,"heading":"Mistake 4: Forgetting the counter","content":"Wrong: コーヒーを2ください ❌ Right: コーヒーを2杯ください ✓ \"2 coffees, please\""}]},{"level":2,"heading":"Bottles vs Glasses","content":"","children":[{"level":3,"heading":"杯 - Served drinks (in cups/glasses)","content":"ビールを2杯 \"2 glasses of beer\" コーヒーを1杯 \"1 cup of coffee\""},{"level":3,"heading":"本 - Bottles/cans","content":"ビールを2本 \"2 bottles of beer\" ペットボトルを3本 \"3 plastic bottles\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"コーヒーを1杯ください \"One coffee, please\" お茶を2杯飲みました \"I drank 2 cups of tea\" ビールが3杯あります \"There are 3 beers\""},{"level":3,"heading":"Intermediate","content":"何杯飲みましたか？ \"How many cups did you drink?\" もう一杯いかがですか？ \"Would you like another cup?\" ご飯を2杯おかわりしました \"I had 2 more bowls of rice\""},{"level":3,"heading":"Advanced","content":"毎日コーヒーを3杯飲みます \"I drink 3 cups of coffee every day\" ラーメンを2杯も食べてしまった \"I ended up eating 2 whole bowls of ramen\" 一杯ずつゆっくり飲みましょう \"Let''s drink slowly, one cup at a time\""}]},{"level":2,"heading":"Related Grammar Points","content":"Counter System Basics - How counters work 本 (hon) - Long objects/bottles 皿 (sara) - Plates of food Sound changes - Phonetic variations おかわり - Refill/seconds いっぱい - Full (adjective)","children":[]}]'::jsonb,
  '["Counter System Basics - How counters work","本 (hon) - Long objects/bottles","皿 (sara) - Plates of food","Sound changes - Phonetic variations","おかわり - Refill/seconds","いっぱい - Full (adjective)"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'counter-hiki',
  'Small Animals (匹)',
  'https://practice-japanese.com/docs/counter-hiki/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 匹 (ひき/びき/ぴき)?","content":"匹 is the counter for small to medium-sized animals. It has three pronunciations (ひき、びき、ぴき) that change depending on the number.\n\n犬を2匹飼っています \"I have 2 dogs\" 金魚が5匹います \"There are 5 goldfish\""},{"level":2,"heading":"Complete 匹 Counter Chart","content":"| Number | Reading | Romaji | Sound | | ------ | ---------- | --------- | ----- | | 1 | いっぴき | ippiki | ぴき | | 2 | にひき | ni-hiki | ひき | | 3 | さんびき | san-biki | びき | | 4 | よんひき | yon-hiki | ひき | | 5 | ごひき | go-hiki | ひき | | 6 | ろっぴき | roppiki | ぴき | | 7 | ななひき | nana-hiki | ひき | | 8 | はっぴき | happiki | ぴき | | 9 | きゅうひき | kyuu-hiki | ひき | | 10 | じゅっぴき | juppiki | ぴき |","children":[{"level":3,"heading":"Larger Numbers","content":"11匹 = じゅういっぴき 20匹 = にじゅっぴき 100匹 = ひゃっぴき"}]},{"level":2,"heading":"Sound Change Pattern","content":"","children":[{"level":3,"heading":"ひき (hiki) - Default","content":"2匹 (にひき) 4匹 (よんひき) 5匹 (ごひき) 7匹 (ななひき) 9匹 (きゅうひき)"},{"level":3,"heading":"びき (biki) - After ん sound","content":"3匹 (さんびき) - after さん"},{"level":3,"heading":"ぴき (piki) - After っ sound","content":"1匹 (いっぴき) - いち becomes いっ 6匹 (ろっぴき) - ろく becomes ろっ 8匹 (はっぴき) - はち becomes はっ 10匹 (じゅっぴき) - じゅう becomes じゅっ\n\nSame pattern as 本 (hon/bon/pon)!"}]},{"level":2,"heading":"What Animals to Count with 匹","content":"","children":[{"level":3,"heading":"Pets","content":"犬 (いぬ) - dogs 猫 (ねこ) - cats ハムスター - hamsters うさぎ - rabbits インコ - parakeets\n\n犬を1匹飼っています \"I have 1 dog\" 猫が2匹います \"There are 2 cats\""},{"level":3,"heading":"Fish and Aquatic Animals","content":"金魚 (きんぎょ) - goldfish 魚 (さかな) - fish かえる - frogs かめ - turtles\n\n金魚を5匹買いました \"I bought 5 goldfish\""},{"level":3,"heading":"Insects","content":"蝶 (ちょう) - butterflies 蜂 (はち) - bees 蚊 (か) - mosquitoes 虫 (むし) - insects/bugs\n\n蝶が3匹飛んでいます \"3 butterflies are flying\""},{"level":3,"heading":"Small to Medium Wild Animals","content":"リス - squirrels きつね - foxes たぬき - raccoon dogs ねずみ - mice/rats へび - snakes"},{"level":3,"heading":"General Rule","content":"Use 匹 for animals smaller than a human or about human-sized."}]},{"level":2,"heading":"Asking \"How Many?\"","content":"","children":[{"level":3,"heading":"何匹 (なんびき)","content":"何匹いますか？ \"How many are there?\" ペットを何匹飼っていますか？ \"How many pets do you have?\" 犬が何匹いますか？ \"How many dogs are there?\" 金魚を何匹買いますか？ \"How many goldfish will you buy?\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Animal + を + 匹 Counter","content":"犬を2匹飼っています \"I have 2 dogs\" 金魚を5匹買いました \"I bought 5 goldfish\" 猫を1匹見ました \"I saw 1 cat\""},{"level":3,"heading":"Animal + が + 匹 Counter + Verb","content":"猫が3匹います \"There are 3 cats\" 犬が2匹走っています \"2 dogs are running\" 魚が10匹泳いでいます \"10 fish are swimming\""},{"level":3,"heading":"匹 Counter + の + Animal","content":"2匹の犬 \"2 dogs\" 5匹の金魚 \"5 goldfish\" 3匹の猫 \"3 cats\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Pet Shop","content":"A: ペットを何匹飼っていますか？ \"How many pets do you have?\" B: 犬を2匹と猫を1匹飼っています \"I have 2 dogs and 1 cat\" A: 大変ですね \"That must be a lot of work\" B: でも、とても可愛いです \"But they''re very cute\""},{"level":3,"heading":"Example 2: Aquarium","content":"A: 金魚を何匹買いますか？ \"How many goldfish will you buy?\" B: 5匹ください \"5, please\" A: 水槽は大きいですか？ \"Is your tank big?\" B: はい、10匹ぐらい入ります \"Yes, about 10 can fit\""},{"level":3,"heading":"Example 3: Park","content":"A: 公園に犬が何匹いますか？ \"How many dogs are in the park?\" B: 3匹います \"There are 3\" A: 大きい犬ですか？ \"Are they big dogs?\" B: いいえ、小さい犬です \"No, they''re small dogs\""}]},{"level":2,"heading":"When NOT to Use 匹","content":"","children":[{"level":3,"heading":"Large Animals - Use 頭 (とう)","content":"Wrong: 象を2匹 ❌ Right: 象を2頭 ✓ \"2 elephants\" Wrong: 馬を3匹 ❌ Right: 馬を3頭 ✓ \"3 horses\"\n\nLarge animals (cows, horses, elephants, etc.) use 頭."},{"level":3,"heading":"Birds (Sometimes) - Use 羽 (わ)","content":"Can use: 鳥を3匹 (acceptable) Better: 鳥を3羽 ✓ \"3 birds\"\n\nFor birds, 羽 is more specific, but 匹 is also used."},{"level":3,"heading":"Rabbits - Use 羽 (わ)","content":"Wrong: うさぎを2匹 ⚠️ Traditional: うさぎを2羽 ✓ \"2 rabbits\"\n\nHistorically, rabbits were counted with 羽 (like birds), but 匹 is now common in casual speech."}]},{"level":2,"heading":"Special Expressions","content":"","children":[{"level":3,"heading":"〜匹ずつ","content":"一匹ずつ見てください \"Please look at each one\" 2匹ずつケージに入れます \"Put 2 in each cage\""},{"level":3,"heading":"〜匹目","content":"1匹目 (いっぴきめ) - the first one 2匹目 (にひきめ) - the second one 3匹目 (さんびきめ) - the third one 3匹目の猫が一番可愛い \"The third cat is the cutest\""},{"level":3,"heading":"〜匹も","content":"10匹も飼っている \"I have as many as 10\" 5匹も捕まえた \"I caught as many as 5\""},{"level":3,"heading":"〜匹しか","content":"1匹しかいない \"There''s only 1\" 2匹しか残っていない \"Only 2 remain\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Wrong sound changes","content":"Wrong: いちひき (1) ❌ Right: いっぴき ✓ Wrong: さんひき (3) ❌ Right: さんびき ✓ Wrong: ろくひき (6) ❌ Right: ろっぴき ✓"},{"level":3,"heading":"Mistake 2: Using 匹 for large animals","content":"Wrong: 象が2匹 ❌ Right: 象が2頭 ✓ \"2 elephants\""},{"level":3,"heading":"Mistake 3: Using 匹 for people","content":"Wrong: 子供が3匹 ❌ Right: 子供が3人 ✓ \"3 children\""},{"level":3,"heading":"Mistake 4: Confusing 何匹 reading","content":"Wrong: なにひき ❌ Right: なんびき ✓ \"How many (animals)?\""}]},{"level":2,"heading":"Pronunciation Practice","content":"","children":[{"level":3,"heading":"Tricky Ones","content":"1匹 = いっぴき (not いちひき) 3匹 = さんびき (not さんひき) 6匹 = ろっぴき (not ろくひき) 8匹 = はっぴき (not はちひき) 10匹 = じゅっぴき (not じゅうひき)"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"猫が3匹います \"There are 3 cats\" 犬を2匹飼っています \"I have 2 dogs\" 金魚を5匹買いました \"I bought 5 goldfish\""},{"level":3,"heading":"Intermediate","content":"ペットを何匹飼っていますか？ \"How many pets do you have?\" 公園に犬が何匹いますか？ \"How many dogs are in the park?\" この水槽には魚が10匹入ります \"10 fish can fit in this tank\""},{"level":3,"heading":"Advanced","content":"うちには猫が3匹、犬が2匹、金魚が10匹います \"We have 3 cats, 2 dogs, and 10 goldfish\" 一匹ずつ丁寧に世話をしています \"I take care of each one carefully\" 子供の頃、ハムスターを5匹飼っていました \"When I was a child, I had 5 hamsters\""}]},{"level":2,"heading":"Related Grammar Points","content":"Counter System Basics - How counters work 頭 (tou) - Large animals counter 羽 (wa) - Birds counter 飼う (kau) - To keep/raise pets いる vs ある - Existence verbs Sound changes - Phonetic variations","children":[]}]'::jsonb,
  '["Counter System Basics - How counters work","頭 (tou) - Large animals counter","羽 (wa) - Birds counter","飼う (kau) - To keep/raise pets","いる vs ある - Existence verbs","Sound changes - Phonetic variations"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'counter-hon',
  'Long Objects (本)',
  'https://practice-japanese.com/docs/counter-hon/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 本 (ほん/ぼん/ぽん)?","content":"本 is the counter for long, cylindrical objects. It has three pronunciations (ほん、ぼん、ぽん) that change depending on the number it follows.\n\nペンを3本買いました \"I bought 3 pens\" ビールを2本ください \"2 bottles of beer, please\""},{"level":2,"heading":"Complete 本 Counter Chart","content":"| Number | Reading | Romaji | Sound Change | | ------ | ---------- | -------- | ------------ | | 1 | いっぽん | ippon | ぽん | | 2 | にほん | nihon | ほん | | 3 | さんぼん | san-bon | ぼん | | 4 | よんほん | yon-hon | ほん | | 5 | ごほん | go-hon | ほん | | 6 | ろっぽん | roppon | ぽん | | 7 | ななほん | nana-hon | ほん | | 8 | はっぽん | happon | ぽん | | 9 | きゅうほん | kyuu-hon | ほん | | 10 | じゅっぽん | juppon | ぽん |","children":[{"level":3,"heading":"Larger Numbers","content":"11本 = じゅういっぽん 20本 = にじゅっぽん 30本 = さんじゅっぽん 100本 = ひゃっぽん"}]},{"level":2,"heading":"Sound Change Pattern","content":"","children":[{"level":3,"heading":"ほん (hon) - Default","content":"2本 (にほん) 4本 (よんほん) 5本 (ごほん) 7本 (ななほん) 9本 (きゅうほん)"},{"level":3,"heading":"ぼん (bon) - After ん sound","content":"3本 (さんぼん) - after さん"},{"level":3,"heading":"ぽん (pon) - After っ sound","content":"1本 (いっぽん) - いち becomes いっ 6本 (ろっぽん) - ろく becomes ろっ 8本 (はっぽん) - はち becomes はっ 10本 (じゅっぽん) - じゅう becomes じゅっ\n\nMemory Tip: When a number ends with っ or ん, the counter sound changes!"}]},{"level":2,"heading":"What to Count with 本","content":"","children":[{"level":3,"heading":"Writing Instruments","content":"ペン - pens 鉛筆 (えんぴつ) - pencils ボールペン - ballpoint pens マーカー - markers 筆 (ふで) - brushes\n\nペンを3本持っています \"I have 3 pens\" 鉛筆を5本買いました \"I bought 5 pencils\""},{"level":3,"heading":"Bottles and Cans","content":"ビール - beer bottles ワイン - wine bottles ジュース - juice bottles/cans ペットボトル - plastic bottles 缶 (かん) - cans\n\nビールを2本ください \"2 beers, please\" 水を1本買いました \"I bought 1 bottle of water\""},{"level":3,"heading":"Trees and Plants","content":"木 (き) - trees 花 (はな) - flowers (with long stems) バラ - roses\n\n庭に木が10本あります \"There are 10 trees in the garden\" バラを3本もらいました \"I received 3 roses\""},{"level":3,"heading":"Long Vegetables","content":"きゅうり - cucumbers 大根 (だいこん) - daikon radishes にんじん - carrots バナナ - bananas\n\nバナナを5本食べました \"I ate 5 bananas\" きゅうりを2本使います \"I''ll use 2 cucumbers\""},{"level":3,"heading":"Sports Equipment","content":"バット - baseball bats ラケット - rackets ゴルフクラブ - golf clubs 釣り竿 (つりざお) - fishing rods\n\nバットを2本持っています \"I have 2 bats\""},{"level":3,"heading":"Umbrellas","content":"傘 (かさ) - umbrellas\n\n傘を1本忘れました \"I forgot 1 umbrella\""},{"level":3,"heading":"Films/Videos","content":"映画 (えいが) - films/movies ビデオ - videos\n\n映画を3本見ました \"I watched 3 films\""},{"level":3,"heading":"Long Items in General","content":"ひも - strings/ropes 棒 (ぼう) - sticks/poles パイプ - pipes レール - rails"}]},{"level":2,"heading":"Asking \"How Many?\"","content":"","children":[{"level":3,"heading":"何本 (なんぼん)","content":"何本ありますか？ \"How many are there?\" 何本買いますか？ \"How many will you buy?\" ペンが何本必要ですか？ \"How many pens do you need?\" 木が何本植えてありますか？ \"How many trees are planted?\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Object + を + 本 Counter","content":"ペンを3本買った \"I bought 3 pens\" ビールを2本飲んだ \"I drank 2 beers\" 映画を5本見た \"I watched 5 movies\""},{"level":3,"heading":"Subject + が + 本 Counter + Verb","content":"木が10本あります \"There are 10 trees\" ペンが3本ありません \"There aren''t 3 pens\""},{"level":3,"heading":"本 Counter + の + Noun","content":"3本のペン \"3 pens\" 2本のビール \"2 beers\" 5本の木 \"5 trees\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Stationery Store","content":"A: ペンを何本買いますか？ \"How many pens will you buy?\" B: 5本ください \"5, please\" A: 色は？ \"What colors?\" B: 黒を3本と赤を2本 \"3 black and 2 red\""},{"level":3,"heading":"Example 2: Convenience Store","content":"A: お飲み物は？ \"Drinks?\" B: ビールを2本ください \"2 beers, please\" A: 冷たいのと常温、どちらがよろしいですか？ \"Cold or room temperature?\" B: 冷たいのでお願いします \"Cold, please\""},{"level":3,"heading":"Example 3: Flower Shop","content":"A: バラは何本入りますか？ \"How many roses do you want?\" B: 12本でお願いします \"12, please\" A: かしこまりました \"Certainly\""},{"level":3,"heading":"Example 4: Movies","content":"A: 週末に映画を何本見ましたか？ \"How many movies did you watch this weekend?\" B: 3本見ました \"I watched 3\" A: すごいですね！ \"That''s amazing!\""}]},{"level":2,"heading":"Special Expressions","content":"","children":[{"level":3,"heading":"〜本ずつ (each/apiece)","content":"一本ずつ取ってください \"Please take one each\" 2本ずつ配ります \"I''ll distribute 2 each\""},{"level":3,"heading":"〜本目 (ordinal)","content":"1本目 (いっぽんめ) - the first one 2本目 (にほんめ) - the second one 3本目 (さんぼんめ) - the third one 3本目の映画が一番面白かった \"The third movie was the most interesting\""},{"level":3,"heading":"〜本も (as many as)","content":"10本も飲んだ \"I drank as many as 10\" 5本も買った \"I bought as many as 5\""},{"level":3,"heading":"〜本しか (only)","content":"1本しかない \"There''s only 1\" 2本しか持っていない \"I only have 2\""}]},{"level":2,"heading":"When NOT to Use 本","content":"","children":[{"level":3,"heading":"Don''t use for:","content":"Short cylindrical objects:\n\nWrong: 電池3本 ❌ Right: 電池3個 ✓ \"3 batteries\"\n\nBooks (despite 本 meaning \"book\"):\n\nWrong: 本3本 ❌ Right: 本3冊 (さんさつ) ✓ \"3 books\"\n\nThick/bulky cylinders:\n\nlogs, tree trunks → might use 個 instead"}]},{"level":2,"heading":"Pronunciation Practice","content":"","children":[{"level":3,"heading":"Common Mistakes","content":"Wrong: さんほん (3) ❌ Right: さんぼん ✓ Wrong: いちほん (1) ❌ Right: いっぽん ✓ Wrong: ろくほん (6) ❌ Right: ろっぽん ✓ Wrong: じゅうほん (10) ❌ Right: じゅっぽん ✓"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Wrong sound changes","content":"Wrong: いちほん ❌ Right: いっぽん ✓ \"1 (long object)\""},{"level":3,"heading":"Mistake 2: Using 本 for books","content":"Wrong: 本を3本読みました ❌ Right: 本を3冊読みました ✓ \"I read 3 books\" 本 as counter = long objects 本 as noun = book"},{"level":3,"heading":"Mistake 3: Confusing with 2 本 (nihon = Japan)","content":"2本 (にほん) = 2 long objects 日本 (にほん) = Japan Context makes it clear!"},{"level":3,"heading":"Mistake 4: Wrong items","content":"Wrong: りんご3本 ❌ Right: りんご3個 ✓ \"3 apples\" (Apples aren''t long/cylindrical)"}]},{"level":2,"heading":"Formal vs Casual","content":"","children":[{"level":3,"heading":"Casual","content":"ペン3本ちょうだい \"Give me 3 pens\" ビール2本 \"2 beers\""},{"level":3,"heading":"Polite","content":"ペンを3本ください \"3 pens, please\" ビールを2本お願いします \"2 beers, please\""},{"level":3,"heading":"Formal/Business","content":"ペンを3本購入いたします \"I will purchase 3 pens\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"ペンが3本あります \"There are 3 pens\" 傘を1本持っています \"I have 1 umbrella\" 木が10本植えてあります \"10 trees are planted\""},{"level":3,"heading":"Intermediate","content":"ビールを何本飲みましたか？ \"How many beers did you drink?\" 鉛筆を5本買ってきてください \"Please buy 5 pencils\" この映画は今年見た映画の中で1本目です \"This is the first movie I''ve seen this year\""},{"level":3,"heading":"Advanced","content":"庭に桜の木を3本植えました \"I planted 3 cherry trees in the garden\" ワインを2本開けましたが、まだ足りません \"We opened 2 bottles of wine, but it''s still not enough\" 一本ずつ丁寧に説明してください \"Please explain each one carefully\""}]},{"level":2,"heading":"Related Grammar Points","content":"Counter System Basics - How counters work Number System Overview - Japanese numbers Sound changes - Phonetic variations 冊 (satsu) - Books counter 個 (ko) - Small objects 本 as noun - Book (different from counter)","children":[]}]'::jsonb,
  '["Counter System Basics - How counters work","Number System Overview - Japanese numbers","Sound changes - Phonetic variations","冊 (satsu) - Books counter","個 (ko) - Small objects","本 as noun - Book (different from counter)"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'counter-kai-times',
  'Times (回)',
  'https://practice-japanese.com/docs/counter-kai-times/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 回 (かい)?","content":"回 (kai) is the counter for times, occurrences, or frequency of actions. It answers \"how many times?\"\n\n日本に3回行きました \"I went to Japan 3 times\" 1日に何回食べますか？ \"How many times a day do you eat?\""},{"level":2,"heading":"Complete 回 Counter Chart","content":"| Number | Reading | Romaji | | ------ | ----------------- | --------- | | 1 回 | いっかい | ikkai | | 2 回 | にかい | ni-kai | | 3 回 | さんかい | san-kai | | 4 回 | よんかい | yon-kai | | 5 回 | ごかい | go-kai | | 6 回 | ろっかい | rokkai | | 7 回 | ななかい | nana-kai | | 8 回 | はちかい/はっかい | hachi-kai | | 9 回 | きゅうかい | kyuu-kai | | 10 回 | じゅっかい | jukkai |","children":[{"level":3,"heading":"Larger Numbers","content":"11回 = じゅういっかい 20回 = にじゅっかい 50回 = ごじゅっかい 100回 = ひゃっかい 1,000回 = せんかい"}]},{"level":2,"heading":"Sound Changes","content":"Minimal sound changes:\n\n1 回 = いっかい (っ inserted) 6 回 = ろっかい (っ inserted) 8 回 = はちかい or はっかい 10 回 = じゅっかい\n\nMuch simpler than 本, 匹, or 杯!"},{"level":2,"heading":"What to Count with 回","content":"","children":[{"level":3,"heading":"Actions/Activities","content":"行く - going 来る - coming 見る - watching/seeing 食べる - eating 飲む - drinking 会う - meeting 読む - reading 言う - saying\n\n映画を3回見ました \"I watched the movie 3 times\" 日本に5回行きました \"I''ve been to Japan 5 times\" もう一回言ってください \"Please say it one more time\""},{"level":3,"heading":"Events/Occurrences","content":"試験 (しけん) - exams 会議 (かいぎ) - meetings 練習 (れんしゅう) - practice 授業 (じゅぎょう) - classes イベント - events\n\n会議が1日に3回あります \"There are 3 meetings a day\" 試験に2回失敗しました \"I failed the exam 2 times\""},{"level":3,"heading":"Frequency Patterns","content":"1日に〜回 - times per day 1週間に〜回 - times per week 1ヶ月に〜回 - times per month 1年に〜回 - times per year\n\n1日に3回食べます \"I eat 3 times a day\" 週に2回ジムに行きます \"I go to the gym twice a week\" 月に1回映画を見ます \"I watch a movie once a month\" 年に4回旅行します \"I travel 4 times a year\""}]},{"level":2,"heading":"Asking \"How Many Times?\"","content":"","children":[{"level":3,"heading":"何回 (なんかい)","content":"何回行きましたか？ \"How many times did you go?\" 何回見ましたか？ \"How many times did you watch it?\" 1日に何回食べますか？ \"How many times a day do you eat?\" 何回練習しましたか？ \"How many times did you practice?\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Action + 回 + Verb","content":"3回行きました \"I went 3 times\" 5回見ました \"I watched it 5 times\" 2回読みました \"I read it 2 times\""},{"level":3,"heading":"Time Period + に + 回","content":"1日に3回 \"3 times a day\" 週に2回 \"Twice a week\" 月に1回 \"Once a month\" 年に何回も \"Many times a year\""},{"level":3,"heading":"回 + の + Noun","content":"3回の練習 \"3 practice sessions\" 2回の試験 \"2 exams\" 5回の会議 \"5 meetings\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Travel","content":"A: 日本に何回行ったことがありますか？ \"How many times have you been to Japan?\" B: 3回行ったことがあります \"I''ve been 3 times\" A: いいですね！ \"That''s great!\""},{"level":3,"heading":"Example 2: Movies","content":"A: この映画、何回見ましたか？ \"How many times have you watched this movie?\" B: もう5回も見ました \"I''ve already watched it 5 times\" A: そんなに面白いんですか？ \"Is it that interesting?\" B: はい、とても面白いです \"Yes, it''s very interesting\""},{"level":3,"heading":"Example 3: Exercise","content":"A: 週に何回運動しますか？ \"How many times a week do you exercise?\" B: 週に3回ぐらいです \"About 3 times a week\" A: すごいですね \"That''s impressive\""},{"level":3,"heading":"Example 4: Studying","content":"A: この単語、何回練習しましたか？ \"How many times did you practice this word?\" B: 10回練習しました \"I practiced it 10 times\" A: それでも覚えられませんか？ \"Still can''t remember it?\" B: はい、難しいです \"Yes, it''s difficult\""}]},{"level":2,"heading":"Special Expressions","content":"","children":[{"level":3,"heading":"もう一回 (one more time)","content":"Very common expression:\n\nもう一回言ってください \"Please say it one more time\" もう一回見たい \"I want to see it one more time\" もう一回やってみます \"I''ll try one more time\""},{"level":3,"heading":"〜回目 (ordinal)","content":"1回目 (いっかいめ) - the first time 2回目 (にかいめ) - the second time 3回目 (さんかいめ) - the third time これは2回目です \"This is the second time\" 3回目で成功しました \"I succeeded on the third try\""},{"level":3,"heading":"何回も (many times)","content":"何回も言いました \"I said it many times\" 何回も行ったことがあります \"I''ve been many times\" 何回も失敗しました \"I failed many times\""},{"level":3,"heading":"〜回ずつ (each time)","content":"一回ずつやってください \"Please do it one time each\" 10回ずつ練習します \"Practice 10 times each\""},{"level":3,"heading":"〜回しか (only)","content":"1回しか行ったことがない \"I''ve only been once\" 2回しか見ていません \"I''ve only seen it twice\""},{"level":3,"heading":"〜回も (as many as)","content":"10回も見た \"I watched it as many as 10 times\" 5回も失敗した \"I failed as many as 5 times\""}]},{"level":2,"heading":"回 vs 度 (ど)","content":"Both mean \"times\" but have different uses:","children":[{"level":3,"heading":"回 (かい) - Counting occurrences","content":"1回、2回、3回... (いっかい、にかい、さんかい) Used for: counting specific instances 日本に3回行きました \"I went to Japan 3 times\""},{"level":3,"heading":"度 (ど) - Also times, but different nuance","content":"一度 (いちど) - once 二度 (にど) - twice 三度 (さんど) - three times More literary, often in set phrases: 一度も〜ない - never (not even once) 何度も - many times もう一度 - one more time"},{"level":3,"heading":"Usage Comparison","content":"Both OK: もう一回 = もう一度 (one more time) 何回も = 何度も (many times) 回 preferred: 1日に3回 ✓ 1日に3度 ⚠️ (less common) 度 preferred: 一度も行ったことがない ✓ \"I''ve never been\" (not even once)"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Confusing 回 with 階 (floors)","content":"Both read かい but different meanings:\n\n3回 = さんかい (3 times) ✓ 3階 = さんがい (3rd floor) ✓ Context makes it clear!"},{"level":3,"heading":"Mistake 2: Using wrong particle","content":"Wrong: 週で2回 ❌ Right: 週に2回 ✓ \"Twice a week\""},{"level":3,"heading":"Mistake 3: Forgetting the counter","content":"Wrong: 3行きました ❌ Right: 3回行きました ✓ \"I went 3 times\""},{"level":3,"heading":"Mistake 4: Using 回 for duration","content":"Wrong: 2回時間がかかる ❌ Right: 2時間かかる ✓ \"It takes 2 hours\" 回 = times/occurrences 時間 = hours/time duration"}]},{"level":2,"heading":"Formal vs Casual","content":"","children":[{"level":3,"heading":"Casual","content":"何回行った？ \"How many times did you go?\" 3回見た \"I watched it 3 times\" もう一回！ \"One more time!\""},{"level":3,"heading":"Polite","content":"何回行きましたか？ \"How many times did you go?\" 3回見ました \"I watched it 3 times\" もう一回お願いします \"One more time, please\""},{"level":3,"heading":"Formal","content":"何回お越しになりましたか？ \"How many times have you come?\" (honorific) 会議は月に2回ございます \"There are 2 meetings per month\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"映画を2回見ました \"I watched the movie twice\" 日本に3回行きました \"I went to Japan 3 times\" もう一回言ってください \"Please say it one more time\""},{"level":3,"heading":"Intermediate","content":"1日に何回歯を磨きますか？ \"How many times a day do you brush your teeth?\" 週に3回ジムに行きます \"I go to the gym 3 times a week\" この本を5回も読みました \"I''ve read this book as many as 5 times\""},{"level":3,"heading":"Advanced","content":"彼女に何回も電話したが、出なかった \"I called her many times, but she didn''t answer\" 一度も日本に行ったことがありません \"I''ve never been to Japan (not even once)\" 3回目の挑戦でやっと成功しました \"I finally succeeded on my third attempt\""}]},{"level":2,"heading":"Related Grammar Points","content":"Counter System Basics - How counters work Frequency expressions - How often Time expressions - When and how long 度 (do) - Alternative times counter 〜ことがある - Experience expression Time particles - に、で usage","children":[]}]'::jsonb,
  '["1 回 = いっかい (っ inserted)","6 回 = ろっかい (っ inserted)","8 回 = はちかい or はっかい","10 回 = じゅっかい","Counter System Basics - How counters work","Frequency expressions - How often","Time expressions - When and how long","度 (do) - Alternative times counter","〜ことがある - Experience expression","Time particles - に、で usage"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'counter-kai',
  'Floors (階)',
  'https://practice-japanese.com/docs/counter-kai/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 階 (かい)?","content":"階 (kai) is the counter for floors or stories of buildings. It has minimal sound changes compared to other counters.\n\n3階に住んでいます \"I live on the 3rd floor\" エレベーターで5階まで \"To the 5th floor by elevator\""},{"level":2,"heading":"Complete 階 Counter Chart","content":"| Number | Reading | Romaji | Note | | ------ | ------------------- | --------- | ------------- | | 1 階 | いっかい | ikkai | ground floor | | 2 階 | にかい | ni-kai | | | 3 階 | さんがい | san-gai | sound change! | | 4 階 | よんかい | yon-kai | | | 5 階 | ごかい | go-kai | | | 6 階 | ろっかい | rokkai | | | 7 階 | ななかい | nana-kai | | | 8 階 | はちかい/はっかい | hachi-kai | | | 9 階 | きゅうかい | kyuu-kai | | | 10 階 | じゅっかい/じっかい | jukkai | |","children":[{"level":3,"heading":"Larger Numbers","content":"11階 = じゅういっかい 20階 = にじゅっかい 30階 = さんじゅっかい 50階 = ごじゅっかい 100階 = ひゃっかい"}]},{"level":2,"heading":"Sound Change Note","content":"The main sound change is:\n\n3 階 = さんがい (not さんかい)\n\nMost other numbers keep かい, though some have variations:\n\n6 階 = ろっかい (っ inserted) 8 階 = はちかい or はっかい 10 階 = じゅっかい or じっかい"},{"level":2,"heading":"Basement Floors","content":"","children":[{"level":3,"heading":"地下 (ちか) = Underground/Basement","content":"地下1階 (ちかいっかい) - B1 地下2階 (ちかにかい) - B2 地下3階 (ちかさんがい) - B3\n\nShortened forms:\n\nB1 = ビーワン B2 = ビーツー 地下 = ちか (basement in general)\n\n駐車場は地下2階です \"The parking lot is on B2\" 地下に行ってください \"Please go to the basement\""}]},{"level":2,"heading":"Asking \"Which Floor?\"","content":"","children":[{"level":3,"heading":"何階 (なんがい/なんかい)","content":"Both readings are acceptable:\n\n何階ですか？ \"Which floor?\" 何階に住んでいますか？ \"Which floor do you live on?\" オフィスは何階ですか？ \"Which floor is the office on?\" エレベーターは何階に止まりますか？ \"Which floors does the elevator stop at?\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"〜階 + に (location particle)","content":"3階に住んでいます \"I live on the 3rd floor\" 5階にオフィスがあります \"The office is on the 5th floor\" 2階に行ってください \"Please go to the 2nd floor\""},{"level":3,"heading":"〜階 + まで (up to)","content":"エレベーターで10階まで \"To the 10th floor by elevator\" 階段で3階まで歩きます \"I''ll walk up to the 3rd floor by stairs\""},{"level":3,"heading":"〜階 + です (identification)","content":"A: オフィスは何階ですか？ \"Which floor is the office?\" B: 5階です \"It''s on the 5th floor\""}]},{"level":2,"heading":"Building Terms","content":"","children":[{"level":3,"heading":"Floor-Related Vocabulary","content":"1階 = ground floor/first floor 最上階 (さいじょうかい) = top floor 中階 (ちゅうかい) = middle floors 高層階 (こうそうかい) = high floors 低層階 (ていそうかい) = low floors"},{"level":3,"heading":"Building Types","content":"〜階建て (〜かいだて) = story building 5階建て = 5-story building 10階建てのビル = 10-story building 20階建てのマンション = 20-story apartment\n\nこのビルは15階建てです \"This is a 15-story building\" 30階建てのタワーマンション \"A 30-story tower apartment\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Elevator","content":"A: 何階ですか？ \"Which floor?\" B: 7階お願いします \"7th floor, please\" A: はい \"Sure\""},{"level":3,"heading":"Example 2: Apartment Viewing","content":"A: こちらのマンションは何階建てですか？ \"How many stories is this apartment building?\" B: 10階建てです \"It''s 10 stories\" A: 今日見るのは何階ですか？ \"Which floor are we viewing today?\" B: 8階です \"The 8th floor\""},{"level":3,"heading":"Example 3: Office Building","content":"A: 田中さんのオフィスは何階ですか？ \"Which floor is Mr. Tanaka''s office?\" B: 15階です \"15th floor\" A: ありがとうございます \"Thank you\""},{"level":3,"heading":"Example 4: Department Store","content":"A: おもちゃ売り場は何階ですか？ \"Which floor is the toy department?\" B: 5階でございます \"It''s on the 5th floor\" A: エレベーターはどこですか？ \"Where''s the elevator?\" B: あちらです \"Over there\""}]},{"level":2,"heading":"Special Expressions","content":"","children":[{"level":3,"heading":"〜階の (possessive)","content":"3階の部屋 \"A room on the 3rd floor\" 5階のレストラン \"The restaurant on the 5th floor\" 最上階の眺め \"The view from the top floor\""},{"level":3,"heading":"〜階まで (up to)","content":"階段で5階まで \"Up to the 5th floor by stairs\" エレベーターで最上階まで \"To the top floor by elevator\""},{"level":3,"heading":"〜階ずつ (floor by floor)","content":"一階ずつ上がります \"Go up floor by floor\" 2階ずつ飛ばして \"Skip every other floor\""}]},{"level":2,"heading":"Floor Counting: US vs Japanese","content":"Important cultural note:","children":[{"level":3,"heading":"American System","content":"1st floor = ground level 2nd floor = one level up"},{"level":3,"heading":"British/Japanese System","content":"Ground floor (1階) = ground level 1st floor (2階) = one level up\n\nIn modern Japanese buildings, 1 階 typically means ground floor (American 1st floor)."}]},{"level":2,"heading":"Department Store Floors","content":"Common floor designations:\n\n地下1階 = 食品売り場 (food) 1階 = 化粧品 (cosmetics) 2階 = 婦人服 (women''s clothing) 3階 = 紳士服 (men''s clothing) 4階 = 子供服 (children''s clothing) 5階 = おもちゃ (toys) 6階 = レストラン街 (restaurants) 屋上 (おくじょう) = rooftop"},{"level":2,"heading":"Room Numbers","content":"Combining floor and room:\n\n301号室 (さんびゃくいちごうしつ) \"Room 301\" (3rd floor, room 1) 502号室 (ごひゃくにごうしつ) \"Room 502\" (5th floor, room 2) 1205号室 (せんにひゃくごごうしつ) \"Room 1205\" (12th floor, room 5)"},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Wrong reading for 3 階","content":"Wrong: さんかい ❌ Right: さんがい ✓ \"3rd floor\""},{"level":3,"heading":"Mistake 2: Confusing 何階 readings","content":"Both are acceptable:\n\nなんがい ✓ なんかい ✓ \"Which floor?\""},{"level":3,"heading":"Mistake 3: Forgetting 地下 for basement","content":"Wrong: マイナス1階 ❌ Right: 地下1階 ✓ \"B1\""},{"level":3,"heading":"Mistake 4: Wrong 〜階建て usage","content":"Wrong: 10階の建物 ⚠️ (unclear) Right: 10階建ての建物 ✓ \"10-story building\""}]},{"level":2,"heading":"Elevator Phrases","content":"","children":[{"level":3,"heading":"Common Elevator Expressions","content":"何階ですか？ \"Which floor?\" 〜階お願いします \"〜th floor, please\" 開 (あけ) - Open 閉 (しめ) - Close エレベーターで参ります \"I''ll take the elevator\" 階段で行きます \"I''ll take the stairs\""},{"level":3,"heading":"Elevator Announcements","content":"1階です \"First floor\" ドアが閉まります \"The doors are closing\" 上に参ります \"Going up\" 下に参ります \"Going down\""}]},{"level":2,"heading":"Formal vs Casual","content":"","children":[{"level":3,"heading":"Casual","content":"何階？ \"Which floor?\" 3階に住んでる \"I live on the 3rd floor\" 5階まで \"To the 5th floor\""},{"level":3,"heading":"Polite","content":"何階ですか？ \"Which floor?\" 3階に住んでいます \"I live on the 3rd floor\" 5階までお願いします \"To the 5th floor, please\""},{"level":3,"heading":"Formal","content":"何階でございますか？ \"Which floor is it?\" (very polite) 3階でございます \"It''s on the 3rd floor\" エレベーターで5階までご案内いたします \"I''ll guide you to the 5th floor by elevator\""}]},{"level":2,"heading":"Related Expressions","content":"","children":[{"level":3,"heading":"上の階 (うえのかい) - Upper floor","content":"上の階がうるさい \"The floor above is noisy\" 一つ上の階 \"One floor up\""},{"level":3,"heading":"下の階 (したのかい) - Lower floor","content":"下の階に降ります \"I''ll go down to the floor below\" 2つ下の階 \"Two floors down\""},{"level":3,"heading":"同じ階 (おなじかい) - Same floor","content":"同じ階に住んでいます \"We live on the same floor\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"3階に住んでいます \"I live on the 3rd floor\" オフィスは5階です \"The office is on the 5th floor\" 地下1階に駐車場があります \"There''s a parking lot on B1\""},{"level":3,"heading":"Intermediate","content":"このビルは何階建てですか？ \"How many stories is this building?\" エレベーターで10階まで行ってください \"Please take the elevator to the 10th floor\" 最上階からの眺めは素晴らしいです \"The view from the top floor is wonderful\""},{"level":3,"heading":"Advanced","content":"私のオフィスは15階建てのビルの8階にあります \"My office is on the 8th floor of a 15-story building\" 地下2階から屋上まで、全部で22階あります \"From B2 to the rooftop, there are 22 floors in total\" 一階ずつエレベーターが止まるので、時間がかかります \"The elevator stops at every floor, so it takes time\""}]},{"level":2,"heading":"Related Grammar Points","content":"Counter System Basics - How counters work Number System Overview - Japanese numbers Location particles - に、で、まで Building vocabulary - Architecture terms Direction words - 上、下、同じ 〜号室 - Room numbers","children":[]}]'::jsonb,
  '["3 階 = さんがい (not さんかい)","6 階 = ろっかい (っ inserted)","8 階 = はちかい or はっかい","10 階 = じゅっかい or じっかい","Counter System Basics - How counters work","Number System Overview - Japanese numbers","Location particles - に、で、まで","Building vocabulary - Architecture terms","Direction words - 上、下、同じ","〜号室 - Room numbers"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'counter-mai',
  'Flat Objects (枚)',
  'https://practice-japanese.com/docs/counter-mai/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 枚 (まい)?","content":"枚 (mai) is the counter for flat, thin objects. Unlike many counters, it doesn''t undergo sound changes and always stays まい.\n\n紙を3枚ください \"3 sheets of paper, please\" Tシャツを2枚買いました \"I bought 2 T-shirts\""},{"level":2,"heading":"Complete 枚 Counter Chart","content":"| Number | Reading | Romaji | | ------ | ---------- | --------- | | 1 | いちまい | ichi-mai | | 2 | にまい | ni-mai | | 3 | さんまい | san-mai | | 4 | よんまい | yon-mai | | 5 | ごまい | go-mai | | 6 | ろくまい | roku-mai | | 7 | ななまい | nana-mai | | 8 | はちまい | hachi-mai | | 9 | きゅうまい | kyuu-mai | | 10 | じゅうまい | juu-mai |","children":[{"level":3,"heading":"Larger Numbers","content":"11枚 = じゅういちまい 20枚 = にじゅうまい 50枚 = ごじゅうまい 100枚 = ひゃくまい 1,000枚 = せんまい\n\nGood news: No sound changes! Always まい."}]},{"level":2,"heading":"What to Count with 枚","content":"","children":[{"level":3,"heading":"Paper and Documents","content":"紙 (かみ) - paper コピー - copies 写真 (しゃしん) - photographs 手紙 (てがみ) - letters はがき - postcards 切手 (きって) - stamps チケット - tickets 名刺 (めいし) - business cards 領収書 (りょうしゅうしょ) - receipts\n\n紙を10枚コピーしてください \"Please make 10 copies\" 写真を5枚撮りました \"I took 5 photos\" 切手を3枚ください \"3 stamps, please\""},{"level":3,"heading":"Dishes and Plates","content":"皿 (さら) - plates/dishes お皿 - plates (polite)\n\nお皿を5枚洗いました \"I washed 5 plates\" 大きい皿を2枚ください \"2 large plates, please\""},{"level":3,"heading":"Clothing (Flat Items)","content":"Tシャツ - T-shirts シャツ - shirts ズボン - pants/trousers スカート - skirts タオル - towels ハンカチ - handkerchiefs 毛布 (もうふ) - blankets 布団 (ふとん) - futons\n\nTシャツを3枚買いました \"I bought 3 T-shirts\" タオルを2枚使いました \"I used 2 towels\" 毛布を1枚貸してください \"Please lend me 1 blanket\""},{"level":3,"heading":"Boards and Panels","content":"板 (いた) - boards ガラス - glass panels CD - CDs DVD - DVDs カード - cards\n\nCDを5枚持っています \"I have 5 CDs\" ガラスを2枚割りました \"I broke 2 glass panels\""},{"level":3,"heading":"Currency","content":"お札 (おさつ) - bills/banknotes 千円札 - 1,000 yen bills\n\n千円札を5枚両替してください \"Please exchange 5 one-thousand yen bills\""},{"level":3,"heading":"Other Flat Objects","content":"畳 (たたみ) - tatami mats じゅうたん - carpets/rugs ポスター - posters カレンダー - calendars"}]},{"level":2,"heading":"Asking \"How Many?\"","content":"","children":[{"level":3,"heading":"何枚 (なんまい)","content":"何枚ありますか？ \"How many are there?\" 何枚必要ですか？ \"How many do you need?\" 紙を何枚コピーしますか？ \"How many sheets will you copy?\" 写真を何枚撮りましたか？ \"How many photos did you take?\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Object + を + 枚 Counter","content":"紙を3枚使いました \"I used 3 sheets of paper\" 写真を10枚撮りました \"I took 10 photos\" シャツを2枚買いました \"I bought 2 shirts\""},{"level":3,"heading":"Subject + が + 枚 Counter + Verb","content":"皿が5枚あります \"There are 5 plates\" 切手が3枚足りません \"We''re 3 stamps short\""},{"level":3,"heading":"枚 Counter + の + Noun","content":"3枚の紙 \"3 sheets of paper\" 5枚の写真 \"5 photographs\" 2枚のTシャツ \"2 T-shirts\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Office","content":"A: コピーを何枚お願いしますか？ \"How many copies would you like?\" B: 20枚お願いします \"20, please\" A: 白黒ですか、カラーですか？ \"Black and white or color?\" B: 白黒で \"Black and white\""},{"level":3,"heading":"Example 2: Shopping","content":"A: このTシャツ、何枚買いますか？ \"How many of these T-shirts will you buy?\" B: 色違いで3枚ください \"3 in different colors, please\" A: かしこまりました \"Certainly\""},{"level":3,"heading":"Example 3: Post Office","content":"A: 切手を何枚ください？ \"How many stamps?\" B: 84円切手を5枚お願いします \"5 of the 84-yen stamps, please\" A: はい、420円になります \"Yes, that''ll be 420 yen\""},{"level":3,"heading":"Example 4: Photography","content":"A: 今日、写真を何枚撮りましたか？ \"How many photos did you take today?\" B: 50枚ぐらい撮りました \"I took about 50\" A: いい写真はありましたか？ \"Were there any good ones?\" B: 10枚ぐらいいいのがあります \"About 10 good ones\""}]},{"level":2,"heading":"Special Expressions","content":"","children":[{"level":3,"heading":"〜枚ずつ (each/apiece)","content":"一枚ずつ取ってください \"Please take one each\" 3枚ずつ配ります \"I''ll distribute 3 to each person\" 2枚ずつコピーしてください \"Please make 2 copies of each\""},{"level":3,"heading":"〜枚目 (ordinal)","content":"1枚目 (いちまいめ) - the first one 2枚目 (にまいめ) - the second one 3枚目 (さんまいめ) - the third one 2枚目の写真が一番きれいです \"The second photo is the prettiest\""},{"level":3,"heading":"〜枚も (as many as)","content":"50枚も撮った \"I took as many as 50\" 10枚も買った \"I bought as many as 10\""},{"level":3,"heading":"〜枚しか (only)","content":"1枚しかない \"There''s only 1\" 3枚しか残っていません \"Only 3 remain\""}]},{"level":2,"heading":"Clothing Specifics","content":"","children":[{"level":3,"heading":"Tops (Use 枚)","content":"シャツ - shirts Tシャツ - T-shirts セーター - sweaters ジャケット - jackets コート - coats\n\nシャツを5枚持っています \"I have 5 shirts\""},{"level":3,"heading":"Bottoms (Also Use 枚)","content":"ズボン - pants/trousers ジーンズ - jeans スカート - skirts\n\nズボンを3枚買いました \"I bought 3 pairs of pants\""},{"level":3,"heading":"Note on Pairs","content":"Unlike English, Japanese doesn''t use \"pair\" for pants. Just count with 枚:\n\nEnglish: 2 pairs of pants Japanese: ズボン2枚 (literally: 2 sheets of pants)"}]},{"level":2,"heading":"Documents and Papers","content":"","children":[{"level":3,"heading":"Office Documents","content":"契約書 (けいやくしょ) - contracts 報告書 (ほうこくしょ) - reports 資料 (しりょう) - materials/documents 書類 (しょるい) - documents\n\n契約書を3枚サインしてください \"Please sign 3 contracts\" 資料を20枚印刷しました \"I printed 20 copies of the materials\""},{"level":3,"heading":"Personal Documents","content":"履歴書 (りれきしょ) - resume/CV 申込書 (もうしこみしょ) - application form\n\n履歴書を2枚準備しました \"I prepared 2 copies of my resume\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using wrong counter for thick items","content":"Wrong: 本を3枚読みました ❌ Right: 本を3冊読みました ✓ \"I read 3 books\" (Books are not flat/thin enough for 枚)"},{"level":3,"heading":"Mistake 2: Using 枚 for non-flat clothing","content":"Questionable: 靴下を2枚 ⚠️ Better: 靴下を2足 (にそく) ✓ \"2 pairs of socks\" Wrong: セーターを3枚 (actually OK!) ✓ Sweaters are flat when folded, so 枚 works"},{"level":3,"heading":"Mistake 3: Forgetting counter entirely","content":"Wrong: 紙を3ください ❌ Right: 紙を3枚ください ✓ \"3 sheets of paper, please\""},{"level":3,"heading":"Mistake 4: Wrong sound changes","content":"This is actually NOT a mistake with 枚 - it never changes!\n\n1枚 = いちまい ✓ (stays まい) 3枚 = さんまい ✓ (stays まい) 8枚 = はちまい ✓ (stays まい)"}]},{"level":2,"heading":"Size Descriptions with 枚","content":"","children":[{"level":3,"heading":"Large/Small","content":"大きい紙を2枚 \"2 large sheets of paper\" 小さいお皿を5枚 \"5 small plates\""},{"level":3,"heading":"A4, B5, etc.","content":"A4の紙を10枚 \"10 sheets of A4 paper\" B5のコピーを20枚 \"20 B5 copies\""}]},{"level":2,"heading":"Currency Specific Usage","content":"","children":[{"level":3,"heading":"Bills/Banknotes","content":"千円札を5枚 \"5 one-thousand yen bills\" 一万円札を3枚 \"3 ten-thousand yen bills\""},{"level":3,"heading":"Not for Coins","content":"Wrong: 100円を3枚 ❌ Right: 100円玉を3個 ✓ \"3 one-hundred yen coins\" Coins use 個, not 枚"}]},{"level":2,"heading":"Practical Situations","content":"","children":[{"level":3,"heading":"At Copy Shop","content":"A: コピーは何枚ですか？ \"How many copies?\" B: 白黒30枚とカラー10枚お願いします \"30 black and white and 10 color, please\""},{"level":3,"heading":"Buying Clothes","content":"A: この柄で何枚お買い上げですか？ \"How many of this pattern will you buy?\" B: 色違いで2枚ください \"2 in different colors, please\""},{"level":3,"heading":"At Restaurant (Plates)","content":"A: お皿は何枚必要ですか？ \"How many plates do you need?\" B: 4枚お願いします \"4, please\""},{"level":3,"heading":"Photography","content":"A: 今日は何枚撮りましたか？ \"How many did you take today?\" B: 100枚以上撮りました \"I took over 100\""}]},{"level":2,"heading":"Formal vs Casual","content":"","children":[{"level":3,"heading":"Casual","content":"紙3枚ちょうだい \"Give me 3 sheets of paper\" 写真何枚撮った？ \"How many photos did you take?\""},{"level":3,"heading":"Polite","content":"紙を3枚ください \"3 sheets of paper, please\" 写真を何枚撮りましたか？ \"How many photos did you take?\""},{"level":3,"heading":"Formal/Business","content":"コピーを50枚お願いいたします \"I would like 50 copies, please\" 書類を3枚提出いたします \"I will submit 3 documents\""}]},{"level":2,"heading":"Combining with Other Counters","content":"","children":[{"level":3,"heading":"枚 + 人","content":"一人3枚ずつ \"3 sheets per person\" 5人で20枚使いました \"5 people used 20 sheets\""},{"level":3,"heading":"枚 + 回","content":"1回に10枚コピーできます \"You can copy 10 sheets at a time\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"紙が10枚あります \"There are 10 sheets of paper\" お皿を5枚洗いました \"I washed 5 plates\" Tシャツを2枚買いました \"I bought 2 T-shirts\""},{"level":3,"heading":"Intermediate","content":"写真を何枚撮りましたか？ \"How many photos did you take?\" コピーを20枚してください \"Please make 20 copies\" 切手を5枚買ってきてください \"Please go buy 5 stamps\""},{"level":3,"heading":"Advanced","content":"一人一枚ずつ資料を配ってください \"Please distribute one copy of the materials to each person\" この書類を3枚ずつホチキスで留めてください \"Please staple these documents in sets of 3\" 写真を100枚以上撮りましたが、良いのは10枚ぐらいでした \"I took over 100 photos, but only about 10 were good\""}]},{"level":2,"heading":"Related Grammar Points","content":"Counter System Basics - How counters work Number System Overview - Japanese numbers 個 (ko) - Small objects counter 本 (hon) - Long objects counter 冊 (satsu) - Books counter Quantity expressions - Expressing amounts","children":[]}]'::jsonb,
  '["Counter System Basics - How counters work","Number System Overview - Japanese numbers","個 (ko) - Small objects counter","本 (hon) - Long objects counter","冊 (satsu) - Books counter","Quantity expressions - Expressing amounts"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'counter-nin',
  'People (人)',
  'https://practice-japanese.com/docs/counter-nin/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 人 (にん/り)?","content":"人 (にん/り) is the counter for people. It''s one of the most essential counters in Japanese with special irregular readings for 1 and 2.\n\n1人 = ひとり (not いちにん!) 2人 = ふたり (not ににん!) 3人 = さんにん 4人 = よにん"},{"level":2,"heading":"Complete People Counter Chart","content":"| Number | Reading | Romaji | Kanji | | ------ | ----------------- | ------------------- | ----- | | 1 | ひとり | hitori | 一人 | | 2 | ふたり | futari | 二人 | | 3 | さんにん | san-nin | 三人 | | 4 | よにん | yo-nin | 四人 | | 5 | ごにん | go-nin | 五人 | | 6 | ろくにん | roku-nin | 六人 | | 7 | しちにん/ななにん | shichi-nin/nana-nin | 七人 | | 8 | はちにん | hachi-nin | 八人 | | 9 | きゅうにん | kyuu-nin | 九人 | | 10 | じゅうにん | juu-nin | 十人 |","children":[{"level":3,"heading":"Larger Numbers","content":"11人 = じゅういちにん 20人 = にじゅうにん 100人 = ひゃくにん 1,000人 = せんにん 10,000人 = いちまんにん"}]},{"level":2,"heading":"Special Readings","content":"","children":[{"level":3,"heading":"ひとり (1 person)","content":"The only reading for one person:\n\n一人で行きます \"I''ll go alone\" 一人暮らし \"Living alone\" 一人っ子 \"Only child\""},{"level":3,"heading":"ふたり (2 people)","content":"The only reading for two people:\n\n二人で食べました \"We ate together (two people)\" 二人とも \"Both people\" 二人きり \"Just the two of us\""},{"level":3,"heading":"Why the Irregularity?","content":"ひとり and ふたり come from native Japanese, while 3+ use Sino-Japanese numbers. This is a historical remnant."}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"〜人 + で (together/with)","content":"一人で \"Alone/by oneself\" 二人で \"Together (two people)\" 三人で行きました \"We went (three people together)\" 家族5人で住んでいます \"We live as a family of 5\""},{"level":3,"heading":"〜人 + が/は (subject marker)","content":"3人が来ました \"3 people came\" 学生が10人います \"There are 10 students\" 友達が2人います \"I have 2 friends\""},{"level":3,"heading":"〜人 + を (object marker)","content":"友達を5人招待しました \"I invited 5 friends\" 3人を選びました \"I selected 3 people\""},{"level":3,"heading":"〜人 + の (possessive)","content":"3人の学生 \"3 students\" 二人の子供 \"2 children\" 5人の友達 \"5 friends\""}]},{"level":2,"heading":"Asking \"How Many People?\"","content":"","children":[{"level":3,"heading":"何人 (なんにん)","content":"何人いますか？ \"How many people are there?\" 何人来ますか？ \"How many people are coming?\" 何人必要ですか？ \"How many people are needed?\" 何人参加しますか？ \"How many people will participate?\""},{"level":3,"heading":"何名 (なんめい) - Polite","content":"In formal situations:\n\n何名様ですか？ \"How many people?\" (polite, at restaurants) ご予約は何名様ですか？ \"How many people for the reservation?\""}]},{"level":2,"heading":"Common Situations","content":"","children":[{"level":3,"heading":"Restaurants","content":"A: 何名様ですか？ \"How many people?\" B: 4人です \"4 people\" A: こちらへどうぞ \"This way, please\""},{"level":3,"heading":"Making Plans","content":"A: パーティーに何人来ますか？ \"How many people are coming to the party?\" B: 10人ぐらい来ます \"About 10 people are coming\" A: じゃあ、食べ物をたくさん用意しないと \"Then we need to prepare a lot of food\""},{"level":3,"heading":"Family","content":"A: ご家族は何人ですか？ \"How many people are in your family?\" B: 4人家族です \"We''re a family of 4\" A: お子さんは？ \"Children?\" B: 二人います \"I have 2\""},{"level":3,"heading":"Work/School","content":"A: チームは何人ですか？ \"How many people are on the team?\" B: 6人です \"6 people\" A: それじゃ、ちょうどいいですね \"That''s just right then\""}]},{"level":2,"heading":"Special Expressions","content":"","children":[{"level":3,"heading":"〜人目 (ordinal)","content":"\"The Nth person\":\n\n一人目 (ひとりめ) - the first person 二人目 (ふたりめ) - the second person 三人目 (さんにんめ) - the third person 彼は3人目の子供です \"He''s the third child\""},{"level":3,"heading":"〜人ずつ (each/apiece)","content":"一人ずつ入ってください \"Please enter one person at a time\" 二人ずつグループを作ってください \"Please make groups of 2\" 3人ずつ分かれましょう \"Let''s divide into groups of 3\""},{"level":3,"heading":"〜人とも (all/both)","content":"二人とも来ました \"Both people came\" 3人とも学生です \"All 3 are students\" 5人とも合格しました \"All 5 passed\""},{"level":3,"heading":"〜人きり (just/only)","content":"二人きりで話したい \"I want to talk with just the two of us\" 一人きりにしないで \"Don''t leave me alone\""},{"level":3,"heading":"〜人前 (serving/portion)","content":"For food servings:\n\n3人前ください \"3 servings, please\" 一人前の料理 \"One serving of food\""}]},{"level":2,"heading":"Formal Alternatives","content":"","children":[{"level":3,"heading":"名 (めい) - Polite Counter","content":"Used in business/formal settings:\n\n参加者は50名です \"There are 50 participants\" 出席者は何名ですか？ \"How many attendees are there?\" 5名様でご予約いただいております \"We have a reservation for 5 people\""},{"level":3,"heading":"方 (かた) - Honorific","content":"Very polite, for guests/customers:\n\nお二方 \"Two people\" (honorific) 何方様ですか？ \"How many people?\" (very polite)"}]},{"level":2,"heading":"Common Phrases","content":"","children":[{"level":3,"heading":"Living Situations","content":"一人暮らし \"Living alone\" 二人暮らし \"Living together (as a couple/two people)\" 家族4人で暮らしています \"We live as a family of 4\""},{"level":3,"heading":"Being Alone","content":"一人で大丈夫です \"I''m fine alone\" 一人でできます \"I can do it by myself\" 一人じゃない \"You''re not alone\""},{"level":3,"heading":"Group Activities","content":"二人で映画を見た \"We watched a movie (two of us)\" 友達3人で遊びに行った \"I went out with 3 friends\" クラス全員30人で写真を撮った \"All 30 classmates took a photo\""}]},{"level":2,"heading":"Number + 人 + Position in Sentence","content":"","children":[{"level":3,"heading":"Beginning (Emphasis)","content":"5人が参加しました \"5 people participated\" 3人が合格した \"3 people passed\""},{"level":3,"heading":"Middle (Natural)","content":"今日は友達が2人来ます \"2 friends are coming today\" パーティーに10人招待した \"I invited 10 people to the party\""},{"level":3,"heading":"End (Count/Total)","content":"A: 何人いますか？ \"How many people are there?\" B: 全部で15人です \"15 people in total\""}]},{"level":2,"heading":"Combining with Other Counters","content":"","children":[{"level":3,"heading":"Age + People","content":"20歳の学生が3人います \"There are 3 twenty-year-old students\" 子供が二人いて、5歳と3歳です \"I have 2 children, 5 and 3 years old\""},{"level":3,"heading":"Time + People","content":"3時間、5人で働きました \"5 people worked for 3 hours\" 一人30分ずつ話しました \"Each person spoke for 30 minutes\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using いちにん for 1 person","content":"Wrong: いちにん ❌ Right: ひとり ✓ \"1 person\""},{"level":3,"heading":"Mistake 2: Using ににん for 2 people","content":"Wrong: ににん ❌ Right: ふたり ✓ \"2 people\""},{"level":3,"heading":"Mistake 3: Wrong reading for 何人","content":"Wrong: なにじん (this means \"what nationality\") ❌ Right: なんにん (how many people) ✓ \"How many people?\""},{"level":3,"heading":"Mistake 4: Forgetting the counter","content":"Wrong: 友達が3来ました ❌ Right: 友達が3人来ました ✓ \"3 friends came\""},{"level":3,"heading":"Mistake 5: Using 人 for animals","content":"Wrong: 犬が2人います ❌ Right: 犬が2匹います ✓ \"There are 2 dogs\""}]},{"level":2,"heading":"Nationality vs Number of People","content":"","children":[{"level":3,"heading":"何人 (なにじん) - \"What nationality?\"","content":"あなたは何人ですか？ (なにじん) \"What nationality are you?\" 私はアメリカ人です \"I''m American\""},{"level":3,"heading":"何人 (なんにん) - \"How many people?\"","content":"何人いますか？ (なんにん) \"How many people are there?\" 5人います \"There are 5 people\"\n\nContext usually makes it clear which is meant!"}]},{"level":2,"heading":"Casual vs Formal","content":"","children":[{"level":3,"heading":"Casual","content":"何人？ \"How many people?\" 二人で行く \"Going with 2 people\" 友達3人 \"3 friends\""},{"level":3,"heading":"Polite","content":"何人いらっしゃいますか？ \"How many people are there?\" (honorific) お二人でいらっしゃいますか？ \"Are you two coming?\""},{"level":3,"heading":"Formal/Business","content":"何名様でしょうか？ \"How many people?\" (very polite) 参加者は50名でございます \"There are 50 participants\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Restaurant Reservation","content":"A: ご予約のお電話ありがとうございます \"Thank you for calling for a reservation\" B: 明日の7時、4人で予約したいです \"I''d like to make a reservation for 4 people at 7pm tomorrow\" A: 4名様ですね。お名前をお願いします \"4 people. Your name, please?\""},{"level":3,"heading":"Example 2: Group Project","content":"A: このプロジェクトは何人でやりますか？ \"How many people will work on this project?\" B: 5人のチームです \"It''s a team of 5\" A: じゃあ、一人ずつ役割を決めましょう \"Then let''s decide roles one person at a time\""},{"level":3,"heading":"Example 3: Party Planning","content":"A: 誕生日パーティーに何人呼ぶ？ \"How many people will you invite to the birthday party?\" B: 友達を10人ぐらい \"About 10 friends\" A: 家族も入れたら全部で何人？ \"Including family, how many in total?\" B: 全部で15人ぐらいかな \"About 15 in total\""},{"level":3,"heading":"Example 4: Travel","content":"A: 旅行は何人で行きますか？ \"How many people are going on the trip?\" B: 友達と二人で行きます \"I''m going with a friend (2 people)\" A: いいですね！楽しんでください \"That''s nice! Have fun!\""}]},{"level":2,"heading":"Special Vocabulary","content":"","children":[{"level":3,"heading":"Family Members","content":"一人っ子 (ひとりっこ) \"Only child\" 二人兄弟 (ふたりきょうだい) \"Two siblings\" 三人家族 (さんにんかぞく) \"Family of 3\" 4人兄弟 (よにんきょうだい) \"Four siblings\""},{"level":3,"heading":"Group Terms","content":"一人一人 (ひとりひとり) \"Each and every person\" 二人三脚 (ににんさんきゃく) \"Three-legged race\" (idiom: working together) 十人十色 (じゅうにんといろ) \"To each their own\" (lit: 10 people, 10 colors)"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"友達が3人来ました \"3 friends came\" クラスに学生が20人います \"There are 20 students in the class\" 一人で住んでいます \"I live alone\""},{"level":3,"heading":"Intermediate","content":"パーティーに何人招待しましたか？ \"How many people did you invite to the party?\" 二人で映画を見に行きませんか？ \"Shall we go see a movie together?\" チームは5人です \"The team has 5 people\""},{"level":3,"heading":"Advanced","content":"この会議には何名出席されますか？ \"How many people will attend this meeting?\" 参加者が100人を超えました \"The number of participants exceeded 100\" 一人一人の意見を聞きたいと思います \"I''d like to hear each person''s opinion\""}]},{"level":2,"heading":"Related Grammar Points","content":"Counter System Basics - How counters work Number System Overview - Japanese numbers 何 (nani/nan) - What/how many 家族 (kazoku) - Family terms で particle - Together/by means of Quantity expressions - Expressing amounts Polite expressions - Formal language","children":[]}]'::jsonb,
  '["Counter System Basics - How counters work","Number System Overview - Japanese numbers","何 (nani/nan) - What/how many","家族 (kazoku) - Family terms","で particle - Together/by means of","Quantity expressions - Expressing amounts","Polite expressions - Formal language"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'counter-tou',
  'Small Animals (匹)',
  'https://practice-japanese.com/docs/counter-tou/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 匹 (ひき/びき/ぴき)?","content":"匹 is the counter for small to medium-sized animals. It has three pronunciations (ひき、びき、ぴき) that change depending on the number.\n\n犬を2匹飼っています \"I have 2 dogs\" 金魚が5匹います \"There are 5 goldfish\""},{"level":2,"heading":"Complete 匹 Counter Chart","content":"| Number | Reading | Romaji | Sound | | ------ | ---------- | --------- | ----- | | 1 | いっぴき | ippiki | ぴき | | 2 | にひき | ni-hiki | ひき | | 3 | さんびき | san-biki | びき | | 4 | よんひき | yon-hiki | ひき | | 5 | ごひき | go-hiki | ひき | | 6 | ろっぴき | roppiki | ぴき | | 7 | ななひき | nana-hiki | ひき | | 8 | はっぴき | happiki | ぴき | | 9 | きゅうひき | kyuu-hiki | ひき | | 10 | じゅっぴき | juppiki | ぴき |","children":[{"level":3,"heading":"Larger Numbers","content":"11匹 = じゅういっぴき 20匹 = にじゅっぴき 100匹 = ひゃっぴき"}]},{"level":2,"heading":"Sound Change Pattern","content":"","children":[{"level":3,"heading":"ひき (hiki) - Default","content":"2匹 (にひき) 4匹 (よんひき) 5匹 (ごひき) 7匹 (ななひき) 9匹 (きゅうひき)"},{"level":3,"heading":"びき (biki) - After ん sound","content":"3匹 (さんびき) - after さん"},{"level":3,"heading":"ぴき (piki) - After っ sound","content":"1匹 (いっぴき) - いち becomes いっ 6匹 (ろっぴき) - ろく becomes ろっ 8匹 (はっぴき) - はち becomes はっ 10匹 (じゅっぴき) - じゅう becomes じゅっ\n\nSame pattern as 本 (hon/bon/pon)!"}]},{"level":2,"heading":"What Animals to Count with 匹","content":"","children":[{"level":3,"heading":"Pets","content":"犬 (いぬ) - dogs 猫 (ねこ) - cats ハムスター - hamsters うさぎ - rabbits インコ - parakeets\n\n犬を1匹飼っています \"I have 1 dog\" 猫が2匹います \"There are 2 cats\""},{"level":3,"heading":"Fish and Aquatic Animals","content":"金魚 (きんぎょ) - goldfish 魚 (さかな) - fish かえる - frogs かめ - turtles\n\n金魚を5匹買いました \"I bought 5 goldfish\""},{"level":3,"heading":"Insects","content":"蝶 (ちょう) - butterflies 蜂 (はち) - bees 蚊 (か) - mosquitoes 虫 (むし) - insects/bugs\n\n蝶が3匹飛んでいます \"3 butterflies are flying\""},{"level":3,"heading":"Small to Medium Wild Animals","content":"リス - squirrels きつね - foxes たぬき - raccoon dogs ねずみ - mice/rats へび - snakes"},{"level":3,"heading":"General Rule","content":"Use 匹 for animals smaller than a human or about human-sized."}]},{"level":2,"heading":"Asking \"How Many?\"","content":"","children":[{"level":3,"heading":"何匹 (なんびき)","content":"何匹いますか？ \"How many are there?\" ペットを何匹飼っていますか？ \"How many pets do you have?\" 犬が何匹いますか？ \"How many dogs are there?\" 金魚を何匹買いますか？ \"How many goldfish will you buy?\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Animal + を + 匹 Counter","content":"犬を2匹飼っています \"I have 2 dogs\" 金魚を5匹買いました \"I bought 5 goldfish\" 猫を1匹見ました \"I saw 1 cat\""},{"level":3,"heading":"Animal + が + 匹 Counter + Verb","content":"猫が3匹います \"There are 3 cats\" 犬が2匹走っています \"2 dogs are running\" 魚が10匹泳いでいます \"10 fish are swimming\""},{"level":3,"heading":"匹 Counter + の + Animal","content":"2匹の犬 \"2 dogs\" 5匹の金魚 \"5 goldfish\" 3匹の猫 \"3 cats\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Pet Shop","content":"A: ペットを何匹飼っていますか？ \"How many pets do you have?\" B: 犬を2匹と猫を1匹飼っています \"I have 2 dogs and 1 cat\" A: 大変ですね \"That must be a lot of work\" B: でも、とても可愛いです \"But they''re very cute\""},{"level":3,"heading":"Example 2: Aquarium","content":"A: 金魚を何匹買いますか？ \"How many goldfish will you buy?\" B: 5匹ください \"5, please\" A: 水槽は大きいですか？ \"Is your tank big?\" B: はい、10匹ぐらい入ります \"Yes, about 10 can fit\""},{"level":3,"heading":"Example 3: Park","content":"A: 公園に犬が何匹いますか？ \"How many dogs are in the park?\" B: 3匹います \"There are 3\" A: 大きい犬ですか？ \"Are they big dogs?\" B: いいえ、小さい犬です \"No, they''re small dogs\""}]},{"level":2,"heading":"When NOT to Use 匹","content":"","children":[{"level":3,"heading":"Large Animals - Use 頭 (とう)","content":"Wrong: 象を2匹 ❌ Right: 象を2頭 ✓ \"2 elephants\" Wrong: 馬を3匹 ❌ Right: 馬を3頭 ✓ \"3 horses\"\n\nLarge animals (cows, horses, elephants, etc.) use 頭."},{"level":3,"heading":"Birds (Sometimes) - Use 羽 (わ)","content":"Can use: 鳥を3匹 (acceptable) Better: 鳥を3羽 ✓ \"3 birds\"\n\nFor birds, 羽 is more specific, but 匹 is also used."},{"level":3,"heading":"Rabbits - Use 羽 (わ)","content":"Wrong: うさぎを2匹 ⚠️ Traditional: うさぎを2羽 ✓ \"2 rabbits\"\n\nHistorically, rabbits were counted with 羽 (like birds), but 匹 is now common in casual speech."}]},{"level":2,"heading":"Special Expressions","content":"","children":[{"level":3,"heading":"〜匹ずつ","content":"一匹ずつ見てください \"Please look at each one\" 2匹ずつケージに入れます \"Put 2 in each cage\""},{"level":3,"heading":"〜匹目","content":"1匹目 (いっぴきめ) - the first one 2匹目 (にひきめ) - the second one 3匹目 (さんびきめ) - the third one 3匹目の猫が一番可愛い \"The third cat is the cutest\""},{"level":3,"heading":"〜匹も","content":"10匹も飼っている \"I have as many as 10\" 5匹も捕まえた \"I caught as many as 5\""},{"level":3,"heading":"〜匹しか","content":"1匹しかいない \"There''s only 1\" 2匹しか残っていない \"Only 2 remain\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Wrong sound changes","content":"Wrong: いちひき (1) ❌ Right: いっぴき ✓ Wrong: さんひき (3) ❌ Right: さんびき ✓ Wrong: ろくひき (6) ❌ Right: ろっぴき ✓"},{"level":3,"heading":"Mistake 2: Using 匹 for large animals","content":"Wrong: 象が2匹 ❌ Right: 象が2頭 ✓ \"2 elephants\""},{"level":3,"heading":"Mistake 3: Using 匹 for people","content":"Wrong: 子供が3匹 ❌ Right: 子供が3人 ✓ \"3 children\""},{"level":3,"heading":"Mistake 4: Confusing 何匹 reading","content":"Wrong: なにひき ❌ Right: なんびき ✓ \"How many (animals)?\""}]},{"level":2,"heading":"Pronunciation Practice","content":"","children":[{"level":3,"heading":"Tricky Ones","content":"1匹 = いっぴき (not いちひき) 3匹 = さんびき (not さんひき) 6匹 = ろっぴき (not ろくひき) 8匹 = はっぴき (not はちひき) 10匹 = じゅっぴき (not じゅうひき)"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"猫が3匹います \"There are 3 cats\" 犬を2匹飼っています \"I have 2 dogs\" 金魚を5匹買いました \"I bought 5 goldfish\""},{"level":3,"heading":"Intermediate","content":"ペットを何匹飼っていますか？ \"How many pets do you have?\" 公園に犬が何匹いますか？ \"How many dogs are in the park?\" この水槽には魚が10匹入ります \"10 fish can fit in this tank\""},{"level":3,"heading":"Advanced","content":"うちには猫が3匹、犬が2匹、金魚が10匹います \"We have 3 cats, 2 dogs, and 10 goldfish\" 一匹ずつ丁寧に世話をしています \"I take care of each one carefully\" 子供の頃、ハムスターを5匹飼っていました \"When I was a child, I had 5 hamsters\""}]},{"level":2,"heading":"Related Grammar Points","content":"Counter System Basics - How counters work 頭 (tou) - Large animals counter 羽 (wa) - Birds counter 飼う (kau) - To keep/raise pets いる vs ある - Existence verbs Sound changes - Phonetic variations","children":[]}]'::jsonb,
  '["Counter System Basics - How counters work","頭 (tou) - Large animals counter","羽 (wa) - Birds counter","飼う (kau) - To keep/raise pets","いる vs ある - Existence verbs","Sound changes - Phonetic variations"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'counter-tsu',
  'General Counter (つ)',
  'https://practice-japanese.com/docs/counter-tsu/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is つ (tsu)?","content":"つ is the general counter in Japanese, used for counting most everyday objects that don''t require a specific counter. It uses native Japanese numbers and is one of the most versatile and commonly used counters.\n\nりんごを3つ買いました \"I bought 3 apples\" 質問が2つあります \"I have 2 questions\""},{"level":2,"heading":"Complete つ Counter List (1-10)","content":"| Number | Hiragana | Romaji | Kanji | | ------ | -------- | --------- | ----- | | 1 | ひとつ | hitotsu | 一つ | | 2 | ふたつ | futatsu | 二つ | | 3 | みっつ | mittsu | 三つ | | 4 | よっつ | yottsu | 四つ | | 5 | いつつ | itsutsu | 五つ | | 6 | むっつ | muttsu | 六つ | | 7 | ななつ | nanatsu | 七つ | | 8 | やっつ | yattsu | 八つ | | 9 | ここのつ | kokonotsu | 九つ | | 10 | とお | too | 十 |\n\nImportant: Native counters are rarely used above 10!"},{"level":2,"heading":"When to Use つ","content":"","children":[{"level":3,"heading":"1. General Objects Without Specific Counter","content":"When you don''t know or need a specific counter:\n\n問題が5つあります \"There are 5 problems\" 理由が3つあります \"There are 3 reasons\" アイデアが2つあります \"I have 2 ideas\""},{"level":3,"heading":"2. Foods and Grocery Items","content":"りんごを4つ買いました \"I bought 4 apples\" 卵を10個ください (can also say: 卵を10買いました) \"10 eggs, please\" パンを2つください \"2 breads, please\""},{"level":3,"heading":"3. Abstract Concepts","content":"質問が3つあります \"I have 3 questions\" 選択肢が4つあります \"There are 4 choices\" 意見が2つあります \"There are 2 opinions\""},{"level":3,"heading":"4. Small Items or Things","content":"お土産を3つもらいました \"I received 3 souvenirs\" プレゼントを2つ買いました \"I bought 2 presents\""},{"level":3,"heading":"5. When Counter is Unclear","content":"If you''re unsure which specific counter to use, つ is always safe:\n\nこれを2つください \"2 of these, please\" あれを5つ持ってきて \"Bring 5 of those\""}]},{"level":2,"heading":"つ vs Specific Counters","content":"","children":[{"level":3,"heading":"Both Are Acceptable","content":"For many items, you can use either つ or a specific counter:\n\nりんごを3つ ✓ りんごを3個 ✓ \"3 apples\" ペンを2つ ✓ ペンを2本 ✓ \"2 pens\""},{"level":3,"heading":"Specific Counter Sounds More Natural","content":"While つ works, specific counters sound more precise:\n\nMore casual: 紙を3つ ⚠️ More natural: 紙を3枚 ✓ \"3 sheets of paper\" More casual: 犬を2つ ❌ (sounds odd) Correct: 犬を2匹 ✓ \"2 dogs\""},{"level":3,"heading":"When つ is Preferred","content":"Casual conversation Abstract concepts When specific counter is unknown Mixed or unclear items"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Object + を + つ Counter","content":"みかんを5つ買った \"I bought 5 oranges\" クッキーを3つ食べた \"I ate 3 cookies\" お土産を2つもらった \"I received 2 souvenirs\""},{"level":3,"heading":"つ Counter + の + Noun","content":"3つのりんご \"3 apples\" 2つの理由 \"2 reasons\" 5つの質問 \"5 questions\""},{"level":3,"heading":"Subject + が + つ Counter + Verb","content":"問題が4つあります \"There are 4 problems\" 選択肢が3つあります \"There are 3 choices\""}]},{"level":2,"heading":"Asking \"How Many?\" with つ","content":"","children":[{"level":3,"heading":"いくつ - \"How many?\"","content":"いくつありますか？ \"How many are there?\" いくつ買いますか？ \"How many will you buy?\" いくつ欲しいですか？ \"How many do you want?\" いくつ必要ですか？ \"How many do you need?\""},{"level":3,"heading":"Answering","content":"Q: いくつありますか？ \"How many are there?\" A: 3つあります \"There are 3\" A: 5つです \"5\""}]},{"level":2,"heading":"Above 10: Switch to Sino Numbers","content":"After 10, switch to Sino-Japanese numbers with 個 (こ):\n\n1-10: つ counter ひとつ、ふたつ、みっつ... とお 11+: 個 counter 11個 (じゅういっこ) 15個 (じゅうごこ) 20個 (にじゅっこ) 100個 (ひゃっこ)\n\nException: You might hear とお for 10, but above that, always use 個 or another specific counter."},{"level":2,"heading":"Pronunciation Tips","content":"","children":[{"level":3,"heading":"Small つ (っ) Sound","content":"Notice the small っ in several numbers:\n\nみっつ (3) よっつ (4) むっつ (6) やっつ (8)\n\nThe small っ creates a brief pause/stop:\n\nみっつ = mi-t-tsu (not mi-tsu) やっつ = ya-t-tsu (not ya-tsu)"},{"level":3,"heading":"Long Vowels","content":"ここのつ = ko-ko-no-tsu とお = to-o (long \"o\" sound)"}]},{"level":2,"heading":"Numbers Above 10 (with 個)","content":"When counting more than 10 items:\n\n11 = じゅういっこ 12 = じゅうにこ 13 = じゅうさんこ 20 = にじゅっこ 30 = さんじゅっこ 50 = ごじゅっこ 100 = ひゃっこ"},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Shopping","content":"A: みかんをいくつ買いますか？ \"How many oranges will you buy?\" B: 5つください \"5, please\" A: はい、かしこまりました \"Yes, certainly\""},{"level":3,"heading":"Example 2: At Home","content":"A: りんごがいくつありますか？ \"How many apples are there?\" B: 3つあります \"There are 3\" A: じゃあ、2つ食べてもいい？ \"Then, can I eat 2?\" B: いいよ \"Sure\""},{"level":3,"heading":"Example 3: Classroom","content":"A: 質問がいくつありますか？ \"How many questions do you have?\" B: 2つあります \"I have 2\" A: どうぞ \"Go ahead\""},{"level":3,"heading":"Example 4: Planning","content":"A: 選択肢がいくつありますか？ \"How many options are there?\" B: 4つあります \"There are 4\" A: 一つずつ説明してください \"Please explain them one by one\""}]},{"level":2,"heading":"Special Expressions with つ","content":"","children":[{"level":3,"heading":"一つ (ひとつ)","content":"Can mean \"one\" or \"a certain\":\n\n一つお願いがあります \"I have one favor to ask\" 一つの方法 \"One method\" 一つ言わせてください \"Let me say one thing\""},{"level":3,"heading":"二つ (ふたつ)","content":"二つの選択肢 \"Two choices\" 二つ返事 \"Immediate reply\" (idiom)"},{"level":3,"heading":"いくつか (ikutsuka)","content":"\"Some/several\":\n\nいくつか質問があります \"I have some questions\" いくつか方法があります \"There are several methods\""},{"level":3,"heading":"いくつも (ikutsumo)","content":"\"Many\":\n\nいくつも問題がある \"There are many problems\" いくつも買いました \"I bought many\""}]},{"level":2,"heading":"Combinations with Other Words","content":"","children":[{"level":3,"heading":"〜ずつ (each/apiece)","content":"2つずつください \"2 each, please\" 一つずつ取ってください \"Please take one at a time\" 3つずつ分けましょう \"Let''s divide into groups of 3\""},{"level":3,"heading":"〜くらい/ぐらい (about)","content":"3つくらい \"About 3\" 5つぐらい欲しい \"I want about 5\""},{"level":3,"heading":"〜しか (only)","content":"2つしかない \"There are only 2\" 一つしか食べなかった \"I only ate one\""}]},{"level":2,"heading":"Age Usage (Informal)","content":"つ can informally indicate age for young children:\n\nA: いくつ？ \"How old?\" B: 5つ \"5 (years old)\"\n\nMore formal: 5 歳 (ごさい)"},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using つ above 10","content":"Wrong: じゅうつ ❌ Right: 10個 (じゅっこ) ✓ \"10 (items)\" Wrong: じゅういちつ ❌ Right: 11個 (じゅういっこ) ✓ \"11 (items)\""},{"level":3,"heading":"Mistake 2: Wrong pronunciation","content":"Wrong: みつつ (3) ❌ Right: みっつ ✓ Wrong: むつつ (6) ❌ Right: むっつ ✓"},{"level":3,"heading":"Mistake 3: Using with living things inappropriately","content":"Sounds odd: 人を3つ ❌ Correct: 人を3人 (さんにん) ✓ \"3 people\" Sounds odd: 犬を2つ ❌ Correct: 犬を2匹 (にひき) ✓ \"2 dogs\""},{"level":3,"heading":"Mistake 4: Forgetting to use a counter","content":"Wrong: りんごを3買いました ❌ Right: りんごを3つ買いました ✓ \"I bought 3 apples\""}]},{"level":2,"heading":"Formal vs Casual","content":"","children":[{"level":3,"heading":"Casual Speech","content":"Often drop particles:\n\nVery casual: これ3つ More complete: これを3つ \"3 of these\" Casual: りんご2つちょうだい Complete: りんごを2つください \"2 apples, please\""},{"level":3,"heading":"Written/Formal","content":"選択肢が4つございます \"There are 4 options\" (formal) 理由を3つ挙げます \"I will raise 3 reasons\""}]},{"level":2,"heading":"Comparison with 個 (こ)","content":"","children":[{"level":3,"heading":"つ - Native, casual, 1-10","content":"3つ (みっつ) \"3 (things)\" Used for: general items, 1-10 range"},{"level":3,"heading":"個 - Sino, can be used for all numbers","content":"3個 (さんこ) \"3 (small objects)\" Used for: small, discrete objects, any number\n\nBoth are often interchangeable for everyday items!"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"りんごを3つ買いました \"I bought 3 apples\" 質問が2つあります \"I have 2 questions\" お土産を5つもらいました \"I received 5 souvenirs\""},{"level":3,"heading":"Intermediate","content":"選択肢が4つありますが、どれがいいですか？ \"There are 4 options, which is good?\" 一つずつ説明してください \"Please explain one by one\" いくつか問題があります \"There are several problems\""},{"level":3,"heading":"Advanced","content":"この問題には3つの解決方法があります \"There are 3 solutions to this problem\" 彼女は一つも間違えなかった \"She didn''t make a single mistake\" いくつもの困難を乗り越えた \"I overcame many difficulties\""}]},{"level":2,"heading":"Related Grammar Points","content":"Number System Overview - Japanese numbers Native vs Sino Numbers - Two systems Counter System Basics - How counters work 個 (こ) counter - Small objects いくつ (ikutsu) - How many Quantity expressions - Expressing amounts Specific counters - When to use what","children":[]}]'::jsonb,
  '["Casual conversation","Abstract concepts","When specific counter is unknown","Mixed or unclear items","Number System Overview - Japanese numbers","Native vs Sino Numbers - Two systems","Counter System Basics - How counters work","個 (こ) counter - Small objects","いくつ (ikutsu) - How many","Quantity expressions - Expressing amounts","Specific counters - When to use what"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'counters-basics',
  'Counter System Basics',
  'https://practice-japanese.com/docs/counters-basics/',
  'conditionals',
  'N5',
  '[{"level":2,"heading":"What are Counters?","content":"Counters (助数詞 josūshi) are special words attached to numbers when counting objects, people, or actions in Japanese. Different counters are used based on the shape, size, or type of object being counted.\n\nWithout counter: 3 ❌ (incomplete) With counter: 3つ ✓ \"3 items\" 3人 ✓ \"3 people\" 3本 ✓ \"3 long objects\""},{"level":2,"heading":"Basic Structure","content":"","children":[{"level":3,"heading":"Pattern: Number + Counter","content":"Number + Counter │ │ 2 + 人 = ふたり \"Two people\" 3 + 本 = さんぼん \"Three long objects\" 5 + 枚 = ごまい \"Five flat objects\""}]},{"level":2,"heading":"Why Counters Exist","content":"Counters serve several purposes:\n\nClassify objects by type or shape Provide clarity about what''s being counted Add specificity to quantities Follow linguistic patterns similar to Chinese\n\nEnglish: \"Three pieces of paper\" (uses \"pieces of\" as measure word) Japanese: 紙3枚 (かみさんまい) (uses 枚 counter for flat objects)"},{"level":2,"heading":"Essential Counter Categories","content":"","children":[{"level":3,"heading":"By Shape","content":"つ - General items 本 (ほん/ぼん/ぽん) - Long, cylindrical objects 枚 (まい) - Flat, thin objects 個 (こ) - Small, compact objects 台 (だい) - Machines, vehicles"},{"level":3,"heading":"By Living Things","content":"人 (にん/り) - People 匹 (ひき/びき/ぴき) - Small animals 頭 (とう) - Large animals 羽 (わ) - Birds, rabbits"},{"level":3,"heading":"By Container/Portion","content":"杯 (はい/ばい/ぱい) - Cups, glasses, bowls 皿 (さら) - Plates/dishes of food 本 (ほん) - Bottles (also long objects)"},{"level":3,"heading":"By Abstract","content":"回 (かい) - Times, occasions 度 (ど) - Degrees, times 階 (かい) - Floors of buildings"}]},{"level":2,"heading":"Sound Changes (Phonetic Variations)","content":"Many counters undergo sound changes when combined with certain numbers:","children":[{"level":3,"heading":"ほん/ぼん/ぽん Pattern","content":"1本 (いっぽん) - 1 long object 2本 (にほん) - 2 long objects 3本 (さんぼん) - 3 long objects 4本 (よんほん) - 4 long objects 6本 (ろっぽん) - 6 long objects 8本 (はっぽん) - 8 long objects 10本 (じゅっぽん/じっぽん) - 10 long objects"},{"level":3,"heading":"ひき/びき/ぴき Pattern","content":"1匹 (いっぴき) - 1 small animal 2匹 (にひき) - 2 small animals 3匹 (さんびき) - 3 small animals 4匹 (よんひき) - 4 small animals 6匹 (ろっぴき) - 6 small animals 8匹 (はっぴき) - 8 small animals 10匹 (じゅっぴき/じっぴき) - 10 small animals"},{"level":3,"heading":"はい/ばい/ぱい Pattern","content":"1杯 (いっぱい) - 1 cup/glass 2杯 (にはい) - 2 cups/glasses 3杯 (さんばい) - 3 cups/glasses 6杯 (ろっぱい) - 6 cups/glasses 8杯 (はっぱい) - 8 cups/glasses 10杯 (じゅっぱい/じっぱい) - 10 cups/glasses"},{"level":3,"heading":"General Sound Change Rules","content":"Numbers that trigger changes:\n\n1 (いち) → often becomes いっ + [counter changes to っ version] 3 (さん) → often triggers ん sound leading to b/p sound 6 (ろく) → often becomes ろっ 8 (はち) → often becomes はっ 10 (じゅう) → often becomes じゅっ or じっ"}]},{"level":2,"heading":"Common Counters Quick Reference","content":"| Counter | Reading | Used For | Example | | ------- | -------------- | ------------- | ---------------- | | つ | tsu | General items | 3 つ (mittsu) | | 個 | こ | Small objects | 3 個 (sanko) | | 人 | にん/り | People | 3 人 (san-nin) | | 本 | ほん/ぼん/ぽん | Long objects | 3 本 (san-bon) | | 枚 | まい | Flat objects | 3 枚 (san-mai) | | 匹 | ひき/びき/ぴき | Small animals | 3 匹 (san-biki) | | 頭 | とう | Large animals | 3 頭 (san-tou) | | 台 | だい | Machines | 3 台 (san-dai) | | 冊 | さつ | Books | 3 冊 (san-satsu) | | 杯 | はい/ばい/ぱい | Cups/bowls | 3 杯 (san-bai) | | 回 | かい | Times | 3 回 (san-kai) | | 階 | かい | Floors | 3 階 (san-gai) |"},{"level":2,"heading":"How to Choose the Right Counter","content":"","children":[{"level":3,"heading":"Step 1: Identify the Object Type","content":"Ask yourself:\n\nWhat shape is it? Is it living or non-living? How is it typically measured?"},{"level":3,"heading":"Step 2: Match to Counter Category","content":"Pencil → long, cylindrical → 本 Paper → flat, thin → 枚 Apple → small, round → 個 or つ Dog → small animal → 匹 Car → machine/vehicle → 台"},{"level":3,"heading":"Step 3: When in Doubt","content":"Use the general counter つ for objects (works for most things):\n\nりんごを3つください \"Three apples, please\" More specific: りんごを3個ください"}]},{"level":2,"heading":"Asking \"How Many?\"","content":"","children":[{"level":3,"heading":"With Counters","content":"Use 何 (なん/なに) + counter:\n\n何人ですか？ (なんにん) \"How many people?\" 何本ありますか？ (なんぼん) \"How many (long objects) are there?\" 何枚ほしいですか？ (なんまい) \"How many (flat objects) do you want?\""},{"level":3,"heading":"General Questions","content":"いくつですか？ \"How many?\" (general, using native numbers) いくつありますか？ \"How many are there?\""}]},{"level":2,"heading":"Position in Sentences","content":"","children":[{"level":3,"heading":"Before Particle","content":"りんごを3個買いました \"I bought 3 apples\" 友達が2人来ました \"Two friends came\""},{"level":3,"heading":"After Noun","content":"学生が5人います \"There are 5 students\" ペンが3本あります \"There are 3 pens\""},{"level":3,"heading":"End of Sentence","content":"A: 何人いますか？ \"How many people are there?\" B: 10人です \"10 people\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"Noun + Counter + Verb","content":"本を2冊読みました \"I read 2 books\" 水を3杯飲みました \"I drank 3 glasses of water\" 友達に5人会いました \"I met 5 friends\""},{"level":3,"heading":"Noun + が + Counter + Verb","content":"学生が3人来ました \"3 students came\" 猫が2匹います \"There are 2 cats\""},{"level":3,"heading":"Counter + の + Noun","content":"3本のペン \"3 pens\" (literally: 3 [long objects] of pen) 2枚の紙 \"2 sheets of paper\" 5人の学生 \"5 students\""}]},{"level":2,"heading":"Special Exceptions","content":"","children":[{"level":3,"heading":"人 (People Counter)","content":"1人 = ひとり (not いちにん) ⚠️ 2人 = ふたり (not ににん) ⚠️ 3人 = さんにん ✓ 4人 = よにん ✓"},{"level":3,"heading":"Days of the Month","content":"Special readings for 1-10:\n\n1日 = ついたち 2日 = ふつか 3日 = みっか 4日 = よっか ... 11日 = じゅういちにち"},{"level":3,"heading":"20 Years Old","content":"20歳 = はたち (special word) Not: にじゅっさい (though also used)"}]},{"level":2,"heading":"Formal vs Casual","content":"","children":[{"level":3,"heading":"Casual Conversation","content":"Often omit counters with context:\n\nりんご3つちょうだい \"Give me 3 apples\" 友達2人と行った \"I went with 2 friends\""},{"level":3,"heading":"Formal/Written","content":"Use specific counters:\n\nりんごを3個購入いたしました \"I purchased 3 apples\" 参加者は50名でございます \"There are 50 participants\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Shopping","content":"A: これをいくつください \"How many of these would you like?\" B: 5個ください \"5, please\" A: 全部で500円です \"That''s 500 yen total\""},{"level":3,"heading":"Example 2: Restaurant","content":"A: 何名様ですか？ \"How many people?\" B: 4人です \"4 people\" A: ビールを何杯お持ちしましょうか？ \"How many beers shall I bring?\" B: 2杯お願いします \"2, please\""},{"level":3,"heading":"Example 3: Pets","content":"A: ペットを飼っていますか？ \"Do you have any pets?\" B: はい、犬を2匹飼っています \"Yes, I have 2 dogs\" A: 猫は？ \"Cats?\" B: 猫は1匹です \"1 cat\""}]},{"level":2,"heading":"Counter with \"About\"","content":"","children":[{"level":3,"heading":"くらい/ぐらい - \"About\"","content":"3個くらい \"About 3 (small objects)\" 10人ぐらい \"About 10 people\" 5本ぐらい \"About 5 (long objects)\""},{"level":3,"heading":"ほど - \"Approximately\"","content":"More formal:\n\n3個ほど \"Approximately 3\" 10人ほど \"Approximately 10 people\""}]},{"level":2,"heading":"Counting Actions","content":"","children":[{"level":3,"heading":"回 (かい) - Times","content":"1回 (いっかい) - once 2回 (にかい) - twice 3回 (さんかい) - 3 times 日本に3回行きました \"I went to Japan 3 times\""},{"level":3,"heading":"度 (ど) - Degrees/Times","content":"1度 (いちど) - once 2度 (にど) - twice 何度も (なんども) - many times 何度も言いました \"I said it many times\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Forgetting counters","content":"Wrong: 3買いました ❌ Right: 3つ買いました ✓ \"I bought 3\""},{"level":3,"heading":"Mistake 2: Wrong counter choice","content":"Unnatural: 紙を3本ください ❌ Right: 紙を3枚ください ✓ \"3 sheets of paper, please\" (本 is for long objects, not flat)"},{"level":3,"heading":"Mistake 3: Wrong sound changes","content":"Wrong: さんほん (should change) ❌ Right: さんぼん ✓ \"3 long objects\" Wrong: いちひき ❌ Right: いっぴき ✓ \"1 small animal\""},{"level":3,"heading":"Mistake 4: Using wrong reading for 人","content":"Wrong: いちにん (1 person) ❌ Right: ひとり ✓ Wrong: ににん (2 people) ❌ Right: ふたり ✓"}]},{"level":2,"heading":"Learning Strategy","content":"","children":[{"level":3,"heading":"Start with These Essential Counters","content":"つ - General (works for most things) 人 - People (most common in conversation) 個 - Small objects 本 - Long objects 枚 - Flat objects"},{"level":3,"heading":"Gradually Add More","content":"匹 - Small animals 台 - Machines 冊 - Books 杯 - Cups/glasses 回 - Times"},{"level":3,"heading":"Don''t Worry About Perfection","content":"Native speakers often use つ or 個 as defaults Context usually makes meaning clear Specific counters sound more natural but aren''t always required"}]},{"level":2,"heading":"Counters in Different Contexts","content":"","children":[{"level":3,"heading":"Shopping","content":"これを3個ください \"3 of these, please\" りんごを5個と梨を2個 \"5 apples and 2 pears\""},{"level":3,"heading":"Restaurants","content":"ビールを2杯 \"2 beers\" 餃子を1皿 \"1 plate of gyoza\""},{"level":3,"heading":"Office/School","content":"コピーを10枚 \"10 copies\" 会議が3回 \"3 meetings\""},{"level":3,"heading":"Home","content":"猫が2匹 \"2 cats\" 車が1台 \"1 car\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic Counting","content":"ペンが3本あります \"There are 3 pens\" 学生が10人います \"There are 10 students\" 紙を5枚ください \"5 sheets of paper, please\""},{"level":3,"heading":"Questions","content":"何個買いますか？ \"How many will you buy?\" 何人来ますか？ \"How many people are coming?\" 何回行きましたか？ \"How many times did you go?\""},{"level":3,"heading":"Answers","content":"5個買います \"I''ll buy 5\" 3人来ます \"3 people are coming\" 2回行きました \"I went twice\""}]},{"level":2,"heading":"Related Grammar Points","content":"Number System Overview - Japanese numbers Native vs Sino Numbers - Two number systems General Counter (つ) - Most versatile counter Specific Counters - Detailed guides for each いくつ (ikutsu) - How many Quantity expressions - Expressing amounts Approximation - About/approximately","children":[]}]'::jsonb,
  '["Classify objects by type or shape","Provide clarity about what''s being counted","Add specificity to quantities","Follow linguistic patterns similar to Chinese","つ - General (works for most things)","人 - People (most common in conversation)","個 - Small objects","本 - Long objects","枚 - Flat objects","匹 - Small animals","台 - Machines","冊 - Books","杯 - Cups/glasses","回 - Times","1 (いち) → often becomes いっ + [counter changes to っ version]","3 (さん) → often triggers ん sound leading to b/p sound","6 (ろく) → often becomes ろっ","8 (はち) → often becomes はっ","10 (じゅう) → often becomes じゅっ or じっ","What shape is it?","Is it living or non-living?","How is it typically measured?","Native speakers often use つ or 個 as defaults","Context usually makes meaning clear","Specific counters sound more natural but aren''t always required","Number System Overview - Japanese numbers","Native vs Sino Numbers - Two number systems","General Counter (つ) - Most versatile counter","Specific Counters - Detailed guides for each","いくつ (ikutsu) - How many","Quantity expressions - Expressing amounts","Approximation - About/approximately"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'da',
  'だ (da) - Plain Copula',
  'https://practice-japanese.com/docs/da/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is だ (da)?","content":"だ (da) is the plain/casual form of the copula, equivalent to です in casual speech. It means \"is/am/are\" and is used in informal situations, writing, and as the dictionary form of the copula.\n\nA は B だ \"A is B\" 私は学生だ \"I am a student\""},{"level":2,"heading":"Basic Usage","content":"","children":[{"level":3,"heading":"With Nouns","content":"これは本だ \"This is a book\" 彼は先生だ \"He is a teacher\" 私は日本人だ \"I am Japanese\" 今日は月曜日だ \"Today is Monday\""},{"level":3,"heading":"With Na-adjectives","content":"この部屋は静かだ \"This room is quiet\" 京都はきれいだ \"Kyoto is beautiful\" 日本語は便利だ \"Japanese is convenient\" 彼は親切だ \"He is kind\""}]},{"level":2,"heading":"When to Use だ","content":"","children":[{"level":3,"heading":"Casual Situations","content":"Use with friends, family, and close acquaintances.\n\n友達: あれは何だ？ \"What''s that?\" 自分: それは本だ \"That''s a book\""},{"level":3,"heading":"Written Language","content":"Common in newspapers, novels, and academic writing.\n\n富士山は日本で一番高い山だ \"Mt. Fuji is Japan''s tallest mountain\" これは重要な問題だ \"This is an important problem\""},{"level":3,"heading":"Inner Thoughts","content":"Used when expressing your own thoughts.\n\n今日は暑いな。大変だ \"It''s hot today. This is tough\" これは難しいな \"This is difficult\""},{"level":3,"heading":"Quotations and Reports","content":"彼は学生だと言った \"He said he''s a student\" これは本当だと思う \"I think this is true\""}]},{"level":2,"heading":"Question Form","content":"","children":[{"level":3,"heading":"だ + か = か (without だ)","content":"In questions, だ is usually dropped before か in casual speech.\n\n学生か？ \"Are you a student?\" 日本人か？ \"Are you Japanese?\" 本当か？ \"Is it true?\"\n\nNote: だか sounds unnatural. Use just か instead."},{"level":3,"heading":"Rising Intonation","content":"Questions can be formed with rising intonation alone.\n\n学生？↗ \"A student?\" 本当？↗ \"Really?\" 明日？↗ \"Tomorrow?\""}]},{"level":2,"heading":"Negative Form","content":"","children":[{"level":3,"heading":"じゃない (janai)","content":"The casual negative of だ is じゃない.\n\n学生じゃない \"I''m not a student\" 本じゃない \"It''s not a book\" 本当じゃない \"It''s not true\" 簡単じゃない \"It''s not easy\""},{"level":3,"heading":"ではない (dewa nai)","content":"More formal written negative form.\n\nこれは本ではない \"This is not a book\" 簡単ではない \"It''s not simple\"\n\nUsage difference:\n\nじゃない: Spoken, casual ではない: Written, formal"}]},{"level":2,"heading":"Comparing です and だ","content":"","children":[{"level":3,"heading":"Politeness Level","content":"Polite (です): 学生です Plain (だ): 学生だ \"I am a student\" Polite (です): 日本人です Plain (だ): 日本人だ \"I am Japanese\""},{"level":3,"heading":"Question Forms","content":"Polite: 学生ですか？ Plain: 学生（だ）？ / 学生か？ \"Are you a student?\" Polite: 本当ですか？ Plain: 本当（だ）？ / 本当か？ \"Really?\""},{"level":3,"heading":"Negative Forms","content":"Polite: 学生じゃないです / 学生ではありません Plain: 学生じゃない / 学生ではない \"I''m not a student\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"～だから (because)","content":"学生だから、お金がない \"Because I''m a student, I don''t have money\" 忙しいんだから \"It''s because I''m busy\" 日本人だから、日本語が話せる \"Because I''m Japanese, I can speak Japanese\""},{"level":3,"heading":"～だけど (but)","content":"学生だけど、働いている \"I''m a student, but I''m working\" 簡単だけど、面白い \"It''s simple, but interesting\""},{"level":3,"heading":"～だと思う (I think)","content":"彼は学生だと思う \"I think he''s a student\" 本当だと思う \"I think it''s true\" 便利だと思う \"I think it''s convenient\""},{"level":3,"heading":"～だって (quotation/emphasis)","content":"彼は学生だって \"He says he''s a student / I heard he''s a student\" 本当だって！ \"It''s true (I''m telling you)!\""}]},{"level":2,"heading":"Special Forms","content":"","children":[{"level":3,"heading":"だろう (probably)","content":"Presumptive form of だ.\n\n学生だろう \"He''s probably a student\" 本当だろう \"It''s probably true\" 明日だろう \"It''s probably tomorrow\""},{"level":3,"heading":"～だった (past)","content":"Past tense of だ.\n\n学生だった \"I was a student\" 昨日だった \"It was yesterday\" きれいだった \"It was beautiful\""},{"level":3,"heading":"～だったら (conditional)","content":"学生だったら、割引がある \"If you''re a student, there''s a discount\" 本当だったら、すごい \"If it''s true, that''s amazing\""}]},{"level":2,"heading":"In Subordinate Clauses","content":"","children":[{"level":3,"heading":"Before な (na for noun modification)","content":"学生だった人 \"A person who was a student\" 有名な人 \"A famous person\""},{"level":3,"heading":"Before の (nominalizer)","content":"学生なのだ → 学生なんだ \"The thing is, I''m a student\" 大変なのだ → 大変なんだ \"The thing is, it''s tough\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Casual Chat","content":"A: これ、何？ \"What''s this?\" B: 辞書だよ \"It''s a dictionary\" A: 誰の？ \"Whose?\" B: 田中のだ \"It''s Tanaka''s\""},{"level":3,"heading":"Example 2: Confirmation","content":"A: 明日、休みだよね？ \"Tomorrow''s a day off, right?\" B: うん、休みだ \"Yeah, it''s a day off\" A: 本当？ \"Really?\" B: 本当だって！ \"It''s true!\""},{"level":3,"heading":"Example 3: Explanation","content":"A: どうして来なかったの？ \"Why didn''t you come?\" B: 忙しかったんだ \"I was busy (that''s why)\" A: 仕事？ \"Work?\" B: そう、仕事だった \"Yeah, it was work\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using だ in polite contexts","content":"Wrong (to strangers/superiors): 学生だ ❌ Right: 学生です ✓ \"I am a student\""},{"level":3,"heading":"Mistake 2: Using だか in questions","content":"Unnatural: 学生だか？ ❌ Natural: 学生？/ 学生か？ ✓ \"Are you a student?\""},{"level":3,"heading":"Mistake 3: Using だ after i-adjectives","content":"Wrong: 大きいだ ❌ Right: 大きい ✓ \"It''s big\" Note: I-adjectives don''t need だ"},{"level":3,"heading":"Mistake 4: Mixing politeness levels","content":"Inconsistent: 私は学生だ。でも、毎日働いています ⚠️ Better: 私は学生だ。でも、毎日働いている ✓ Or: 私は学生です。でも、毎日働いています ✓"}]},{"level":2,"heading":"Gender Associations","content":"While だ itself is gender-neutral, it''s often associated with masculine speech when used in casual conversation. Women might use more です or だわ variations.","children":[{"level":3,"heading":"Neutral/Masculine","content":"これは本だ \"This is a book\" 面白いな \"This is interesting\""},{"level":3,"heading":"Feminine variations","content":"これは本だわ \"This is a book\" 面白いわね \"This is interesting\"\n\nNote: These distinctions are becoming less strict in modern Japanese."}]},{"level":2,"heading":"In Written Japanese","content":"","children":[{"level":3,"heading":"Newspapers","content":"今日は晴れだ \"Today is sunny\" 事故の原因は不明だ \"The cause of the accident is unknown\""},{"level":3,"heading":"Novels/Narratives","content":"彼は学生だった \"He was a student\" それは本当のことだ \"That''s the truth\""},{"level":3,"heading":"Academic Writing","content":"これは重要な問題だ \"This is an important problem\" その理由は明らかだ \"The reason is clear\""}]},{"level":2,"heading":"Sentence-ending Particles with だ","content":"","children":[{"level":3,"heading":"だよ (emphasis)","content":"本当だよ \"It''s true (I''m telling you)\" 学生だよ \"I''m a student (you know)\""},{"level":3,"heading":"だね (agreement)","content":"そうだね \"That''s right, isn''t it\" 便利だね \"It''s convenient, isn''t it\""},{"level":3,"heading":"だな (contemplative)","content":"難しいな \"This is difficult (hmm)\" そうだな \"That''s right (let me think)\""},{"level":3,"heading":"だぞ (strong emphasis - masculine)","content":"本当だぞ \"It''s really true!\" 危ないぞ \"It''s dangerous!\""}]},{"level":2,"heading":"Practice Sentences","content":"これは私のだ \"This is mine\" 彼は学生だ \"He''s a student\" それは本当だ \"That''s true\" 簡単じゃない \"It''s not easy\" 昨日は月曜日だった \"Yesterday was Monday\" たぶん明日だろう \"It''s probably tomorrow\" 学生だから、忙しい \"Because I''m a student, I''m busy\" 本だけど、面白くない \"It''s a book, but it''s not interesting\" 誰のペンだ？ \"Whose pen is this?\" どこだ？ \"Where is it?\""},{"level":2,"heading":"Related Grammar Points","content":"です (desu) - Polite copula である (de aru) - Formal copula じゃない (janai) - Negative forms だった (datta) - Past forms Casual Speech Patterns - Informal Japanese Sentence-ending Particles - ね, よ, な, etc.","children":[]}]'::jsonb,
  '["じゃない: Spoken, casual","ではない: Written, formal","です (desu) - Polite copula","である (de aru) - Formal copula","じゃない (janai) - Negative forms","だった (datta) - Past forms","Casual Speech Patterns - Informal Japanese","Sentence-ending Particles - ね, よ, な, etc."]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'dakara-connector',
  'だから (Therefore)',
  'https://practice-japanese.com/docs/dakara-connector/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is だから?","content":"だから is a sentence connector meaning \"therefore,\" \"that''s why,\" \"so,\" or \"because of that.\" It shows cause-and-effect relationships, where the second sentence is a result or consequence of the first. It''s casual and commonly used in conversation.\n\n疲れた。だから休む。 \"I''m tired. Therefore, I''ll rest.\" 雨だ。だから傘を持って行く。 \"It''s raining. So I''ll take an umbrella.\""},{"level":2,"heading":"Basic Usage","content":"","children":[{"level":3,"heading":"Cause and effect","content":"お腹が空いた。だから食べる。 \"I''m hungry. That''s why I''ll eat.\" 忙しい。だから行けない。 \"I''m busy. So I can''t go.\" Result follows reason"},{"level":3,"heading":"Explaining reasons","content":"寒い。だからコートを着る。 \"It''s cold. That''s why I''m wearing a coat.\" 高い。だから買わない。 \"It''s expensive. So I won''t buy it.\" Justifying action"}]},{"level":2,"heading":"Position in Sentence","content":"","children":[{"level":3,"heading":"Beginning of second sentence","content":"疲れた。だから寝る。 \"I''m tired. Therefore, I''ll sleep.\" After period New sentence"}]},{"level":2,"heading":"Polite and Casual","content":"","children":[{"level":3,"heading":"だから = casual","content":"忙しい。だから行けない。 \"I''m busy. So I can''t go.\" Conversational tone"},{"level":3,"heading":"ですから = polite","content":"忙しいです。ですから行けません。 \"I''m busy. Therefore, I can''t go.\" Polite version"},{"level":3,"heading":"だから with polite endings","content":"忙しい。だから行けません。 \"I''m busy. So I can''t go.\" Mixed style common"}]},{"level":2,"heading":"Common Contexts","content":"","children":[{"level":3,"heading":"Explaining decisions","content":"明日テストがある。だから今日は勉強する。 \"I have a test tomorrow. That''s why I''ll study today.\" Justifying choices"},{"level":3,"heading":"Refusing invitations","content":"A: 映画に行かない？ B: 疲れてる。だから今日はやめとく。 A: \"Want to go to a movie?\" B: \"I''m tired. So I''ll pass today.\" Giving reasons for \"no\""},{"level":3,"heading":"Daily reasoning","content":"雨だ。だから家にいる。 \"It''s raining. So I''m staying home.\" Everyday logic"}]},{"level":2,"heading":"だから vs それで","content":"","children":[{"level":3,"heading":"だから = that''s why (emphasis on reason)","content":"疲れた。だから休む。 \"I''m tired. That''s why I''ll rest.\" Strong causal link Personal reasoning"},{"level":3,"heading":"それで = and so/then (emphasis on result)","content":"疲れた。それで休むことにした。 \"I was tired. And so I decided to rest.\" Neutral narration Sequence of events"}]},{"level":2,"heading":"だから at Sentence Start","content":"","children":[{"level":3,"heading":"Starting explanation","content":"A: なぜ来なかった？ B: だから、忙しかったんだよ。 A: \"Why didn''t you come?\" B: \"That''s why—I was busy.\" Emphasizing reason"},{"level":3,"heading":"Expressing frustration","content":"だから言ったでしょう！ \"That''s why I told you!\" Emphatic, annoyed tone \"I told you so\""}]},{"level":2,"heading":"から vs だから","content":"","children":[{"level":3,"heading":"から = within sentence","content":"疲れたから休む。 \"I''ll rest because I''m tired.\" Single sentence Direct connection"},{"level":3,"heading":"だから = between sentences","content":"疲れた。だから休む。 \"I''m tired. Therefore, I''ll rest.\" Two sentences Stronger separation"}]},{"level":2,"heading":"だから for Conclusions","content":"","children":[{"level":3,"heading":"Drawing conclusions","content":"彼は毎日練習している。だから上手だ。 \"He practices every day. That''s why he''s good.\" Logical result"}]},{"level":2,"heading":"Negative Sentences","content":"","children":[{"level":3,"heading":"Works with negatives","content":"お金がない。だから買えない。 \"I don''t have money. So I can''t buy it.\" 時間がない。だから行かない。 \"I don''t have time. So I won''t go.\" Negative cause and effect"}]},{"level":2,"heading":"Formal Alternatives","content":"","children":[{"level":3,"heading":"したがって (therefore - formal)","content":"利益が減少した。したがって対策が必要だ。 \"Profits decreased. Therefore, countermeasures are necessary.\" Written, formal"},{"level":3,"heading":"そのため (for that reason - formal)","content":"天候が悪化した。そのため中止となった。 \"The weather worsened. For that reason, it was canceled.\" Formal reports"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Using within same sentence","content":"Wrong: 疲れただから休む ⚠️ Correct: 疲れた。だから休む。 ✓ Or: 疲れたから休む。 ✓ Need sentence break or use から"},{"level":3,"heading":"Confusing with ですから in casual speech","content":"Unnatural: 疲れた。ですから休む。△ Natural: 疲れた。だから休む。✓ \"I''m tired. So I''ll rest.\" ですから too formal for casual context"}]},{"level":2,"heading":"Emphasis Patterns","content":"","children":[{"level":3,"heading":"だからこそ (precisely because)","content":"難しい。だからこそ挑戦したい。 \"It''s difficult. Precisely because of that, I want to challenge it.\" Strong emphasis"},{"level":3,"heading":"だからね (you see/you know)","content":"だからね、そういうことなんだ。 \"So you see, that''s how it is.\" Softening, explanatory"}]},{"level":2,"heading":"In Arguments","content":"","children":[{"level":3,"heading":"Making a point","content":"A: 行くべきだよ。 B: だから、時間がないって言ってるでしょう。 A: \"You should go.\" B: \"That''s why I''m saying I don''t have time.\" Assertive reasoning"}]},{"level":2,"heading":"Multiple だから","content":"","children":[{"level":3,"heading":"Sequential reasoning","content":"疲れた。だから休む。だから明日頑張れる。 \"I''m tired. So I''ll rest. So I can work hard tomorrow.\" Chain of reasons Can be excessive"}]},{"level":2,"heading":"Starting Conversations","content":"","children":[{"level":3,"heading":"Continuing from context","content":"だから、昨日の話だけど... \"So, about yesterday''s topic...\" Resuming discussion"}]},{"level":2,"heading":"Complaint Tone","content":"","children":[{"level":3,"heading":"Expressing annoyance","content":"だから言ったじゃない！ \"That''s why I said it!\" \"I told you so\" feeling Frustrated tone"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"疲れた。だから休む。 \"I''m tired. So I''ll rest.\" 雨だ。だから傘を持つ。 \"It''s raining. So I''ll take an umbrella.\" 忙しい。だから行けない。 \"I''m busy. So I can''t go.\" お腹が空いた。だから食べる。 \"I''m hungry. So I''ll eat.\""},{"level":3,"heading":"Intermediate","content":"明日は大切な試験がある。だから今日は 早く寝たほうがいい。十分な睡眠を 取ることが大事だ。 \"I have an important exam tomorrow. So I should go to bed early today. Getting enough sleep is important.\" この仕事は難しい。だから時間がかかる。 でも諦めずに続ければ、必ずできるように なる。 \"This work"}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'dakara',
  'だから (dakara) - Therefore',
  'https://practice-japanese.com/docs/dakara/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is だから?","content":"だから means \"therefore,\" \"that''s why,\" or \"so.\" It connects two sentences, with the second being a logical conclusion or result of the first.\n\n雨が降っている。だから、家にいる \"It''s raining. Therefore, I''m staying home\" 疲れた。だから、休む \"I''m tired. So, I''ll rest\" 好きだ。だから、買った \"I like it. That''s why I bought it\""},{"level":2,"heading":"Formation","content":"","children":[{"level":3,"heading":"Pattern: [Sentence 1]. だから、[Sentence 2]","content":"忙しい。だから、行けない \"I''m busy. Therefore, I can''t go\" 高かった。だから、買わなかった \"It was expensive. That''s why I didn''t buy it\" Connects two complete sentences"}]},{"level":2,"heading":"Basic Usage","content":"","children":[{"level":3,"heading":"Expressing logical conclusions","content":"明日テストがある。だから、今日は勉強する \"I have a test tomorrow. So, I''ll study today\" お金がない。だから、買えない \"I don''t have money. Therefore, I can''t buy it\" 彼は親切だ。だから、みんなに好かれている \"He''s kind. That''s why everyone likes him\""},{"level":3,"heading":"Explaining decisions","content":"このレストランは美味しい。だから、よく来る \"This restaurant is delicious. That''s why I come often\" 危険だ。だから、行かない \"It''s dangerous. So, I won''t go\""}]},{"level":2,"heading":"だから vs から","content":"","children":[{"level":3,"heading":"だから - Connects sentences","content":"疲れた。だから、休む \"I''m tired. So, I''ll rest\" Two separate sentences Stronger separation"},{"level":3,"heading":"から - Within one sentence","content":"疲れたから、休む \"Because I''m tired, I''ll rest\" One sentence Closer connection"},{"level":3,"heading":"Both express similar meaning","content":"雨だ。だから、行かない (two sentences) 雨だから、行かない (one sentence) \"It''s raining, so I won''t go\" Choose based on emphasis and style"}]},{"level":2,"heading":"Politeness Levels","content":"","children":[{"level":3,"heading":"Casual: だから","content":"疲れた。だから、帰る \"I''m tired. So, I''m going home\" With friends and family"},{"level":3,"heading":"Polite: ですから","content":"疲れました。ですから、失礼します \"I''m tired. Therefore, I''ll excuse myself\" More formal"},{"level":3,"heading":"Very polite: そのため / したがって","content":"雨天です。そのため、中止です \"It''s rainy weather. Therefore, it''s cancelled\" Formal writing/announcements"}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Making Decisions","content":"A: 明日、何する？ \"What are you doing tomorrow?\" B: 雨の予報だよね。だから、家で映画でも見ようかな \"It''s forecast to rain, right? So, maybe I''ll watch a movie at home\" A: いいね \"Sounds good\""},{"level":3,"heading":"Example 2: Explaining","content":"A: どうして日本語を勉強してるの？ \"Why are you studying Japanese?\" B: 日本が好きだから。だから、将来日本に住みたい \"Because I like Japan. That''s why I want to live in Japan in the future\""},{"level":3,"heading":"Example 3: Argument","content":"A: もう遅いよ \"It''s already late\" B: だから、急ごうって言ってるでしょ！ \"That''s why I''m saying let''s hurry!\" A: 分かったよ \"Okay, okay\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using in one continuous sentence","content":"Unnatural: 疲れただから、休む ❌ Right: 疲れた。だから、休む ✓ Or: 疲れたから、休む ✓ だから connects separate sentences!"},{"level":3,"heading":"Mistake 2: Starting conversation with だから","content":"Unnatural start: だから、行かないんだ ⚠️ Better: そういうわけで、行かないんだ ✓ \"That''s why I''m not going\" Need context first for だから"},{"level":3,"heading":"Mistake 3: Too casual in formal writing","content":"Too casual: 予算がない。だから、中止 ⚠️ Better: 予算がないため、中止 ✓ \"No budget. Therefore, cancelled\" Use そのため/したがって in formal texts"}]},{"level":2,"heading":"Emphatic Usage","content":"","children":[{"level":3,"heading":"Emphatic \"that''s why!\"","content":"だから言ったでしょ！ \"That''s why I told you!\" / \"I told you so!\" だからダメなんだよ \"That''s why it''s no good!\" Strong emphasis"},{"level":3,"heading":"Frustrated tone","content":"A: どうして？ \"Why?\" B: だから！忙しいって言ってるでしょ！ \"I said! I''m telling you I''m busy!\" Shows frustration/annoyance"}]},{"level":2,"heading":"だからこそ (Precisely because)","content":"","children":[{"level":3,"heading":"Emphasizing the reason","content":"難しい。だからこそ、やりがいがある \"It''s difficult. Precisely because of that, it''s rewarding\" 好きだ。だからこそ、応援したい \"I like (them). That''s exactly why I want to support (them)\" Stronger emphasis than だから"}]},{"level":2,"heading":"Common Expressions","content":"","children":[{"level":3,"heading":"だから何？ (So what?)","content":"A: 彼、東大出身なんだって \"They say he''s from Tokyo University\" B: だから何？ \"So what?\" Dismissive, challenging"},{"level":3,"heading":"だからといって (Even so/Just because)","content":"お金持ちだ。だからといって、幸せとは限らない \"He''s rich. Even so, that doesn''t mean he''s happy\" Countering a conclusion"}]},{"level":2,"heading":"In Explanations","content":"","children":[{"level":3,"heading":"Building logical flow","content":"このレストランは安い。 だから、学生に人気がある。 だから、いつも混んでいる。 \"This restaurant is cheap. That''s why it''s popular with students. That''s why it''s always crowded.\" Connecting multiple conclusions"}]},{"level":2,"heading":"Formal Alternatives","content":"","children":[{"level":3,"heading":"そのため (Therefore - formal)","content":"雨天です。そのため、中止です \"It''s rainy. Therefore, it''s cancelled\" More formal than だから"},{"level":3,"heading":"したがって (Consequently - formal)","content":"売り上げが減少した。したがって、人員削減が必要だ \"Sales decreased. Consequently, personnel reduction is necessary\" Formal, written"},{"level":3,"heading":"それゆえ (Hence - literary)","content":"彼は天才だった。それゆえ、孤独だった \"He was a genius. Hence, he was lonely\" Literary, formal"}]},{"level":2,"heading":"Responding to だから","content":"","children":[{"level":3,"heading":"Acknowledging","content":"そうなんだ \"I see\" なるほどね \"I understand\" だよね \"Right\""},{"level":3,"heading":"Disagreeing","content":"でも、それは違うと思う \"But I think that''s different\" だからって、それはないでしょ \"Even so, that''s not right\""}]},{"level":2,"heading":"Casual Conversation Patterns","content":"","children":[{"level":3,"heading":"Short exchanges","content":"A: 疲れた \"I''m tired\" B: だから休めって言ったでしょ \"That''s why I told you to rest\""},{"level":3,"heading":"Explaining behavior","content":"A: なんで怒ってるの？ \"Why are you angry?\" B: 約束破ったでしょ。だから怒ってるの \"You broke your promise, right? That''s why I''m angry\""}]},{"level":2,"heading":"Written Usage","content":"","children":[{"level":3,"heading":"In essays (casual)","content":"私は音楽が好きだ。だから、毎日ギターを練習している。 \"I like music. Therefore, I practice guitar every day.\" Okay in informal essays"},{"level":3,"heading":"In formal writing","content":"Less formal: 売上が減少した。だから、対策が必要だ。 More formal: 売上が減少した。そのため、対策が必要である。 \"Sales decreased. Therefore, measures are necessary.\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"忙しい。だから、行けない \"I''m busy. So, I can''t go\" 高い。だから、買わない \"It''s expensive. Therefore, I won''t buy it\" 好きだ。だから、毎日見る \"I like it. That''s why I watch it every day\""},{"level":3,"heading":"Intermediate","content":"昨日遅くまで起きていた。だから、今日は眠い \"I stayed up late yesterday. That''s why I''m sleepy today\" このアプリは便利だ。だから、多くの人が使っている \"This app is convenient. That''s why many people use it\" 彼女は努力家だ。だから、必ず成功すると思う \"She''s hardworking. That''s why I think she''ll definitely succeed\""},{"level":3,"heading":"Advanced","content":"環境問題は深刻だ。だから、一人一人が意識を 変える必要がある。だからこそ、教育が重要なのだ。 \"Environmental problems are serious. Therefore, each person needs to change their awareness. That''s precisely why education is important.\" 彼は誰よりも練習した。だから、優勝できた。 しかし、だからといって油断してはいけない。 \"He practiced more than anyone. That''s why he could win. However, even so, he mustn''t let his guard down.\""}]},{"level":2,"heading":"Related Grammar Points","content":"から - Because (in one sentence) ので - Because (polite) そのため - Therefore (formal) したがって - Consequently だからこそ - Precisely because Conjunctions - Connecting sentences","children":[]}]'::jsonb,
  '["から - Because (in one sentence)","ので - Because (polite)","そのため - Therefore (formal)","したがって - Consequently","だからこそ - Precisely because","Conjunctions - Connecting sentences"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'dake-detail',
  'だけ (Only)',
  'https://practice-japanese.com/docs/dake-detail/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is だけ?","content":"だけ (dake) is a particle that means \"only,\" \"just,\" or \"merely\" and indicates limitation or the extent of something. It emphasizes that something is limited to what is mentioned and nothing more.\n\nEnglish equivalents:\n\n\"only\" \"just\" \"merely\" \"as much as\" \"to the extent of\"\n\nこれだけです。 Kore dake desu. \"This is all.\" / \"Only this.\" 見るだけで分かる。 Miru dake de wakaru. \"You can tell just by looking.\""},{"level":2,"heading":"Basic Usage","content":"","children":[{"level":3,"heading":"Structure","content":"[Noun/Verb/Adjective] + だけ\n\nだけ can attach to various parts of speech:\n\n水だけ (mizu dake) - \"only water\" 見るだけ (miru dake) - \"just look\" これだけ (kore dake) - \"only this\" 少しだけ (sukoshi dake) - \"just a little\""},{"level":3,"heading":"With Nouns","content":"水だけ飲んだ。 Mizu dake nonda. \"I drank only water.\" 一人だけ来た。 Hitori dake kita. \"Only one person came.\" 日本だけではない。 Nihon dake de wa nai. \"It''s not only Japan.\" 彼だけが知っている。 Kare dake ga shitte iru. \"Only he knows.\""},{"level":3,"heading":"With Verbs (Dictionary Form)","content":"見るだけ。 Miru dake. \"Just look.\" / \"Only looking.\" 食べるだけでいい。 Taberu dake de ii. \"Just eating is fine.\" / \"You only need to eat.\" 待つだけだった。 Matsu dake datta. \"I just waited.\" / \"All I did was wait.\""},{"level":3,"heading":"With Quantities","content":"少しだけください。 Sukoshi dake kudasai. \"Just a little, please.\" 三つだけ買った。 Mittsu dake katta. \"I bought only three.\" 100円だけある。 Hyaku-en dake aru. \"I have only 100 yen.\" 二人だけ残った。 Futari dake nokotta. \"Only two people remained.\""}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"だけで (With just/only)","content":"Indicates something is accomplished with only the mentioned thing:\n\nこれだけで十分です。 Kore dake de juubun desu. \"This alone is enough.\" 見るだけで楽しい。 Miru dake de tanoshii. \"It''s fun just to look.\" 考えるだけで怖い。 Kangaeru dake de kowai. \"It''s scary just to think about it.\" 聞くだけで分かる。 Kiku dake de wakaru. \"You can tell just by listening.\""},{"level":3,"heading":"だけに (Precisely because/All the more)","content":"Emphasizes that something is natural given the circumstances:\n\n彼は天才だけに、すぐ理解した。 Kare wa tensai da ke ni, sugu rikai shita. \"Precisely because he''s a genius, he understood immediately.\" 高いだけに品質がいい。 Takai dake ni hinshitsu ga ii. \"Precisely because it''s expensive, the quality is good.\" 有名なだけに人が多い。 Yuumei na dake ni hito ga ooi. \"Precisely because it''s famous, there are many people.\""},{"level":3,"heading":"できるだけ (As much as possible)","content":"できるだけ早く来てください。 Dekiru dake hayaku kite kudasai. \"Please come as early as possible.\" できるだけ頑張ります。 Dekiru dake ganbarimasu. \"I''ll try as hard as I can.\" できるだけ安く買いたい。 Dekiru dake yasuku kaitai. \"I want to buy it as cheaply as possible.\""},{"level":3,"heading":"Verb + だけ + Verb","content":"Expresses \"as much as you want/can\":\n\n食べたいだけ食べてください。 Tabetai dake tabete kudasai. \"Please eat as much as you want.\" 好きなだけ取ってください。 Suki na dake totte kudasai. \"Please take as much as you like.\" 飲めるだけ飲んだ。 Nomeru dake nonda. \"I drank as much as I could.\" 行きたいだけ行った。 Ikitai dake itta. \"I went as much as I wanted.\""},{"level":3,"heading":"それだけ (That''s all/Only that)","content":"それだけです。 Sore dake desu. \"That''s all.\" それだけじゃない。 Sore dake ja nai. \"That''s not all.\" / \"It''s not only that.\" それだけのことだ。 Sore dake no koto da. \"It''s just that.\" / \"That''s all it is.\""}]},{"level":2,"heading":"Expressing \"All I did was...\"","content":"","children":[{"level":3,"heading":"Past actions with だけ","content":"待つだけだった。 Matsu dake datta. \"All I did was wait.\" 見ているだけだった。 Mite iru dake datta. \"I was just watching.\" 考えただけで何もしなかった。 Kangaeta dake de nani mo shinakatta. \"I just thought about it but didn''t do anything.\""}]},{"level":2,"heading":"だけあって (As expected/Worthy of)","content":"Indicates something meets expectations:\n\nプロだけあって上手だ。 Puro dake atte jouzu da. \"As expected of a professional, he''s skilled.\" 高いだけあって品質がいい。 Takai dake atte hinshitsu ga ii. \"As befitting its high price, the quality is good.\" 有名なだけあって人気がある。 Yuumei na dake atte ninki ga aru. \"As befitting something famous, it''s popular.\""},{"level":2,"heading":"Particle Combinations","content":"","children":[{"level":3,"heading":"だけは (At least/Only... [exclusive])","content":"これだけは覚えてください。 Kore dake wa oboete kudasai. \"Please remember at least this.\" 彼だけは信じられる。 Kare dake wa shinjirareru. \"At least he can be trusted.\" / \"Only he can be trusted.\" 今日だけは休みたい。 Kyou dake wa yasumitai. \"I want to rest at least today.\""},{"level":3,"heading":"だけが (Only... [subject marker])","content":"彼だけが知っている。 Kare dake ga shitte iru. \"Only he knows.\" 時間だけが問題だ。 Jikan dake ga mondai da. \"Time is the only problem.\" これだけが残った。 Kore dake ga nokotta. \"Only this remained.\""},{"level":3,"heading":"だけを (Only... [object marker])","content":"水だけを飲む。 Mizu dake wo nomu. \"I drink only water.\" 彼だけを信じる。 Kare dake wo shinjiru. \"I trust only him.\""},{"level":3,"heading":"だけに (To/for only)","content":"彼だけに教えた。 Kare dake ni oshieta. \"I taught only him.\" / \"I told only him.\" 友達だけに話した。 Tomodachi dake ni hanashita. \"I told only my friends.\""}]},{"level":2,"heading":"Expressing Extent or Degree","content":"","children":[{"level":3,"heading":"Verb potential + だけ","content":"走れるだけ走った。 Hashireru dake hashitta. \"I ran as much as I could.\" 言えるだけ言った。 Ieru dake itta. \"I said as much as I could say.\" やれるだけやった。 Yareru dake yatta. \"I did as much as I could do.\""},{"level":3,"heading":"Describing amounts","content":"たくさんあるだけ持ってきた。 Takusan aru dake motte kita. \"I brought as many as there were.\" 分かるだけ説明した。 Wakaru dake setsumei shita. \"I explained as much as I understood.\""}]},{"level":2,"heading":"Time Expressions with だけ","content":"一日だけ休む。 Ichinichi dake yasumu. \"I''ll rest just one day.\" 今だけ安い。 Ima dake yasui. \"It''s cheap only now.\" 三時間だけ待った。 San-jikan dake matta. \"I waited only three hours.\" 少しの間だけ。 Sukoshi no aida dake. \"Just for a short while.\""},{"level":2,"heading":"Negative Contexts","content":"","children":[{"level":3,"heading":"With ない","content":"それだけではない。 Sore dake de wa nai. \"It''s not only that.\" 見るだけではダメだ。 Miru dake de wa dame da. \"Just looking is not enough.\" 一人だけでは無理だ。 Hitori dake de wa muri da. \"It''s impossible with just one person.\""}]},{"level":2,"heading":"Comparison: だけ vs しか","content":"","children":[{"level":3,"heading":"だけ - Neutral limitation (can be positive or negative)","content":"水だけ飲んだ。 Mizu dake nonda. \"I drank only water.\" [Neutral statement] 三つだけ買った。 Mittsu dake katta. \"I bought only three.\" [Neutral]"},{"level":3,"heading":"しか - Must use negative, emphasizes insufficiency","content":"水しか飲まなかった。 Mizu shika nomanakatta. \"I drank nothing but water.\" [Emphasis on lack] 三つしか買えなかった。 Mittsu shika kaenakatta. \"I could only buy three.\" [Not enough] See: [しか (Only - negative)](/docs/shika-detail)"}]},{"level":2,"heading":"Formal Expressions","content":"","children":[{"level":3,"heading":"In formal writing","content":"これだけのことです。 Kore dake no koto desu. \"It''s only this matter.\" それだけでございます。 Sore dake de gozaimasu. \"That is all.\" [Very polite] 一点だけ質問があります。 Itten dake shitsumon ga arimasu. \"I have just one question.\""}]},{"level":2,"heading":"Set Phrases","content":"","children":[{"level":3,"heading":"Common expressions","content":"今だけ！ Ima dake! \"Only now!\" [Limited time] これだけ？ Kore dake? \"Is this all?\" / \"Only this?\" それだけ。 Sore dake. \"That''s all.\" / \"That''s it.\" だけど (dakedo) \"But...\" / \"However...\" [Conjunction] ただそれだけ Tada sore dake \"Just that, that''s all\""}]},{"level":2,"heading":"Casual Speech","content":"","children":[{"level":3,"heading":"Shortened forms","content":"それだけ → それだけ (no shortening) 見るだけ → 見るだけ (no shortening) But casual context: これだけでいい？ Kore dake de ii? \"Is just this okay?\""}]},{"level":2,"heading":"Multiple Limitations","content":"","children":[{"level":3,"heading":"Stacking だけ","content":"今日だけ、ここだけ、あなただけ。 Kyou dake, koko dake, anata dake. \"Only today, only here, only you.\" 一つだけ、少しだけ。 Hitotsu dake, sukoshi dake. \"Just one, just a little.\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using だけ with Negative Like しか","content":"❌ 水だけ飲まなかった (confusing - sounds odd) ✓ 水だけ飲んだ (positive with だけ) ✓ 水しか飲まなかった (negative with しか)\n\nだけ is typically neutral; use しか for negative emphasis."},{"level":3,"heading":"Mistake 2: Wrong Word Order","content":"❌ だけこれです ✓ これだけです\n\nだけ follows the word it limits."},{"level":3,"heading":"Mistake 3: Redundant Particles","content":"❌ 水をだけを飲む ✓ 水だけを飲む or 水だけ飲む\n\nDon''t double particles unnecessarily."},{"level":3,"heading":"Mistake 4: Confusing だけに Meanings","content":"Context determines meaning:\n\n高いだけに (precisely because it''s expensive) 彼だけに (only to him/only for him)\n\nPay attention to context."}]},{"level":2,"heading":"Summary","content":"Key Points:\n\nだけ means \"only,\" \"just,\" \"merely\" Indicates limitation to what''s mentioned Structure: [Word] + だけ Common patterns: だけで (with just), だけに (precisely because), できるだけ (as much as possible) Verb + だけ + Verb (as much as you want/can) Combines with particles: だけは, だけが, だけを Neutral tone (unlike しか which requires negative) Essential for expressing limitations and extent","children":[]}]'::jsonb,
  '["\"only\"","\"just\"","\"merely\"","\"as much as\"","\"to the extent of\"","高いだけに (precisely because it''s expensive)","彼だけに (only to him/only for him)","だけ means \"only,\" \"just,\" \"merely\"","Indicates limitation to what''s mentioned","Structure: [Word] + だけ","Common patterns: だけで (with just), だけに (precisely because), できるだけ (as much as possible)","Verb + だけ + Verb (as much as you want/can)","Combines with particles: だけは, だけが, だけを","Neutral tone (unlike しか which requires negative)","Essential for expressing limitations and extent"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'dake-extent',
  '〜だけ (As much as)',
  'https://practice-japanese.com/docs/dake-extent/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 〜だけ?","content":"〜だけ (dake) expresses limitation or extent, meaning \"only,\" \"just,\" or \"as much as.\" It indicates that something is limited to a specific amount, degree, or scope.\n\nEnglish equivalent: \"only,\" \"just,\" \"as much as,\" \"to the extent that\"\n\nStructure: [Noun/Verb/Adjective] + だけ\n\nこれ (kore - this) + だけ = これだけ \"only this\" / \"just this\" 食べる (taberu - eat) + だけ = 食べるだけ \"only eat\" / \"just eat\" できる (dekiru - can do) + だけ = できるだけ \"as much as possible\""},{"level":2,"heading":"Basic Formation","content":"","children":[{"level":3,"heading":"With nouns","content":"[Noun] + だけ\n\nこれだけ (kore dake) - \"only this\" 一人だけ (hitori dake) - \"only one person\" 日本語だけ (nihongo dake) - \"only Japanese\" 今日だけ (kyou dake) - \"only today\""},{"level":3,"heading":"With verbs (dictionary form)","content":"[Verb dictionary form] + だけ\n\n見るだけ (miru dake) - \"just look\" 食べるだけ (taberu dake) - \"just eat\" 聞くだけ (kiku dake) - \"just listen\""},{"level":3,"heading":"With quantities","content":"[Number/Amount] + だけ\n\n三つだけ (mittsu dake) - \"only three\" 少しだけ (sukoshi dake) - \"just a little\" これだけ (kore dake) - \"only this much\""}]},{"level":2,"heading":"Expressing \"Only\"","content":"","children":[{"level":3,"heading":"Limitation","content":"これだけください。 Kore dake kudasai. \"Just this, please.\" 一人だけ来た。 Hitori dake kita. \"Only one person came.\" 日本語だけ話せます。 Nihongo dake hanasemasu. \"I can speak only Japanese.\" 今日だけ休みます。 Kyou dake yasumimasu. \"I''ll rest only today.\""}]},{"level":2,"heading":"できるだけ (As much as possible)","content":"","children":[{"level":3,"heading":"Maximum effort","content":"できるだけ早く来てください。 Dekiru dake hayaku kite kudasai. \"Please come as soon as possible.\" できるだけ頑張ります。 Dekiru dake ganbarimasu. \"I''ll try as hard as possible.\" できるだけ安く買いたい。 Dekiru dake yasuku kaitai. \"I want to buy it as cheaply as possible.\""}]},{"level":2,"heading":"Verb + だけ (Just doing)","content":"","children":[{"level":3,"heading":"Simple action","content":"見るだけ。 Miru dake. \"Just looking.\" 聞くだけでいい。 Kiku dake de ii. \"Just listening is fine.\" 食べるだけ食べた。 Taberu dake tabeta. \"I ate as much as I wanted.\" できるだけやってみる。 Dekiru dake yatte miru. \"I''ll try as much as I can.\""}]},{"level":2,"heading":"Quantity Limits","content":"","children":[{"level":3,"heading":"Specific amounts","content":"三つだけください。 Mittsu dake kudasai. \"Just three, please.\" 少しだけ待ってください。 Sukoshi dake matte kudasai. \"Please wait just a little.\" もう少しだけ時間がある。 Mou sukoshi dake jikan ga aru. \"There''s just a little more time.\""}]},{"level":2,"heading":"それだけ (That''s all/Just that)","content":"","children":[{"level":3,"heading":"Common expression","content":"それだけです。 Sore dake desu. \"That''s all.\" それだけ？ Sore dake? \"Is that all?\" それだけで十分だ。 Sore dake de juubun da. \"That alone is enough.\" それだけのことだ。 Sore dake no koto da. \"It''s just that.\""}]},{"level":2,"heading":"だけで (Only with/Just by)","content":"","children":[{"level":3,"heading":"Means or method","content":"これだけで大丈夫。 Kore dake de daijoubu. \"This alone is okay.\" 見るだけで分かる。 Miru dake de wakaru. \"You can tell just by looking.\" 聞いただけで嬉しい。 Kiita dake de ureshii. \"I''m happy just hearing it.\""}]},{"level":2,"heading":"Comparative Extent","content":"","children":[{"level":3,"heading":"Proportional relationships","content":"勉強しただけ成績が上がる。 Benkyou shita dake seiseki ga agaru. \"Grades improve as much as you study.\" 頑張っただけ結果が出る。 Ganbatta dake kekka ga deru. \"Results come out as much as you try hard.\" 食べただけ太る。 Tabeta dake futoru. \"You gain weight as much as you eat.\""}]},{"level":2,"heading":"Negative Context","content":"","children":[{"level":3,"heading":"Nothing but/mere","content":"見ただけ。 Mita dake. \"I just looked.\" (didn''t do anything else) 名前を知っているだけだ。 Namae wo shitte iru dake da. \"I only know the name.\" 話を聞いただけです。 Hanashi wo kiita dake desu. \"I just heard the story.\""}]},{"level":2,"heading":"だけど/だけれど","content":"","children":[{"level":3,"heading":"\"But\" after だけ","content":"好きなだけ食べていいけど、太るよ。 Suki na dake tabete ii kedo, futoru yo. \"You can eat as much as you like, but you''ll gain weight.\" 一つだけど、高い。 Hitotsu dake do, takai. \"It''s only one, but it''s expensive.\"\n\nNote: This is だけ + けど/けれど, not a single grammar pattern."}]},{"level":2,"heading":"Polite Forms","content":"これだけでお願いします。 Kore dake de onegai shimasu. \"Just this, please.\" 少しだけお時間をいただけますか。 Sukoshi dake ojikan wo itadakemasu ka. \"Could I have just a little of your time?\" 今日だけお休みをいただきます。 Kyou dake oyasumi wo itadakimasu. \"I''ll take just today off.\""},{"level":2,"heading":"Common Expressions","content":"それだけ (sore dake) - \"that''s all\" / \"just that\" これだけ (kore dake) - \"only this\" できるだけ (dekiru dake) - \"as much as possible\" 〜だけで (dake de) - \"just by/with\" 少しだけ (sukoshi dake) - \"just a little\" 今日だけ (kyou dake) - \"only today\" 一人だけ (hitori dake) - \"only one person\" 見るだけ (miru dake) - \"just look\""},{"level":2,"heading":"Comparing だけ and しか","content":"","children":[{"level":3,"heading":"Only (inclusive) vs only (exclusive)","content":"だけ - Positive nuance:\n\nこれだけ買った。 Kore dake katta. \"I bought only this.\" (neutral/positive)\n\nしか - Negative nuance (requires negative verb):\n\nこれしか買わなかった。 Kore shika kawanakatta. \"I bought only this.\" (emphasizes limitation)\n\n| Pattern | Verb form | Nuance | | ------- | --------- | ---------------- | | だけ | Any | Neutral | | しか | Negative | Emphasizes limit |"}]},{"level":2,"heading":"Emphasizing Small Amount","content":"","children":[{"level":3,"heading":"Just a bit","content":"ちょっとだけ待って。 Chotto dake matte. \"Wait just a moment.\" 少しだけ食べた。 Sukoshi dake tabeta. \"I ate just a little.\" 一口だけ味見した。 Hitokuchi dake ajimi shita. \"I tasted just one bite.\""}]},{"level":2,"heading":"Maximum Capacity","content":"","children":[{"level":3,"heading":"To the limit","content":"飲めるだけ飲んだ。 Nomeru dake nonda. \"I drank as much as I could.\" 持てるだけ持った。 Moteru dake motta. \"I carried as much as I could hold.\" できるだけのことをした。 Dekiru dake no koto wo shita. \"I did everything I could.\""}]},{"level":2,"heading":"Time Expressions","content":"","children":[{"level":3,"heading":"Duration limits","content":"今だけ。 Ima dake. \"Only now.\" 今日だけは許す。 Kyou dake wa yurusu. \"I''ll forgive you just for today.\" 一週間だけ待つ。 Isshuukan dake matsu. \"I''ll wait only one week.\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using だけ with negative when しか is better","content":"For emphasizing limitation negatively, しか is more natural:\n\n△ 一つだけ持っていない ✓ 一つしか持っていない"},{"level":3,"heading":"Mistake 2: Wrong particle order","content":"❌ だけを三つ ✓ 三つだけ(を)\n\nだけ comes after the noun/number."},{"level":3,"heading":"Mistake 3: Confusing だけど and だけ","content":"だけ = only だけど = だけ + けど (but) 一つだけ = only one 一つだけど = only one, but..."},{"level":3,"heading":"Mistake 4: Wrong position","content":"❌ だけこれ ✓ これだけ\n\nだけ comes after what it modifies."}]},{"level":2,"heading":"Summary","content":"Key Points:\n\nだけ expresses \"only,\" \"just,\" or \"as much as\" Formation: [Noun/Verb/Quantity] + だけ できるだけ = \"as much as possible\" だけで = \"just by/with\" Verb + だけ = proportional relationship Neutral nuance (unlike しか) それだけ = \"that''s all\" Essential for expressing limits and extent","children":[]}]'::jsonb,
  '["だけ expresses \"only,\" \"just,\" or \"as much as\"","Formation: [Noun/Verb/Quantity] + だけ","できるだけ = \"as much as possible\"","だけで = \"just by/with\"","Verb + だけ = proportional relationship","Neutral nuance (unlike しか)","それだけ = \"that''s all\"","Essential for expressing limits and extent"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'dare',
  '誰 (dare) - Who',
  'https://practice-japanese.com/docs/dare/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 誰 (dare)?","content":"誰 (だれ - dare) means \"who\" in Japanese. It''s used to ask about people''s identity or to inquire who is performing an action.\n\n誰ですか？ \"Who is it?\" 誰が来ますか？ \"Who will come?\" 誰と行きますか？ \"Who will you go with?\""},{"level":2,"heading":"Basic Usage","content":"","children":[{"level":3,"heading":"Asking Identity","content":"誰ですか？ \"Who is it?\" あの人は誰ですか？ \"Who is that person?\" これは誰の本ですか？ \"Whose book is this?\""},{"level":3,"heading":"Asking Who Does Something","content":"誰が来ますか？ \"Who will come?\" 誰が言いましたか？ \"Who said it?\" 誰がやりましたか？ \"Who did it?\""}]},{"level":2,"heading":"With Particles","content":"","children":[{"level":3,"heading":"が (subject marker)","content":"When asking who is the subject:\n\n誰が来ますか？ \"Who will come?\" 誰が好きですか？ \"Who do you like?\" 誰がしましたか？ \"Who did it?\""},{"level":3,"heading":"を (object marker)","content":"When asking about the object:\n\n誰を待っていますか？ \"Who are you waiting for?\" 誰を見ましたか？ \"Who did you see?\" 誰を呼びますか？ \"Who will you call?\""},{"level":3,"heading":"と (with)","content":"Asking who someone does something with:\n\n誰と行きますか？ \"Who will you go with?\" 誰と話していますか？ \"Who are you talking with?\" 誰と住んでいますか？ \"Who do you live with?\""},{"level":3,"heading":"に (to/for)","content":"Asking who receives an action:\n\n誰に会いますか？ \"Who will you meet?\" 誰にあげますか？ \"Who will you give it to?\" 誰に聞きますか？ \"Who will you ask?\""},{"level":3,"heading":"から (from)","content":"Asking who something comes from:\n\n誰から聞きましたか？ \"Who did you hear it from?\" 誰からもらいましたか？ \"Who did you receive it from?\" 誰から電話がありましたか？ \"Who called?\" (lit: from whom was there a call)"},{"level":3,"heading":"の (possession)","content":"Asking about ownership:\n\n誰のですか？ \"Whose is it?\" これは誰の本ですか？ \"Whose book is this?\" 誰のかばんですか？ \"Whose bag is it?\""}]},{"level":2,"heading":"Politeness: 誰 (dare) vs どなた (donata)","content":"","children":[{"level":3,"heading":"誰 (dare) - Neutral/Casual","content":"誰ですか？ \"Who is it?\" 誰が来る？ \"Who''s coming?\""},{"level":3,"heading":"どなた (donata) - Polite","content":"Used in formal situations or when showing respect:\n\nどなたですか？ \"Who is it?\" (polite) どなたがいらっしゃいますか？ \"Who is coming?\" (honorific + polite) 失礼ですが、どなたですか？ \"Excuse me, but who are you?\""}]},{"level":2,"heading":"Common Question Patterns","content":"","children":[{"level":3,"heading":"Basic Identity","content":"誰ですか？ \"Who is it?\" 彼は誰ですか？ \"Who is he?\" あの人は誰？ \"Who is that person?\""},{"level":3,"heading":"Asking About Actions","content":"誰が食べましたか？ \"Who ate it?\" 誰が来ますか？ \"Who will come?\" 誰がやりますか？ \"Who will do it?\""},{"level":3,"heading":"With Companions","content":"誰と行きますか？ \"Who will you go with?\" 誰と話していますか？ \"Who are you talking with?\" 誰と一緒ですか？ \"Who are you with?\""},{"level":3,"heading":"Asking for Help/Direction","content":"誰に聞けばいいですか？ \"Who should I ask?\" 誰に相談しますか？ \"Who will you consult?\""}]},{"level":2,"heading":"Common Expressions","content":"","children":[{"level":3,"heading":"誰か (dareka) - Someone/Anybody","content":"誰か来ましたか？ \"Did someone come?\" 誰かいますか？ \"Is anyone there?\" 誰か知っていますか？ \"Does anyone know?\""},{"level":3,"heading":"誰も〜ない (daremo~nai) - Nobody/No one","content":"誰もいない \"No one is here\" 誰も知らない \"Nobody knows\" 誰も来なかった \"Nobody came\""},{"level":3,"heading":"誰でも (daredemo) - Anyone/Everyone","content":"誰でもいい \"Anyone is fine\" 誰でもできる \"Anyone can do it\" 誰でも知っている \"Everyone knows\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Party","content":"A: パーティーに誰が来ますか？ \"Who will come to the party?\" B: 田中さんと山田さんが来ます \"Tanaka and Yamada will come\" A: 誰か他に来ますか？ \"Will anyone else come?\" B: たぶん鈴木さんも来ます \"Probably Suzuki will come too\""},{"level":3,"heading":"Example 2: Lost Item","content":"A: これは誰のですか？ \"Whose is this?\" B: わかりません \"I don''t know\" A: 誰か知っていますか？ \"Does anyone know?\" B: 誰も知らないと思います \"I don''t think anyone knows\""},{"level":3,"heading":"Example 3: Phone Call","content":"A: 誰から電話がありましたか？ \"Who called?\" B: 母から電話がありました \"My mother called\" A: 何時に電話がありましたか？ \"What time did she call?\" B: 3時頃です \"Around 3 o''clock\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using を when が is needed","content":"Wrong: 誰を来ますか？ ❌ Right: 誰が来ますか？ ✓ \"Who will come?\""},{"level":3,"heading":"Mistake 2: Forgetting particle","content":"Less natural: 誰行きますか？ ⚠️ Better: 誰が行きますか？ ✓ \"Who will go?\""},{"level":3,"heading":"Mistake 3: Using だれか in negative sentences","content":"Wrong: 誰か来ませんでした ❌ Right: 誰も来ませんでした ✓ \"Nobody came\""}]},{"level":2,"heading":"Asking About Multiple People","content":"","children":[{"level":3,"heading":"誰と誰 (who and who)","content":"誰と誰が来ますか？ \"Who and who will come?\" パーティーには誰と誰がいましたか？ \"Who all were at the party?\""},{"level":3,"heading":"何人 (how many people)","content":"Often used together with 誰:\n\n誰が来ますか？何人ですか？ \"Who will come? How many people?\""}]},{"level":2,"heading":"In Relative Clauses","content":"昨日会った人は誰ですか？ \"Who is the person you met yesterday?\" ここにいる人は誰ですか？ \"Who is the person here?\""},{"level":2,"heading":"Politeness Levels","content":"","children":[{"level":3,"heading":"Casual","content":"誰？ \"Who?\" 誰が来る？ \"Who''s coming?\" 誰と行く？ \"Who are you going with?\""},{"level":3,"heading":"Polite","content":"誰ですか？ \"Who is it?\" 誰が来ますか？ \"Who will come?\" 誰と行きますか？ \"Who will you go with?\""},{"level":3,"heading":"Very Polite","content":"どなたですか？ \"Who is it?\" (polite) どなたがいらっしゃいますか？ \"Who is coming?\" (honorific) どなたとご一緒ですか？ \"Who are you with?\" (very polite)"}]},{"level":2,"heading":"Special Patterns","content":"","children":[{"level":3,"heading":"誰のため (for whose sake)","content":"誰のためにしますか？ \"For whose sake will you do it?\" これは誰のためですか？ \"For whom is this?\""},{"level":3,"heading":"誰に対して (toward whom)","content":"誰に対して言いましたか？ \"To whom did you say it?\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic Questions","content":"誰ですか？ \"Who is it?\" あの人は誰ですか？ \"Who is that person?\" 誰が来ますか？ \"Who will come?\" 誰と行きますか？ \"Who will you go with?\""},{"level":3,"heading":"With Ownership","content":"これは誰のですか？ \"Whose is this?\" 誰の本ですか？ \"Whose book is it?\" 誰のかばんですか？ \"Whose bag is it?\""},{"level":3,"heading":"With Actions","content":"誰が言いましたか？ \"Who said it?\" 誰を待っていますか？ \"Who are you waiting for?\" 誰に会いましたか？ \"Who did you meet?\" 誰から聞きましたか？ \"Who did you hear it from?\""},{"level":3,"heading":"Common Phrases","content":"誰かいますか？ \"Is anyone there?\" 誰もいません \"No one is here\" 誰でもいいです \"Anyone is fine\" 誰か知っていますか？ \"Does anyone know?\""}]},{"level":2,"heading":"Related Grammar Points","content":"Question Words (5W1H) - Overview Particles - が, を, と, に usage どなた (donata) - Polite form 誰か/誰も/誰でも - Someone/nobody/anyone か (ka) - Question particle Yes/No Questions - Question formation","children":[]}]'::jsonb,
  '["Question Words (5W1H) - Overview","Particles - が, を, と, に usage","どなた (donata) - Polite form","誰か/誰も/誰でも - Someone/nobody/anyone","か (ka) - Question particle","Yes/No Questions - Question formation"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'darou-deshou',
  '〜だろう/でしょう (Probably)',
  'https://practice-japanese.com/docs/darou-deshou/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is 〜だろう/でしょう?","content":"〜だろう (casual) and 〜でしょう (polite) express probability, conjecture, or predictions, meaning \"probably,\" \"I think,\" \"likely,\" or \"I suppose.\" They show the speaker''s estimation about something.\n\n明日は晴れるだろう \"It will probably be sunny tomorrow\" 彼は来るでしょう \"He''ll probably come\" 難しいだろうね \"It''s probably difficult\""},{"level":2,"heading":"Formation","content":"","children":[{"level":3,"heading":"Pattern: Plain form + だろう/でしょう","content":"With verbs:\n\n行く → 行くだろう / 行くでしょう 来た → 来たでしょう 行かない → 行かないだろう\n\nWith い-adjectives:\n\n高い → 高いだろう / 高いでしょう 良い → 良いでしょう 高くない → 高くないだろう\n\nWith な-adjectives:\n\n便利 → 便利だろう / 便利でしょう 簡単 → 簡単でしょう\n\nWith nouns:\n\n学生 → 学生だろう / 学生でしょう 雨 → 雨だろう / 雨でしょう"}]},{"level":2,"heading":"だろう vs でしょう","content":"","children":[{"level":3,"heading":"だろう - Casual","content":"明日は雨だろう \"It''ll probably rain tomorrow\" Use with friends, family More masculine"},{"level":3,"heading":"でしょう - Polite","content":"明日は雨でしょう \"It will probably rain tomorrow\" Standard polite form Gender neutral"},{"level":3,"heading":"でしょ - Very casual","content":"明日は雨でしょ \"It''ll probably rain tomorrow, right?\" Super casual Rising intonation"}]},{"level":2,"heading":"Basic Usage","content":"","children":[{"level":3,"heading":"Making predictions","content":"明日は晴れるだろう \"It will probably be sunny tomorrow\" 彼は成功するでしょう \"He will probably succeed\" 試験は難しいだろう \"The exam will probably be difficult\""},{"level":3,"heading":"Expressing probability","content":"たぶん来るだろう \"He''ll probably come\" そうでしょう \"That''s probably so\" 10時頃につくでしょう \"We''ll probably arrive around 10\""},{"level":3,"heading":"Seeking agreement","content":"いいでしょう？ \"It''s good, right?\" そうでしょ？ \"Right?\" / \"I told you so\" 知ってるでしょう？ \"You know, right?\""}]},{"level":2,"heading":"Key Characteristics","content":"","children":[{"level":3,"heading":"Shows uncertainty","content":"Less certain than はずだ More certain than かもしれない Moderate probability (60-80%) Speaker''s estimation"},{"level":3,"heading":"Can soften statements","content":"Direct: 高い \"It''s expensive\" Softened: 高いでしょう \"It''s probably expensive\" Less assertive"}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Making Plans","content":"A: 明日、天気どうかな \"I wonder about tomorrow''s weather\" B: たぶん晴れるでしょう \"It''ll probably be sunny\" A: じゃあ、ピクニック行ける \"Then we can go on a picnic\""},{"level":3,"heading":"Example 2: Seeking Confirmation","content":"A: これ、正しいでしょう？ \"This is correct, right?\" B: うん、そうだと思う \"Yeah, I think so\" A: よかった \"Good\""},{"level":3,"heading":"Example 3: Prediction","content":"A: 彼、受かるかな \"I wonder if he''ll pass\" B: 頑張ってたから、受かるでしょう \"He was trying hard, so he''ll probably pass\" A: そうだといいね \"I hope so\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Confusing with かもしれない","content":"だろう: Probably (60-80% sure) 来るだろう \"He''ll probably come\" かもしれない: Might (30-50% sure) 来るかもしれない \"He might come\" Different probability levels!"},{"level":3,"heading":"Mistake 2: Using です before だろう","content":"Wrong: 学生ですだろう ❌ Right: 学生だろう ✓ Or: 学生でしょう ✓ Don''t use です with だろう!"},{"level":3,"heading":"Mistake 3: Wrong intonation","content":"でしょう↓ - Statement (probably) でしょう↑ - Question (right?) Intonation matters!"}]},{"level":2,"heading":"でしょう for Confirmation","content":"","children":[{"level":3,"heading":"Rising intonation (right?/isn''t it?)","content":"知ってるでしょう？ \"You know, right?\" そうでしょう？ \"Right?\" / \"See? I told you\" 言ったでしょう？ \"I told you, didn''t I?\" Seeking agreement"}]},{"level":2,"heading":"Polite Predictions","content":"","children":[{"level":3,"heading":"Formal でしょう","content":"会議は3時に始まるでしょう \"The meeting will probably start at 3\" 彼は来ないでしょう \"He probably won''t come\" それは難しいでしょう \"That will probably be difficult\""}]},{"level":2,"heading":"Past Conjecture","content":"","children":[{"level":3,"heading":"だっただろう/だったでしょう","content":"昨日は寒かっただろう \"It was probably cold yesterday\" 彼は知っていたでしょう \"He probably knew\" 大変だったでしょう \"It was probably tough\""}]},{"level":2,"heading":"Negative Forms","content":"","children":[{"level":3,"heading":"～ないだろう/でしょう","content":"来ないだろう \"He probably won''t come\" 高くないでしょう \"It probably isn''t expensive\" 簡単ではないだろう \"It''s probably not easy\""}]},{"level":2,"heading":"With Question Words","content":"","children":[{"level":3,"heading":"Adding uncertainty","content":"誰が来るでしょうか \"Who will come, I wonder?\" いつ終わるだろう \"When will it end, I wonder?\" どうなるでしょう \"What will happen?\""}]},{"level":2,"heading":"だろうか (I wonder)","content":"","children":[{"level":3,"heading":"Expressing doubt","content":"本当だろうか \"I wonder if it''s true\" 彼は来るだろうか \"I wonder if he''ll come\" うまくいくだろうか \"I wonder if it will go well\" Self-questioning"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"たぶん～だろう/でしょう","content":"たぶん来るでしょう \"He''ll probably come\" たぶん大丈夫だろう \"It''s probably okay\" Emphasizes probability"},{"level":3,"heading":"きっと～だろう/でしょう","content":"きっと成功するでしょう \"He''ll surely succeed\" きっといい結果が出るだろう \"We''ll surely get good results\" More certain than just だろう"},{"level":3,"heading":"おそらく～だろう/でしょう","content":"おそらく雨が降るでしょう \"It will probably rain\" おそらく来ないだろう \"He probably won''t come\" Formal, written"}]},{"level":2,"heading":"でしょ (Super Casual)","content":"","children":[{"level":3,"heading":"Very informal","content":"そうでしょ！ \"Right?!\" / \"See!\" 知ってたでしょ \"You knew, right?\" いいでしょ \"It''s good, right?\" With rising tone"}]},{"level":2,"heading":"Softening Statements","content":"","children":[{"level":3,"heading":"Making opinions less direct","content":"Direct: 難しい \"It''s difficult\" Softer: 難しいでしょう \"It''s probably difficult\" Direct: 間違いだ \"It''s wrong\" Softer: 間違いだろう \"It''s probably wrong\""}]},{"level":2,"heading":"In Formal Contexts","content":"","children":[{"level":3,"heading":"Business/Official","content":"結果は来週発表されるでしょう \"The results will probably be announced next week\" この提案は承認されるでしょう \"This proposal will probably be approved\" More formal tone"}]},{"level":2,"heading":"Expressing Sympathy","content":"","children":[{"level":3,"heading":"大変でしょう (That must be tough)","content":"忙しいでしょう \"You must be busy\" 疲れたでしょう \"You must be tired\" 大変でしょうね \"That must be tough\" Shows understanding"}]},{"level":2,"heading":"だろうと思う (I think probably)","content":"","children":[{"level":3,"heading":"Combining patterns","content":"来るだろうと思う \"I think he''ll probably come\" 成功するだろうと思います \"I think it will probably succeed\" Double probability marker"}]},{"level":2,"heading":"Rhetorical Questions","content":"","children":[{"level":3,"heading":"でしょう？for emphasis","content":"誰だって知ってるでしょう？ \"Everyone knows, right?\" 当然でしょう？ \"Of course, right?\" 分かるでしょう？ \"You understand, right?\""}]},{"level":2,"heading":"Comparing Certainty Levels","content":"","children":[{"level":3,"heading":"From least to most certain","content":"Least: かもしれない (might) ↓ だろう/でしょう (probably) ↓ と思う (I think) ↓ はずだ (should be) ↓ Most: にちがいない (must be)"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"明日は晴れるでしょう \"It will probably be sunny tomorrow\" 彼は来ないだろう \"He probably won''t come\" 難しいでしょうね \"It''s probably difficult\""},{"level":3,"heading":"Intermediate","content":"この映画は面白いでしょう。 みんなが褒めていましたから \"This movie is probably interesting. Because everyone was praising it\" 彼は今頃家に着いただろう \"He''s probably arrived home by now\" そうでしょう？だから言ったのに \"Right? That''s why I told you\""},{"level":3,"heading":"Advanced","content":"経済状況を考えると、今年の成長率は 低くなるでしょう。専門家も同じ意見です。 おそらく2%程度だろうと予想されています。 \"Considering the economic situation, this year''s growth rate will probably be low. Experts have the same opinion. It''s predicted to be probably around 2%.\" 彼の経験と能力を考えれば、 このプロジェクトを成功させるでしょう。 困難はあるだろうが、必ず乗り越えられると 信じています。 \"Considering his experience and ability, he will probably make this project succeed. There will probably be difficulties, but I believe he can definitely overcome them.\""}]},{"level":2,"heading":"Related Grammar Points","content":"〜かもしれない - Might (less certain) 〜はずだ - Should be (expected) 〜にちがいない - Must be (very certain) 〜と思う - I think だろうか - I wonder Probability expressions - Expressing likelihood","children":[]}]'::jsonb,
  '["〜かもしれない - Might (less certain)","〜はずだ - Should be (expected)","〜にちがいない - Must be (very certain)","〜と思う - I think","だろうか - I wonder","Probability expressions - Expressing likelihood"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'dasu',
  '〜出す (dasu) - Start doing',
  'https://practice-japanese.com/docs/dasu/',
  'other',
  NULL,
  '[{"level":2,"heading":"〜出す (dasu) - Start Doing","content":"〜出す attaches to verb stems to indicate the sudden or spontaneous beginning of an action. Unlike 始める which is neutral, 出す emphasizes that something starts abruptly or unexpectedly.\n\nVerb stem + 出す 走り出す - start running (suddenly) 泣き出す - start crying (suddenly) 降り出す - start falling (rain/snow) Emphasis: Sudden start Unexpected beginning"},{"level":2,"heading":"Formation","content":"","children":[{"level":3,"heading":"Basic pattern","content":"る-verbs: 食べる → 食べ出す \"start eating suddenly\" 見る → 見出す \"start watching suddenly\" う-verbs: 走る → 走り出す \"start running suddenly\" 泣く → 泣き出す \"start crying suddenly\" 降る → 降り出す \"start falling suddenly\" Irregular: する → し出す \"start doing suddenly\" 来る → 来出す (rare)"}]},{"level":2,"heading":"Core Meaning","content":"","children":[{"level":3,"heading":"Sudden beginning","content":"雨が降り出した \"It started raining (suddenly)\" Implies: Wasn''t raining, then suddenly was Not gradual, but abrupt start 子供が泣き出した \"The child started crying\" Implies: Sudden onset of crying Unexpected or quick start"}]},{"level":2,"heading":"Common Uses","content":"","children":[{"level":3,"heading":"Weather","content":"雨が降り出す \"rain starts falling\" 雪が降り出す \"snow starts falling\" 風が吹き出す \"wind starts blowing\" Natural phenomena Sudden weather changes Very common usage"},{"level":3,"heading":"Emotions","content":"泣き出す \"start crying\" 笑い出す \"start laughing\" 怒り出す \"start getting angry\" Emotional outbursts Sudden reactions"},{"level":3,"heading":"Physical actions","content":"走り出す \"start running\" 歩き出す \"start walking\" 動き出す \"start moving\" Sudden movement Beginning motion"},{"level":3,"heading":"Speech","content":"話し出す \"start talking\" 歌い出す \"start singing\" 叫び出す \"start shouting\" Beginning to speak/sing Often suddenly"}]},{"level":2,"heading":"Nuance: Sudden vs Gradual","content":"","children":[{"level":3,"heading":"Compare with 始める","content":"話し始める \"begin talking\" Neutral, planned 話し出す \"start talking (suddenly)\" Abrupt, unplanned 食べ始める \"begin eating\" Normal start 食べ出す \"start eating (suddenly)\" Unexpected start, perhaps rudely"}]},{"level":2,"heading":"Transitivity","content":"","children":[{"level":3,"heading":"Usually becomes intransitive","content":"Even if base verb is transitive: 読む (transitive) → 読み出す Often used intransitively: 本を読み出す (less common) 急に読み出した \"suddenly started reading\" The 出す compound emphasizes the action itself Object often omitted"}]},{"level":2,"heading":"Context Examples","content":"","children":[{"level":3,"heading":"Natural contexts","content":"会議中に笑い出した \"Started laughing during the meeting\" Implies: Inappropriate timing いきなり走り出した \"Suddenly started running\" Implies: Without warning 雨が降り出したから帰ろう \"It started raining so let''s go home\" Implies: Sudden weather change"}]},{"level":2,"heading":"With Different Verbs","content":"","children":[{"level":3,"heading":"Movement verbs","content":"飛び出す \"jump out, dash out\" 飛び込む (base) → 飛び出す Burst out suddenly 転がり出す \"start rolling\" Roll and exit/emerge"},{"level":3,"heading":"Discovery/realization","content":"見出す \"discover, find out\" Special meaning here Not just \"start seeing\" But \"discover\" or \"recognize value\" 才能を見出す \"discover talent\""},{"level":3,"heading":"Beginning processes","content":"動き出す \"start moving\" (machines, vehicles) 車が動き出した \"The car started moving\" 機械が動き出す \"The machine starts operating\" Mechanical or large-scale movement"}]},{"level":2,"heading":"Spontaneity Element","content":"","children":[{"level":3,"heading":"Uncontrolled or natural","content":"笑い出す Often can''t help it Spontaneous laughter 泣き出す Bursting into tears Not planned crying Implies lack of full control Natural, spontaneous reaction"}]},{"level":2,"heading":"Time Frame","content":"","children":[{"level":3,"heading":"Immediate beginning","content":"Focus on the moment action starts: 急に降り出した \"Suddenly started falling\" The moment it began Not the continuation Emphasizes start point"}]},{"level":2,"heading":"Common Collocations","content":"","children":[{"level":3,"heading":"Frequent combinations","content":"降り出す (rain/snow) Most common with weather 泣き出す (crying) Very common emotional 走り出す (running) Common physical action 笑い出す (laughing) Common reaction These are natural, established Learn as set phrases"}]},{"level":2,"heading":"Conjugation","content":"","children":[{"level":3,"heading":"Regular る-verb conjugation","content":"降り出す: Present: 降り出す Past: 降り出した Negative: 降り出さない Te-form: 降り出して Potential: 降り出せる Conjugates like any る-verb Second verb determines conjugation"}]},{"level":2,"heading":"In Sentences","content":"","children":[{"level":3,"heading":"Example patterns","content":"Noun が Verb-出す: 雨が降り出した \"Rain started falling\" 子供が泣き出した \"Child started crying\" いきなり/急に + Verb-出す: いきなり走り出した \"Suddenly started running\" 急に笑い出した \"Suddenly started laughing\" Emphasizing suddenness Natural sentence patterns"}]},{"level":2,"heading":"Formality","content":"","children":[{"level":3,"heading":"Neutral formality","content":"Can be used in: - Casual conversation - Formal speech - Writing Formality comes from: - Conjugation style - Context - Surrounding grammar Not inherently casual or formal"}]},{"level":2,"heading":"Common Mistakes","content":"❌ Using for planned actions ✗ 明日から勉強し出す (use 始める instead) \"Start studying from tomorrow\" ✓ Use for spontaneous actions ✓ 急に勉強し出した \"Suddenly started studying\" ❌ Overusing where 始める fits Choose based on nuance ✓ Match verb to context Sudden → 出す Neutral → 始める"},{"level":2,"heading":"Special Meanings","content":"","children":[{"level":3,"heading":"Some compounds have unique meanings","content":"見出す Not \"start seeing\" But \"discover, find\" 思い出す Not \"start thinking\" But \"remember, recall\" These are lexicalized Learn as separate vocabulary Different from regular pattern"}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'de-aru',
  'である (de aru) - Formal Copula',
  'https://practice-japanese.com/docs/de-aru/',
  'other',
  NULL,
  '[{"level":2,"heading":"What is である (de aru)?","content":"である (de aru) is a formal copula used primarily in written Japanese. It''s more formal than だ and commonly found in newspapers, academic papers, official documents, and formal speeches.\n\nA は B である \"A is B\" これは重要な問題である \"This is an important problem\""},{"level":2,"heading":"Formation","content":"である comes from the combination of:\n\nで (de) - te-form of だ ある (aru) - to exist/be"},{"level":2,"heading":"Basic Usage","content":"","children":[{"level":3,"heading":"With Nouns","content":"彼は学生である \"He is a student\" これは事実である \"This is a fact\" 富士山は日本の象徴である \"Mt. Fuji is a symbol of Japan\" 問題の原因は不明である \"The cause of the problem is unknown\""},{"level":3,"heading":"With Na-adjectives","content":"この方法は有効である \"This method is effective\" その結果は明らかである \"The result is clear\" この問題は重要である \"This problem is important\" 彼の態度は不適切である \"His attitude is inappropriate\""}]},{"level":2,"heading":"When to Use である","content":"","children":[{"level":3,"heading":"Written Documents","content":"Primary use is in formal written contexts.\n\n報告書: この計画は実現可能である \"Report: This plan is feasible\" 論文: これは重要な発見である \"Paper: This is an important discovery\" 契約書: 以下の条件が適用である \"Contract: The following conditions apply\""},{"level":3,"heading":"Newspapers","content":"Very common in newspaper articles.\n\n今日は晴天である \"Today is clear weather\" 事故の原因は調査中である \"The cause of the accident is under investigation\" この政策は効果的である \"This policy is effective\""},{"level":3,"heading":"Academic Writing","content":"Standard in academic and scholarly texts.\n\nこの理論は正しい \"This theory is correct\" 結論は明白である \"The conclusion is obvious\" この仮説は証明可能である \"This hypothesis is provable\""},{"level":3,"heading":"Formal Speeches","content":"Used in formal public speaking.\n\n我が国は平和国家である \"Our country is a peaceful nation\" これは重大な決定である \"This is a significant decision\""}]},{"level":2,"heading":"Tense Forms","content":"","children":[{"level":3,"heading":"Present/Non-past","content":"これは事実である \"This is a fact\" 彼は医者である \"He is a doctor\""},{"level":3,"heading":"Past: であった","content":"これは事実であった \"This was a fact\" 彼は学生であった \"He was a student\" その結果は予想外であった \"The result was unexpected\""},{"level":3,"heading":"Negative: ではない","content":"これは事実ではない \"This is not a fact\" 簡単ではない \"It is not simple\" 彼は学生ではない \"He is not a student\""},{"level":3,"heading":"Past Negative: ではなかった","content":"これは事実ではなかった \"This was not a fact\" 彼は学生ではなかった \"He was not a student\""}]},{"level":2,"heading":"Compared to Other Copulas","content":"","children":[{"level":3,"heading":"である vs だ vs です","content":"Plain (だ): 彼は学生だ Formal Written (である): 彼は学生である Polite Spoken (です): 彼は学生です All mean: \"He is a student\""},{"level":3,"heading":"Formality Scale","content":"From casual to most formal:\n\n1. だ - Casual 2. です - Polite 3. である - Formal written 4. でございます - Extremely polite 学生だ → 学生です → 学生である → 学生でございます"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"～であるが (however)","content":"これは事実であるが、証明は難しい \"This is a fact, however, proving it is difficult\" 便利であるが、高価である \"It''s convenient, but expensive\""},{"level":3,"heading":"～であるから (because)","content":"彼は専門家であるから、信頼できる \"Because he is an expert, he can be trusted\" 重要であるから、慎重に扱うべきだ \"Because it''s important, it should be handled carefully\""},{"level":3,"heading":"～であると思う/考える","content":"これは正しいであると思う \"I think this is correct\" 彼は有能であると考える \"I believe he is competent\""},{"level":3,"heading":"～である必要がある (need to be)","content":"この問題は解決される必要がある \"This problem needs to be solved\" より効率的である必要がある \"It needs to be more efficient\""}]},{"level":2,"heading":"Variations and Related Forms","content":"","children":[{"level":3,"heading":"であろう (probably)","content":"Presumptive form - formal version of だろう.\n\nこれは事実であろう \"This is probably a fact\" 彼は学生であろう \"He is probably a student\" 有効であろう \"It will probably be effective\""},{"level":3,"heading":"であれば (if it is)","content":"Conditional form.\n\n学生であれば、割引が適用される \"If you are a student, a discount applies\" 可能であれば、参加したい \"If possible, I''d like to participate\""},{"level":3,"heading":"であって (being and)","content":"Te-form for connecting clauses.\n\nこれは事実であって、否定できない \"This is a fact and cannot be denied\" 重要であって、緊急である \"It''s important and urgent\""},{"level":3,"heading":"であり (being and)","content":"Another connective form.\n\n彼は教師であり、研究者である \"He is a teacher and a researcher\" これは問題であり、課題である \"This is a problem and a challenge\""}]},{"level":2,"heading":"In Different Contexts","content":"","children":[{"level":3,"heading":"Newspaper Headlines","content":"経済成長率は３％である \"Economic growth rate is 3%\" 気温は過去最高である \"Temperature is the highest on record\" 原因は不明である \"The cause is unknown\""},{"level":3,"heading":"Academic Papers","content":"この方法は効果的である \"This method is effective\" 結果は以下の通りである \"The results are as follows\" この理論は実証可能である \"This theory is verifiable\""},{"level":3,"heading":"Official Announcements","content":"新しい規則は明日から有効である \"The new rules are effective from tomorrow\" この決定は最終的である \"This decision is final\" 参加は任意である \"Participation is voluntary\""},{"level":3,"heading":"Legal Documents","content":"本契約は有効である \"This contract is valid\" 以下の条項が適用である \"The following terms apply\" 違反者は処罰の対象である \"Violators are subject to punishment\""}]},{"level":2,"heading":"Common Expressions","content":"","children":[{"level":3,"heading":"～とは何であるか (What is~?)","content":"愛とは何であるか \"What is love?\" 正義とは何であるか \"What is justice?\""},{"level":3,"heading":"～であるべき (should be)","content":"リーダーであるべき資質 \"Qualities one should have as a leader\" 守るべき規則である \"It is a rule that should be followed\""},{"level":3,"heading":"～であるがゆえに (precisely because)","content":"専門家であるがゆえに理解できる \"Can understand precisely because being an expert\" 複雑であるがゆえに興味深い \"Interesting precisely because it''s complex\""}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Mistake 1: Using である in casual conversation","content":"Wrong (casual context): 今日は月曜日である ❌ Right: 今日は月曜日だ ✓ \"Today is Monday\""},{"level":3,"heading":"Mistake 2: Using である in polite speech","content":"Wrong (to superiors): 学生である ❌ Right: 学生です ✓ \"I am a student\""},{"level":3,"heading":"Mistake 3: Using である with i-adjectives","content":"Wrong: 大きいである ❌ Right: 大きい ✓ \"It is big\" Note: I-adjectives don''t use copulas"},{"level":3,"heading":"Mistake 4: Overusing in informal writing","content":"Too formal: 昨日は楽しかったである Natural: 昨日は楽しかった \"Yesterday was fun\""}]},{"level":2,"heading":"Conversion Examples","content":"","children":[{"level":3,"heading":"Casual → Formal Written","content":"Casual: これは本だ Formal: これは本である \"This is a book\" Casual: 彼は学生だった Formal: 彼は学生であった \"He was a student\" Casual: 簡単じゃない Formal: 簡単ではない \"It''s not simple\""},{"level":3,"heading":"Polite Spoken → Formal Written","content":"Spoken: これは重要です Written: これは重要である \"This is important\" Spoken: 結果は明らかです Written: 結果は明らかである \"The result is clear\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic Statements","content":"これは事実である \"This is a fact\" 彼は専門家である \"He is an expert\" 日本は島国である \"Japan is an island nation\" この方法は有効である \"This method is effective\""},{"level":3,"heading":"Past Tense","content":"それは誤りであった \"That was an error\" 彼は学生であった \"He was a student\" 結果は予想外であった \"The result was unexpected\""},{"level":3,"heading":"Negative Forms","content":"これは真実ではない \"This is not the truth\" 簡単ではない \"It is not simple\" 彼の意見は正しくはない \"His opinion is not correct\""},{"level":3,"heading":"Complex Sentences","content":"日本は技術大国であり、経済大国である \"Japan is a technological powerhouse and an economic power\" この問題は複雑であるが、解決可能である \"This problem is complex, but solvable\" 教育は重要であり、必要不可欠である \"Education is important and indispensable\" 彼は優秀な学生であって、将来有望である \"He is an excellent student and has a promising future\""}]},{"level":2,"heading":"With Nominalizers","content":"","children":[{"level":3,"heading":"こと + である","content":"重要なことである \"It is an important matter\" 知っておくべきことである \"It is something one should know\" 忘れてはならないことである \"It is something that must not be forgotten\""},{"level":3,"heading":"もの + である","content":"これは私のものである \"This is mine\" 誰のものであるか \"Whose it is\""}]},{"level":2,"heading":"Stylistic Notes","content":"","children":[{"level":3,"heading":"Register Differences","content":"である is rarely used in:\n\nCasual conversation Personal emails Text messages Social media posts Daily communication\n\nである is appropriate for:\n\nAcademic papers News articles Official reports Business documents Formal presentations Legal documents"},{"level":3,"heading":"Modern Usage Trends","content":"In contemporary Japanese, である is being used less frequently even in written contexts, with です becoming more common in some formal writing.\n\nTraditional formal: これは重要である Modern formal: これは重要です \"This is important\""}]},{"level":2,"heading":"Transitioning Between Styles","content":"","children":[{"level":3,"heading":"Newspaper Style Mixing","content":"Newspapers often mix である with other forms within the same article:\n\n事故が発生した。原因は不明である。警察は調査している。 \"An accident occurred. The cause is unknown. Police are investigating.\""},{"level":3,"heading":"Academic Writing","content":"Academic papers maintain consistent である usage:\n\nこの研究は重要である。方法は以下の通りである。結果は明白である。 \"This research is important. The method is as follows. The results are clear.\""}]},{"level":2,"heading":"Related Grammar Points","content":"だ (da) - Plain copula です (desu) - Polite copula でございます - Very polite copula じゃない (janai) - Negative forms Formal & Written Style - である style writing Newspaper Japanese - Common である usage Academic Writing - Scholarly contexts","children":[]}]'::jsonb,
  '["で (de) - te-form of だ","ある (aru) - to exist/be","Casual conversation","Personal emails","Text messages","Social media posts","Daily communication","Academic papers","News articles","Official reports","Business documents","Formal presentations","Legal documents","だ (da) - Plain copula","です (desu) - Polite copula","でございます - Very polite copula","じゃない (janai) - Negative forms","Formal & Written Style - である style writing","Newspaper Japanese - Common である usage","Academic Writing - Scholarly contexts"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'dearu-style',
  'である Style',
  'https://practice-japanese.com/docs/dearu-style/',
  'other',
  NULL,
  '[{"level":2,"heading":"である Style","content":"である style (だ・である調) is a formal, assertive writing style used in academic papers, newspapers, essays, and formal documents. It''s more objective and authoritative than です・ます style.\n\nCasual: 学生だ Polite: 学生です Formal written: 学生である である = formal, written Assertive tone Objective stance"},{"level":2,"heading":"Basic Formation","content":"","children":[{"level":3,"heading":"Copula patterns","content":"Noun/な-adjective + である これは本である \"This is a book\" 彼は学生である \"He is a student\" 重要である \"Is important\" 便利である \"Is convenient\" Declarative Assertive Formal register"}]},{"level":2,"heading":"Negative Form","content":"","children":[{"level":3,"heading":"ではない","content":"これは本ではない \"This is not a book\" 学生ではない \"Is not a student\" 重要ではない \"Is not important\" Formal negative Written style Objective tone"}]},{"level":2,"heading":"Past Tense","content":"","children":[{"level":3,"heading":"であった","content":"彼は学生であった \"He was a student\" 重要であった \"Was important\" 問題であった \"Was a problem\" Past declarative Formal past Historical statements"}]},{"level":2,"heading":"With Verbs","content":"","children":[{"level":3,"heading":"Plain form + のである","content":"彼は来るのである \"He will come\" (emphatic) これが理由なのである \"This is the reason\" (emphatic) そうなのである \"That is so\" (emphatic) Explanatory Emphatic assertion Written emphasis"}]},{"level":2,"heading":"である vs です","content":"","children":[{"level":3,"heading":"Key differences","content":"です: - Polite conversational - Spoken language - Respectful to listener - Business, daily use である: - Formal written - Assertive, objective - Academic, newspapers - No direct addressee Different contexts Different registers"}]},{"level":2,"heading":"In Academic Writing","content":"","children":[{"level":3,"heading":"Standard usage","content":"この研究は重要である \"This research is important\" 結果は明らかである \"The results are clear\" 問題は複雑である \"The problem is complex\" Scholarly tone Objective stance Authoritative voice"}]},{"level":2,"heading":"Newspaper Style","content":"","children":[{"level":3,"heading":"Journalistic writing","content":"政府は発表した。新政策である \"Government announced. It''s a new policy\" 事故は深刻である \"The accident is serious\" 原因は不明である \"The cause is unknown\" News reporting Factual tone Objective reporting"}]},{"level":2,"heading":"Essay Writing","content":"","children":[{"level":3,"heading":"Opinion pieces","content":"教育は重要である \"Education is important\" この問題は深刻である \"This problem is serious\" 変化が必要である \"Change is necessary\" Argumentative writing Thesis statements Formal essays"}]},{"level":2,"heading":"Mixing Styles (Usually Avoided)","content":"","children":[{"level":3,"heading":"Consistency important","content":"❌ Inconsistent: これは本である。それは便利です。 ✓ Consistent: これは本である。それは便利である。 OR: これは本です。それは便利です。 Maintain one style Don''t mix である and です Professional appearance"}]},{"level":2,"heading":"であろう Form","content":"","children":[{"level":3,"heading":"Speculation/probability","content":"彼は学生であろう \"He is probably a student\" 重要であろう \"It will probably be important\" 問題であろう \"It will probably be a problem\" Formal speculation Written conjecture Scholarly hedging"}]},{"level":2,"heading":"である + と思う","content":"","children":[{"level":3,"heading":"Opinion expression","content":"これは重要であると思う \"I think this is important\" 問題であると考える \"I consider it a problem\" 必要であると言える \"One can say it''s necessary\" Formal opinion Academic hedging Objective assertion"}]},{"level":2,"heading":"Adverbial である","content":"","children":[{"level":3,"heading":"〜であり","content":"これは重要であり、必要である \"This is important and necessary\" 彼は学生であり、研究者である \"He is a student and a researcher\" Connecting clauses Listing qualities Formal conjunction"}]},{"level":2,"heading":"Conditional である","content":"","children":[{"level":3,"heading":"であれば","content":"学生であれば割引がある \"If (you''re) a student, there''s a discount\" 重要であれば対処する \"If important, will address it\" Formal conditional Written hypothesis Academic reasoning"}]},{"level":2,"heading":"Causal である","content":"","children":[{"level":3,"heading":"であるから/であるので","content":"重要であるから注目すべきだ \"Because it''s important, should pay attention\" 学生であるので割引を受ける \"Because (I''m) a student, receive discount\" Formal causation Logical reasoning Academic argument"}]},{"level":2,"heading":"Classical Influence","content":"","children":[{"level":3,"heading":"Literary heritage","content":"である comes from: にてある (classical) → である (modern) Classical roots Literary tradition Formal register"}]},{"level":2,"heading":"In Questions (Rare)","content":"","children":[{"level":3,"heading":"であるか","content":"これは問題であるか \"Is this a problem?\" 重要であるか \"Is it important?\" Rhetorical questions Formal inquiry Not common in questions Usually statements"}]},{"level":2,"heading":"With Adjectives","content":"","children":[{"level":3,"heading":"である after な-adjectives","content":"静かである \"is quiet\" 便利である \"is convenient\" 必要である \"is necessary\" 重要である \"is important\" 明らかである \"is clear\" Standard pattern Formal adjective endings Academic vocabulary"}]},{"level":2,"heading":"Emphasis Pattern","content":"","children":[{"level":3,"heading":"のである","content":"これが理由なのである \"This IS the reason\" (emphatic) 重要なのである \"It IS important\" (emphatic) そうなのである \"That IS so\" (emphatic) Strong assertion Emphatic explanation Written emphasis"}]},{"level":2,"heading":"Adjectival Clause","content":"","children":[{"level":3,"heading":"である as modifier","content":"学生である人 \"Person who is a student\" 重要である問題 \"Problem that is important\" 必要である条件 \"Condition that is necessary\" Modifying nouns Formal relative clauses Written style"}]},{"level":2,"heading":"Technical Writing","content":"","children":[{"level":3,"heading":"Scientific papers","content":"実験は成功である \"The experiment is a success\" 結果は有意である \"The result is significant\" 仮説は正しいと思われる \"The hypothesis is thought to be correct\" Scientific tone Objective reporting Technical precision"}]},{"level":2,"heading":"Legal Documents","content":"","children":[{"level":3,"heading":"Formal contracts","content":"契約は有効である \"The contract is valid\" 条件は以下である \"The conditions are as follows\" 義務である \"Is an obligation\" Legal language Binding statements Formal declarations"}]},{"level":2,"heading":"Historical Writing","content":"","children":[{"level":3,"heading":"Past events","content":"徳川家康は将軍であった \"Tokugawa Ieyasu was a shogun\" 江戸時代は平和であった \"The Edo period was peaceful\" Historical facts Formal narration Objective history"}]},{"level":2,"heading":"Textbook Style","content":"","children":[{"level":3,"heading":"Educational materials","content":"これは例である \"This is an example\" 重要なポイントである \"Is an important point\" 基本である \"Is fundamental\" Educational tone Clear assertions Instructional writing"}]},{"level":2,"heading":"Common Mistakes","content":"❌ Using in conversation である sounds stiff in speech ✓ Use です in conversation です for spoken politeness ❌ Mixing with です ✗ これは本である。それは便利です。 ✓ Stay consistent Choose one style throughout ❌ Overusing in casual writing Too formal for blogs, casual texts ✓ Match formality to context である for formal documents only ❌ Using with verbs directly ✗ 行くである ✓ Use の or other nominalizers ✓ 行くのである Understanding context crucial Written vs spoken distinction Formality awareness needed"},{"level":2,"heading":"Practical Examples","content":"","children":[{"level":3,"heading":"Academic writing","content":"この理論は重要である \"This theory is important\" 研究の目的は明らかである \"The research objective is clear\" 結果は以下の通りである \"The results are as follows\" 方法は適切であると考えられる \"The method is considered appropriate\" Scholarly tone Objective assertions Formal reasoning"},{"level":3,"heading":"Newspaper articles","content":"政府の対応は不十分である \"The government''s response is insufficient\" 経済状況は深刻である \"The economic situation is serious\" 原因は調査中である \"The cause is under investigation\" Journalistic style Factual reporting Objective tone"},{"level":3,"heading":"Essays","content":"教育制度の改革が必要である \"Educational system reform is necessary\" この問題は複雑であり、簡単な解決策はない \"This problem is complex, and there''s no simple solution\" 社会の変化は避けられないものである \"Social change is unavoidable\" Argumentative writing Formal opinion Thesis development Academic discourse Logical presentation"}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'degree-adverbs',
  'Degree Adverbs (とても、少し)',
  'https://practice-japanese.com/docs/degree-adverbs/',
  'verb-conjugation',
  NULL,
  '[{"level":2,"heading":"Degree Adverbs","content":"Degree adverbs express the intensity, extent, or amount of an action or quality. They answer \"how much?\" or \"to what degree?\" and modify adjectives, verbs, and other adverbs to show strength or weakness of description.\n\nとても美味しい \"Very delicious\" 少し疲れた \"A little tired\" すごく寒い \"Extremely cold\""},{"level":2,"heading":"High Degree Adverbs","content":"","children":[{"level":3,"heading":"とても (Very)","content":"とても美味しい \"Very delicious\" とても忙しい \"Very busy\" とても好き \"Like very much\" Standard \"very\" Polite, neutral"},{"level":3,"heading":"すごく (Extremely/Really)","content":"すごく美味しい \"Really delicious\" すごく楽しい \"Extremely fun\" すごく高い \"Really expensive\" Stronger than とても More casual"},{"level":3,"heading":"非常に (Extremely - Formal)","content":"非常に重要だ \"Extremely important\" 非常に危険だ \"Extremely dangerous\" Formal contexts Written language"},{"level":3,"heading":"かなり (Quite/Considerably)","content":"かなり難しい \"Quite difficult\" かなり疲れた \"Considerably tired\" かなり寒い \"Quite cold\" Significant degree"},{"level":3,"heading":"めちゃくちゃ/めっちゃ (Super - Casual)","content":"めっちゃ美味しい \"Super delicious\" めちゃくちゃ楽しい \"Super fun\" Very casual Young people Kansai origin"},{"level":3,"heading":"本当に (Really/Truly)","content":"本当に美味しい \"Really delicious\" 本当に嬉しい \"Truly happy\" 本当に分からない \"Really don''t understand\" Emphasizes truth"},{"level":3,"heading":"大変 (Very/Extremely)","content":"大変難しい \"Very difficult\" 大変お世話になりました \"Thank you very much\" (formal) Formal emphasis Trouble/difficulty nuance"}]},{"level":2,"heading":"Medium Degree Adverbs","content":"","children":[{"level":3,"heading":"まあまあ (So-so/Fairly)","content":"まあまあ面白い \"Fairly interesting\" まあまあ美味しい \"So-so delicious\" Medium satisfaction Not great, not bad"},{"level":3,"heading":"結構 (Fairly/Quite)","content":"結構大きい \"Fairly big\" 結構好き \"Quite like\" 結構難しい \"Fairly difficult\" More than expected Considerable amount"},{"level":3,"heading":"わりと/割と (Relatively/Fairly)","content":"わりと簡単 \"Relatively easy\" 割と近い \"Fairly close\" Compared to expectation Moderate degree"}]},{"level":2,"heading":"Low Degree Adverbs","content":"","children":[{"level":3,"heading":"少し/ちょっと (A little/Slightly)","content":"少し疲れた \"A little tired\" ちょっと寒い \"Slightly cold\" 少し待って \"Wait a little\" Small degree Softens statements"},{"level":3,"heading":"あまり〜ない (Not very - with negative)","content":"あまり美味しくない \"Not very delicious\" あまり好きじゃない \"Don''t like very much\" あまり分からない \"Don''t understand well\" With negative only Mild negation"},{"level":3,"heading":"ほとんど〜ない (Hardly/Almost not)","content":"ほとんど分からない \"Hardly understand\" ほとんど食べない \"Hardly eat\" Very little Near zero"},{"level":3,"heading":"全然〜ない (Not at all)","content":"全然分からない \"Don''t understand at all\" 全然好きじゃない \"Don''t like at all\" 全然面白くない \"Not interesting at all\" Complete negation Absolute zero"}]},{"level":2,"heading":"Comparison Adverbs","content":"","children":[{"level":3,"heading":"もっと (More)","content":"もっと速く \"More fast / Faster\" もっと大きい \"Bigger\" もっと欲しい \"Want more\" Increased degree Comparison"},{"level":3,"heading":"ずっと (Much more/Far)","content":"ずっと美味しい \"Much more delicious\" ずっと大きい \"Much bigger\" ずっと前 \"Much earlier/Long ago\" Significant difference"},{"level":3,"heading":"さらに (Even more/Furthermore)","content":"さらに難しい \"Even more difficult\" さらに良い \"Even better\" Formal addition Incremental increase"}]},{"level":2,"heading":"Emphasis Adverbs","content":"","children":[{"level":3,"heading":"本当に (Really/Truly)","content":"本当に美味しい \"Really delicious\" 本当にありがとう \"Thank you so much\" Genuine emphasis"},{"level":3,"heading":"実に (Really/Indeed - Formal)","content":"実に素晴らしい \"Really wonderful\" 実に興味深い \"Indeed interesting\" Formal emphasis"},{"level":3,"heading":"まったく (Completely/Totally)","content":"まったく同じ \"Exactly the same\" まったく分からない \"Don''t understand at all\" Complete degree"}]},{"level":2,"heading":"Slang Degree Adverbs","content":"","children":[{"level":3,"heading":"ちょー/超 (Super - Casual)","content":"ちょー楽しい \"Super fun\" 超美味しい \"Super delicious\" Young speech Very casual"},{"level":3,"heading":"めっちゃ (Very - Casual)","content":"めっちゃ嬉しい \"So happy\" めっちゃ好き \"Like so much\" Kansai influence Common in youth speech"}]},{"level":2,"heading":"With Different Parts of Speech","content":"","children":[{"level":3,"heading":"Modifying adjectives","content":"とても大きい \"Very big\" すごく美味しい \"Really delicious\" Most common usage"},{"level":3,"heading":"Modifying verbs","content":"とても好き \"Like very much\" すごく疲れた \"Got really tired\" Degree of action/state"},{"level":3,"heading":"Modifying adverbs","content":"とても速く走る \"Run very fast\" すごくよく分かる \"Understand really well\" Layered modification"}]},{"level":2,"heading":"Negative Degree Expressions","content":"","children":[{"level":3,"heading":"With ない forms","content":"あまり〜ない \"not very\" 全然〜ない \"not at all\" ほとんど〜ない \"hardly\" ちっとも〜ない \"not at all\" さっぱり〜ない \"not at all\" 全然分からない \"Don''t understand at all\" Must use negative"}]},{"level":2,"heading":"Position in Sentence","content":"","children":[{"level":3,"heading":"Before what it modifies","content":"とても美味しい料理 \"Very delicious food\" すごく速く走る \"Run really fast\" Precedes modified element"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"〜すぎる (Too much)","content":"食べすぎる \"Eat too much\" 高すぎる \"Too expensive\" Excessive degree"}]},{"level":2,"heading":"Formal vs Casual Scale","content":"Formal: 非常に > 大変 > とても > かなり Casual: めちゃくちゃ > めっちゃ > すごく > とても Choose by context"},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Using あまり with positive","content":"Wrong: あまり美味しい ⚠️ Correct: あまり美味しくない ✓ \"Not very delicious\" Requires negative"},{"level":3,"heading":"Overusing とても","content":"Repetitive: とても、とても、とても ⚠️ Better: Vary with すごく, 本当に ✓ Vary your vocabulary"}]},{"level":2,"heading":"Subtle Differences","content":"","children":[{"level":3,"heading":"とても vs すごく","content":"とても: Neutral, polite \"Very delicious\" すごく: More casual, stronger \"Really delicious\" Formality level differs"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"とても美味しい \"Very delicious\" 少し疲れた \"A little tired\" すごく楽しい \"Really fun\" かなり難しい \"Quite difficult\" 全然分からない \"Don''t understand at all\" もっと欲しい \"Want more\""},{"level":3,"heading":"Intermediate","content":"この料理はとても美味しいです。 少し辛いですが、すごく 気に入りました。 \"This dish is very delicious. It''s a little spicy, but I really like it.\" 昨日のテストはかなり難しかった。 全然分からない問題もあった。 もっと勉強すればよかった。 \"Yesterday''s test was quite difficult. There were also problems I didn''t understand at all. I should have studied more.\" 彼女は日本語がとても上手です。 本当によく勉強しています。 私もずっと頑張らなければ ならない。 \"She''s very good at Japanese. She really studies well. I also must work much harder.\" この本は少し難しいですが、 非常に面白いです。時間を かけてゆっくり読んでいます。 \"This book is a little difficult, but extremely interesting. I''m taking time to read it slowly.\""}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'demo-shikashi',
  'でも/しかし (But/However)',
  'https://practice-japanese.com/docs/demo-shikashi/',
  'other',
  NULL,
  '[{"level":2,"heading":"What are でも and しかし?","content":"でも and しかし both mean \"but\" or \"however,\" introducing contrast or contradiction between sentences. でも is casual and conversational, while しかし is formal and literary. They connect two opposing or contrasting ideas.\n\n疲れた。でも頑張る。 \"I''m tired. But I''ll do my best.\" 雨だった。しかし出かけた。 \"It was raining. However, I went out.\""},{"level":2,"heading":"Basic Usage","content":"","children":[{"level":3,"heading":"でも (casual \"but\")","content":"高い。でも買う。 \"It''s expensive. But I''ll buy it.\" 難しい。でもやる。 \"It''s difficult. But I''ll do it.\" Casual, spoken"},{"level":3,"heading":"しかし (formal \"however\")","content":"雨が降った。しかし試合は続いた。 \"It rained. However, the game continued.\" 努力した。しかし成功しなかった。 \"I tried hard. However, I didn''t succeed.\" Formal, written"}]},{"level":2,"heading":"Position in Sentence","content":"","children":[{"level":3,"heading":"Beginning of second sentence","content":"寒い。でも外に出る。 \"It''s cold. But I''ll go outside.\" After period New sentence starts"}]},{"level":2,"heading":"Polite and Casual","content":"","children":[{"level":3,"heading":"でも = casual","content":"忙しい。でも行く。 \"I''m busy. But I''ll go.\" Conversational Everyday speech"},{"level":3,"heading":"しかし = formal","content":"困難である。しかし諦めない。 \"It''s difficult. However, I won''t give up.\" Written language Formal speech"}]},{"level":2,"heading":"Common Contexts","content":"","children":[{"level":3,"heading":"でも in daily conversation","content":"疲れた。でもまだ仕事がある。 \"I''m tired. But I still have work.\" お腹空いた。でもダイエット中だ。 \"I''m hungry. But I''m on a diet.\" Everyday situations"},{"level":3,"heading":"しかし in formal writing","content":"計画は順調だった。しかし問題が発生した。 \"The plan was going smoothly. However, a problem occurred.\" 結果は良好だった。しかし課題も残った。 \"The results were good. However, challenges remained.\" Reports, essays"}]},{"level":2,"heading":"でも vs しかし Comparison","content":"","children":[{"level":3,"heading":"Register difference","content":"Casual: 難しい。でもやる。 \"It''s difficult. But I''ll do it.\" Formal: 難しい。しかしやる。 \"It''s difficult. However, I will do it.\" Same meaning, different tone"}]},{"level":2,"heading":"でも at Sentence Start","content":"","children":[{"level":3,"heading":"Can start conversation","content":"A: 今日は忙しい？ B: でも、ちょっとなら大丈夫。 A: \"Are you busy today?\" B: \"Well, but if it''s just a little, it''s okay.\" Softens response"}]},{"level":2,"heading":"Multiple でも/しかし","content":"","children":[{"level":3,"heading":"Can use repeatedly","content":"疲れた。でも頑張る。でも休みたい。 \"I''m tired. But I''ll try hard. But I want to rest.\" Multiple contrasts Can sound repetitive"}]},{"level":2,"heading":"Combining with Other Words","content":"","children":[{"level":3,"heading":"でもね (but you know)","content":"好きだよ。でもね、今は忙しい。 \"I like it. But you know, I''m busy now.\" Softening, casual"},{"level":3,"heading":"しかしながら (however - very formal)","content":"結果は良い。しかしながら改善の余地がある。 \"The results are good. However, there is room for improvement.\" Very formal/academic"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Using でも with formal writing","content":"Inappropriate: 研究は進んだ。でも問題がある。△ Better: 研究は進んだ。しかし問題がある。✓ \"Research progressed. However, there are problems.\" Match formality level"},{"level":3,"heading":"Using しかし in casual speech","content":"Unnatural: お腹空いた。しかし食べない。△ Natural: お腹空いた。でも食べない。✓ \"I''m hungry. But I won''t eat.\" Too stiff for conversation"}]},{"level":2,"heading":"でも as \"Even\"","content":"","children":[{"level":3,"heading":"Different meaning","content":"でも私は行く \"Even I will go\" (different でも) 誰でも \"Anyone\" (different でも) Context determines meaning"}]},{"level":2,"heading":"Alternatives","content":"","children":[{"level":3,"heading":"だけど/けど (casual)","content":"忙しい。だけど行く。 \"I''m busy. But I''ll go.\" More casual than でも"},{"level":3,"heading":"が (but - mid-sentence)","content":"忙しいが、行く。 \"I''m busy, but I''ll go.\" Within same sentence"}]},{"level":2,"heading":"Emphasis with でも","content":"","children":[{"level":3,"heading":"Strong contrast","content":"嫌いだ。でも食べる。 \"I hate it. But I''ll eat it.\" Emphasizes contradiction"}]},{"level":2,"heading":"Formal Writing Patterns","content":"","children":[{"level":3,"heading":"しかし in essays","content":"人口は増えた。しかし資源は限られている。 \"Population increased. However, resources are limited.\" Academic, analytical"}]},{"level":2,"heading":"Negative Contrasts","content":"","children":[{"level":3,"heading":"With negative sentences","content":"勉強しなかった。でもテストに合格した。 \"I didn''t study. But I passed the test.\" Unexpected outcome"}]},{"level":2,"heading":"Time-based Contrasts","content":"昨日は晴れた。でも今日は雨だ。 \"Yesterday was sunny. But today it''s raining.\" Temporal contrast"},{"level":2,"heading":"Degree Contrast","content":"少し高い。でも買う。 \"It''s a bit expensive. But I''ll buy it.\" とても高い。しかし必要だ。 \"It''s very expensive. However, it''s necessary.\" Comparing degrees"},{"level":2,"heading":"In Questions","content":"","children":[{"level":3,"heading":"Contrasting question","content":"A: 行く？ B: うーん...でも、疲れてる。 A: \"Will you go?\" B: \"Hmm... but I''m tired.\" Hesitant response"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"でも examples: 疲れた。でも頑張る。 \"I''m tired. But I''ll try hard.\" 高い。でも欲しい。 \"It''s expensive. But I want it.\" 難しい。でもやる。 \"It''s difficult. But I''ll do it.\" しかし examples: 雨だった。しかし出かけた。 \"It was raining. However, I went out.\" 努力した。しかし失敗した。 \"I tried hard. However, I failed.\""},{"level":3,"heading":"Intermediate","content":"昨日はとても忙しかった。でも今日は 少し時間がある。一緒に買い物に 行かない？ \"Yesterday was very busy. But today I have a little time. Want to go shopping together?\" この商品は高価である。しかし品質は 確かだ。長く使えることを考えれば、 妥当な価格と言える。 \"This product is expensive. However, the quality is certain. Considering it can be used for a long time, it can be said to be a reasonable price.\" 日本語の勉強は難しい。でも毎日 少しずつ続ければ、必ず上達する。 諦めないことが大切だ。 \"Studying Japanese is difficult. But if you continue little by little every day, you''ll definitely improve. Not giving up is important.\""}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'demonstratives',
  'Demonstratives (これ、それ、あれ)',
  'https://practice-japanese.com/docs/demonstratives/',
  'other',
  NULL,
  '[{"level":2,"heading":"Demonstratives (これ、それ、あれ)","content":"Demonstratives are words that point to things, meaning \"this,\" \"that,\" \"this one,\" \"that one.\" Japanese has three main demonstratives based on distance: これ (this), それ (that), and あれ (that over there). They form part of the ko-so-a-do system.\n\nこれは本です \"This is a book\" (near speaker) それは何ですか \"What is that?\" (near listener) あれは学校です \"That over there is a school\" (far from both)"},{"level":2,"heading":"Three-Way Distance System","content":"","children":[{"level":3,"heading":"これ (kore) - This","content":"これは何ですか \"What is this?\" これください \"This one, please\" Near speaker Within reach Close proximity"},{"level":3,"heading":"それ (sore) - That","content":"それは何ですか \"What is that?\" それをください \"That one, please\" Near listener Medium distance Close to you (listener)"},{"level":3,"heading":"あれ (are) - That over there","content":"あれは何ですか \"What is that over there?\" あれを見て \"Look at that\" Far from both Distant from speaker and listener Over there"}]},{"level":2,"heading":"Distance Guidelines","content":"","children":[{"level":3,"heading":"これ - Near me","content":"In my hand On my desk Right here Within my reach Close to speaker"},{"level":3,"heading":"それ - Near you","content":"In your hand On your desk Near listener You can reach it Close to listener"},{"level":3,"heading":"あれ - Far away","content":"Across the room Outside Not near either person Pointing at distance Away from both"}]},{"level":2,"heading":"Basic Usage","content":"","children":[{"level":3,"heading":"Asking \"What is this/that?\"","content":"これは何ですか \"What is this?\" それは何ですか \"What is that?\" あれは何ですか \"What is that over there?\" Common question pattern"},{"level":3,"heading":"Identifying things","content":"これは本です \"This is a book\" それはペンです \"That is a pen\" あれは富士山です \"That over there is Mt. Fuji\" Identification"}]},{"level":2,"heading":"As Pronouns","content":"","children":[{"level":3,"heading":"Standing alone","content":"これ、それ、あれ are pronouns Stand alone as subjects/objects Don''t modify nouns directly これがいい \"This one is good\" それを買う \"Buy that one\""},{"level":3,"heading":"With particles","content":"これは \"this (topic)\" それが \"that (subject)\" あれを \"that over there (object)\" これの \"of this\" Work with all particles"}]},{"level":2,"heading":"これ/それ/あれ vs この/その/あの","content":"","children":[{"level":3,"heading":"これ/それ/あれ = Pronouns (stand alone)","content":"これは本です \"This is a book\" これを買う \"Buy this\" Stand-alone words Act as nouns"},{"level":3,"heading":"この/その/あの = Modifiers (before nouns)","content":"この本 \"This book\" その人 \"That person\" あの山 \"That mountain over there\" Modify nouns Come before nouns"}]},{"level":2,"heading":"Common Patterns","content":"","children":[{"level":3,"heading":"これは〜です (This is...)","content":"これは私の本です \"This is my book\" これは高いです \"This is expensive\" Identification pattern"},{"level":3,"heading":"これをください (This one, please)","content":"これをください \"This one, please\" それをください \"That one, please\" Shopping phrase"},{"level":3,"heading":"これでいい (This is fine)","content":"これでいいです \"This is fine\" それでいい？ \"Is that okay?\" Acceptance pattern"}]},{"level":2,"heading":"Pointing at Things","content":"","children":[{"level":3,"heading":"Physical objects","content":"これ見て \"Look at this\" それ取って \"Pass me that\" あれ何？ \"What''s that over there?\" Physical reference"}]},{"level":2,"heading":"Abstract Reference","content":"","children":[{"level":3,"heading":"Referring to situations/ideas","content":"それはいい考えだ \"That''s a good idea\" これは難しい問題だ \"This is a difficult problem\" Not physical objects Concepts, situations"},{"level":3,"heading":"この/その/あの件 (this/that matter)","content":"その件について \"About that matter\" あの件は解決した \"That matter was resolved\" Referring to situations"}]},{"level":2,"heading":"それで (And so/So then)","content":"","children":[{"level":3,"heading":"Connector meaning","content":"雨が降った。それで家にいた。 \"It rained. So I stayed home.\" それで？ \"And then? / So?\" Transition word Different from \"that\""}]},{"level":2,"heading":"Time Reference","content":"","children":[{"level":3,"heading":"Temporal demonstratives","content":"この前 \"the other day/before\" その時 \"at that time\" あの時 \"at that time (past)\" Time periods Not physical distance"}]},{"level":2,"heading":"Question Word","content":"","children":[{"level":3,"heading":"どれ (dore) - Which one","content":"どれがいいですか \"Which one is good?\" どれを買う？ \"Which will you buy?\" Question form Asking for selection"}]},{"level":2,"heading":"Plural Forms","content":"","children":[{"level":3,"heading":"これら/それら/あれら (These/Those)","content":"これらは私のです \"These are mine\" それらは高い \"Those are expensive\" Plural demonstratives Formal/written"}]},{"level":2,"heading":"In Conversations","content":"","children":[{"level":3,"heading":"Natural dialogue","content":"A: これは何？ \"What''s this?\" B: それは辞書だよ \"That''s a dictionary\" Speaker''s これ = listener''s それ Perspective shift"}]},{"level":2,"heading":"With いい (Good/Okay)","content":"","children":[{"level":3,"heading":"これでいい pattern","content":"これでいい？ \"Is this okay?\" それでいいです \"That''s fine\" Acceptance/approval Common phrase"}]},{"level":2,"heading":"Common Mistakes","content":"","children":[{"level":3,"heading":"Using これ/それ/あれ before nouns","content":"Wrong: これ本 ⚠️ Correct: この本 ✓ \"This book\" Wrong: それ人 ⚠️ Correct: その人 ✓ \"That person\" Use この/その/あの before nouns"},{"level":3,"heading":"Confusing それ and あれ","content":"それ: Near listener \"That (near you)\" あれ: Far from both \"That over there\" Distance matters"}]},{"level":2,"heading":"Shopping Context","content":"","children":[{"level":3,"heading":"Pointing at items","content":"これをください \"This one, please\" (near you/clerk) あれを見せてください \"Please show me that one\" (far away) それはいくらですか \"How much is that?\" (near clerk) Common shopping phrases"}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Basic","content":"これは何ですか \"What is this?\" それはペンです \"That is a pen\" あれは富士山です \"That over there is Mt. Fuji\" これをください \"This one, please\" どれがいいですか \"Which one is good?\""},{"level":3,"heading":"Intermediate","content":"A: これは何ですか。 B: それは日本語の辞書です。 A: \"What is this?\" B: \"That''s a Japanese dictionary.\" これとそれ、どちらがいいですか。 \"Which is better, this or that?\" あれを見てください。あれが 私の学校です。 \"Please look at that. That over there is my school.\" この本とその本はどう違いますか。 これは簡単で、それは難しいです。 \"How do this book and that book differ? This one is easy, and that one is difficult.\""}]}]'::jsonb,
  '[]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
  category = EXCLUDED.category,
  jlpt_level = EXCLUDED.jlpt_level,
  sections = EXCLUDED.sections,
  examples = EXCLUDED.examples,
  updated_at = NOW();

INSERT INTO grammar_topics (slug, title, source_url, category, jlpt_level, sections, examples)
VALUES (
  'desu-ka-vs-darou-ka',
  'ですか vs だろうか',
  'https://practice-japanese.com/docs/desu-ka-vs-darou-ka/',
  'other',
  NULL,
  '[{"level":2,"heading":"Overview","content":"ですか and だろうか are both used to ask questions, but they have very different nuances and uses:\n\nですか - Direct questions asking others だろうか - Wondering, rhetorical questions, self-questioning\n\n明日は雨ですか？ \"Will it rain tomorrow?\" (asking someone) 明日は雨だろうか？ \"I wonder if it will rain tomorrow?\" (pondering)"},{"level":2,"heading":"ですか - Direct Questions","content":"","children":[{"level":3,"heading":"Basic Usage","content":"Used when directly asking someone for information:\n\nこれは何ですか？ \"What is this?\" どこに行きますか？ \"Where are you going?\" わかりますか？ \"Do you understand?\" いくらですか？ \"How much is it?\""},{"level":3,"heading":"Characteristics","content":"Expects an answer from the listener Polite and direct Used in conversations The asker genuinely doesn''t know"},{"level":3,"heading":"Common Situations","content":"お名前は何ですか？ \"What is your name?\" これでいいですか？ \"Is this okay?\" 何時に始まりますか？ \"What time does it start?\" どうしましたか？ \"What happened?\""}]},{"level":2,"heading":"だろうか - Wondering/Pondering","content":"","children":[{"level":3,"heading":"Basic Usage","content":"Used when wondering about something, often without expecting an answer:\n\n彼は来るだろうか？ \"I wonder if he''ll come?\" これで大丈夫だろうか？ \"I wonder if this is okay?\" 本当だろうか？ \"I wonder if it''s true?\" どうすればいいだろうか？ \"I wonder what I should do?\""},{"level":3,"heading":"Characteristics","content":"Self-questioning or rhetorical More literary or formal Often used in writing or narration Expresses doubt or uncertainty May not expect an answer"}]},{"level":2,"heading":"Detailed Comparison","content":"","children":[{"level":3,"heading":"ですか - Seeking Information","content":"明日の天気はどうですか？ \"How''s tomorrow''s weather?\" (asking someone) ↓ Expects: \"晴れです\" or \"雨です\""},{"level":3,"heading":"だろうか - Wondering","content":"明日の天気はどうだろうか？ \"I wonder how tomorrow''s weather will be?\" ↓ Not necessarily expecting an answer"}]},{"level":2,"heading":"でしょうか - Middle Ground","content":"でしょうか is more polite than だろうか and can be used in conversation:\n\nこれでいいでしょうか？ \"Would this be alright?\" (polite inquiry) 雨が降るでしょうか？ \"Do you think it will rain?\" (polite wondering) 大丈夫でしょうか？ \"Will it be okay?\" (concerned question)","children":[{"level":3,"heading":"Usage Context","content":"More polite than ですか Can be used when asking someone Shows uncertainty or seeking reassurance Common in business/formal settings"}]},{"level":2,"heading":"だろうか Patterns","content":"","children":[{"level":3,"heading":"In Narration/Writing","content":"Common in essays, novels, and formal writing:\n\n人生とは何だろうか？ \"What is life?\" これが正しい答えだろうか？ \"Is this the correct answer?\" 彼の言葉は本当だろうか？ \"Are his words true?\""},{"level":3,"heading":"Internal Monologue","content":"A: (考えている) \"Thinking...\" 明日までに終わるだろうか... \"I wonder if I''ll finish by tomorrow...\" 彼女は怒っているだろうか... \"I wonder if she''s angry...\""},{"level":3,"heading":"Rhetorical Questions","content":"これが愛と言えるだろうか？ \"Can this be called love?\" 本当にそれでいいのだろうか？ \"Is that really okay?\" 誰がこんなことをしたのだろうか？ \"Who could have done such a thing?\""}]},{"level":2,"heading":"Politeness Levels","content":"","children":[{"level":3,"heading":"Most Formal","content":"〜でございますか？ \"Is it...?\" (very formal) よろしいでしょうか？ \"Would it be alright?\" (very polite)"},{"level":3,"heading":"Standard Polite","content":"〜ですか？ \"Is it...?\" 〜でしょうか？ \"Is it...?\" (polite wondering)"},{"level":3,"heading":"Neutral/Literary","content":"〜だろうか？ \"I wonder if...?\" 〜であろうか？ \"Could it be...?\" (very formal/literary)"},{"level":3,"heading":"Casual","content":"〜かな？ \"I wonder...?\" 〜だろう？ \"Probably...?\" / \"Right?\""}]},{"level":2,"heading":"Common Mistake Patterns","content":"","children":[{"level":3,"heading":"Mistake 1: Using だろうか to ask someone directly","content":"Awkward: 名前は何だろうか？ (to someone) ⚠️ Better: お名前は何ですか？ ✓ \"What is your name?\" だろうか sounds like you''re pondering, not asking!"},{"level":3,"heading":"Mistake 2: Using ですか for internal thoughts","content":"Unnatural: (心の中で) 大丈夫ですか？ ⚠️ Better: (心の中で) 大丈夫だろうか？ ✓ \"I wonder if it''s okay?\" (thinking to oneself)"},{"level":3,"heading":"Mistake 3: Wrong politeness level","content":"Too casual: 先生、これでいいだろう？ ❌ Better: 先生、これでいいでしょうか？ ✓ \"Teacher, would this be alright?\""}]},{"level":2,"heading":"Context Determines Meaning","content":"","children":[{"level":3,"heading":"だろうか Can Be a Real Question","content":"In formal speeches or presentations:\n\nこれは正しいと言えるだろうか？ \"Can we say this is correct?\" (to audience) 我々はどうすべきだろうか？ \"What should we do?\" (to group)"},{"level":3,"heading":"ですか Can Express Surprise","content":"With certain intonation:\n\n本当ですか？！ \"Really?!\" (surprised) そうですか... \"Is that so...\" (skeptical)"}]},{"level":2,"heading":"Related Expressions","content":"","children":[{"level":3,"heading":"かな (casual wondering)","content":"大丈夫かな？ \"I wonder if it''s okay?\" 来るかな？ \"I wonder if they''ll come?\" できるかな？ \"I wonder if I can do it?\""},{"level":3,"heading":"かしら (feminine, old-fashioned)","content":"本当かしら？ \"I wonder if it''s true?\" どうしたのかしら？ \"I wonder what happened?\""},{"level":3,"heading":"のだろうか (explanatory wondering)","content":"なぜ来なかったのだろうか？ \"Why didn''t they come, I wonder?\" どうなるのだろうか？ \"What will happen, I wonder?\""}]},{"level":2,"heading":"Common Conversations","content":"","children":[{"level":3,"heading":"Example 1: Direct Question (ですか)","content":"A: すみません、これはいくらですか？ \"Excuse me, how much is this?\" B: 2000円です \"It''s 2000 yen\" A: カードは使えますか？ \"Can I use a card?\" B: はい、使えます \"Yes, you can\""},{"level":3,"heading":"Example 2: Wondering (だろうか)","content":"(独り言) \"Thinking to oneself\" 明日は晴れるだろうか... \"I wonder if it will be sunny tomorrow...\" 試験に合格できるだろうか... \"I wonder if I can pass the exam...\" 彼は来るだろうか... \"I wonder if he''ll come...\""},{"level":3,"heading":"Example 3: Polite Inquiry (でしょうか)","content":"A: こちらでよろしいでしょうか？ \"Would this be alright?\" B: はい、大丈夫です \"Yes, that''s fine\" A: 明日までに仕上がるでしょうか？ \"Will it be finished by tomorrow?\" B: はい、間に合います \"Yes, we''ll make it in time\""}]},{"level":2,"heading":"In Different Contexts","content":"","children":[{"level":3,"heading":"Conversation (Use ですか)","content":"今日は何曜日ですか？ \"What day is today?\" 駅はどこですか？ \"Where is the station?\" この本は面白いですか？ \"Is this book interesting?\""},{"level":3,"heading":"Writing/Narration (Use だろうか)","content":"人類の未来はどうなるだろうか？ \"What will become of humanity''s future?\" これが最後の機会だろうか？ \"Is this the last chance?\" 彼の決断は正しかっただろうか？ \"Was his decision correct?\""},{"level":3,"heading":"Business Email (Use でしょうか)","content":"こちらの提案でよろしいでしょうか？ \"Would this proposal be acceptable?\" ご都合はいかがでしょうか？ \"How would this be convenient for you?\" 明日お伺いしてもよろしいでしょうか？ \"Would it be alright to visit tomorrow?\""}]},{"level":2,"heading":"Tense Usage","content":"","children":[{"level":3,"heading":"Present/Future","content":"ですか: 明日来ますか？ \"Will you come tomorrow?\" だろうか: 明日来るだろうか？ \"I wonder if they''ll come tomorrow?\""},{"level":3,"heading":"Past","content":"ですか: 昨日行きましたか？ \"Did you go yesterday?\" だろうか: 昨日行っただろうか？ \"I wonder if they went yesterday?\""}]},{"level":2,"heading":"With Question Words","content":"","children":[{"level":3,"heading":"ですか (asking someone)","content":"誰ですか？ \"Who is it?\" 何ですか？ \"What is it?\" どこですか？ \"Where is it?\" いつですか？ \"When is it?\""},{"level":3,"heading":"だろうか (wondering)","content":"誰だろうか？ \"I wonder who it is?\" 何だろうか？ \"I wonder what it is?\" どこだろうか？ \"I wonder where it is?\" いつだろうか？ \"I wonder when it is?\""}]},{"level":2,"heading":"Emphasis and Certainty","content":"","children":[{"level":3,"heading":"ですか - Neutral Question","content":"本当ですか？ \"Is it true?\" 間違っていますか？ \"Is it wrong?\""},{"level":3,"heading":"だろうか - Expressing Doubt","content":"本当だろうか？ \"I wonder if it''s true?\" (doubtful) 間違っているだろうか？ \"Could I be wrong?\" (uncertain)"},{"level":3,"heading":"でしょうか - Polite Uncertainty","content":"本当でしょうか？ \"Could it be true?\" (polite doubt) 間違っているでしょうか？ \"Might I be mistaken?\" (humble)"}]},{"level":2,"heading":"Literary and Formal Usage","content":"","children":[{"level":3,"heading":"Essay Writing (だろうか)","content":"この問題をどう解決すべきだろうか？ \"How should we solve this problem?\" 教育の真の目的とは何だろうか？ \"What is the true purpose of education?\" 現代社会における幸福とは何を意味するだろうか？ \"What does happiness mean in modern society?\""},{"level":3,"heading":"Academic Writing (であろうか)","content":"Very formal, literary:\n\nこれは正しい解釈と言えるであろうか？ \"Could this be called a correct interpretation?\" このデータは信頼できるものであろうか？ \"Could this data be considered reliable?\""}]},{"level":2,"heading":"Casual Alternatives","content":"","children":[{"level":3,"heading":"かな - Most Common Casual","content":"大丈夫かな？ \"I wonder if it''s okay?\" できるかな？ \"I wonder if I can do it?\" 来るかな？ \"I wonder if they''ll come?\"\n\nGender-neutral, widely used."},{"level":3,"heading":"だろう - Casual Wondering","content":"本当だろう？ \"It''s true, right?\" 行くだろう？ \"You''ll go, right?\" 知ってるだろう？ \"You know, right?\"\n\nMore assertive than かな."},{"level":3,"heading":"かしら - Feminine, Old-fashioned","content":"大丈夫かしら？ \"I wonder if it''s okay?\" (feminine) 来るかしら？ \"I wonder if they''ll come?\" (feminine)\n\nLess common in modern speech."}]},{"level":2,"heading":"Negative Forms","content":"","children":[{"level":3,"heading":"ですか (Negative Questions)","content":"行かないですか？ \"Won''t you go?\" 食べませんか？ \"Won''t you eat?\" わからないですか？ \"Don''t you understand?\""},{"level":3,"heading":"だろうか (Negative Wondering)","content":"行かないだろうか？ \"I wonder if they won''t go?\" わからないだろうか？ \"Could they not understand?\" 間に合わないだろうか？ \"I wonder if we won''t make it in time?\""}]},{"level":2,"heading":"Special Patterns","content":"","children":[{"level":3,"heading":"〜だろうか、いや〜だろう","content":"Answering one''s own question:\n\n彼は来るだろうか？いや、来ないだろう \"Will he come? No, he probably won''t\" これで大丈夫だろうか？いや、もっと必要だろう \"Is this enough? No, probably need more\""},{"level":3,"heading":"〜ではないだろうか (suggestion)","content":"Suggesting something tentatively:\n\nこれが原因ではないだろうか？ \"Couldn''t this be the cause?\" もっと良い方法があるのではないだろうか？ \"Might there be a better method?\""},{"level":3,"heading":"〜だろうか、〜だろうか (multiple wonderings)","content":"彼は来るだろうか？来ないだろうか？ \"Will he come? Won''t he come?\" どうしようか？行こうか？やめようか？ \"What should I do? Should I go? Should I stop?\""}]},{"level":2,"heading":"Usage by Context","content":"","children":[{"level":3,"heading":"Daily Conversation → ですか","content":"今何時ですか？ \"What time is it now?\" ご飯食べましたか？ \"Did you eat?\" 疲れていますか？ \"Are you tired?\""},{"level":3,"heading":"Internal Thoughts → だろうか/かな","content":"(心の中で) どうしようかな... \"What should I do...\" 大丈夫だろうか... \"I wonder if it''s okay...\" 間に合うかな... \"I wonder if I''ll make it...\""},{"level":3,"heading":"Business/Formal → でしょうか","content":"ご確認いただけますでしょうか？ \"Could you please confirm?\" こちらでお間違いないでしょうか？ \"Is this correct?\" ご都合はいかがでしょうか？ \"How is your schedule?\""}]},{"level":2,"heading":"Comparison Chart","content":"| Pattern | Formality | Usage | Expects Answer | | ---------- | --------------- | ---------------- | -------------- | | ですか | Polite | Direct questions | Yes | | でしょうか | Very polite | Polite inquiry | Usually yes | | だろうか | Formal/Literary | Wondering | Usually no | | だろう | Casual | Confirmation | Sometimes | | かな | Casual | Wondering | No | | かしら | Feminine | Wondering | No |"},{"level":2,"heading":"Common Expressions","content":"","children":[{"level":3,"heading":"With ですか","content":"そうですか \"Is that so?\" 本当ですか？ \"Really?\" 大丈夫ですか？ \"Are you okay?\" いいですか？ \"Is it okay?\""},{"level":3,"heading":"With だろうか","content":"そうだろうか \"I wonder if that''s so\" 本当だろうか \"I wonder if it''s true\" どうなるだろうか \"I wonder what will happen\" 何ができるだろうか \"What can be done, I wonder\""},{"level":3,"heading":"With でしょうか","content":"そうでしょうか \"Do you think so?\" 本当でしょうか \"Could it be true?\" よろしいでしょうか \"Would it be alright?\" 大丈夫でしょうか \"Will it be okay?\""}]},{"level":2,"heading":"Sentence Position","content":"","children":[{"level":3,"heading":"ですか - End of Sentence Only","content":"これは何ですか？ ✓ \"What is this?\" これは、ですか、何？ ❌"},{"level":3,"heading":"だろうか - Can Appear Mid-Sentence","content":"In complex sentences:\n\n彼が来るだろうか、来ないだろうか、それが問題だ \"Whether he''ll come or not, that''s the question\" これで良いだろうかと考えている \"I''m thinking whether this is good\""}]},{"level":2,"heading":"Practice Sentences","content":"","children":[{"level":3,"heading":"Direct Questions (ですか)","content":"お名前は何ですか？ \"What is your name?\" どこから来ましたか？ \"Where did you come from?\" 日本語が話せますか？ \"Can you speak Japanese?\" これはいくらですか？ \"How much is this?\""},{"level":3,"heading":"Wondering (だろうか)","content":"明日は晴れるだろうか？ \"I wonder if it will be sunny tomorrow?\" これで間に合うだろうか？ \"I wonder if this will be enough?\" 彼は何を考えているのだろうか？ \"What could he be thinking?\" 人生とは何だろうか？ \"What is life?\""},{"level":3,"heading":"Polite Inquiry (でしょうか)","content":"こちらでよろしいでしょうか？ \"Would this be alright?\" お時間はございますでしょうか？ \"Do you have time?\" もう一度説明していただけますでしょうか？ \"Could you explain once more?\" 来週でも大丈夫でしょうか？ \"Would next week be okay?\""},{"level":3,"heading":"Casual Wondering (かな)","content":"明日晴れるかな？ \"I wonder if it''ll be sunny tomorrow?\" できるかな？ \"I wonder if I can do it?\" 誰かな？ \"I wonder who it is?\" どうしようかな？ \"What should I do?\""}]},{"level":2,"heading":"Summary","content":"","children":[{"level":3,"heading":"Use ですか when:","content":"Asking someone directly Having a conversation Expecting an answer Being polite and clear"},{"level":3,"heading":"Use だろうか when:","content":"Wondering to yourself Writing essays or narratives Asking rhetorical questions Being literary or philosophical"},{"level":3,"heading":"Use でしょうか when:","content":"Being very polite In business situations Showing uncertainty politely Seeking reassurance"},{"level":3,"heading":"Use かな when:","content":"Speaking casually Wondering out loud Talking to friends Not expecting an answer"}]},{"level":2,"heading":"Related Grammar Points","content":"Yes/No Questions - Basic question formation か (ka) Question Usage - Question particle の (no) Questions - Casual questions でしょう expressions - Probability and politeness だろう casual form - Casual probability かもしれない - Expressing possibility Question intonation - Rising pitch patterns Rhetorical questions - Questions not expecting answers","children":[]}]'::jsonb,
  '["ですか - Direct questions asking others","だろうか - Wondering, rhetorical questions, self-questioning","Expects an answer from the listener","Polite and direct","Used in conversations","The asker genuinely doesn''t know","Self-questioning or rhetorical","More literary or formal","Often used in writing or narration","Expresses doubt or uncertainty","May not expect an answer","More polite than ですか","Can be used when asking someone","Shows uncertainty or seeking reassurance","Common in business/formal settings","Asking someone directly","Having a conversation","Expecting an answer","Being polite and clear","Wondering to yourself","Writing essays or narratives","Asking rhetorical questions","Being literary or philosophical","Being very polite","In business situations","Showing uncertainty politely","Seeking reassurance","Speaking casually","Wondering out loud","Talking to friends","Not expecting an answer","Yes/No Questions - Basic question formation","か (ka) Question Usage - Question particle","の (no) Questions - Casual questions","でしょう expressions - Probability and politeness","だろう casual form - Casual probability","かもしれない - Expressing possibility","Question intonation - Rising pitch patterns","Rhetorical questions - Questions not expecting answers"]'::jsonb
) ON CONFLICT (slug) DO UPDATE SET
  title = EXCLUDED.title,
  source_url = EXCLUDED.source_url,
